static void view2() {
struct bg2 poems[] = {
	{1, "1 На стражата си ще застана и на кулата ще се изправя и ще се взирам, за да видя какво ще ми говори Той и какво да отговоря на укора си."},
	{2, "2 И ГОСПОД ми отговори и каза: Напиши видението и го изложи ясно на плочи, за да може да се чете лесно."},
	{3, "3 Защото видението се отнася за определено време, бърза към края и няма да излъже. Ако се бави, чакай го, защото непременно ще дойде, няма да закъснее."},
	{4, "4 Ето, душата му се надигна, не е права в него. А праведният чрез вярата си ще живее."},
	{5, "5 Освен това виното е коварно. Горделивият човек не стои мирен, той разширява желанието си като Шеол и като смъртта не се насища, и трупа при себе си всичките народи, и събира при себе си всичките племена."},
	{6, "6 Няма ли всички тези да надигнат притча против него, подигравателна песен в гатанки за него, като кажат: Горко на онзи, който умножава каквото не е негово — докога? — и се товари със залози!"},
	{7, "7 Няма ли да станат внезапно онези, които те хапят, и да се надигнат онези, които ще те разтреперят? И ти ще им станеш плячка."},
	{8, "8 Понеже ти обра много народи, всички останали от племената ще те оберат заради кръвта на хората и насилието над земята, над града и над всичките му жители."},
	{9, "9 Горко на онзи, който придобива със зло неправедна печалба за дома си, за да постави гнездото си нависоко, за да се избави от ръката на бедствието!"},
	{10, "10 Ти си замислил срам за дома си, като си изтребил много народи и си съгрешил против душата си."},
	{11, "11 Защото камък от стената ще извика и греда от гредореда ще му отговори."},
	{12, "12 Горко на онзи, който гради град с кръв и утвърждава град с беззаконие!"},
	{13, "13 Ето, не е ли от ГОСПОДА на Войнствата, че племената се трудят за огъня и народите се мъчат за суета?"},
	{14, "14 Защото земята ще бъде пълна с познаването на славата на ГОСПОДА, както водите покриват морето."},
	{15, "15 Горко на онзи, който напива ближния си — на теб, който примесваш злобата си и ги опиваш, за да гледаш голотата им!"},
	{16, "16 Наситил си се със срам вместо слава; пий и ти, и нека се открие краекожието ти! Чашата на десницата ГОСПОДНА ще се обърне към теб и гнусно безчестие ще покрие славата ти."},
	{17, "17 Защото насилието над Ливан ще те покрие и изтребването на уплашените животни заради кръвта на хората и насилието над земята, над града и всичките му жители."},
	{18, "18 Какво ползва изваяният идол, че го е изваял майсторът му? Или изленият идол, учител на лъжата, че който го е изработил, се уповава на изработката си, да прави неми идоли?"},
	{19, "19 Горко на онзи, който казва на дървото: Събуди се! — и на немия камък: Стани! Той ли ще го научи? Ето, той е обкован със злато и сребро и никакво дихание няма в него."},
	{20, "20 А ГОСПОД е в светия Си храм — мълчи пред Него, цяла земьо!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct bg3 poems[] = {
	{1, "1 Молитва на пророк Авакум, по шигионот."},
	{2, "2 ГОСПОДИ, чух вестта за Теб и се убоях. ГОСПОДИ, оживявай делото Си сред годините, сред годините го изявявай. В гнева Си помни милостта."},
	{3, "3 Бог идва от Теман и Светият — от хълма Фаран. (Села.) Славата Му покри небесата и земята се изпълни с прославата Му."},
	{4, "4 Сиянието Му е като светлината, лъчи излизат от ръката Му и там е скривалището на силата Му."},
	{5, "5 Пред Него ходи мор и язва излиза след Него."},
	{6, "6 Той застана и разклати земята, погледна и разтрепери народите; и вечните планини се разпаднаха, древните хълмове се приведоха. Пътищата Му са вечни."},
	{7, "7 Видях шатрите на Етиопия под скръб, треперят завесите на мадиамската земя."},
	{8, "8 Против реките ли се разяри ГОСПОД? Против реките ли беше гневът Ти, против морето ли беше яростта ти, че си възседнал на конете Си, на победоносните Си колесници?"},
	{9, "9 Открит, оголен е лъкът Ти, пръчките на наказанието са заклети според словото Ти. (Села.) Ти проряза земята с реки."},
	{10, "10 Видяха Те планините и се убояха, водният потоп нахлу, бездната нададе гласа си, вдигна нависоко ръцете си."},
	{11, "11 Слънцето и луната застанаха в жилището си при светлината на Твоите летящи стрели, при сиянието на Твоето святкащо копие."},
	{12, "12 С негодувание крачиш по земята, с гняв вършееш народите."},
	{13, "13 Излязъл си за спасение на народа Си, за спасение на помазаника Си. Разбил си главата от дома на безбожието, открил си основите до шията. (Села.)"},
	{14, "14 Пронизал си със собствените му копия главата на воините му, които като вихрушка се устремиха да ме разпръснат и радостта им беше като да изядат скришно сиромаха."},
	{15, "15 Преминал си през морето с конете Си, пенещите се големи води."},
	{16, "16 Чух, и вътрешностите ми се разтресоха, устните ми трепереха от гласа, гнилота проникна в костите ми и на мястото си се разтреперих, защото трябва да чакам скръбния ден, когато се изкачи против народа онзи, който ще ни нападне."},
	{17, "17 И да не цъфти смокинята, и да няма реколта по лозите, и да пропадне произведението на маслината, и нивите да не произведат храна, и овцете да изчезнат от кошарата, и да няма говеда в оборите,"},
	{18, "18 аз пак ще се радвам в ГОСПОДА, ще се веселя в Бога на спасението си."},
	{19, "19 Господ БОГ е силата ми; Той прави краката ми като на елените и ми дава да ходя по височините ми. За първия певец, на моите струнни инструменти."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
