#include <map>
#include <string>
class Bible61
{
	struct bg1	{ int val; const char *msg; };
	struct bg2	{ int val; const char *msg; };
	struct bg3	{ int val; const char *msg; };
public:
static void view1() {
struct bg1 poems[] = {
	{1, "1 Симон Петър, слуга и апостол на Иисус Христос, до онези, които чрез правдата на нашия Бог и Спасител Иисус Христос са получили еднаква с нас скъпоценна вяра:"},
	{2, "2 Благодат и мир да ви се умножи в познаването на Бога и на Иисус, нашия Господ!"},
	{3, "3 Понеже Неговата божествена сила ни е подарила всичко потребно за живота и за благочестието чрез познаването на Този, който ни е призовал чрез Своята слава и съвършенство,"},
	{4, "4 чрез които ни се подариха най-големи и скъпоценни обещания, за да станете чрез тях участници на божественото естество, като сте избягали от произлязлата от страстите поквара в света —"},
	{5, "5 точно затова, като положите всяко старание, прибавете на вярата си добродетел, на добродетелта – познание,"},
	{6, "6 на познанието – себеобуздание, на себеобузданието – търпение, на търпението – благочестие,"},
	{7, "7 на благочестието – братолюбие и на братолюбието – любов."},
	{8, "8 Защото, ако тези добродетели се намират у вас и се умножават, те ви правят да не сте безделни, нито безплодни в познаването на нашия Господ Иисус Христос."},
	{9, "9 Защото онзи, у когото те не се намират, е сляп, късоглед и е забравил, че е бил очистен от старите си грехове."},
	{10, "10 Затова, братя, постарайте се още повече да затвърдявате вашето призвание и избиране, защото, като вършите тези неща, никога няма да се спънете."},
	{11, "11 Понеже така изобилно ще ви се даде достъп във вечното царство на нашия Господ и Спасител Иисус Христос."},
	{12, "12 Затова винаги ще бъда готов да ви напомням за тези работи, ако и да ги знаете и да сте утвърдени в истината, която сега имате пред вас."},
	{13, "13 Мисля, че е правилно, докато съм в тази телесна хижа, да ви подтиквам чрез напомняне,"},
	{14, "14 понеже зная, че скоро ще напусна хижата си, както и ми извести нашият Господ Иисус Христос."},
	{15, "15 Даже ще се постарая, така че вие и след смъртта ми да можете винаги да си припомняте тези работи."},
	{16, "16 Защото, когато ви обявихме силата и пришествието на нашия Господ Иисус Христос, ние не следвахме хитро измислени басни, а бяхме очевидци на Неговото величие."},
	{17, "17 Защото Той прие от Бог Отец почест и слава, когато от великолепната Слава дойде до Него такъв глас: „Този е Моят възлюбен Син, в когото благоволих.“"},
	{18, "18 И ние чухме този глас да идва от небето, когато бяхме с Него на святата планина."},
	{19, "19 И така, ние държим пророческото слово още по-твърдо и вие добре правите, че внимавате на него като на светило, което свети в тъмно място, докато се зазори и зорницата изгрее в сърцата ви."},
	{20, "20 И това да знаете преди всичко, че никое пророчество в Писанието не става от собствено разяснение,"},
	{21, "21 защото никога не е идвало пророчество по волята на човека, а (светите) Божии хора са говорили, движени от Светия Дух."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct bg2 poems[] = {
	{1, "1 Но е имало също и лъжливи пророци между народа, както и между вас ще има лъжливи учители, които ще въведат тайно гибелни ереси, като се отричат даже от Господаря, който ги е купил, и ще навлекат на себе си бърза погибел."},
	{2, "2 И мнозина ще последват техните безчинства и заради тях пътят на истината ще се похули."},
	{3, "3 От алчност те ще ви използуват с престорени думи; но тяхната присъда, отдавна приготвена, не се забавя и тяхната погибел не дреме."},
	{4, "4 Защото, ако Бог не пощади и ангели, когато съгрешиха, а ги хвърли в мрачните ровове на ада и предаде да бъдат пазени за съд;"},
	{5, "5 и ако не пощади стария свят, но запази с още седем души Ной, проповедника на правдата, като докара потоп върху света на безбожните;"},
	{6, "6 така също, ако Той осъди на разорение градовете Содом и Гомора, като ги обърна на пепел и ги постави за пример на онези, които след това щяха да вършат нечестие,"},
	{7, "7 но избави праведния Лот, който се измъчваше поради развратния живот на безбожните –"},
	{8, "8 защото този праведник, като живееше между тях, от ден на ден измъчваше праведната си душа, като гледаше и слушаше беззаконните им дела –"},
	{9, "9 то знае Господ как да избави благочестивите от изкушения, а неправедните да държи под наказание за съдния ден,"},
	{10, "10 а особено тези, които следват плътта в нечисти похоти и презират властта. Дръзки и самонадеяни, те не се страхуват да хулят славните същества;"},
	{11, "11 докато ангелите, които са по-големи по мощ и сила, не представят против тях хулителна присъда пред Господа."},
	{12, "12 Тези обаче като диви животни, по природа създадени, за да бъдат хващани и изтребвани, хулят за неща, които не разбират, и ще погинат изцяло в своята поквара,"},
	{13, "13 като получат заплатата на неправдата. Те считат за удоволствие да разкошестват денем, те са петна и позор, които се наслаждават на измамите си, докато се гощават с вас,"},
	{14, "14 като очите им са пълни с прелюбодейство и с непрестанен грях, като подмамват неутвърдени души. Сърцето им е научено на алчност, те са деца на проклятие,"},
	{15, "15 които оставиха правия път и се отклониха, като последваха пътя на Валаам Веоров, който обикна заплатата на неправдата,"},
	{16, "16 но беше изобличен за своето беззаконие, когато нямо магаре проговори с човешки глас и възпря безумието на пророка."},
	{17, "17 Те са безводни кладенци, мъгли, тласкани от буря, за които е запазена тъмнината на мрака (до века)."},
	{18, "18 Защото, като говорят с надуто празнословие, те подмамват чрез плътски страсти и безнравственост онези, които едвам са избягали от живеещите в заблуда."},
	{19, "19 Обещават им свобода, докато те сами са роби на покварата; защото, от когото е победен някой, от него бива и поробен."},
	{20, "20 Защото, ако след като са избягали от мръсотиите на света чрез познаването на Господа и Спасителя Иисус Христос, те пак се заплитат в тях и са победени, то последното състояние е по-лошо за тях от първото."},
	{21, "21 Понеже за тях щеше да бъде по-добре да не бяха познали пътя на правдата, отколкото, след като са го познали, да се отвърнат от предадената им свята заповед."},
	{22, "22 С тях се е случило според истинската пословица: „Псето се върна на бълвоча си и окъпаната свиня се върна да се валя в тинята.“"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct bg3 poems[] = {
	{1, "1 Ето, възлюбени, пиша ви това второ писмо; и в тези двете събуждам чрез напомняне вашия чист разум,"},
	{2, "2 за да помните думите, изговорени по-напред от светите пророци, и заповедта на Господа и Спасителя, дадена чрез изпратените при вас апостоли."},
	{3, "3 Преди всичко знайте това, че в последните дни ще дойдат подиграватели с подигравки, които ще ходят по своите собствени страсти и ще казват:"},
	{4, "4 Къде е обещаното Му пришествие? Защото, откакто са се поминали бащите ни, всичко си стои така, както е било от началото на създанието."},
	{5, "5 Защото те своеволно не признават това, че чрез Божието слово от началото е имало небеса и земя, сплотена от водата и сред водата,"},
	{6, "6 но пак посредством нея тогавашният свят, потопен от водата, загина."},
	{7, "7 Така, със същото слово, са съхранени и днешните небе и земя, пазени за огън до деня на съда и погибелта на нечестивите хора."},
	{8, "8 Но това да не бъде скрито от вас, възлюбени, че за Господа един ден е като хиляда години и хиляда години – като един ден."},
	{9, "9 Господ не забавя това, което е обещал, както някои смятат това за забавяне, а дълго ни търпи; понеже не иска да погинат някои, а всички да дойдат на покаяние."},
	{10, "10 Но Господният Ден ще дойде като крадец, когато небето ще отмине с бучене и елементите нажежени ще се разпаднат, и земята и делата по нея ще изгорят."},
	{11, "11 И така, понеже всичко това ще се разпадне, какви трябва да сте вие в своя свят живот и благочестие,"},
	{12, "12 като очаквате и ускорявате идването на Божия Ден, за който небето възпламенено ще се разпадне и нажежените елементи ще се стопят?"},
	{13, "13 Но ние според обещанието Му очакваме нови небеса и нова земя, в които обитава правда."},
	{14, "14 Затова, възлюбени, като очаквате тези неща, старайте се да се намерите от Него в мир, чисти и непорочни."},
	{15, "15 И считайте дълготърпението на нашия Господ за спасение, както ви е писал и възлюбеният ни брат Павел според дадената му мъдрост,"},
	{16, "16 както пише и във всичките си писма, когато говори в тях за тези работи — в които има някои неща, мъчни за разбиране, които невежите и неутвърдените изопачават, както и другите Писания, за своя собствена гибел."},
	{17, "17 И така, вие, възлюбени, като знаете това предварително, се пазете, да не би да се завлечете от заблуждението на беззаконните и да отпаднете от твърдостта си."},
	{18, "18 Но растете в благодатта и познанието на нашия Господ и Спасител Иисус Христос. На Него да бъде слава и сега, и в деня на вечността. Амин."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};