struct cs1 poems[] = {
	{1, "1 Stalo se pak po smrti Saulově, když se navrátil David od porážky Amalechitských, že pobyl v Sicelechu za dva dni."},
	{2, "2 A aj, dne třetího přišel jeden z vojska Saulova, maje roucho roztržené a prach na hlavě své. Kterýž když přišel k Davidovi, padl na zemi a poklonil se."},
	{3, "3 I řekl jemu David: Odkud jdeš? Jemuž odpověděl: Z vojska Izraelského utekl jsem."},
	{4, "4 Opět řekl jemu David: Cože se stalo? Medle, pověz mi. Kterýž odpověděl: To, že utekl lid z boje, a množství lidu padlo a zbito jest; též i Saul i Jonata syn jeho zbiti jsou."},
	{5, "5 Řekl ještě David mládenci, kterýž mu to oznámil: Kterak ty víš, že umřel Saul i Jonata syn jeho?"},
	{6, "6 Odpověděl mládenec, kterýž to oznamoval jemu: Náhodou přišel jsem na horu Gelboe, a aj, Saul nalehl byl na kopí své, a vozové i jezdci postihali ho."},
	{7, "7 Kterýžto ohlédna se zpátkem, uzřel mne a zavolal na mne. I řekl jsem: Aj, teď jsem."},
	{8, "8 Tedy řekl mi: Kdo jsi ty? Odpověděl jsem jemu: Amalechitský jsem."},
	{9, "9 I řekl mi: Přistup medle sem a zabí mne, nebo mne obklíčila úzkost, a ještě všecka duše má jest ve mně."},
	{10, "10 Protož stoje nad ním, zabil jsem ho, nebo jsem věděl, že nebude živ po svém pádu. A vzal jsem korunu, kteráž byla na hlavě jeho, i záponu, kteráž byla na rameni jeho, a teď jsem to přinesl ku pánu svému."},
	{11, "11 Tedy David uchytiv roucho své, roztrhl je; tolikéž i všickni muži, kteříž s ním byli."},
	{12, "12 A nesouce smutek, plakali a postili se až do večera pro Saule a pro Jonatu syna jeho, i pro lid Hospodinův a pro dům Izraelský, že padli od meče."},
	{13, "13 Řekl pak David mládenci, kterýž mu to oznámil: Odkud jsi ty? Odpověděl: Syn muže příchozího Amalechitského jsem."},
	{14, "14 Opět mu řekl David: Kterak jsi směl vztáhnouti ruku svou, abys zahubil pomazaného Hospodinova?"},
	{15, "15 A zavolav David jednoho z mládenců, řekl jemu: Přistoupě, oboř se na něj. Kterýžto udeřil ho, tak že umřel."},
	{16, "16 I řekl jemu David: Krev tvá budiž na hlavu tvou, neboť jsou ústa tvá svědčila na tebe, řkouce: Já jsem zabil pomazaného Hospodinova."},
	{17, "17 Tedy naříkal David naříkáním tímto nad Saulem a nad Jonatou synem jeho,"},
	{18, "18 (Přikázav však, aby synové Judovi učeni byli stříleti z luku, jakož psáno v knize Upřímého.):"},
	{19, "19 Ó kráso Izraelská, na výsostech tvých zraněni, jakť jsou padli udatní!"},
	{20, "20 Neoznamujtež v Gát, ani toho ohlašujte na ulicích Aškalon, aby se neveselily dcery Filistinských, a neplésaly dcery neobřezaných."},
	{21, "21 Ó hory Gelboe, ani rosa, ani déšť nespadej na vás, ani tu buď pole úrodné; nebo tam jest povržen štít udatných, štít Saulův, jako by nebyl pomazán olejem."},
	{22, "22 Od krve raněných a od tuku udatných lučiště Jonatovo nikdy zpět neodskočilo, a meč Saulův nenavracoval se prázdný."},
	{23, "23 Saul a Jonata milí a utěšení v životě svém, také při smrti své nejsou rozloučeni. Nad orlice bystřejší, nad lvy silnější byli."},
	{24, "24 Dcery Izraelské, plačte Saule, kterýž vás odíval červcem dvakrát barveným rozkošně, kterýž dával ozdoby zlaté na roucha vaše."},
	{25, "25 Ach, jakť jsou padli udatní u prostřed boje? Jonata na výsostech tvých zabit jest."},
	{26, "26 Velice jsem po tobě teskliv, bratře můj Jonato. Byl jsi mi příjemný náramně; vzácnější u mne byla milost tvá nežli milost žen."},
	{27, "27 Ach, jakť jsou padli udatní, a zahynula odění válečná."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
