#include <string>
class Bible37
{
	struct cs1	{ int val; const char *msg; };
	struct cs2	{ int val; const char *msg; };
public:
static void view1() {
struct cs1 poems[] = {
	{1, "1 Léta druhého Daria krále, měsíce šestého, prvního dne téhož měsíce, stalo se slovo Hospodinovo skrze Aggea proroka k Zorobábelovi synu Salatielovu, knížeti Judskému, a k Jozue synu Jozadakovu, knězi nejvyššímu, řkoucí:"},
	{2, "2 Takto dí Hospodin zástupů, řka: Lid tento praví, že nepřišel čas, čas, v němž by dům Hospodinův staven byl."},
	{3, "3 Pročež stalo se slovo Hospodinovo skrze Aggea proroka, řkoucí:"},
	{4, "4 Jest-liž vám čas, abyste vy seděli v domích svých taflovaných, a dům tento pustý stál?"},
	{5, "5 Protož nyní takto praví Hospodin zástupů: Přiložte srdce své k cestám svým."},
	{6, "6 Sejete mnoho, a shromažďujete málo; jíte, ale nebýváte nasyceni; pijete, ale neuhašujete žízně; obláčíte se, ale žádný nemůže se zahříti; a ten, kdož sobě mzdu shromažďuje, shromažďuje ji do pytlíka děravého."},
	{7, "7 Takto praví Hospodin zástupů: Přiložte srdce své k cestám svým."},
	{8, "8 Vstupte na tuto horu, a svezouce dříví, stavějte dům tento, i oblíbím sobě to, a budu oslaven, praví Hospodin."},
	{9, "9 Patříte na mnoho, a aj, maličko jest, a což vnášíte do domu, já to rozdmychuji. Proč se to děje? dí Hospodin zástupů. I pro dům můj, že pustý jest, ješto vy jeden každý pečlivi jste o dům svůj."},
	{10, "10 Protož zavřelo se nebe nad vámi, aby nedávalo rosy, země také zavřela se, aby nevydávala úrody své."},
	{11, "11 A tak zavolal jsem sucha na zemi tuto, a na tyto hory, i na obilé, i na víno, a na olej, i na to, což by měla vydávati země, i na lidi, i na dobytek, i na všecku práci rukou."},
	{12, "12 I uposlechl Zorobábel syn Salatielův, a Jozue syn Jozadakův, kněz nejvyšší, i všickni ostatkové lidu hlasu Hospodina Boha svého, a slov Aggea proroka, poněvadž jej poslal Hospodin Bůh jejich; nebo se bál lid tváři Hospodinovy."},
	{13, "13 Tedy Aggeus posel Hospodinův mluvil lidu v poselství Hospodinovu, řka: Já s vámi jsem, praví Hospodin."},
	{14, "14 V tom vzbudil Hospodin ducha Zorobábele syna Salatielova, knížete Judského, a ducha Jozue syna Jozadakova, kněze nejvyššího, a ducha ostatků všeho lidu, a přišedše, dělali dílo v domě Hospodina zástupů, Boha svého."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct cs2 poems[] = {
	{1, "1 Dne dvadcátého čtvrtého, měsíce šestého, léta druhého Daria krále."},
	{2, "2 Měsíce sedmého, dvadcátého prvního dne téhož měsíce, stalo se slovo Hospodinovo skrze Aggea proroka, řkoucí:"},
	{3, "3 Mluv nyní k Zorobábelovi synu Salatielovu, knížeti Judskému, a Jozue synu Jozadakovu, knězi nejvyššímu, i k ostatkům lidu, řka:"},
	{4, "4 Kdo jest mezi vámi pozůstalý, ješto viděl dům ten v slávě jeho první? A jaký vy jej nyní vidíte? Zdaliž není proti onomu jako nic před očima vašima?"},
	{5, "5 A však nyní posilň se Zorobábeli, praví Hospodin, posilň se i Jozue synu Jozadakův, kněže nejvyšší, a posilň se všecken lide země této, praví Hospodin, a dodělejte; nebo já s vámi jsem, praví Hospodin zástupů,"},
	{6, "6 Podlé slova, jímž jsem smlouvu učinil s vámi, když jste vycházeli z Egypta. Duch také můj stane u prostřed vás, nebojtež se."},
	{7, "7 Nebo takto praví Hospodin zástupů: Po malém času, aj, já ještě jednou pohnu nebem i zemí, a mořem i suchostí."},
	{8, "8 Pohnu, pravím, všemi národy, a přijdou k Žádoucímu všechněm národům, i naplním dům tento slávou, praví Hospodin zástupů."},
	{9, "9 Méť jest stříbro a mé jest zlato, praví Hospodin zástupů."},
	{10, "10 Větší bude sláva domu tohoto posledního, než onoho prvního, praví Hospodin zástupů; nebo na tomto místě způsobím pokoj, praví Hospodin zástupů."},
	{11, "11 Dvadcátého čtvrtého dne, devátého měsíce, léta druhého Daria, stalo se slovo Hospodinovo skrze Aggea proroka, řkoucí:"},
	{12, "12 Takto praví Hospodin zástupů: Vzeptej se nyní kněží na zákon, řka:"},
	{13, "13 Ej, kdyby někdo nesl maso svaté v křídle sukně své, aneb dotkl by se křídlem svým chleba, neb vaření, neb vína, neb oleje, neb jakéžkoli krmě, zdali bude posvěcen? I odpověděli kněží a řekli: Nikoli."},
	{14, "14 Tedy řekl Aggeus: Jestliže někdo jsa nečistý od těla mrtvého, dotkl by se něčeho z těch věcí, bude-li nečisté? I odpověděli kněží a řekli: Nečisté bude."},
	{15, "15 Tedy odpovídaje Aggeus, řekl: Tak lid tento a tak národ tento před tváří mou, praví Hospodin, a tak všecko dílo rukou jejich, i cožkoli obětovali tam, nečisté bylo."},
	{16, "16 Nyní tedy přiložte medle srdce své od tohoto dne až do onoho, jakž přestal kladen býti kámen na kameni v domě Hospodinově,"},
	{17, "17 Od toho času, když přišel někdo k hromadě dvadcíti, bylo jen deset; když přišel k kádi, aby nabral padesáte z presu, bylo jen dvadcet."},
	{18, "18 Bil jsem vás suchem a rzí, i krupobitím všecko dílo rukou vašich, však žádný z vás neobrátil se ke mně, praví Hospodin."},
	{19, "19 Přiložte již srdce své od tohoto dne až do onoho, ode dne dvadcátého čtvrtého, měsíce devátého, až do dne, v kterémž založen byl chrám Hospodinův, (přiložte srdce své)."},
	{20, "20 Zdali již jest semeno v obilnici? Anobrž ani vinný kmen, ani fík, ani strom jablek zrnatých, ani strom olivový nevydal ovoce. Od tohoto dne požehnám."},
	{21, "21 Potom stalo se slovo Hospodinovo po druhé k Aggeovi, dvadcátého čtvrtého dne téhož měsíce, řkoucí:"},
	{22, "22 Mluv k Zorobábelovi knížeti Judskému, řka: Já pohnu nebem i zemí,"},
	{23, "23 A podvrátím stolici království, a zkazím sílu království pohanských; podvrátím, pravím, vůz i ty, kteříž na něm jezdí, a sejdou koni i jezdci jejich jeden každý od meče bratra svého. [ (Haggai 2:24) V ten den, praví Hospodin zástupů, vezmu tebe, Zorobábeli synu Salatielův, služebníče můj, praví Hospodin, a učiním tě jako pečetní prsten; nebo jsem tě vyvolil, praví Hospodin zástupů. ]"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};