#include <map>
#include <string>
class Bible36
{
	struct da1	{ int val; const char *msg; };
	struct da2	{ int val; const char *msg; };
	struct da3	{ int val; const char *msg; };
public:
static void view1() {
struct da1 poems[] = {
	{1, "1 HERRENS ord, som kom til Zefanias, en Søn af Kusji, en Søn af Gedalja, en Søn af Amarja, en Søn af Ezekias, i de Dage da Josias, Amons Søn, var Konge i Juda."},
	{2, "2 Jeg bortriver, bortriver alt fra Jorden, lyder det fra HERREN;"},
	{3, "3 jeg bortriver Folk og Fæ, jeg bortriver Himlens Fugle og Havets Fisk. Gudløse bringer jeg til Fald, og Syndere rydder jeg, bort fra Jorden, lyder det fra HERREN."},
	{4, "4 Jeg udrækker Hånden mod Juda og alle Jerusalems Borgere. Jeg fjerner den sidste Ba'al fra dette Sted og Afgudspræsternes Navn med Præsterne"},
	{5, "5 og dem, som på Tagene tilbeder Himlens Hær, og dem, som tilbeder HERREN og sværger til bam, men også sværger ved Milkom,"},
	{6, "6 og dem, som veg bort fra HERREN, ej søger, ej rådspørger HERREN."},
	{7, "7 Stille for den Herre HERREN! Thi hans Dag er nær; thi HERREN har et Offer rede, han har helliget de budne."},
	{8, "8 Og på HERRENs Offerdag skal det ske: Da vil jeg hjemsøge Fyrsterne og Kongens Sønner og alle dem, som er klædt i udenlandsk Dragt."},
	{9, "9 Den Dag hjemsøget jeg alle, som hopper over Tærsklen, som fylder deresHerresHus med Vold og Svig."},
	{10, "10 Den Dag skal det ske, så lyder det fra HERREN: Hør Skrig fra Fiskeporten og Jamren fra den nye Bydel. fra Højene et vældigt Brag!"},
	{11, "11 Beboerne i Morteren jamrer, thi slettet er alt Kræmmerfolket, udryddet enhver, som vejer Sølv."},
	{12, "12 Til den Tid skal det ske: Jeg ransager Jerusalem med Lygter og hjemsøger Mændene der, som ligger i Ro på deres Bærme, som siger i deres Hjerte: 'HERREN gør hverken godt eller ondt.'"},
	{13, "13 Deres Gods skal gøres til Bytte, deres Huse skal ødelægges. De skal vel bygge Huse, men ej bo deri, vel plante Vingårde, men Vinen skal de ikke drikke."},
	{14, "14 Nær er HERRENs Dag, den store, den er nær og kommer hastigt. Hør, HERRENs Dag, den bitre! Da udstøder Helten Skrig."},
	{15, "15 Den Dag er en Vredens Dag, en Trængselens og Nødens Dag, en Ødelæggelsens og Ødets Dag, en Mørkets og Mulmets Dag, en Skyernes og Tågens Dag,"},
	{16, "16 en Hornets og Krigsskrigets Dag imod de faste Stæder og imod de knejsende Tinder."},
	{17, "17 Over Menneskene bringer jeg Trængsel; som blinde vanker de om, fordi de synded mod HERREN. Deres Blod øses ud som Støv, deres Livssaft ligesom Skarn."},
	{18, "18 Hverken deres Sølv eller Guld evner at frelse dem på HERRENs Vredes Dag, når hele Jorden fortæres af hans Nidkærheds Ild; thi Undergang, ja brat Tilintetgørelse bringer jeg over alle, som bor på Jorden."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct da2 poems[] = {
	{1, "1 Saml jer, saml jer, I Folk, som ej kender Skam,"},
	{2, "2 før l endnu er blevet som flyvende Avner, før HERRENs glødende Harme kommer over eder, før HERRENs Vredes Dag kommer over eder."},
	{3, "3 Søg HERREN, alle l ydmyge i Landet, som holder hans Bud, søg Retfærd, søg Ydmyghed! Måske kan l skjule jer på HERRENs Vredes Dag."},
	{4, "4 Thi Gaza skal ligge forladt og Askalon øde, Asdod drives bort ved Middag, Ekron udryddes."},
	{5, "5 Ve dem, som bor ved Stranden, Kreternes Folk; HERRENs Ord over dig, Kana'an, Filisternes Land! Jeg gør dig til intet, så ingen bor der."},
	{6, "6 Du skal blive til Græsmark for Hyrder, til Folde for Småkvæg."},
	{7, "7 Og Stranden skal tilfalde Resten af Judas Hus; de skal græsse derpå. Lejr skal de slå ved Kvæld i Askalons Huse. Thi HERREN deres Gud ser til dem og vender deres Skæbne."},
	{8, "8 Jeg hørte Moabs Spot, Atnmoniternes hånende Ord, med hvilke de spotted mit Folk, hovmoded sig over deres Land."},
	{9, "9 Derfor, så sandt jeg lever, lyder det fra Hærskarers HERRE, Israels Gud: Moab skal blive som Sodoma, Ammoniterne som Gomorra, Jord for Nælder, et Salthul, Ørken til evig Tid. Resten af mit Folk skal plyndre dem; hvad der levnes af mit Folk skal eje dem."},
	{10, "10 Dette skal times dem for deres Hovmod, fordi de hånede Hærskarers HERREs Folk og gjorde sig store over for det."},
	{11, "11 Frygtelig er HERREN for dem; thi han udrydder alle Jordens Guder, og alle Hedningemes fjerne Strande skal tilbede ham, hver på sit Sted."},
	{12, "12 Også I Ætiopere skal falde for HERRENs Sværd,"},
	{13, "13 og han udrækker Hånden mod Nord og tilintetgør Assur, Nineve gør han til Ødemark, tørt som en Ørk;"},
	{14, "14 Hjorde skal lejre sig deri, hvert Slettens Dyr; på dets Søjlehoveder sover Pelikan og Rørdrum, i Vinduet skriger Uglen, Ravnen på Tærsklen."},
	{15, "15 Det er den jublende By, som lå så trygt, som sagde i sit Hjerte: 'Jeg og ellers ingen.' Hvor er den dog blevet øde, et Raststed for Dyr! Enhver, som kommer forbi den håner med Fløjt og Hånd."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct da3 poems[] = {
	{1, "1 Ve den genstridige, urene, grumme By!"},
	{2, "2 Den hører ej HERRENs Røst, tager ikke mod Tugt. På HERREN stoler den ej, holder sig ej til Gud."},
	{3, "3 Fyrsterne i dens Midte er brølende Løver, dens dommere som Ulve ved Kvæld, der ej levner til Morgen;"},
	{4, "4 Skrydere er dens Profeter, troløse Mænd, dens Præster vanærer det hellige, øver Vold mod Loven."},
	{5, "5 HERREN er retfærdig i dens Midte, gør ikke Uret; hver Morgen gør han Ret, ej svigte Lyset, til Uret kender han ikke."},
	{6, "6 I Stridslarm udrydded jeg Folk, deres Tinder er øde, deres Gader lagde jeg øde, så ingen går der, deres Byer er hærget, mennesketomme, ingen bor der."},
	{7, "7 Jeg tænkte: 'Den må dog frygte mig, tage mod Tugt; intet af alt, hvad jeg bød, vil gå den ad Glemme.' Men des tidligere var de i Gang med al deres Ondskab."},
	{8, "8 Bi derfor på mig, så lyder det fra HERREN, på Dagen, jeg fremstår som Vidne! Thi min Ret er at sanke Folkene, samle Rigerne og udøse over dem min Vrede, al min Harmglød; thi af min Nidkærheds Ild skal al Jorden fortæres."},
	{9, "9 Thi da vil jeg give Folkene rene Læber, så de alle påkalder HERREN og tjener ham endrægtigt."},
	{10, "10 Fra Landet hinsides Floden bringer de mig Bukke, fra Patros kommer de med Afgrødeoffer til mig."},
	{11, "11 Hin bag skal du ikke beskæmmes i al din tid, med hvilken du synded mod mig; thi da vil jeg fjerne fra dig de jublende stolte, du skal ikke hovmode dig mer på mit hellige Bjerg."},
	{12, "12 Jeg levner i din Midte et Folk, som er ydmygt og ringe, og Israels Rest skal lide på HERRENs Navn."},
	{13, "13 Uret øver de ikke og taler ej Løgn, der findes ej i deres Mund en svigefuld Tunge; thi de skal græsse og raste uden at skræmmes."},
	{14, "14 Jubl, du Zions Datter, Israel, råb højt, glæd dig og fryd dig af hele dit Hjerte, Jerusalems Datter!"},
	{15, "15 HERREN har slettet din Dom, bortdrevet dine Fjender. I din Midte er Israels Konge, HERREN, ej mere skuer du ondt."},
	{16, "16 På hin Dag skal der siges til Jerusalem: Frygt ikke, Zion, lad ikke Hænderne synke!"},
	{17, "17 I dig er HERREN din Gud, en Helt, som frelser. Han glæder sig over dig med Fryd, han tier i sin Kærlighed, han fryder sig over dig med Jubel som på Festens Dag;"},
	{18, "18 den Tid er omme, da du bærer på Skam over ham."},
	{19, "19 Se, på hin Tid gør jeg Ende på alle, som kuede dig, og jeg frelser, hvad der halter, og sanker det spredte og giver dem Ære og Ry på hele Jorden."},
	{20, "20 På hin Tid bringer jeg eder hjem, og på hin Tid samler jeg eder; thi jeg giver eder Ry og Ære blandt alle Jordens Folkeslag, når jeg vender eders Skæbne for eders Øjne, siger HERREN."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};