static void view3() {
struct da3 poems[] = {
	{1, "1 Ligeså, I Hustruer! underordner eder under eders egne Mænd, for at, selv om nogle ere genstridige imod Ordet, de kunne vindes uden Ord ved Hustruernes Vandel,"},
	{2, "2 når de iagttage eders kyske Vandel i Frygt."},
	{3, "3 Eders Prydelse skal ikke være den udvortes med Hårfletning og påhængte Guldsmykker eller Klædedragt,"},
	{4, "4 men Hjertets skjulte Menneske med den sagtmodige og stille Ånds uforkrænkelige Prydelse, hvilket er meget kosteligt for Gud."},
	{5, "5 Thi således var det også, at fordum de hellige Kvinder, som håbede på Gud, prydede sig, idet de underordnede sig under deres egne Mænd,"},
	{6, "6 som Sara var Abraham lydig, så hun kaldte ham Herre, hun, hvis Børn I ere blevne, når I gøre det gode og ikke frygte nogen Rædsel."},
	{7, "7 Ligeså I Mænd! lever med Forstand sammen med eders Hustruer som med et svagere Kar, og beviser dem Ære som dem, der også ere Medarvinger til Livets Nådegave, for at eders Bønner ikke skulle hindres."},
	{8, "8 Og til Slutning værer alle enssindede, medlidende, kærlige imod Brødrene, barmhjertige, ydmyge;"},
	{9, "9 betaler ikke ondt med ondt, eller Skældsord med Skældsord, men tværtimod velsigner, thi dertil bleve I kaldede, at I skulle arve Velsignelse."},
	{10, "10 Thi 'den, som vil elske Livet og se gode Dage, skal holde sin Tunge fra ondt og sine Læber fra at tale Svig;"},
	{11, "11 han vende sig fra ondt og gøre godt; han søge Fred og jage efter den!"},
	{12, "12 Thi Herrens Øjne ere over de retfærdige, og hans Øren til deres Bøn; men Herrens Ansigt er over dem, som gøre ondt.'"},
	{13, "13 Og hvem er der, som kan volde eder ondt, dersom I ere nidkære for det gode?"},
	{14, "14 Men om I også måtte lide for Retfærdigheds Skyld, er I salige. Nærer ingen Frygt for dem, og forfærdes ikke;"},
	{15, "15 men helliger den Herre Kristus i eders Hjerter, altid rede til at forsvare eder over for enhver, som kræver eder til Regnskab for det Håb, der er i eder, men med Sagtmodighed og Frygt,"},
	{16, "16 idet I have en god Samvittighed, for at de, der laste eders gode Vandel i Kristus, må blive til Skamme, når de bagtale eder som Ugerningsmænd."},
	{17, "17 Thi det er bedre, om det så er Guds Villie, at lide, når man gør godt, end når man gør ondt."},
	{18, "18 Thi også Kristus led een Gang for Synder, en retfærdig for uretfærdige, for at han kunde føre os hen til Gud, han, som led Døden i Kødet, men blev levendegjort i Ånden,"},
	{19, "19 i hvilken han også gik hen og prædikede for Ånderne, som vare i Forvaring,"},
	{20, "20 som fordum vare genstridige, dengang Guds Langmodighed ventede i Noas Dage, medens Arken byggedes, i hvilken få, nemlig otte, Sjæle bleve frelste igennem Vand,"},
	{21, "21 hvilket nu også frelser eder i sit Modbillede som Dåb, der ikke er Fjernelse af Kødets Urenhed, men en god Samvittigheds Pagt med Gud ved Jesu Kristi Opstandelse,"},
	{22, "22 han, som er faren til Himmelen og er ved Guds højre Hånd, efter at Engle og Myndigheder og Kræfter ere ham underlagte."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct da4 poems[] = {
	{1, "1 Efterdi da Kristus har lidt i Kødet, så skulle også I væbne eder med det samme Sind (thi den, som har lidt i Kødet, er hørt op med Synd),"},
	{2, "2 så at I ikke fremdeles leve den øvrige Tid i Kødet efter Menneskers Lyster, men efter Guds Villie."},
	{3, "3 Thi det er nok i den forbigangne Tid at have gjort Hedningernes Villie, idet I have vandret i Uterlighed, Lyster, Fylderi, Svir, Drik og skammelig Afgudsdyrkelse;"},
	{4, "4 hvorfor de forundre sig og spotte, når I ikke løbe med til den samme Ryggesløshedens Pøl;"},
	{5, "5 men de skulle gøre ham Regnskab, som er rede til at dømme levende og døde."},
	{6, "6 Thi derfor blev Evangeliet forkyndt også for døde, for at de vel skulde være dømte på Menneskers Vis i Kødet, men leve på Guds Vis i Ånden."},
	{7, "7 Men alle Tings Ende er kommen nær; værer derfor årvågne og ædru til Bønner!"},
	{8, "8 Hav fremfor alt en inderlig Kærlighed til hverandre; thi 'Kærlighed skjuler en Mangfoldighed af Synder'."},
	{9, "9 Vær gæstfri imod hverandre uden Knurren."},
	{10, "10 Eftersom enhver har fået en Nådegave, skulle I tjene hverandre dermed som gode Husholdere over Guds mangfoldige Nåde."},
	{11, "11 Taler nogen, han tale som Guds Ord; har nogen en Tjeneste, han tjene, efter som Gud forlener ham Styrke dertil, for at Gud må æres i alle Ting ved Jesus Kristus, hvem Herligheden og Magten tilhører i Evighedernes Evigheder! Amen."},
	{12, "12 I elskede! undrer eder ikke over den Ild, som brænder iblandt eder til eders Prøvelse, som om der hændtes eder noget underligt;"},
	{13, "13 men glæder eder i samme Mål, som I have Del i Kristi Lidelser, for at I også kunne glæde og fryde eder ved hans Herligheds Åbenbarelse."},
	{14, "14 Dersom I hånes for Kristi Navns Skyld, ere I salige; thi Herlighedens og Guds Ånd hviler over eder."},
	{15, "15 Thi ingen af eder bør lide som Morder eller Tyv eller Ugerningsmand eller som en, der blander sig i anden Mands Sager;"},
	{16, "16 men lider han som en Kristen, da skamme han sig ikke, men prise Gud for dette Navn!"},
	{17, "17 Thi det er Tiden til, at Dommen skal begynde med Guds Hus: men begynder den først med os, hvad Ende vil det da få med dem, som ere genstridige imod Guds Evangelium?"},
	{18, "18 Og dersom den retfærdige med Nød og neppe bliver frelst, hvor skal da den ugudelige og Synderen blive af?"},
	{19, "19 Derfor skulle også de, som lide efter Guds Villie, befale den trofaste Skaber deres Sjæle, idet de gøre det gode."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view5() {
struct da5 poems[] = {
	{1, "1 De Ældste iblandt eder formaner jeg som Medældste og Vidne til Kristi Lidelser, som den, der også har Del i Herligheden, der skal åbenbares:"},
	{2, "2 Vogter Guds Hjord hos eder, og fører Tilsyn med den, ikke tvungne, men frivilligt, ikke for slet Vindings Skyld, men med Redebonhed;"},
	{3, "3 ikke heller som de, der ville herske over Menighederne, men som Mønstre for Hjorden;"},
	{4, "4 og når da Overhyrden åbenbares, skulle I få Herlighedens uvisnelige Krans."},
	{5, "5 Ligeså, I unge! underordner eder under de ældre; og ifører eder alle Ydmyghed imod hverandre; thi 'Gud står de hoffærdige imod, men de ydmyge giver han Nåde.'"},
	{6, "6 Derfor ydmyger eder under Guds vældige Hånd, for at han i sin Tid må ophøje eder."},
	{7, "7 Kaster al eders Sørg på ham, thi han har Omsorg for eder."},
	{8, "8 Vær ædru, våger; eders Modstander, Djævelen, går omkring som en brølende Løve, søgende, hvem han kan opsluge."},
	{9, "9 Står ham imod, faste i Troen, vidende, at de samme Lidelser fuldbyrdes på eders Brødre i Verden."},
	{10, "10 Men al Nådes Gud, som kaldte eder til sin evige Herlighed i Kristus Jesus efter en kort Tids Lidelse, han vil selv fuldelig berede eder, styrke, bekræfte, grundfæste eder!"},
	{11, "11 Ham tilhører Magten i Evighedernes Evigheder! Amen."},
	{12, "12 Med Silvanus, den trofaste Broder (thi det holder jeg ham for), har jeg i Korthed skrevet eder til for at formane og bevidne, at dette er Guds sande Nåde, hvori I stå."},
	{13, "13 Den medudvalgte i Babylon og min Søn, Markus, hilser eder."},
	{14, "14 Hilser hverandre med Kærligheds Kys! Fred være med eder alle, som ere i Kristus!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
