#include <map>
#include <string>
class Bible32
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
	struct de3	{ int val; const char *msg; };
	struct de4	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Das Wort des HERRN erging an Jona, den Sohn Amittais, also:"},
	{2, "2 Mache dich auf, gehe nach Ninive, in die große Stadt, und predige wider sie; denn ihre Bosheit ist vor mein Angesicht heraufgekommen!"},
	{3, "3 Da machte sich Jona auf, um von dem Angesicht des HERRN hinweg nach Tarsis zu fliehen, und ging nach Japho hinab und fand daselbst ein Schiff, das nach Tarsis fuhr. Da gab er sein Fahrgeld und stieg ein, um mit ihnen nach Tarsis zu fahren, hinweg von dem Angesicht des HERRN."},
	{4, "4 Aber der HERR schleuderte einen starken Wind auf das Meer, so daß ein großer Sturm entstand und das Schiff zu scheitern drohte."},
	{5, "5 Da fürchteten sich die Schiffsleute und schrieen, ein jeder zu seinem Gott, und warfen die Geräte, die im Schiffe waren, ins Meer, um es dadurch zu erleichtern. Jona aber war in den untersten Schiffsraum hinabgestiegen, hatte sich niedergelegt und war fest eingeschlafen."},
	{6, "6 Da trat der Schiffskapitän zu ihm und sprach: Was schläfst du so fest? Stehe auf, rufe deinen Gott an! Vielleicht wird dieser Gott unser gedenken, daß wir nicht untergehen."},
	{7, "7 Und sie sprachen einer zum andern: Kommt, wir wollen das Los werfen, damit wir erfahren, um wessen willen uns dieses Unglück begegnet ist! Und sie warfen die Lose, und das Los fiel auf Jona."},
	{8, "8 Da sprachen sie zu ihm: Sage uns doch, um wessen willen uns dieses Unglück begegnet ist! Was ist dein Gewerbe, und wo kommst du her? Welches ist dein Land, und von welchem Volk bist du?"},
	{9, "9 Er sprach: Ich bin ein Hebräer und fürchte den HERRN, den Gott des Himmels, welcher das Meer und das Trockene gemacht hat."},
	{10, "10 Da gerieten die Männer in große Furcht und sprachen: Warum hast du das getan? Denn die Männer wußten, daß er vor dem Angesicht des HERRN floh; denn er hatte es ihnen kundgetan."},
	{11, "11 Und sie fragten ihn: Was sollen wir mit dir machen, damit das Meer uns in Ruhe lasse? Denn das Meer tobte immer ärger."},
	{12, "12 Er sprach zu ihnen: Nehmt mich und werft mich ins Meer, so wird das Meer sich gegen euch beruhigen! Denn ich weiß wohl, daß dieser große Sturm um meinetwillen über euch gekommen ist."},
	{13, "13 Da strengten sich die Leute an, das Ufer wieder zu erreichen; aber sie vermochten es nicht; denn das Meer tobte immer ärger gegen sie."},
	{14, "14 Da schrieen sie zu dem HERRN und sprachen: \"Ach, HERR! laß uns doch nicht um dieses Mannes Seele willen untergehen, rechne uns aber auch nicht unschuldiges Blut zu; denn du, HERR, hast getan, was dir wohlgefiel!\""},
	{15, "15 Darauf nahmen sie Jona und warfen ihn ins Meer; und das Meer hörte auf mit seinem Wüten."},
	{16, "16 Da bekamen die Männer große Ehrfurcht vor dem HERRN und brachten dem HERRN ein Schlachtopfer und taten Gelübde."},
	{17, "17 (2-1) Und der HERR bestellte einen großen Fisch, Jona zu verschlingen; und Jona war im Bauche des Fisches drei Tage und drei Nächte lang."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 (2-2) Und Jona flehte aus dem Bauch des Fisches zu dem HERRN, seinem Gott, und sprach:"},
	{2, "2 (2-3) Als mir angst war, rief ich zu dem HERRN, und er erhörte mich; aus dem Bauch der Hölle schrie ich, und du hörtest meine Stimme!"},
	{3, "3 (2-4) Und doch hattest du mich in die Tiefe geschleudert, mitten ins Meer, daß mich die Strömung umspülte; alle deine Wogen und Wellen gingen über mich."},
	{4, "4 (2-5) Und ich sprach: Ich bin von deinen Augen verstoßen; dennoch will ich fortfahren zu schauen nach deinem heiligen Tempel!"},
	{5, "5 (2-6) Die Wasser umringten mich bis an die Seele, die Tiefe umgab mich, Meergras umschlang mein Haupt."},
	{6, "6 (2-7) Zu den Gründen der Berge sank ich hinunter; die Erde war auf ewig hinter mir verriegelt; da hast du, HERR, mein Gott, mein Leben aus dem Verderben geführt!"},
	{7, "7 (2-8) Da meine Seele bei mir verschmachtete, gedachte ich an den HERRN, und mein Gebet kam zu dir in deinen heiligen Tempel."},
	{8, "8 (2-9) Die Verehrer nichtiger Götzen verlassen ihre Gnade;"},
	{9, "9 (2-10) ich aber will dir mit lauter Stimme danken und dir opfern; was ich gelobt habe, das will ich bezahlen; das Heil kommt vom HERRN!"},
	{10, "10 (2-11) Und der HERR gebot dem Fisch; der spie Jona ans Land."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct de3 poems[] = {
	{1, "1 Und das Wort des HERRN erging zum zweitenmal an Jona, also:"},
	{2, "2 Mache dich auf, gehe nach Ninive, in die große Stadt, und predige ihnen die Predigt, die ich dir sagen werde!"},
	{3, "3 Da machte sich Jona auf und ging nach Ninive, nach dem Wort des HERRN. Ninive aber war eine große Stadt Gottes, drei Tagereisen groß."},
	{4, "4 Und Jona fing an, eine Tagereise weit in die Stadt hineinzugehen, und predigte und sprach: Noch vierzig Tage, und Ninive wird zerstört!"},
	{5, "5 Und die Leute von Ninive glaubten Gott und riefen ein Fasten aus und legten Säcke an, vom Größten bis zum Kleinsten."},
	{6, "6 Und das Wort gelangte bis zum König von Ninive; der stand von seinem Throne auf, legte seinen Mantel von sich, hüllte sich in einen Sack und setzte sich in die Asche."},
	{7, "7 Und er ließ ausrufen und sagen zu Ninive: \"Auf Befehl des Königs und seiner Großen: Menschen und Vieh, Rinder und Schafe sollen nichts genießen, sie sollen weder weiden noch Wasser trinken;"},
	{8, "8 sondern Menschen und Vieh sollen sich in Säcke hüllen und mit Macht zu Gott rufen und sollen sich abwenden, ein jeder von seinem bösen Wege und von dem Unrecht, das an seinen Händen klebt!"},
	{9, "9 Wer weiß, Gott könnte andern Sinnes werden, es sich gereuen lassen und abstehen von seinem grimmigen Zorn, so daß wir nicht untergehen!\""},
	{10, "10 Da nun Gott ihre Taten sah, daß sie sich abwandten von ihren bösen Wegen, reute ihn das Übel, das er ihnen angedroht hatte, und er tat es nicht."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct de4 poems[] = {
	{1, "1 Das aber mißfiel Jona gar sehr, und er ward zornig."},
	{2, "2 Und Jona flehte zum HERRN und sprach: Ach, HERR, ist's nicht das, was ich mir sagte, als ich noch in meinem Lande war, dem ich auch durch die Flucht nach Tarsis zuvorkommen wollte? Denn ich wußte, daß du ein gnädiger und barmherziger Gott bist, langmütig und von großer Gnade, und lässest dich des Übels gereuen!"},
	{3, "3 Und nun, HERR, nimm doch meine Seele von mir; denn es ist besser, ich sterbe, als daß ich lebe!"},
	{4, "4 Da sprach der HERR: Ist es recht, daß du so zürnst?"},
	{5, "5 Hierauf ging Jona zur Stadt hinaus und ließ sich östlich von der Stadt nieder und machte sich daselbst eine Hütte und saß unter ihrem Schatten, bis er sähe, wie es der Stadt ergehen würde."},
	{6, "6 Da beorderte Gott, der HERR, eine Rizinusstaude, die wuchs über Jona empor, um seinem Haupt Schatten zu spenden und ihn von seiner üblen Laune zu befreien; und Jona freute sich sehr über den Rizinus."},
	{7, "7 Da beorderte Gott ein Würmlein, als die Morgenröte am andern Morgen aufstieg; das stach den Rizinus, daß er verdorrte."},
	{8, "8 Und als die Sonne aufging, beorderte Gott einen trockenen Ostwind, und die Sonne stach Jona aufs Haupt, so daß er ganz matt wurde; und er wünschte sich den Tod und sprach: \"Es wäre besser, ich stürbe, als daß ich am Leben bleibe!\""},
	{9, "9 Da sprach Gott zu Jona: Ist es recht, daß du so zürnst um des Rizinus willen? Er sprach: Ja, ich zürne mit Recht bis zum Tod!"},
	{10, "10 Da sprach der HERR: Dich jammert des Rizinus, um den du dich doch nicht bemüht und den du nicht großgezogen hast, der in einer Nacht entstanden und in einer Nacht verdorben ist."},
	{11, "11 Und mich sollte der großen Stadt Ninive nicht jammern, in welcher mehr denn hundertzwanzigtausend Menschen sind, die ihre rechte Hand nicht von ihrer linken unterscheiden können; dazu so viel Vieh! "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};