#include <map>
#include <string>
class Bible37
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Im zweiten Jahre des Königs Darius, am ersten Tage des sechsten Monats, erging das Wort des HERRN durch den Propheten Haggai an Serubbabel, den Sohn Sealtiels, den Statthalter von Juda, und an Josua, den Sohn Jozadaks, den Hohenpriester, also:"},
	{2, "2 So spricht der HERR der Heerscharen: Dies Volk sagt: \"Die Zeit ist noch nicht gekommen, daß das Haus des HERRN gebaut werde!\""},
	{3, "3 Da erging das Wort des HERRN durch den Propheten Haggai also:"},
	{4, "4 Ist es aber für euch an der Zeit, in euren getäfelten Häusern zu wohnen, während dieses Haus in Trümmern liegt?"},
	{5, "5 Und nun spricht der HERR der Heerscharen also: Achtet auf eure Wege!"},
	{6, "6 Ihr säet viel und bringet wenig ein; ihr esset und werdet doch nicht satt; ihr trinket und habt doch nicht genug; ihr kleidet euch und werdet doch nicht warm; und wer einen Lohn verdient, der legt ihn in einen durchlöcherten Beutel!"},
	{7, "7 So spricht der HERR der Heerscharen: Achtet genau auf eure Wege!"},
	{8, "8 Geht auf den Berg und holet Holz und bauet das Haus! Das wird mir gefallen und mir zur Ehre gereichen, spricht der HERR."},
	{9, "9 Ihr erwartet viel, aber es ward wenig daraus; und brachtet ihr es heim, so blies ich es weg! Warum das? So spricht der HERR der Heerscharen: Um meines Hauses willen, das wüste liegt, während jeder von euch seinem Hause zuläuft!"},
	{10, "10 Darum hat der Himmel über euch seinen Tau zurückgehalten und die Erde ihren Ertrag."},
	{11, "11 Und ich habe die Dürre gerufen ins Land und über die Berge, über Korn, Most und Öl und über alles, was der Erdboden hervorbringt, auch über Menschen und Vieh und über alle Arbeit der Hände."},
	{12, "12 Da horchten Serubbabel, der Sohn Sealtiels, und Josua, der Sohn Jozadaks, der Hohepriester, und alles übrige Volk auf die Stimme des HERRN, ihres Gottes, und auf die Worte des Propheten Haggai, weil der HERR, ihr Gott, ihn gesandt hatte, und das Volk fürchtete sich vor dem HERRN."},
	{13, "13 Da sprach Haggai, der Bote des HERRN, im Auftrage des HERRN zum Volk: Ich bin mit euch! spricht der HERR."},
	{14, "14 Und der HERR erweckte den Geist Serubbabels, des Sohnes Sealtiels, des Statthalters von Juda, und den Geist Josuas, des Sohnes Jozadaks, des Hohenpriesters, auch den Geist des ganzen übrigen Volkes, daß sie kamen und ihre Arbeit am Hause des HERRN der Heerscharen, ihres Gottes, in Angriff nahmen"},
	{15, "15 am vierundzwanzigsten Tage des sechsten Monats, im zweiten Jahre des Königs Darius."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 Am einundzwanzigsten Tage des siebenten Monats erging das Wort des HERRN durch den Propheten Haggai also:"},
	{2, "2 Sage doch zu Serubbabel, dem Sohne Sealtiels, dem Statthalter von Juda, und zu Josua, dem Sohne Jozadaks, dem Hohenpriester, und zu dem übrigen Volk und sprich:"},
	{3, "3 Wer ist unter euch übriggeblieben, der dies Haus in seiner frühern Herrlichkeit gesehen hat? Und wie sehet ihr es jetzt? Ist es nicht so viel wie nichts in euren Augen?"},
	{4, "4 Aber nun sei stark, Serubbabel, spricht der HERR; auch du Josua, sei stark, du Sohn Jozadaks, du Hoherpriester, und alles Volk des Landes, seid stark, spricht der HERR, und arbeitet! Denn ich bin mit euch, spricht der HERR der Heerscharen."},
	{5, "5 Das Wort, gemäß welchem ich mit euch einen Bund gemacht habe, als ihr aus Ägypten zoget, soll bestehen, und mein Geist bleibt in eurer Mitte; fürchtet euch nicht!"},
	{6, "6 Denn also spricht der HERR der Heerscharen: Noch einen kleinen Augenblick, und ich erschüttere den Himmel und die Erde, das Meer und das Trockene;"},
	{7, "7 und ich will auch alle Nationen erschüttern, und es werden die Kostbarkeiten aller Nationen kommen, und ich will dieses Haus mit Herrlichkeit erfüllen, spricht der HERR der Heerscharen."},
	{8, "8 Mein ist das Silber, und mein ist das Gold, spricht der HERR der Heerscharen."},
	{9, "9 Es soll die Herrlichkeit dieses letzten Hauses größer werden, als die des ersten war, spricht der HERR der Heerscharen; und an diesem Orte will ich Frieden geben, spricht der HERR der Heerscharen."},
	{10, "10 AM vierundzwanzigsten Tage des neunten Monats, im zweiten Jahre des Darius, erging das Wort des HERRN durch den Propheten Haggai also:"},
	{11, "11 So spricht der HERR der Heerscharen: Frage doch die Priester über das Gesetz und sprich:"},
	{12, "12 Wenn jemand heiliges Fleisch im Zipfel seines Kleides trägt und mit seinem Zipfel Brot oder ein Gericht oder Wein oder Öl oder irgend eine Speise berührt, wird dieses dadurch heilig? Die Priester antworteten und sprachen: Nein!"},
	{13, "13 Da sprach Haggai: Wenn aber jemand, der sich an einer Leiche verunreinigt hat, eines von diesen Dingen anrührt, wird es dadurch unrein? Die Priester antworteten: Es wird unrein!"},
	{14, "14 Da antwortete Haggai und sprach: Ebenso ist auch dieses Volk und diese Nation vor mir, spricht der HERR; so ist jedes Werk ihrer Hände und was sie daselbst opfern: unrein ist es!"},
	{15, "15 Und nun, beachtet doch, wie es euch ergangen ist vor diesem Tage und früher, ehe man Stein auf Stein legte am Tempel des HERRN!"},
	{16, "16 Bevor dies geschah, kam man zum Kornhaufen von zwanzig Scheffeln, so waren es nur zehn; kam man zur Kufe, um fünfzig Eimer zu schöpfen, so waren es bloß zwanzig!"},
	{17, "17 Ich schlug euch mit Getreidebrand und mit Vergilben und Hagel alles Werk eurer Hände; dennoch seid ihr nicht umgekehrt zu mir, spricht der HERR."},
	{18, "18 Gebt nun acht von diesem Tage an und weiterhin, vom vierundzwanzigsten Tage des neunten Monats an, von dem Tage an, da der Tempel des HERRN gegründet worden ist, merket auf!"},
	{19, "19 Ist etwa der Same noch im Speicher? So hat auch der Weinstock, der Feigen, Granatäpfel und Olivenbaum noch nichts getragen; aber von diesem Tage an will ich segnen!"},
	{20, "20 Und das Wort des HERRN erging zum zweitenmal an Haggai am vierundzwanzigsten Tage des Monats also:"},
	{21, "21 Sage zu Serubbabel, dem Statthalter von Juda: Ich erschüttere den Himmel und die Erde"},
	{22, "22 und will Königsthrone umstoßen und die Macht der heidnischen Königreiche zertrümmern, und ich will die Kriegswagen umstoßen samt ihren Reitern, daß Roß und Mann zu Boden sinken und ein jeder umkomme durch das Schwert des andern."},
	{23, "23 An jenem Tage, spricht der HERR der Heerscharen, will ich dich, Serubbabel, Sohn Sealtiels, meinen Knecht, nehmen und dich anstecken wie einen Siegelring; denn dich habe ich erwählt, spricht der HERR der Heerscharen! "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};