#include <map>
#include <string>
class Bible39
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
	struct de3	{ int val; const char *msg; };
	struct de4	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Dies ist der Ausspruch, das Wort des HERRN an Israel, durch die Hand Maleachis:"},
	{2, "2 Ich habe euch Liebe erwiesen, spricht der HERR. So sprechet ihr: Womit hast du uns Liebe erwiesen?"},
	{3, "3 Ist nicht Esau Jakobs Bruder? spricht der HERR. Dennoch habe ich Jakob geliebt, Esau aber habe ich gehaßt und sein Gebirge zur Wildnis gemacht und sein Erbteil den Schakalen der Wüste gegeben."},
	{4, "4 Wenn aber Edom spräche: \"Wir sind zwar zerstört, wir wollen aber die Trümmer wieder aufbauen\", so sagt der HERR der Heerscharen: Sie mögen bauen, ich aber will niederreißen; und man wird sie nennen: \"Land der Gottlosigkeit\" und \"das Volk, über das der HERR ewiglich zürnt\"."},
	{5, "5 Wenn eure Augen das sehen, so werdet ihr sagen: Der HERR sei hochgepriesen über Israels Grenzen hinaus!"},
	{6, "6 Ein Sohn soll seinen Vater ehren und ein Knecht seinen Herrn! Bin ich nun Vater, wo ist meine Ehre? Bin ich Herr, wo ist die Furcht vor mir? spricht der HERR der Heerscharen zu euch Priestern, die ihr meinen Namen verächtlich macht."},
	{7, "7 Und fragt ihr: \"Womit haben wir deinen Namen verächtlich gemacht?\" Damit, daß ihr auf meinem Altar verunreinigtes Brot darbringt! Fragt ihr aber: \"Womit haben wir dich verunreinigt?\" Damit, daß ihr sagt: \"Der Tisch des HERRN ist geringzuschätzen!\""},
	{8, "8 Und wenn ihr ein blindes Tier zum Opfer bringt, ist das nichts Böses, und wenn ihr ein lahmes oder krankes darbringt, ist das auch nichts Böses? Bringe es doch deinem Statthalter! Wird er dir gnädig sein oder Rücksicht auf dich nehmen? spricht der HERR der Heerscharen."},
	{9, "9 Und nun besänftiget doch das Angesicht Gottes, daß er uns gnädig sei! Wird er, weil solches von eurer Hand geschehen ist, auf jemand von euch Rücksicht nehmen?"},
	{10, "10 Es schließe doch lieber gleich jemand von euch die Türen des Tempels zu, daß ihr nicht vergeblich mein Altarfeuer anzündet! Ich habe kein Wohlgefallen an euch, spricht der HERR der Heerscharen, und die Opfergabe, die von euren Händen kommt, gefällt mir nicht!"},
	{11, "11 Denn vom Aufgang der Sonne bis zu ihrem Niedergang ist mein Name groß unter den Heiden, und allenthalben werden meinem Namen Räucherwerk und reine Opfergabe dargebracht; denn groß ist mein Name unter den Heiden, spricht der HERR der Heerscharen."},
	{12, "12 Ihr aber entheiligt ihn damit, daß ihr sagt: \"Der Tisch des Herrn darf verunreinigt werden, und die Speise, die von ihm kommt, ist geringzuschätzen!\""},
	{13, "13 Und ihr sprecht: \"Siehe, ist es auch der Mühe wert?\" Und ihr verachtet ihn, spricht der HERR der Heerscharen, und bringet Geraubtes und Lahmes und Krankes herbei und bringet solches als Opfergabe dar. Sollte ich das von eurer Hand wohlgefällig annehmen? spricht der Herr."},
	{14, "14 Nein, verflucht sei der Betrüger, der in seiner Herde ein männliches Tier hat und ein Gelübde tut und dann doch dem Herrn ein schadhaftes opfert! Denn ich bin ein großer König, spricht der HERR der Heerscharen, und mein Name ist gefürchtet unter den Heiden."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 Und nun, ihr Priester, dieses Gebot gilt euch!"},
	{2, "2 Wenn ihr nicht hören wollt und es euch nicht von Herzen angelegen sein lasset, meinem Namen die Ehre zu geben, spricht der HERR der Heerscharen, so schleudere ich den Fluch wider euch und verfluche eure Segenssprüche; und ich habe sie auch schon verflucht; denn ihr nehmt es nicht zu Herzen!"},
	{3, "3 Siehe, ich schelte euch die Saat und will euch Kot ins Angesicht streuen, den Kot eurer Feste, und man wird euch zu ihm hintragen;"},
	{4, "4 und ihr sollt erfahren, daß ich euch diesen Befehl gesandt habe, auf daß mein Bund mit Levi bestehe, spricht der HERR der Heerscharen."},
	{5, "5 Mein Bund mit ihm war Leben und Friede, und ich verlieh ihm beides, damit er mich fürchtete, und er fürchtete mich auch und hatte Ehrfurcht vor meinem Namen."},
	{6, "6 Das Gesetz der Wahrheit war in seinem Munde, und nichts Verkehrtes wurde auf seinen Lippen erfunden; er wandelte mit mir friedsam und rechtschaffen, und viele brachte er zur Umkehr von der Missetat."},
	{7, "7 Denn des Priesters Lippen sollen die Erkenntnis bewahren, und aus seinem Munde soll man das Gesetz erfragen; denn er ist ein Bote des HERRN der Heerscharen."},
	{8, "8 Ihr aber seid vom Wege abgewichen, ihr seid schuld, daß viele am Gesetz Anstoß genommen haben, ihr habt den Bund mit Levi mißbraucht, spricht der HERR der Heerscharen."},
	{9, "9 Darum habe auch ich euch beim ganzen Volk verachtet und unwert gemacht, weil ihr meine Wege nicht bewahrt, sondern bei Anwendung des Gesetzes die Person ansehet."},
	{10, "10 Haben wir nicht alle einen Vater? Hat uns nicht ein Gott erschaffen? Warum sind wir denn so treulos, einer gegen den andern, und entweihen den Bund unsrer Väter?"},
	{11, "11 Juda hat treulos gehandelt und einen Greuel verübt in Israel und Jerusalem; denn Juda hat das Heiligtum des HERRN entweiht, welches er liebte, und hat die Tochter eines fremden Gottes gefreit."},
	{12, "12 Der HERR wolle dem Manne, der solches tut, Zeugen und Verteidiger ausrotten aus den Zelten Jakobs, und jeden, der dem HERRN der Heerscharen Speisopfer darbringt!"},
	{13, "13 Und zum andern tut ihr auch das: Ihr bedeckt den Altar des HERRN mit Tränen, mit Weinen und Seufzen, so daß er sich nicht mehr zum Speisopfer wenden und es nicht mit Wohlgefallen aus euren Händen annehmen mag."},
	{14, "14 Und ihr fragt: \"Warum?\" Weil der HERR Zeuge war zwischen dir und dem Weibe deiner Jugend, welcher du nun untreu geworden bist, obschon sie deine Gefährtin und das Weib deines Bundes ist!"},
	{15, "15 Und hat er sie nicht eins gemacht und geistesverwandt mit ihm? Und wonach soll das eine trachten? Nach göttlichem Samen! So hütet euch denn in eurem Geiste, und niemand werde dem Weibe seiner Jugend untreu!"},
	{16, "16 Denn ich hasse die Ehescheidung, spricht der HERR, der Gott Israels, und daß man sein Kleid mit Frevel zudeckt, spricht der HERR der Heerscharen; darum hütet euch in eurem Geist und seid nicht treulos!"},
	{17, "17 Ihr habt den HERRN bemüht mit euren Reden; und ihr fragt noch: \"Womit haben wir ihn bemüht?\" Damit, daß ihr sagt: \"Wer Böses tut, der ist gut in den Augen des HERRN, und an solchen hat er ein Wohlgefallen!\" oder \"Wo ist der Gott des Gerichts?\""},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct de3 poems[] = {
	{1, "1 Siehe, ich sende meinen Engel, der vor mir her den Weg bereiten soll; und plötzlich wird kommen zu seinem Tempel der Herr, den ihr suchet, und der Engel des Bundes, nach dem ihr begehret; siehe, er kommt, spricht der HERR der Heerscharen!"},
	{2, "2 Wer aber wird den Tag seiner Zukunft ertragen, und wer wird bestehen, wenn er erscheint? Denn er ist wie das Feuer des Goldschmieds und wie die Lauge der Wäscher."},
	{3, "3 Er wird sitzen und schmelzen und das Silber reinigen; er wird die Kinder Levis reinigen und sie läutern wie Gold und Silber; dann werden sie dem HERRN Speisopfer bringen in Gerechtigkeit;"},
	{4, "4 und das Speisopfer von Juda und Jerusalem wird dem HERRN wohlgefallen, wie in der grauen Vorzeit und wie in den längst vergangenen Jahren."},
	{5, "5 Und ich werde mich euch nahen zum Gericht und will ein schneller Zeuge sein wider die Zauberer und wider die Ehebrecher und wider die Meineidigen und wider die, welche die Tagelöhner, Witwen und Waisen übervorteilen und die Fremdlinge unterdrücken und mich nicht fürchten, spricht der HERR der Heerscharen."},
	{6, "6 Denn ich, der HERR, verändere mich nicht, darum seid ihr, Kinder Jakobs, nicht aufgerieben worden."},
	{7, "7 Seit den Tagen eurer Väter seid ihr von meinen Geboten abgewichen und habt sie nicht befolgt. Kehret um zu mir, so will ich mich zu euch kehren, spricht der HERR der Heerscharen; aber ihr fragt: \"Worin sollen wir umkehren?\""},
	{8, "8 Soll ein Mensch Gott berauben, wie ihr mich beraubet? Aber ihr fragt: \"Wessen haben wir dich beraubt?\" Der Zehnten und der Abgaben!"},
	{9, "9 Mit dem Fluch seid ihr belegt worden, denn mich habt ihr betrogen, ihr, das ganze Volk!"},
	{10, "10 Bringet aber den Zehnten ganz in das Kornhaus, auf daß Speise in meinem Hause sei, und prüfet mich doch dadurch, spricht der HERR der Heerscharen, ob ich euch nicht des Himmels Fenster auftun und euch Segen in überreicher Fülle herabschütten werde!"},
	{11, "11 Und ich will für euch den Fresser schelten, daß er euch die Frucht der Erde nicht verderbe und daß euch der Weinstock auf dem Felde nicht fehlschlage, spricht der HERR der Heerscharen."},
	{12, "12 Also werden alle Nationen euch selig preisen; denn ihr werdet ein Land der Wonne werden, spricht der HERR der Heerscharen."},
	{13, "13 Ihr habt harte Worte wider mich ausgestoßen, spricht der HERR. Und ihr fragt noch: \"Was haben wir untereinander wider dich geredet?\""},
	{14, "14 Ihr habt gesagt: \"Es ist umsonst, daß man Gott dient, und was nützt es uns, seine Ordnung zu halten und vor dem HERRN der Heerscharen in Trauer einherzugehen?"},
	{15, "15 Und nun preisen wir die Übermütigen selig; denn die Uebeltäter stehen aufrecht und die, welche Gott versucht haben, kommen davon!\""},
	{16, "16 Da besprachen sich auch die Gottesfürchtigen miteinander, und der HERR merkte darauf und hörte es, und ein Gedenkbuch ward vor ihm geschrieben für die, welche den HERRN fürchten und seinen Namen hochachten."},
	{17, "17 Und sie werden von mir, spricht der HERR der Heerscharen, am Tage, den ich bereite, als mein auserwähltes Eigentum behandelt werden, und ich will ihrer schonen, wie ein Mann seines Sohnes schont, der ihm dient."},
	{18, "18 Da werdet ihr wiederum sehen, was für ein Unterschied besteht zwischen dem Gerechten und dem Gottlosen, zwischen dem, der Gott dient, und dem, der ihm nicht dient."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct de4 poems[] = {
	{1, "1 (3-19) Denn siehe, der Tag kommt, brennend wie ein Ofen! Da werden alle Übermütigen und alle, die gottlos handeln, wie Stoppeln sein, und der zukünftige Tag wird sie anzünden, spricht der HERR der Heerscharen, daß ihnen weder Wurzel noch Zweig übrigbleibt."},
	{2, "2 (3-20) Euch aber, die ihr meinen Namen fürchtet, soll aufgehen die Sonne der Gerechtigkeit und Heilung unter ihren Flügeln; und ihr werdet herauskommen und hüpfen wie Kälber aus dem Stall!"},
	{3, "3 (3-21) Und ihr werdet die Gottlosen zertreten; denn sie werden wie Asche sein unter euren Fußsohlen am Tage, da ich handle, spricht der HERR der Heerscharen."},
	{4, "4 (3-22) Seid eingedenk des Gesetzes Moses, meines Knechtes, das ich ihm auf dem Horeb für ganz Israel befohlen habe, der Pflichten und der Rechte!"},
	{5, "5 (3-23) Siehe, ich sende euch den Propheten Elia, ehe denn da komme der große und furchtbare Tag des HERRN;"},
	{6, "6 (3-24) der soll das Herz der Väter den Kindern und das Herz der Kinder wieder ihren Vätern zuwenden, damit ich bei meinem Kommen das Land nicht mit dem Banne schlagen muß!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};