#include <map>
#include <string>
class Bible50
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
	struct de3	{ int val; const char *msg; };
	struct de4	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Paulus und Timotheus, Knechte Jesu Christi, an alle Heiligen in Christus Jesus, die in Philippi sind, samt den Aufsehern und Dienern:"},
	{2, "2 Gnade sei mit euch und Friede von Gott, unsrem Vater und dem Herrn Jesus Christus!"},
	{3, "3 Ich danke meinem Gott, so oft ich euer gedenke,"},
	{4, "4 allezeit, in jedem Gebet für euch alle, indem ich das Gebet mit Freuden tue"},
	{5, "5 wegen eurer Gemeinschaft am Evangelium vom ersten Tage an bis jetzt,"},
	{6, "6 und weil ich davon überzeugt bin, daß der, welcher in euch ein gutes Werk angefangen hat, es auch vollenden wird bis auf den Tag Jesu Christi."},
	{7, "7 Es ist ja nur billig, daß ich so von euch allen denke, weil ich euch im Herzen trage, sowohl in meinen Banden als auch bei der Verteidigung und Bekräftigung des Evangeliums, damit ihr alle mit mir der Gnade teilhaftig seid."},
	{8, "8 Denn Gott ist mein Zeuge, wie mich nach euch allen verlangt in der herzlichen Liebe Jesu Christi."},
	{9, "9 Und um das bitte ich, daß eure Liebe noch mehr und mehr reich werde an Erkenntnis und allem Empfindungsvermögen,"},
	{10, "10 damit ihr zu prüfen vermöget, worauf es ankommt, so daß ihr lauter und unanstößig seid auf den Tag Jesu Christi,"},
	{11, "11 erfüllt mit Frucht der Gerechtigkeit, die durch Jesus Christus gewirkt wird zur Ehre und zum Lobe Gottes."},
	{12, "12 Ich will aber, Brüder, daß ihr wisset, wie alles, was mir begegnet ist, nur mehr zur Förderung des Evangeliums ausgeschlagen hat"},
	{13, "13 so daß in der ganzen kaiserlichen Kaserne und bei den übrigen allen bekannt geworden ist, daß ich um Christi willen gebunden bin,"},
	{14, "14 und daß die Mehrzahl der Brüder im Herrn, durch meine Bande ermutigt, es desto kühner wagen, das Wort Gottes zu reden ohne Furcht;"},
	{15, "15 etliche predigen zwar Christus auch aus Neid und Streitsucht, andere aber aus guter Gesinnung:"},
	{16, "16 diese aus Liebe, weil sie wissen, daß ich zur Verteidigung des Evangeliums bestimmt bin;"},
	{17, "17 jene aber, die es aus Parteisucht tun, verkündigen Christus nicht lauter, da sie beabsichtigen, meinen Banden noch Trübsal hinzuzufügen."},
	{18, "18 Was tut es? Wenn nur auf alle Weise, sei es zum Vorwand oder in Wahrheit, Christus verkündigt wird, so freue ich mich darüber und will mich auch freuen!"},
	{19, "19 Denn ich weiß, daß mir das zum Heil ausschlagen wird durch eure Fürbitte und die Handreichung des Geistes Jesu Christi,"},
	{20, "20 nach meiner Erwartung und Hoffnung, daß ich in nichts zuschanden werde, sondern daß in aller Freimütigkeit, wie allezeit, so auch jetzt, Christus hochgepriesen werde an meinem Leibe, es sei durch Leben oder durch Tod."},
	{21, "21 Denn für mich ist Christus das Leben, und das Sterben ist mein Gewinn."},
	{22, "22 Wenn aber das Leben im Fleische mir Gelegenheit gibt zu fruchtbarer Wirksamkeit, so weiß ich nicht, was ich wählen soll."},
	{23, "23 Denn ich werde von beidem bedrängt: Ich habe Lust, abzuscheiden und bei Christus zu sein, was auch viel besser wäre;"},
	{24, "24 aber es ist nötiger, im Fleische zu bleiben um euretwillen."},
	{25, "25 Und weil ich davon überzeugt bin, so weiß ich, daß ich bleiben werde; und ich will auch gerne bei euch allen verbleiben zu eurer Förderung und Freude im Glauben,"},
	{26, "26 damit ihr um soviel mehr zu rühmen habet in Christus Jesus meinethalben, wegen meiner erneuten Anwesenheit bei euch."},
	{27, "27 Wandelt nur würdig des Evangeliums Christi, damit, ob ich komme und euch sehe, oder abwesend bin, ich von euch höre, daß ihr feststehet in einem Geiste und einmütig miteinander kämpfet für den Glauben des Evangeliums"},
	{28, "28 und euch in keiner Weise einschüchtern lasset von den Widersachern, was für sie eine Anzeige des Verderbens, für euch aber des Heils ist, und zwar von Gott."},
	{29, "29 Denn euch wurde in bezug auf Christus die Gnade verliehen, nicht nur an ihn zu glauben, sondern auch um seinetwillen zu leiden,"},
	{30, "30 indem ihr denselben Kampf habt, den ihr an mir sahet und nun von mir höret."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 Gibt es nun irgendwelche Ermahnung in Christus, gibt es Zuspruch der Liebe, gibt es Gemeinschaft des Geistes, gibt es Herzlichkeit und Erbarmen,"},
	{2, "2 so machet meine Freude völlig, indem ihr eines Sinnes seid, gleiche Liebe habet, einmütig und auf eines bedacht seid,"},
	{3, "3 nichts tut aus Parteigeist oder eitler Ruhmsucht, sondern durch Demut einer den andern höher achtet als sich selbst,"},
	{4, "4 indem jeder nicht nur das Seine ins Auge faßt, sondern auch das des andern."},
	{5, "5 Denn ihr sollt so gesinnt sein, wie Jesus Christus auch war,"},
	{6, "6 welcher, da er sich in Gottes Gestalt befand, es nicht wie einen Raub festhielt, Gott gleich zu sein;"},
	{7, "7 sondern sich selbst entäußerte, die Gestalt eines Knechtes annahm und den Menschen ähnlich wurde,"},
	{8, "8 und in seiner äußern Erscheinung wie ein Mensch erfunden, sich selbst erniedrigte und gehorsam wurde bis zum Tod, ja bis zum Kreuzestod."},
	{9, "9 Darum hat ihn auch Gott über alle Maßen erhöht und ihm den Namen geschenkt, der über allen Namen ist,"},
	{10, "10 damit in dem Namen Jesu sich alle Knie derer beugen, die im Himmel und auf Erden und unter der Erde sind,"},
	{11, "11 und alle Zungen bekennen, daß Jesus Christus der Herr sei, zur Ehre Gottes, des Vaters."},
	{12, "12 Darum, meine Geliebten, wie ihr allezeit gehorsam gewesen seid, nicht allein in meiner Gegenwart, sondern jetzt noch vielmehr in meiner Abwesenheit, vollendet eure Rettung mit Furcht und Zittern;"},
	{13, "13 denn Gott ist es, der in euch sowohl das Wollen als auch das Vollbringen wirkt, nach Seinem Wohlgefallen."},
	{14, "14 Tut alles ohne Murren und Bedenken,"},
	{15, "15 damit ihr unsträflich seid und lauter, untadelige Gotteskinder, mitten unter einem verdrehten und verkehrten Geschlecht, unter welchem ihr scheinet als Lichter in der Welt,"},
	{16, "16 indem ihr das Wort des Lebens darbietet, mir zum Ruhm auf den Tag Christi, daß ich nicht vergeblich gelaufen bin, noch vergeblich gearbeitet habe."},
	{17, "17 Sollte ich aber auch wie ein Trankopfer ausgegossen werden über dem Opfer und dem Gottesdienst eures Glaubens, so bin ich doch froh und freue mich mit euch allen;"},
	{18, "18 gleicherweise sollt auch ihr froh sein und euch mit mir freuen!"},
	{19, "19 Ich hoffe aber in dem Herrn Jesus, Timotheus bald zu euch zu senden, damit ich ermutigt werde, wenn ich erfahre, wie es um euch steht."},
	{20, "20 Denn ich habe sonst niemand von gleicher Gesinnung, der so redlich für eure Anliegen sorgen wird;"},
	{21, "21 denn sie suchen alle das Ihre, nicht das, was Christi Jesu ist!"},
	{22, "22 Wie er sich aber bewährt hat, das wisset ihr, daß er nämlich, wie ein Kind dem Vater, mit mir Dienst getan hat für das Evangelium."},
	{23, "23 Diesen nun hoffe ich sofort zu senden, sobald ich absehen kann, wie es mit mir gehen wird."},
	{24, "24 Ich bin aber voll Zuversicht im Herrn, daß auch ich selbst bald kommen werde."},
	{25, "25 Doch habe ich es für notwendig erachtet, Epaphroditus, meinen Bruder und Mitarbeiter und Mitstreiter, der auch als euer Gesandter und Diener meiner Not mir zu Hilfe kam, zu euch zu senden;"},
	{26, "26 denn er hatte Verlangen nach euch allen und war bekümmert, weil ihr gehört hattet, daß er krank gewesen sei."},
	{27, "27 Er war auch wirklich todkrank; aber Gott hat sich seiner erbarmt, und nicht nur über ihn, sondern auch über mich, damit ich nicht eine Traurigkeit um die andere hätte."},
	{28, "28 Desto schleuniger habe ich ihn nun gesandt, damit ihr durch seinen Anblick wieder froh werdet und auch ich weniger Traurigkeit habe."},
	{29, "29 So nehmt ihn nun auf im Herrn mit aller Freude und haltet solche Männer in Ehren;"},
	{30, "30 denn um des Werkes Christi willen ist er dem Tode nahe gekommen, da er sein Leben dransetzte, um mir zu dienen an eurer Statt."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct de3 poems[] = {
	{1, "1 Im übrigen, meine Brüder, freuet euch in dem Herrn! Euch dasselbe zu schreiben, ist mir nicht lästig; euch aber macht es gewiß."},
	{2, "2 Habt acht auf die Hunde, habt acht auf die bösen Arbeiter, habt acht auf die Zerschneidung!"},
	{3, "3 Denn wir sind die Beschneidung, die wir Gott im Geiste dienen und uns in Christus Jesus rühmen und nicht auf Fleisch vertrauen,"},
	{4, "4 wiewohl auch ich mein Vertrauen auf Fleisch setzen könnte. Wenn ein anderer meint, er könne auf Fleisch vertrauen, ich viel mehr;"},
	{5, "5 der ich am achten Tag beschnitten bin, aus dem Geschlecht Israel, vom Stamme Benjamin, ein Hebräer von Hebräern, nach dem Gesetz ein Pharisäer,"},
	{6, "6 nach dem Eifer ein Verfolger der Gemeinde, nach der Gerechtigkeit im Gesetze untadelig gewesen."},
	{7, "7 Aber was mir Gewinn war, das habe ich um Christi willen für Schaden gerechnet;"},
	{8, "8 ja ich achte nun auch alles für Schaden gegenüber der alles übertreffenden Erkenntnis Christi Jesu, meines Herrn, um dessentwillen ich alles eingebüßt habe, und ich achte es für Unrat, damit ich Christus gewinne"},
	{9, "9 und in ihm erfunden werde, daß ich nicht meine eigene Gerechtigkeit (die aus dem Gesetz) habe, sondern die, welche durch den Glauben an Christus erlangt wird, die Gerechtigkeit aus Gott auf Grund des Glaubens,"},
	{10, "10 zu erkennen ihn und die Kraft seiner Auferstehung und die Gemeinschaft seiner Leiden, daß ich seinem Tode ähnlich werde,"},
	{11, "11 ob ich vielleicht zur Auferstehung aus den Toten gelangen möchte."},
	{12, "12 Nicht daß ich es schon erlangt habe oder schon vollendet sei, ich jage aber darnach, daß ich das auch ergreife, wofür ich von Christus ergriffen worden bin."},
	{13, "13 Brüder, ich halte mich selbst nicht dafür, daß ich es ergriffen habe;"},
	{14, "14 eins aber tue ich: Ich vergesse, was dahinten ist, und strecke mich aus nach dem, was vor mir ist, und jage nach dem Ziel, dem Kampfpreis der himmlischen Berufung Gottes in Christus Jesus."},
	{15, "15 So viele nun vollkommen sind, wollen wir also gesinnt sein; und wenn ihr über etwas anders denket, so wird euch Gott auch das offenbaren."},
	{16, "16 Nur laßt uns, wozu wir auch gelangt sein mögen, nach derselben Richtschnur wandeln und dasselbe erstreben."},
	{17, "17 Werdet meine Nachahmer, ihr Brüder, und sehet auf die, welche so wandeln, wie ihr uns zum Vorbilde habt."},
	{18, "18 Denn viele wandeln, wie ich euch oft gesagt habe, nun aber auch weinend sage, als «Feinde des Kreuzes Christi»,"},
	{19, "19 welcher Ende das Verderben ist, deren Gott der Bauch ist, die sich ihrer Schande rühmen und aufs Irdische erpicht sind."},
	{20, "20 Unser Bürgerrecht aber ist im Himmel, von woher wir auch als Retter den Herrn Jesus Christus erwarten,"},
	{21, "21 welcher den Leib unsrer Niedrigkeit umgestalten wird, daß er gleichgestaltet werde dem Leibe seiner Herrlichkeit, vermöge der Kraft, durch welche er sich auch alles untertan machen kann!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct de4 poems[] = {
	{1, "1 Darum, meine geliebten und ersehnten Brüder, meine Freude und meine Krone, stehet also fest im Herrn!"},
	{2, "2 Euodia ermahne ich und Syntyche ermahne ich, eines Sinnes zu sein im Herrn."},
	{3, "3 Ja, ich bitte auch dich, mein treuer Mitknecht, nimm dich ihrer an, die mit mir als Streiter gedient haben am Evangelium, samt Clemens und meinen übrigen Mitarbeitern, deren Namen im Buch des Lebens sind."},
	{4, "4 Freuet euch im Herrn allezeit; und abermal sage ich: Freuet euch!"},
	{5, "5 Eure Sanftmut lasset alle Menschen erfahren! Der Herr ist nahe!"},
	{6, "6 Sorget um nichts; sondern in allem lasset durch Gebet und Flehen mit Danksagung eure Anliegen vor Gott kundwerden."},
	{7, "7 Und der Friede Gottes, der allen Verstand übersteigt, wird eure Herzen und Sinne bewahren in Christus Jesus!"},
	{8, "8 Im übrigen, meine Brüder, was wahrhaftig, was ehrbar, was gerecht, was keusch, was liebenswert, was wohllautend, was irgend eine Tugend oder ein Lob ist, dem denket nach;"},
	{9, "9 was ihr auch gelernt und empfangen und gehört und an mir gesehen habt, das tut; so wird der Gott des Friedens mit euch sein."},
	{10, "10 Ich bin aber hoch erfreut worden im Herrn, daß ihr euch wieder soweit erholt habt, um für mich sorgen zu können; worauf ihr auch sonst bedacht waret, aber ihr waret nicht in der Lage dazu."},
	{11, "11 Nicht Mangels halber sage ich das; denn ich habe gelernt, mit der Lage zufrieden zu sein, in welcher ich mich befinde."},
	{12, "12 Ich verstehe mich so gut aufs Armsein wie aufs Reichsein;"},
	{13, "13 ich bin in allem und für alles geübt, sowohl satt zu sein, als zu hungern, sowohl Überfluß zu haben, als Mangel zu leiden. Ich vermag alles durch den, der mich stark macht."},
	{14, "14 Doch habt ihr wohlgetan, daß ihr euch meiner bedrängten Lage annahmet."},
	{15, "15 Ihr wisset aber auch, ihr Philipper, daß im Anfang des Evangeliums, als ich von Mazedonien auszog, keine Gemeinde sich mit mir geteilt hat in die Rechnung der Einnahmen und Ausgaben, als ihr allein;"},
	{16, "16 ja auch nach Thessalonich habt ihr mir einmal, und sogar zweimal, zur Deckung meiner Bedürfnisse gesandt."},
	{17, "17 Nicht daß ich nach der Gabe verlange, sondern ich verlange darnach, daß die Frucht reichlich ausfalle auf eurer Rechnung."},
	{18, "18 Ich habe alles, was ich brauche, und habe Überfluß; ich bin völlig versorgt, seitdem ich von Epaphroditus eure Gabe empfangen habe, einen lieblichen Wohlgeruch, ein angenehmes Opfer, Gott wohlgefällig."},
	{19, "19 Mein Gott aber befriedige alle eure Bedürfnisse nach seinem Reichtum in Herrlichkeit, in Christus Jesus!"},
	{20, "20 Unsrem Gott und Vater aber sei die Ehre von Ewigkeit zu Ewigkeit! Amen."},
	{21, "21 Grüßet alle Heiligen in Christus Jesus! Es grüßen euch die Brüder, die bei mir sind."},
	{22, "22 Es grüßen euch alle Heiligen, allermeist aber die von des Kaisers Hause."},
	{23, "23 Die Gnade unsres Herrn Jesus Christus sei mit eurem Geiste! Amen. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};