#include <map>
#include <string>
class Bible52
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
	struct de3	{ int val; const char *msg; };
	struct de4	{ int val; const char *msg; };
	struct de5	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Paulus und Silvanus und Timotheus an die Gemeinde der Thessalonicher in Gott, dem Vater und dem Herrn Jesus Christus: Gnade sei mit euch und Friede von Gott, unsrem Vater und dem Herrn Jesus Christus!"},
	{2, "2 Wir danken Gott allezeit für euch alle, wenn wir euch erwähnen in unsren Gebeten,"},
	{3, "3 da wir uns unablässig erinnern an euer Glaubenswerk und eure Liebesarbeit und euer Beharren in der Hoffnung auf unsren Herrn Jesus Christus, vor unsrem Gott und Vater."},
	{4, "4 Denn wir wissen, von Gott geliebte Brüder, um eure Erwählung:"},
	{5, "5 daß unser Evangelium nicht nur im Wort zu euch kam, sondern auch in Kraft und im heiligen Geist und in großer Gewißheit, wie ihr ja wisset, wie wir unter euch gewesen sind um euretwillen."},
	{6, "6 Und ihr seid unsere und des Herrn Nachfolger geworden, indem ihr das Wort unter viel Trübsal aufnahmet mit Freude des heiligen Geistes,"},
	{7, "7 so daß ihr Vorbilder geworden seid für alle Gläubigen in Mazedonien und Achaja."},
	{8, "8 Denn von euch aus ist das Wort des Herrn erschollen, nicht nur in Mazedonien und Achaja; sondern allerorten ist es kund geworden, wie ihr an Gott glaubet, so daß wir nicht nötig haben, davon zu reden;"},
	{9, "9 denn sie selbst erzählen von uns, wie wir bei euch Eingang gefunden und wie ihr euch von den Abgöttern zu Gott bekehrt habt, um dem lebendigen und wahren Gott zu dienen"},
	{10, "10 und seinen Sohn vom Himmel zu erwarten, welchen er von den Toten auferweckt hat, Jesus, der uns vor dem zukünftigen Zorn errettet."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 Denn ihr wisset selbst, Brüder, daß unser Eingang bei euch nicht vergeblich war;"},
	{2, "2 sondern, wiewohl wir zuvor gelitten hatten und mißhandelt worden waren zu Philippi, wie ihr wisset, gewannen wir dennoch Freudigkeit in unsrem Gott, bei euch das Evangelium Gottes zu verkünden unter viel Kampf."},
	{3, "3 Denn unsre Predigt entspringt nicht dem Irrtum, noch unreinen Absichten, auch keinem Betrug;"},
	{4, "4 sondern gleichwie wir von Gott geprüft und mit dem Evangelium betraut worden sind, so reden wir, nicht als solche, die den Menschen gefallen wollen, sondern Gott, der unsre Herzen prüft."},
	{5, "5 Denn wir sind nie mit Schmeichelworten gekommen, wie ihr wisset, noch mit verblümter Habsucht (Gott ist Zeuge);"},
	{6, "6 wir haben auch nicht Ehre von Menschen gesucht, weder von euch noch von andern,"},
	{7, "7 da wir doch als Apostel Christi würdevoll hätten auftreten können, sondern wir benahmen uns liebevoll in eurer Mitte, wie eine stillende Mutter ihre Kinder pflegt."},
	{8, "8 Und wir sehnten uns so sehr nach euch, daß wir willig waren, euch nicht nur das Evangelium Gottes mitzuteilen, sondern auch unsre Seelen, weil ihr uns lieb geworden waret."},
	{9, "9 Denn ihr erinnert euch, ihr Brüder, unsrer Arbeit und Mühe; wir arbeiteten Tag und Nacht, um niemand von euch beschwerlich zu fallen, und predigten euch dabei das Evangelium Gottes."},
	{10, "10 Ihr selbst seid Zeugen, und Gott, wie heilig, gerecht und untadelig wir bei euch, den Gläubigen, gewesen sind,"},
	{11, "11 wie ihr ja wisset, daß wir jeden einzelnen von euch, wie ein Vater seine Kinder, ermahnt und ermutigt und beschworen haben,"},
	{12, "12 würdig zu wandeln des Gottes, der euch zu seinem Reich und seiner Herrlichkeit beruft."},
	{13, "13 Darum danken wir auch Gott unablässig, daß ihr das von uns empfangene Wort der Predigt Gottes aufnahmet, nicht als Menschenwort, sondern als das, was es in Wahrheit ist, als Gottes Wort, welches auch in euch, den Gläubigen, wirkt."},
	{14, "14 Denn ihr, Brüder, seid Nachahmer der Gemeinden Gottes geworden, die in Judäa in Christus Jesus sind, weil ihr von euren eigenen Volksgenossen dasselbe erlitten habt, wie sie von den Juden,"},
	{15, "15 welche auch den Herrn Jesus getötet und die Propheten und uns verfolgt haben und Gott nicht gefallen und allen Menschen zuwider sind;"},
	{16, "16 sie wehren uns, zu den Heiden zu reden, damit diese gerettet werden, womit sie das Maß ihrer Sünden allezeit voll machen; das Zorngericht setzte ihnen nun aber schnell ein Ziel!"},
	{17, "17 Wir aber, Brüder, nachdem wir euer für eine kleine Weile beraubt waren, dem Angesicht, nicht dem Herzen nach, haben uns vor großem Verlangen um so mehr bemüht, euer Angesicht zu sehen."},
	{18, "18 Darum wollten wir auch zu euch kommen, ich Paulus einmal, sogar zweimal, und Satan hat uns verhindert."},
	{19, "19 Denn wer ist unsre Hoffnung oder Freude oder Krone des Ruhms? Seid nicht auch ihr es vor unsrem Herrn Jesus Christus bei seiner Wiederkunft?"},
	{20, "20 Ja, ihr seid unsre Ehre und Freude!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct de3 poems[] = {
	{1, "1 Darum, weil wir es nicht länger aushielten, zogen wir vor, allein in Athen zu bleiben;"},
	{2, "2 und sandten Timotheus, unsren Bruder, der Gottes Diener und unser Mitarbeiter am Evangelium ist, daß er euch stärke und ermahne in betreff eures Glaubens,"},
	{3, "3 damit niemand wankend werde in diesen Trübsalen; denn ihr wisset selbst, daß wir dazu bestimmt sind."},
	{4, "4 Denn als wir bei euch waren, sagten wir euch voraus, daß wir Trübsale würden leiden müssen, wie es auch gekommen ist und ihr wisset."},
	{5, "5 Darum hielt ich es auch nicht mehr länger aus, sondern ließ mich nach eurem Glauben erkundigen, ob nicht etwa der Versucher euch versucht habe und unsre Arbeit umsonst gewesen sei."},
	{6, "6 Nun aber, da Timotheus von euch zu uns zurückgekehrt ist und uns gute Nachricht gebracht hat von eurem Glauben und eurer Liebe, und daß ihr uns allezeit in gutem Andenken habet und darnach verlanget, uns zu sehen, gleichwie wir euch,"},
	{7, "7 da sind wir deshalb, ihr Brüder, eurethalben bei all unserer Not und Trübsal getröstet worden, durch euren Glauben!"},
	{8, "8 Denn nun leben wir, wenn ihr feststehet im Herrn."},
	{9, "9 Denn was können wir Gott für einen Dank abstatten für euch ob all der Freude, die wir euretwegen genießen vor unserm Gott?"},
	{10, "10 Tag und Nacht flehen wir aufs allerdringendste, euer Angesicht sehen und die Mängel eures Glaubens ergänzen zu dürfen."},
	{11, "11 Er selbst aber, Gott unser Vater und unser Herr Jesus, lenke unsren Weg zu euch!"},
	{12, "12 Euch aber möge der Herr voll und überströmend machen in der Liebe zueinander und zu allen, gleichwie auch wir sie haben zu euch,"},
	{13, "13 auf daß eure Herzen gestärkt und untadelig erfunden werden in Heiligkeit vor unsrem Gott und Vater bei der Wiederkunft unsres Herrn Jesus mit allen seinen Heiligen."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct de4 poems[] = {
	{1, "1 Weiter nun, ihr Brüder, bitten und ermahnen wir euch in dem Herrn Jesus, daß ihr in dem, was ihr von uns gelernt habt, nämlich wie ihr wandeln und Gott gefallen sollt, noch mehr zunehmet."},
	{2, "2 Denn ihr wisset, welche Gebote wir euch gegeben haben durch den Herrn Jesus."},
	{3, "3 Denn das ist der Wille Gottes, eure Heiligung, daß ihr euch der Unzucht enthaltet;"},
	{4, "4 daß jeder von euch wisse, sein eigenes Gefäß in Heiligung und Ehre zu besitzen,"},
	{5, "5 nicht mit leidenschaftlicher Gier wie die Heiden, die Gott nicht kennen;"},
	{6, "6 daß niemand zuweit greife und seinen Bruder im Handel übervorteile; denn der Herr ist ein Rächer für das alles, wie wir euch zuvor gesagt und bezeugt haben."},
	{7, "7 Denn Gott hat uns nicht zur Unreinigkeit berufen, sondern zur Heiligung."},
	{8, "8 Darum also, wer sich darüber hinwegsetzt, der verachtet nicht Menschen, sondern Gott, der auch seinen heiligen Geist in uns gegeben hat."},
	{9, "9 Über die Bruderliebe aber habt ihr nicht nötig, daß man euch schreibe; denn ihr seid selbst von Gott gelehrt, einander zu lieben,"},
	{10, "10 und das tut ihr auch an allen Brüdern, die in ganz Mazedonien sind. Wir ermahnen euch aber, ihr Brüder, darin noch mehr zuzunehmen"},
	{11, "11 und eure Ehre darein zu setzen, ein ruhiges Leben zu führen, eure eigenen Angelegenheiten zu besorgen und mit euren eigenen Händen zu arbeiten, ganz wie wir euch befohlen haben,"},
	{12, "12 damit ihr ehrbar wandelt vor denen draußen und niemandes Hilfe bedürfet."},
	{13, "13 Wir wollen euch aber, ihr Brüder, nicht in Unwissenheit lassen in betreff der Entschlafenen, damit ihr nicht traurig seid wie die andern, die keine Hoffnung haben."},
	{14, "14 Denn wenn wir glauben, daß Jesus gestorben und auferstanden ist, so wird Gott auch die Entschlafenen durch Jesus mit ihm führen."},
	{15, "15 Denn das sagen wir euch in einem Worte des Herrn, daß wir, die wir leben und bis zur Wiederkunft des Herrn übrigbleiben, den Entschlafenen nicht zuvorkommen werden;"},
	{16, "16 denn er selbst, der Herr, wird, wenn der Befehl ergeht und die Stimme des Erzengels und die Posaune Gottes erschallt, vom Himmel herniederfahren, und die Toten in Christus werden auferstehen zuerst."},
	{17, "17 Darnach werden wir, die wir leben und übrigbleiben, zugleich mit ihnen entrückt werden in Wolken, zur Begegnung mit dem Herrn, in die Luft, und also werden wir bei dem Herrn sein allezeit."},
	{18, "18 So tröstet nun einander mit diesen Worten!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view5() {
struct de5 poems[] = {
	{1, "1 Von den Zeiten und Stunden aber braucht man euch Brüdern nicht zu schreiben."},
	{2, "2 Denn ihr wisset ja genau, daß der Tag des Herrn kommen wird wie ein Dieb in der Nacht."},
	{3, "3 Wenn sie sagen werden: «Friede und Sicherheit», dann wird sie das Verderben plötzlich überfallen wie die Wehen eine schwangere Frau, und sie werden nicht entfliehen."},
	{4, "4 Ihr aber, Brüder, seid nicht in der Finsternis, daß euch der Tag wie ein Dieb überfallen könnte;"},
	{5, "5 ihr seid allzumal Kinder des Lichts und Kinder des Tages. Wir sind nicht von der Nacht, noch von der Finsternis."},
	{6, "6 So lasset uns auch nicht schlafen wie die andern, sondern lasset uns wachen und nüchtern sein!"},
	{7, "7 Denn die da schlafen, die schlafen des Nachts, und die Betrunkenen sind des Nachts betrunken;"},
	{8, "8 wir aber, die wir dem Tage angehören, wollen nüchtern sein, angetan mit dem Panzer des Glaubens und der Liebe und mit dem Helm der Hoffnung des Heils."},
	{9, "9 Denn Gott hat uns nicht zum Zorn gericht bestimmt, sondern zum Besitze des Heils durch unsren Herrn Jesus Christus,"},
	{10, "10 der für uns gestorben ist, damit wir, ob wir wachen oder schlafen, zugleich mit ihm leben sollen."},
	{11, "11 Darum ermahnet einander und erbauet einer den andern, wie ihr auch tut."},
	{12, "12 Wir bitten euch aber, ihr Brüder, anerkennet diejenigen, welche an euch arbeiten und euch im Herrn vorstehen und euch ermahnen;"},
	{13, "13 haltet sie um ihres Werkes willen desto größerer Liebe wert; lebet im Frieden mit ihnen!"},
	{14, "14 Wir ermahnen euch aber, Brüder: Verwarnet die Unordentlichen, tröstet die Kleinmütigen, nehmet euch der Schwachen an, seid geduldig gegen jedermann!"},
	{15, "15 Sehet zu, daß niemand Böses mit Bösem vergelte, sondern trachtet allezeit darnach, Gutes zu tun, aneinander und an jedermann!"},
	{16, "16 Seid allezeit fröhlich!"},
	{17, "17 Betet ohne Unterlaß!"},
	{18, "18 Seid in allem dankbar; denn das ist der Wille Gottes in Christus Jesus für euch."},
	{19, "19 Den Geist dämpfet nicht,"},
	{20, "20 die Weissagung verachtet nicht;"},
	{21, "21 prüfet aber alles. Das Gute behaltet,"},
	{22, "22 enthaltet euch des Bösen in jeglicher Gestalt!"},
	{23, "23 Er selbst aber, der Gott des Friedens, heilige euch durch und durch, und euer ganzes Wesen, der Geist, die Seele und der Leib, werde unsträflich bewahrt bei der Wiederkunft unsres Herrn Jesus Christus!"},
	{24, "24 Treu ist er, der euch beruft; er wird es auch tun."},
	{25, "25 Brüder, betet für uns!"},
	{26, "26 Grüßet die Brüder alle mit dem heiligen Kuß!"},
	{27, "27 Ich beschwöre euch bei dem Herrn, daß dieser Brief allen heiligen Brüdern gelesen werde."},
	{28, "28 Die Gnade unsres Herrn Jesus Christus sei mit euch! "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};