#include <map>
#include <string>
class Bible53
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
	struct de3	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Paulus und Silvanus und Timotheus an die Gemeinde der Thessalonicher in Gott, unsrem Vater und dem Herrn Jesus Christus."},
	{2, "2 Gnade sei mit euch und Friede von Gott, dem Vater und dem Herrn Jesus Christus!"},
	{3, "3 Wir sind Gott allezeit zu danken schuldig für euch, Brüder, wie es sich geziemt, weil euer Glaube über die Maßen wächst und die Liebe eines jeden einzelnen von euch zunimmt allen gegenüber,"},
	{4, "4 so daß wir selbst uns euer rühmen in den Gemeinden Gottes wegen eurer Standhaftigkeit und Glaubenstreue in allen euren Verfolgungen und Drangsalen, die ihr zu ertragen habt:"},
	{5, "5 ein Beweis des gerechten Gerichtes Gottes, daß ihr gewürdigt werdet des Königreiches Gottes, für das ihr leidet,"},
	{6, "6 wie es denn gerecht ist vor Gott, denen, die euch bedrücken, mit Bedrückung zu vergelten,"},
	{7, "7 euch aber, die ihr bedrückt werdet, mit Erquickung samt uns, bei der Offenbarung unsres Herrn Jesus Christus vom Himmel her, samt den Engeln seiner Kraft,"},
	{8, "8 wenn er mit Feuerflammen Rache nehmen wird an denen, die Gott nicht anerkennen und die dem Evangelium unsres Herrn Jesus nicht gehorsam sind,"},
	{9, "9 welche Strafe erleiden werden, ewiges Verderben, von dem Angesicht des Herrn und von der Herrlichkeit seiner Kraft,"},
	{10, "10 wenn er kommen wird, um an jenem Tage verherrlicht zu werden in seinen Heiligen und bewundert in denen, die gläubig geworden sind, denn unser Zeugnis hat bei euch Glauben gefunden."},
	{11, "11 Zu diesem Zweck flehen wir auch allezeit für euch, daß unser Gott euch der Berufung würdig mache und alles Wohlgefallen der Güte und das Werk des Glaubens in Kraft erfülle,"},
	{12, "12 auf daß der Name unsres Herrn Jesus Christus in euch verherrlicht werde und ihr in ihm, nach der Gnade unsres Gottes und des Herrn Jesus Christus."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 Wir bitten euch aber, Brüder, betreffs der Wiederkunft unsres Herrn Jesus Christus und unsrer Vereinigung mit ihm:"},
	{2, "2 Lasset euch nicht so schnell aus der Fassung bringen oder gar in Schrecken jagen, weder durch einen Geist, noch durch eine Rede, noch durch einen angeblich von uns stammenden Brief, als wäre der Tag des Herrn schon da."},
	{3, "3 Niemand soll euch irreführen in irgendeiner Weise, denn es muß unbedingt zuerst der Abfall kommen und der Mensch der Sünde, der Sohn des Verderbens,"},
	{4, "4 geoffenbart werden, der Widersacher, der sich über alles erhebt, was Gott oder Gegenstand der Verehrung heißt, so daß er sich in den Tempel Gottes setzt und sich selbst als Gott erklärt."},
	{5, "5 Denket ihr nicht mehr daran, daß ich euch solches sagte, als ich noch bei euch war?"},
	{6, "6 Und nun wisset ihr ja, was noch aufhält, daß er geoffenbart werde zu seiner Zeit."},
	{7, "7 Denn das Geheimnis der Gesetzlosigkeit ist schon an der Arbeit, nur muß der, welcher jetzt aufhält, erst aus dem Wege geschafft werden;"},
	{8, "8 und dann wird der Gesetzlose geoffenbart werden, welchen der Herr Jesus durch den Geist seines Mundes aufreiben, und den er durch die Erscheinung seiner Wiederkunft vernichten wird,"},
	{9, "9 ihn, dessen Auftreten nach der Wirkung des Satans erfolgt, unter Entfaltung aller betrügerischen Kräfte, Zeichen und Wunder"},
	{10, "10 und aller Verführung der Ungerechtigkeit unter denen, die verlorengehen, weil sie die Liebe zur Wahrheit nicht angenommen haben, durch die sie hätten gerettet werden können."},
	{11, "11 Darum sendet ihnen Gott kräftigen Irrtum, daß sie der Lüge glauben,"},
	{12, "12 damit alle gerichtet werden, die der Wahrheit nicht geglaubt, sondern Wohlgefallen an der Ungerechtigkeit gehabt haben."},
	{13, "13 Wir aber sind Gott allezeit zu danken schuldig für euch, vom Herrn geliebte Brüder, daß Gott euch von Anfang an zum Heil erwählt hat, in der Heiligung des Geistes und im Glauben an die Wahrheit,"},
	{14, "14 wozu er euch auch berufen hat durch unser Evangelium, zur Erlangung der Herrlichkeit unsres Herrn Jesus Christus."},
	{15, "15 So stehet denn nun fest, ihr Brüder, und haltet fest an den Überlieferungen, die ihr gelehrt worden seid, sei es durch ein Wort oder durch einen Brief von uns."},
	{16, "16 Er selbst aber, unser Herr Jesus Christus, und Gott, unser Vater, der uns geliebt und einen ewigen Trost und eine gute Hoffnung durch Gnade gegeben hat,"},
	{17, "17 tröste eure Herzen und stärke euch in jedem guten Wort und Werk!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct de3 poems[] = {
	{1, "1 Im übrigen betet für uns, ihr Brüder, daß das Wort des Herrn laufe und verherrlicht werde, wie bei euch,"},
	{2, "2 und daß wir errettet werden von den widrigen und bösen Menschen; denn nicht alle haben den Glauben."},
	{3, "3 Aber der Herr ist treu, der wird euch stärken und bewahren vor dem Argen."},
	{4, "4 Wir trauen euch aber zu im Herrn, daß ihr tut und tun werdet, was wir euch gebieten."},
	{5, "5 Der Herr aber lenke eure Herzen zu der Liebe Gottes und zu der Geduld Christi!"},
	{6, "6 Wir gebieten euch aber, ihr Brüder, im Namen unsres Herrn Jesus Christus, daß ihr euch von jedem Bruder zurückziehet, der unordentlich wandelt und nicht nach der Überlieferung, die ihr von uns empfangen habt."},
	{7, "7 Denn ihr wisset selbst, wie ihr uns nachahmen sollt; denn wir lebten nicht unordentlich unter euch,"},
	{8, "8 wir haben auch nicht umsonst bei jemand Brot gegessen, sondern mit Mühe und Anstrengung Tag und Nacht gearbeitet, um niemand von euch zur Last zu fallen."},
	{9, "9 Nicht daß wir kein Recht dazu hätten, sondern um euch an uns ein Beispiel zu geben, damit ihr uns nachahmen möchtet."},
	{10, "10 Denn als wir bei euch waren, geboten wir euch dies: wenn jemand nicht arbeiten will, soll er auch nicht essen."},
	{11, "11 Wir hören nämlich, daß etliche von euch unordentlich wandeln und nicht arbeiten, sondern unnütze Dinge treiben."},
	{12, "12 Solchen gebieten wir und ermahnen sie durch unsren Herrn Jesus Christus, daß sie mit stiller Arbeit ihr eigenes Brot verdienen."},
	{13, "13 Ihr aber, Brüder, werdet nicht müde, Gutes zu tun!"},
	{14, "14 Wenn aber jemand unsrem brieflichen Wort nicht gehorcht, den kennzeichnet dadurch, daß ihr nicht mit ihm umgehet, damit er sich schämen muß;"},
	{15, "15 doch haltet ihn nicht für einen Feind, sondern weiset ihn zurecht als einen Bruder."},
	{16, "16 Er aber, der Herr des Friedens, gebe euch den Frieden immerdar und auf alle Weise! Der Herr sei mit euch allen!"},
	{17, "17 Der Gruß mit meiner, des Paulus, Hand; dies ist das Zeichen in jedem Briefe, so schreibe ich."},
	{18, "18 Die Gnade unsres Herrn Jesus Christus sei mit euch allen! Amen. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};