#include <map>
#include <string>
class Bible60
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
	struct de3	{ int val; const char *msg; };
	struct de4	{ int val; const char *msg; };
	struct de5	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Petrus, Apostel Jesu Christi, an die auserwählten Fremdlinge in der Zerstreuung, in Pontus, Galatien, Kappadozien, Asien und Bithynien,"},
	{2, "2 nach der Vorsehung Gottes des Vaters, in der Heiligung des Geistes, zum Gehorsam und zur Besprengung mit dem Blute Jesu Christi; Gnade und Friede widerfahre euch mehr und mehr!"},
	{3, "3 Gelobt sei der Gott und Vater unsres Herrn Jesus Christus, der uns nach seiner großen Barmherzigkeit wiedergeboren hat zu einer lebendigen Hoffnung durch die Auferstehung Jesu Christi von den Toten,"},
	{4, "4 zu einem unvergänglichen und unbefleckten und unverwelklichen Erbe, das im Himmel aufbehalten wird für euch,"},
	{5, "5 die ihr in Gottes Macht durch den Glauben bewahrt werdet zu dem Heil, das bereit ist, geoffenbart zu werden in der letzten Zeit;"},
	{6, "6 in welcher ihr frohlocken werdet, die ihr jetzt ein wenig, wo es sein muß, traurig seid in mancherlei Anfechtungen,"},
	{7, "7 damit die Bewährung eures Glaubens, die viel kostbarer ist als die des vergänglichen Goldes (das durchs Feuer erprobt wird), Lob, Preis und Ehre zur Folge habe bei der Offenbarung Jesu Christi;"},
	{8, "8 welchen ihr nicht gesehen und doch lieb habt, an welchen ihr jetzt glaubet, ohne ihn zu sehen, und über den ihr euch freuen werdet mit unaussprechlicher und herrlicher Freude,"},
	{9, "9 wenn ihr das Endziel eures Glaubens davontraget, der Seelen Seligkeit!"},
	{10, "10 Nach dieser Seligkeit suchten und forschten die Propheten, die von der euch widerfahrenen Gnade geweissagt haben."},
	{11, "11 Sie forschten, auf welche und welcherlei Zeit der Geist Christi in ihnen hindeute, der die für Christus bestimmten Leiden und die darauf folgende Herrlichkeit zuvor bezeugte."},
	{12, "12 Ihnen wurde geoffenbart, daß sie nicht sich selbst, sondern euch dienten mit dem, was euch nunmehr durch die verkündigt worden ist, welche euch das Evangelium durch den vom Himmel gesandten heiligen Geist gepredigt haben, Dinge, in welche auch die Engel hineinzuschauen gelüstet."},
	{13, "13 Darum umgürtet die Lenden eures Gemütes, seid nüchtern und setzet eure Hoffnung ganz auf die Gnade, die euch dargeboten wird in der Offenbarung Jesu Christi."},
	{14, "14 Als gehorsame Kinder gewöhnet euch nicht wieder die Begierden an, denen ihr früher in eurer Unwissenheit fröntet;"},
	{15, "15 sondern wie der, welcher euch berufen hat, heilig ist, werdet auch ihr heilig in eurem ganzen Wandel."},
	{16, "16 Denn es steht geschrieben: «Ihr sollt heilig sein! Denn ich bin heilig.»"},
	{17, "17 Und wenn ihr den als Vater anrufet, der ohne Ansehen der Person richtet nach eines jeglichen Werk, so führet euren Wandel, solange ihr hier wallet, mit Furcht,"},
	{18, "18 da ihr ja wisset, daß ihr nicht mit vergänglichen Dingen, mit Silber oder Gold, losgekauft worden seid von eurem eitlen, von den Vätern überlieferten Wandel,"},
	{19, "19 sondern mit dem kostbaren Blute Christi, als eines unschuldigen und unbefleckten Lammes,"},
	{20, "20 der zwar zuvor ersehen war vor Grundlegung der Welt, aber geoffenbart wurde am Ende der Zeiten um euretwillen,"},
	{21, "21 die ihr durch ihn gläubig seid an Gott, der ihn von den Toten auferweckt und ihm Herrlichkeit gegeben hat, so daß euer Glaube auch Hoffnung ist auf Gott."},
	{22, "22 Nachdem ihr eure Seelen durch Gehorsam gegen die Wahrheit gereinigt habt zu ungeheuchelter Bruderliebe, so liebet einander nachhaltig und von Herzen,"},
	{23, "23 als die da wiedergeboren sind nicht aus vergänglichem, sondern aus unvergänglichem Samen, durch das lebendige und bleibende Gotteswort!"},
	{24, "24 Denn «alles Fleisch ist wie Gras und alle seine Herrlichkeit wie des Grases Blume. Das Gras ist verdorrt und die Blume abgefallen; aber des Herrn Wort bleibt in Ewigkeit.»"},
	{25, "25 Das ist aber das Wort, welches euch als frohe Botschaft verkündigt worden ist."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 So leget nun ab alle Bosheit und allen Betrug und Heuchelei und Neid und alle Verleumdungen,"},
	{2, "2 und seid als neugeborene Kindlein begierig nach der vernünftigen, unverfälschten Milch, damit ihr durch sie zunehmet zum Heil,"},
	{3, "3 wenn ihr anders geschmeckt habt, daß der Herr freundlich ist."},
	{4, "4 Da ihr zu ihm gekommen seid, als zu dem lebendigen Stein, der von den Menschen zwar verworfen, bei Gott aber auserwählt und köstlich ist,"},
	{5, "5 so lasset auch ihr euch nun aufbauen als lebendige Steine zum geistlichen Hause, zum heiligen Priestertum, um geistliche Opfer zu opfern, die Gott angenehm sind durch Jesus Christus."},
	{6, "6 Darum steht in der Schrift: «Siehe, ich lege in Zion einen auserwählten, wertvollen Eckstein; und wer an ihn glaubt, soll nicht zuschanden werden.»"},
	{7, "7 Für euch nun, die ihr glaubet, hat er Wert; für die Ungläubigen aber ist der Stein, den die Bauleute verworfen haben und der zum Eckstein geworden ist, ein Stein des Anstoßens und ein Fels des Ärgernisses."},
	{8, "8 Sie stoßen sich, weil sie dem Wort nicht glauben, wozu sie auch gesetzt sind."},
	{9, "9 Ihr aber seid ein auserwähltes Geschlecht, ein königliches Priestertum, ein heiliges Volk, ein Volk des Eigentums, damit ihr die Tugenden dessen verkündiget, der euch aus der Finsternis zu seinem wunderbaren Licht berufen hat,"},
	{10, "10 die ihr einst nicht ein Volk waret, nun aber Gottes Volk seid, und einst nicht begnadigt waret, nun aber begnadigt seid."},
	{11, "11 Geliebte, ich ermahne euch als Fremdlinge und Pilgrime: Enthaltet euch von den fleischlichen Lüsten, welche wider die Seele streiten;"},
	{12, "12 und führet einen guten Wandel unter den Heiden, damit sie da, wo sie euch als Übeltäter verleumden, doch auf Grund der guten Werke, die sie sehen, Gott preisen am Tage der Untersuchung."},
	{13, "13 Seid untertan aller menschlichen Ordnung,"},
	{14, "14 um des Herrn willen, es sei dem König als dem Oberhaupt, oder den Statthaltern als seinen Gesandten zur Bestrafung der Übeltäter und zum Lobe derer, die Gutes tun."},
	{15, "15 Denn das ist der Wille Gottes, daß ihr durch Gutestun den unverständigen und unwissenden Menschen den Mund stopfet;"},
	{16, "16 als Freie, und nicht als hättet ihr die Freiheit zum Deckmantel der Bosheit, sondern als Knechte Gottes."},
	{17, "17 Ehret jedermann, liebet die Bruderschaft, fürchtet Gott, ehret den König!"},
	{18, "18 Die Hausknechte seien mit aller Furcht den Herren untertan, nicht nur den guten und milden, sondern auch den wunderlichen!"},
	{19, "19 Denn das ist Gnade, wenn jemand aus Gewissenhaftigkeit gegen Gott Kränkungen erträgt, indem er Unrecht leidet."},
	{20, "20 Denn was ist das für ein Ruhm, wenn ihr Streiche erduldet, weil ihr gefehlt habt? Wenn ihr aber für Gutestun leidet und es erduldet, das ist Gnade bei Gott."},
	{21, "21 Denn dazu seid ihr berufen, weil auch Christus für euch gelitten und euch ein Vorbild hinterlassen hat, daß ihr seinen Fußstapfen nachfolget."},
	{22, "22 «Er hat keine Sünde getan, es ist auch kein Betrug in seinem Munde erfunden worden»;"},
	{23, "23 er schalt nicht, da er gescholten ward, er drohte nicht, da er litt, sondern übergab es dem, der gerecht richtet;"},
	{24, "24 er hat unsere Sünden selbst hinaufgetragen an seinem Leibe auf das Holz, damit wir, der Sünde gestorben, der Gerechtigkeit leben möchten; «durch seine Wunden seid ihr heil geworden.»"},
	{25, "25 Denn ihr waret «wie irrende Schafe», nun aber seid ihr bekehrt zu dem Hirten und Hüter eurer Seelen."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct de3 poems[] = {
	{1, "1 Gleicherweise sollen auch die Frauen ihren eigenen Männern untertan sein, damit, wenn auch etliche dem Worte nicht glauben, sie durch der Frauen Wandel ohne Wort gewonnen werden,"},
	{2, "2 wenn sie euren in Furcht keuschen Wandel ansehen."},
	{3, "3 Euer Schmuck soll nicht der äußerliche sein, mit Haarflechten und Goldumhängen und Kleideranlegen,"},
	{4, "4 sondern der verborgene Mensch des Herzens mit dem unvergänglichen Schmuck des sanften und stillen Geistes, welcher vor Gott wertvoll ist."},
	{5, "5 Denn so haben sich einst auch die heiligen Frauen geschmückt, welche ihre Hoffnung auf Gott setzten und ihren Männern untertan waren,"},
	{6, "6 wie Sara dem Abraham gehorchte und ihn «Herr» nannte; deren Töchter ihr geworden seid, wenn ihr Gutes tut und euch durch keine Drohung abschrecken lasset."},
	{7, "7 Und ihr Männer, wohnet mit Vernunft bei dem weiblichen Teil als dem schwächeren und erweiset ihnen Ehre als solchen, die auch Miterben der Gnade des Lebens sind, und damit eure Gebete nicht gehindert werden."},
	{8, "8 Endlich aber seid alle gleichgesinnt, mitleidig, brüderlich, barmherzig, demütig!"},
	{9, "9 Vergeltet nicht Böses mit Bösem, oder Scheltwort mit Scheltwort, sondern im Gegenteil segnet, weil ihr dazu berufen seid, daß ihr Segen ererbet."},
	{10, "10 Denn «wem das Leben lieb ist und wer gute Tage sehen will, der bewahre seine Zunge vor Bösem und seine Lippen, daß sie nicht trügen;"},
	{11, "11 er wende sich vom Bösen und tue Gutes, er suche den Frieden und jage ihm nach!"},
	{12, "12 Denn die Augen des Herrn sehen auf die Gerechten, und seine Ohren merken auf ihr Flehen; das Angesicht des Herrn aber ist gegen die gerichtet, welche Böses tun.»"},
	{13, "13 Und wer will euch schaden, wenn ihr euch des Guten befleißiget?"},
	{14, "14 Aber wenn ihr auch um Gerechtigkeit willen zu leiden habt, seid ihr selig. Ihr Drohen aber fürchtet nicht und erschrecket nicht; sondern heiliget den Herrn Christus in euren Herzen!"},
	{15, "15 und seid allezeit bereit zur Verantwortung gegen jedermann, der Rechenschaft fordert über die Hoffnung, die in euch ist,"},
	{16, "16 aber mit Sanftmut und Furcht; und habet ein gutes Gewissen, damit die, welche euren guten Wandel in Christus verlästern, zuschanden werden mit ihren Verleumdungen."},
	{17, "17 Denn es ist besser, wenn der Wille Gottes es so haben will, ihr leidet für Gutestun, als für Bösestun."},
	{18, "18 Denn auch Christus hat einmal für Sünden gelitten, ein Gerechter für Ungerechte, auf daß er uns zu Gott führte, und er wurde getötet nach dem Fleisch, aber lebendig gemacht nach dem Geist,"},
	{19, "19 in welchem er auch hinging und den Geistern im Gefängnis predigte,"},
	{20, "20 die einst nicht gehorchten, als Gottes Langmut zuwartete in den Tagen Noahs, während die Arche zugerichtet wurde, in welcher wenige, nämlich acht Seelen, hindurchgerettet wurden durchs Wasser."},
	{21, "21 Als Abbild davon rettet nun auch uns die Taufe, welche nicht ein Abtun fleischlichen Schmutzes ist, sondern die an Gott gerichtete Bitte um ein gutes Gewissen, durch die Auferstehung Jesu Christi,"},
	{22, "22 welcher seit seiner Himmelfahrt zur Rechten Gottes ist, wo ihm Engel und Gewalten und Kräfte untertan sind."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct de4 poems[] = {
	{1, "1 Da nun Christus am Fleische gelitten hat, so wappnet auch ihr euch mit derselben Gesinnung; denn wer am Fleische gelitten hat, der hat mit den Sünden abgeschlossen,"},
	{2, "2 um die noch verbleibende Zeit im Fleische nicht mehr den Lüsten der Menschen, sondern dem Willen Gottes zu leben."},
	{3, "3 Denn es ist genug, daß ihr die vergangene Zeit des Lebens nach heidnischem Willen zugebracht habt, indem ihr euch gehen ließet in Ausschweifungen, Lüsten, Trunksucht, Schmausereien, Zechgelagen und unerlaubtem Götzendienst."},
	{4, "4 Das befremdet sie, daß ihr nicht mitlaufet in denselben heillosen Schlamm, und darum lästern sie;"},
	{5, "5 sie werden aber dem Rechenschaft geben müssen, der bereit ist zu richten die Lebendigen und die Toten."},
	{6, "6 Denn dazu ist auch Toten das Evangelium verkündigt worden, daß sie gerichtet werden als Menschen am Fleisch, aber Gott gemäß leben im Geist."},
	{7, "7 Es ist aber nahe gekommen das Ende aller Dinge. So seid nun verständig und nüchtern zum Gebet."},
	{8, "8 Vor allem aber habet gegeneinander nachhaltige Liebe; denn die Liebe deckt eine Menge von Sünden."},
	{9, "9 Seid gastfrei gegeneinander ohne Murren!"},
	{10, "10 Dienet einander, ein jeder mit der Gabe, die er empfangen hat, als gute Haushalter der mannigfachen Gnade Gottes:"},
	{11, "11 Wenn jemand redet, so rede er es als Gottes Wort; wenn jemand dient, so tue er es als aus dem Vermögen, das Gott darreicht, auf daß in allem Gott verherrlicht werde durch Jesus Christus, welchem die Herrlichkeit und die Gewalt gehört von Ewigkeit zu Ewigkeit! Amen."},
	{12, "12 Geliebte, lasset euch die unter euch entstandene Feuerprobe nicht befremden, als widerführe euch etwas Fremdartiges;"},
	{13, "13 sondern je mehr ihr der Leiden Christi teilhaftig seid, freuet euch, damit ihr auch bei der Offenbarung seiner Herrlichkeit frohlocken könnt."},
	{14, "14 Selig seid ihr, wenn ihr um des Namens Christi willen geschmäht werdet! Denn der Geist der Herrlichkeit und Gottes ruht auf euch; bei ihnen ist er verlästert, bei euch aber gepriesen."},
	{15, "15 Niemand aber unter euch leide als Mörder oder Dieb oder Übeltäter, oder weil er sich in fremde Dinge mischt;"},
	{16, "16 leidet er aber als Christ, so schäme er sich nicht, verherrliche aber Gott mit diesem Namen!"},
	{17, "17 Denn es ist Zeit, daß das Gericht anfange am Hause Gottes; wenn aber zuerst bei uns, wie wird das Ende derer sein, die sich von dem Evangelium Gottes nicht überzeugen lassen?"},
	{18, "18 Und wenn der Gerechte kaum gerettet wird, wo will der Gottlose und Sünder erscheinen?"},
	{19, "19 So mögen denn die, welche nach Gottes Willen leiden, dem treuen Schöpfer ihre Seelen anbefehlen und dabei tun, was recht ist."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view5() {
struct de5 poems[] = {
	{1, "1 Die Ältesten unter euch ermahne ich nun als Mitältester und Zeuge der Leiden Christi, aber auch als Mitgenosse der Herrlichkeit, die geoffenbart werden soll:"},
	{2, "2 Weidet die Herde Gottes bei euch, nicht gezwungen, sondern freiwillig, nicht aus schnöder Gewinnsucht, sondern aus Zuneigung,"},
	{3, "3 nicht als Herrscher über die euch zugewiesenen Seelen, sondern als Vorbilder der Herde!"},
	{4, "4 So werdet ihr, wenn der Oberhirt erscheint, den unverwelklichen Ehrenkranz davontragen!"},
	{5, "5 Gleicherweise ihr Jüngeren, seid untertan den Ältesten; umschürzet euch aber alle gegenseitig mit der Demut! Denn «Gott widersteht den Hoffärtigen, aber den Demütigen gibt er Gnade»."},
	{6, "6 So demütiget euch nun unter die gewaltige Hand Gottes, damit er euch erhöhe zu seiner Zeit!"},
	{7, "7 Alle eure Sorge werfet auf ihn; denn er sorgt für euch!"},
	{8, "8 Seid nüchtern und wachet! Euer Widersacher, der Teufel, geht umher wie ein brüllender Löwe und sucht, wen er verschlingen könne;"},
	{9, "9 dem widerstehet, fest im Glauben, da ihr wisset, daß eure Brüder in der Welt die gleichen Leiden erdulden."},
	{10, "10 Der Gott aller Gnade aber, der euch zu seiner ewigen Herrlichkeit in Christus berufen hat, wird euch selbst nach kurzem Leiden zubereiten, festigen, stärken, gründen."},
	{11, "11 Sein ist die Kraft von Ewigkeit zu Ewigkeit! Amen."},
	{12, "12 Durch Silvanus, der, wie ich glaube, euch ein treuer Bruder ist, habe ich euch in Kürze geschrieben, um euch zu ermahnen und zu bezeugen, daß dies die wahre Gnade Gottes ist, in welcher ihr stehet."},
	{13, "13 Es grüßt euch die Mitauserwählte in Babylon und Markus, mein Sohn. Grüßet einander mit dem Kuß der Liebe!"},
	{14, "14 Friede sei mit euch allen, die in Christus Jesus sind! "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};