#include <map>
#include <string>
class Bible61
{
	struct de1	{ int val; const char *msg; };
	struct de2	{ int val; const char *msg; };
	struct de3	{ int val; const char *msg; };
public:
static void view1() {
struct de1 poems[] = {
	{1, "1 Symeon Petrus, Knecht und Apostel Jesu Christi, an die, welche einen gleich wertvollen Glauben erlangt haben wie wir an die Gerechtigkeit unsres Gottes und Retters Jesus Christus:"},
	{2, "2 Gnade und Friede widerfahre euch mehr und mehr in der Erkenntnis Gottes und unsres Herrn Jesus!"},
	{3, "3 Nachdem seine göttliche Kraft uns alles, was zum Leben und zur Gottseligkeit dient, geschenkt hat, durch die Erkenntnis dessen, der uns kraft seiner Herrlichkeit und Tugend berufen hat,"},
	{4, "4 durch welche uns die teuersten und größten Verheißungen geschenkt sind, damit ihr durch dieselben göttlicher Natur teilhaftig werdet, nachdem ihr dem in der Welt durch die Lust herrschenden Verderben entflohen seid,"},
	{5, "5 so setzet nun all euren Fleiß zu dem hinzu und reichet dar in eurem Glauben die Tugend, in der Tugend aber die Erkenntnis,"},
	{6, "6 in der Erkenntnis aber die Enthaltsamkeit, in der Enthaltsamkeit aber die Ausdauer, in der Ausdauer aber die Gottseligkeit,"},
	{7, "7 in der Gottseligkeit aber die Bruderliebe, in der Bruderliebe aber die Liebe zu allen Menschen."},
	{8, "8 Denn wo solches reichlich bei euch vorhanden ist, wird es euch nicht müßig noch unfruchtbar machen für die Erkenntnis unsres Herrn Jesus Christus."},
	{9, "9 Wer aber solches nicht hat, der ist blind, kurzsichtig und hat die Reinigung seiner ehemaligen Sünden vergessen."},
	{10, "10 Darum, meine Brüder, befleißiget euch desto mehr, eure Berufung und Erwählung fest zu machen. Denn wo ihr solches tut, werdet ihr niemals straucheln;"},
	{11, "11 denn so wird euch der Eingang in das ewige Reich unsres Herrn und Retters Jesus Christus reichlich gewährt werden."},
	{12, "12 Darum will ich euch stets daran erinnern, wiewohl ihr es wisset und in der gegenwärtigen Wahrheit befestigt seid."},
	{13, "13 Ich halte es aber für billig, solange ich in dieser Hütte bin, euch durch solche Erinnerung aufzuwecken,"},
	{14, "14 da ich weiß, daß ich meine Hütte bald ablegen muß, wie mir auch unser Herr Jesus Christus eröffnet hat."},
	{15, "15 Ich will mich aber befleißigen, daß ihr auch nach meinem Abschied allezeit etwas habet, wodurch ihr euer Gedächtnis auffrischen könnet."},
	{16, "16 Denn wir sind nicht klug ersonnenen Fabeln gefolgt, als wir euch die Kraft und Wiederkunft unsres Herrn Jesus Christus kundtaten, sondern wir sind Augenzeugen seiner Herrlichkeit gewesen."},
	{17, "17 Denn er empfing von Gott dem Vater Ehre und Herrlichkeit, als eine Stimme von der hocherhabenen Herrlichkeit daherkam, des Inhalts: «Dies ist mein lieber Sohn, an welchem ich Wohlgefallen habe!»"},
	{18, "18 Und diese Stimme hörten wir vom Himmel her kommen, als wir mit ihm auf dem heiligen Berge waren."},
	{19, "19 Und wir halten nun desto fester an dem prophetischen Wort, und ihr tut wohl, darauf zu achten als auf ein Licht, das an einem dunklen Orte scheint, bis der Tag anbricht und der Morgenstern aufgeht in euren Herzen;"},
	{20, "20 wobei ihr das zuerst wissen müßt, daß keine Weissagung der Schrift ein Werk eigener Deutung ist."},
	{21, "21 Denn niemals wurde durch menschlichen Willen eine Weissagung hervorgebracht, sondern vom heiligen Geist getrieben redeten heilige Menschen, von Gott gesandt."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct de2 poems[] = {
	{1, "1 Es gab aber auch falsche Propheten unter dem Volk, wie auch unter euch falsche Lehrer sein werden, welche verderbliche Sekten nebeneinführen und durch Verleugnung des Herrn, der sie erkauft hat, ein schnelles Verderben über sich selbst bringen werden."},
	{2, "2 Und viele werden ihren Ausschweifungen nachfolgen, und um ihretwillen wird der Weg der Wahrheit verlästert werden."},
	{3, "3 Und aus Habsucht werden sie euch mit betrügerischen Worten ausbeuten; aber das Urteil über sie ist von alters her nicht müßig, und ihr Verderben schlummert nicht."},
	{4, "4 Denn wenn Gott die Engel, die gesündigt hatten, nicht verschonte, sondern sie in Banden der Finsternis der Hölle übergab, um sie zum Gericht aufzubehalten,"},
	{5, "5 und wenn er die alte Welt nicht verschonte, sondern Noah, den Prediger der Gerechtigkeit, als achten beschützte, als er die Sündflut über die Welt der Gottlosen brachte,"},
	{6, "6 auch die Städte Sodom und Gomorra einäscherte und so zum Untergang verurteilte, womit er sie zukünftigen Gottlosen zum Beispiel setzte,"},
	{7, "7 während er den gerechten Lot herausrettete, der durch den ausschweifenden Lebenswandel der Zuchtlosen geplagt worden war"},
	{8, "8 denn dadurch, daß er es mitansehen und mitanhören mußte, quälte der Gerechte, der unter ihnen wohnte, Tag für Tag seine gerechte Seele mit ihren gottlosen Werken;"},
	{9, "9 so weiß der Herr die Gottseligen aus der Prüfung zu erretten, die Ungerechten aber für den Tag des Gerichts zur Bestrafung aufzubehalten,"},
	{10, "10 allermeist die, welche aus Begierde nach Befleckung dem Fleische nachlaufen und die Herrschaft verachten. Verwegen, frech, wie sie sind, fürchten sie sich nicht, die Majestäten zu lästern,"},
	{11, "11 wo doch Engel, die an Stärke und Macht größer sind, kein lästerndes Urteil wider sie bei dem Herrn vorbringen."},
	{12, "12 Diese aber, wie unvernünftige Tiere von Natur zum Fang und Verderben geboren, lästern über das, was sie nicht verstehen, und werden in ihrer Verdorbenheit umkommen,"},
	{13, "13 indem sie so den Lohn der Ungerechtigkeit davontragen. Sie halten die zeitliche Wollust für Vergnügen, sind Schmutz und Schandflecken und schwelgen bei ihren Liebesmahlen und wenn sie mit euch zusammen schmausen;"},
	{14, "14 dabei haben sie Augen voll Ehebruch, hören nie auf zu sündigen, locken an sich die unbefestigten Seelen, haben ein Herz, geübt in der Habsucht, sind Kinder des Fluchs."},
	{15, "15 Weil sie den richtigen Weg verlassen haben, irren sie jetzt herum und folgen dem Wege Bileams, des Sohnes Beors, welcher den Lohn der Ungerechtigkeit liebte;"},
	{16, "16 aber er bekam die Strafe für seine Übertretung: das stumme Lasttier redete mit Menschenstimme und wehrte der Torheit des Propheten."},
	{17, "17 Solche Menschen sind Brunnen ohne Wasser, und Wolken, vom Sturmwind getrieben, welchen das Dunkel der Finsternis aufbehalten ist."},
	{18, "18 Denn mit hochfahrenden, nichtigen Reden locken sie durch ausschweifende Fleischeslust diejenigen an sich, welche denen, die in der Irre gehen, kaum entflohen waren,"},
	{19, "19 dabei verheißen sie ihnen Freiheit, wo sie doch selbst Knechte des Verderbens sind; denn wovon jemand überwunden ist, dessen Sklave ist er geworden."},
	{20, "20 Denn wenn sie durch die Erkenntnis des Herrn und Retters Jesus Christus den Befleckungen der Welt entflohen sind, aber wieder darin verstrickt werden und unterliegen, so wird es mit ihnen zuletzt ärger als zuerst."},
	{21, "21 Denn es wäre für sie besser, daß sie den Weg der Gerechtigkeit nie erkannt hätten, als daß sie nach erlangter Erkenntnis sich wieder abwenden von dem ihnen überlieferten heiligen Gebot."},
	{22, "22 Es ist ihnen ergangen nach dem wahren Sprichwort: «Der Hund frißt wieder, was er gespien hat, und die Sau wälzt sich nach der Schwemme wieder im Kot!»"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct de3 poems[] = {
	{1, "1 Geliebte, dies ist schon der zweite Brief, den ich euch schreibe, um durch Erinnerung euren lauteren Sinn aufzuwecken,"},
	{2, "2 damit ihr der Worte gedenket, die von den heiligen Propheten vorausgesagt worden sind, und dessen, was der Herr und Retter euch durch die Apostel aufgetragen hat,"},
	{3, "3 wobei ihr vor allem das wissen müßt, daß in den letzten Tagen Spötter kommen werden, die in ihrer Spötterei nach ihren eigenen Lüsten wandeln und sagen:"},
	{4, "4 «Wo ist die Verheißung seiner Wiederkunft? denn seitdem die Väter entschlafen sind, bleibt alles so, wie es am Anfang der Schöpfung war!»"},
	{5, "5 Dabei vergessen sie aber absichtlich, daß schon vorlängst Himmel waren und eine Erde aus Wasser und durch Wasser entstanden ist durch Gottes Wort;"},
	{6, "6 und daß durch diese die damalige Welt infolge einer Wasserflut zugrunde ging."},
	{7, "7 Die jetzigen Himmel aber und die Erde werden durch dasselbe Wort fürs Feuer aufgespart und bewahrt für den Tag des Gerichts und des Verderbens der gottlosen Menschen."},
	{8, "8 Dieses eine aber sei euch nicht verborgen, Geliebte, daß ein Tag vor dem Herrn ist wie tausend Jahre, und tausend Jahre wie ein Tag!"},
	{9, "9 Der Herr säumt nicht mit der Verheißung, wie etliche es für ein Säumen halten, sondern er ist langmütig gegen uns, da er nicht will, daß jemand verloren gehe, sondern daß jedermann Raum zur Buße habe."},
	{10, "10 Es wird aber der Tag des Herrn kommen wie ein Dieb; da werden die Himmel mit Krachen vergehen, die Elemente aber vor Hitze sich auflösen und die Erde und die Werke darauf verbrennen."},
	{11, "11 Da nun dies alles derart aufgelöst wird, wie sehr solltet ihr euch auszeichnen durch heiligen Wandel und Gottseligkeit,"},
	{12, "12 dadurch, daß ihr erwartet und beschleuniget die Ankunft des Tages Gottes, an welchem die Himmel in Glut sich auflösen und die Elemente vor Hitze zerschmelzen werden!"},
	{13, "13 Wir erwarten aber einen neuen Himmel und eine neue Erde, nach seiner Verheißung, in welchen Gerechtigkeit wohnt."},
	{14, "14 Darum, Geliebte, weil ihr solches erwartet, so befleißiget euch, daß ihr unbefleckt und tadellos vor ihm in Frieden erfunden werdet!"},
	{15, "15 Und die Geduld unsres Herrn achtet für euer Heil, wie auch unser lieber Bruder Paulus nach der ihm verliehenen Weisheit euch geschrieben hat,"},
	{16, "16 wie auch in allen Briefen, wo er davon spricht, in welchen etliches schwer zu verstehen ist, was die Ungelehrten und Unbefestigten verdrehen, wie auch die übrigen Schriften, zu ihrem eigenen Verderben."},
	{17, "17 Ihr aber, Geliebte, da ihr solches zum voraus wisset, hütet euch, daß ihr nicht durch die Verführung gewissenloser Menschen mitfortgerissen werdet und euren eigenen festen Stand verlieret!"},
	{18, "18 Wachset dagegen in der Gnade und Erkenntnis unsres Herrn und Retters Jesus Christus! Sein ist die Herrlichkeit, sowohl jetzt, als für den Tag der Ewigkeit! "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};