static void view18() {
struct fa18 poems[] = {
	{1, "1  و بعد از روزهای‌ بسیار، كلام‌ خداوند در سال‌ سوم‌، به‌ ایلیا نازل‌ شده‌، گفت‌: برو و خود را به‌ اَخاب‌ بنما و من‌ بر زمین‌ باران‌ خواهم‌ بارانید. "},
	{2, "2  پس‌ ایلیا روانه‌ شد تا خود را به‌ اَخاب‌ بنماید و قحط‌ در سامره‌ سخت‌ بود. "},
	{3, "3  و اَخاب‌ عوبدیا را كه‌ ناظر خانۀ او بود، احضار نمود و عوبدیا از خداوند بسیار می‌ترسید. "},
	{4, "4  و هنگامی‌ كه‌ ایزابل‌ انبیای‌ خداوند را هلاك‌ می‌ساخت‌، عوبدیا صد نفر از انبیا را گرفته‌، ایشان‌ را پنجاه‌ پنجاه‌ در مغاره‌ پنهان‌ كرد و ایشان‌ را به‌ نان‌ و آب‌ پرورد. "},
	{5, "5  و اَخاب‌ به‌ عوبدیا گفت‌: در زمین‌ نزد تمامی‌ چشمه‌های‌ آب‌ و همۀ نهرها برو كه‌ شاید علف‌ پیدا كرده‌، اسبان‌ و قاطران‌ را زنده‌ نگاه‌ داریم‌ و همۀ بهایم‌ از ما تلف‌ نشوند. "},
	{6, "6  پس‌ زمین‌ را در میان‌ خود تقسیم‌ كردند تا در آن‌ عبور نمایند؛ اَخاب‌ به‌ یك‌ راه‌ تنها رفت‌، و عوبدیا به‌ راه‌ دیگر، تنها رفت‌. "},
	{7, "7  و چون‌ عوبدیا در راه‌ بود، اینك‌ ایلیا بدو برخورد؛ و او وی‌ را شناخته‌، به‌ روی‌ خود در افتاده‌، گفت‌: آیا آقای‌ من‌ ایلیا، تو هستی‌؟ "},
	{8, "8  اورا جواب‌ داد كه‌ من‌ هستم‌؛ برو و به‌ آقای‌ خود بگو كه‌ اینك‌ ایلیاست‌. "},
	{9, "9  گفت‌: چه‌ گناه‌ كرده‌ام‌ كه‌ بندۀ خود را به‌ دست‌ اَخاب‌ تسلیم‌ می‌كنی‌ تا مرا بكشد. "},
	{10, "10  به‌ حیات‌ یهُوَه‌، خدای‌ تو قسم‌ كه‌ قومی‌ و مملكتی‌ نیست‌، كه‌ آقایم‌ به‌ جهت‌ طلب‌ تو آنجا نفرستاده‌ باشد و چون‌ می‌گفتند كه‌ اینجا نیست‌ به‌ آن‌ مملكت‌ و قوم‌ قَسَم‌ می‌داد كه‌ تو را نیافته‌اند. "},
	{11, "11  و حال‌ می‌گویی‌ برو به‌ آقای‌ خود بگو كه‌ اینك‌ ایلیاست‌؟ "},
	{12, "12  و واقع‌ خواهد شد كه‌ چون‌ از نزد تو رفته‌ باشم‌، روح‌ خداوند تو را به‌ جایی‌ كه‌ نمی‌دانم‌، بردارد و وقتی‌ كه‌ بروم‌ و به‌ اَخاب‌ خبر دهم‌ و او تو را نیابد، مرا خواهد كشت‌. و بنده‌ات‌ از طفولیت‌ خود از خداوند می‌ترسد. "},
	{13, "13  مگر آقایم‌ اطلاّع‌ ندارد از آنچه‌ من‌ هنگامی‌ كه‌ ایزابل‌ انبیای‌ خداوند را می‌كشت‌ كردم‌، كه‌ چگونه‌ صد نفر از انبیای‌ خداوند را پنجاه‌ پنجاه‌ در مغاره‌ای‌ پنهان‌ كرده‌، ایشان‌ را به‌ نان‌ و آب‌ پروردم‌؟ "},
	{14, "14  و حال‌ تو می‌گویی‌ برو و آقای‌ خود را بگو كه‌ اینك‌ ایلیاست‌؟ و مرا خواهد كشت‌. "},
	{15, "15  ایلیا گفت‌: به‌ حیات‌ یهُوَه‌، صبایوت‌ كه‌ به‌ حضور وی‌ ایستاده‌ام‌ قسم‌ كه‌ خود را امروز به‌ وی‌ ظاهر خواهم‌ نمود. "},
	{16, "16  پس‌ عوبدیا برای‌ ملاقات‌ اَخاب‌ رفته‌، او را خبر داد؛ و اَخاب‌ به‌ جهت‌ ملاقات‌ ایلیا آمد. "},
	{17, "17  و چون‌ اَخاب‌ ایلیا را دید، اَخاب‌ وی‌ را گفت‌: آیا تو هستی‌ كه‌ اسرائیل‌ را مضطرب‌ می‌سازی‌؟ "},
	{18, "18  گفت‌: من‌ اسرائیل‌ را مضطرب‌ نمی‌سازم‌، بلكه‌ تو و خاندان‌ پدرت‌؛ چونكه‌ اوامر خداوند را ترك‌ كردید و تو پیروی‌ بعلیم‌ را نمودی‌. "},
	{19, "19  پس‌ الان‌ بفرست‌ و تمام‌ اسرائیل‌ رانزد من‌ بر كوه‌ كرمل‌ جمع‌ كن‌ و انبیای‌ بعل‌ را نیز چهارصد و پنجاه‌ نفر، و انبیای‌ اشیریم‌ را چهارصد نفر كه‌ بر سفرۀ ایزابل‌ می‌خورند. "},
	{20, "20  پس‌ اَخاب‌ نزد جمیع‌ بنی‌اسرائیل‌ فرستاده‌، انبیا را بر كوه‌ كرمل‌ جمع‌ كرد. "},
	{21, "21  و ایلیا به‌ تمامی‌ قوم‌ نزدیك‌ آمده‌، گفت‌: تا به‌ كی‌ در میان‌ دو فرقه‌ می‌لنگید؟ اگر یهُوَه‌ خداست‌، او را پیروی‌ نمایید! و اگر بَعْل‌ است‌، وی‌ را پیروی‌ نمایید! اما قوم‌ در جواب‌ او هیچ‌ نگفتند. "},
	{22, "22  پس‌ ایلیا به‌ قوم‌ گفت‌: من‌ تنها نبی یهُوَه‌ باقی‌ مانده‌ام‌ و انبیای‌ بَعل‌ چهارصد و پنجاه‌ نفرند. "},
	{23, "23  پس‌ به‌ ما دو گاو بدهند و یك‌ گاو به‌ جهت‌ خود انتخاب‌ كرده‌، و آن‌ را قطعه‌ قطعه‌ نموده‌، آن‌ را بر هیزم‌ بگذارند و آتش‌ ننهند؛ و من‌ گاو دیگر را حاضر ساخته‌، بر هیزم‌ می‌گذارم‌ و آتش‌ نمی‌نهم‌. "},
	{24, "24  و شما اسم‌ خدای‌ خود را بخوانید و من‌ نام‌ یهُوَه‌ را خواهم‌ خواند؛ و آن‌ خدایی‌ كه‌ به‌ آتش‌ جواب‌ دهد، او خدا باشد. و تمامی‌ قوم‌ در جواب‌ گفتند: نیكو گفتی‌. "},
	{25, "25  پس‌ ایلیا به‌ انبیای‌ بَعْل‌ گفت‌: یك‌ گاو برای‌ خود انتخاب‌ كرده‌، شما اول‌ آن‌ را حاضر سازید زیرا كه‌ بسیار هستید و به‌ نام‌ خدای‌ خود بخوانید، اما آتش‌ نگذارید. "},
	{26, "26  پس‌ گاو را كه‌ به‌ ایشان‌ داده‌ شده‌ بود، گرفتند و آن‌ را حاضر ساخته‌، نام‌ بعل‌ را از صبح‌ تا ظهر خوانده‌، می‌گفتند: ای‌ بعل‌ ما را جواب‌ بده‌. لیكن‌ هیچ‌ صدا یا جوابی‌ نبود و ایشان‌ بر مذبحی‌ كه‌ ساخته‌ بودند، جست‌ و خیز می‌نمودند. "},
	{27, "27  و به‌ وقت‌ ظهر، ایلیا ایشان‌ را مسخره‌ نموده‌، گفت‌: به‌ آواز بلند بخوانید زیرا كه‌ او خداست‌! شاید متفكّر است‌ یا به‌ خلوت‌ رفته‌، یا در سفر می‌باشد، یا شاید كه‌ در خواب‌ است‌ و باید او را بیدار كرد! "},
	{28, "28  و ایشان‌ به‌ آوازبلند می‌خواندند و موافق‌ عادت‌ خود، خویشتن‌ را به‌ تیغها و نیزه‌ها مجروح‌ می‌ساختند، به‌ حدی‌ كه‌ خون‌ بر ایشان‌ جاری‌ می‌شد. "},
	{29, "29  و بعد از گذشتن‌ ظهر تا وقت‌ گذرانیدن‌ هدیۀ عصری‌، ایشان‌ نبوت‌ می‌كردند؛ لیكن‌ نه‌ آوازی‌ بود و نه‌ كسی‌ كه‌ جواب‌ دهد یا توجه‌ نماید. "},
	{30, "30  آنگاه‌ ایلیا به‌ تمامی‌ قوم‌ گفت‌: نزد من‌ بیایید. و تمامی‌ قوم‌ نزد وی‌ آمدند و مذبح‌ یهُوَه‌ را كه‌ خراب‌ شده‌ بود، تعمیر نمود. "},
	{31, "31  و ایلیا موافق‌ شمارۀ اسباط‌ بنی‌یعقوب‌ كه‌ كلام‌ خداوند بر وی‌ نازل‌ شده‌، گفته‌ بود كه‌ نام‌ تو اسرائیل‌ خواهد بود، دوازده‌ سنگ‌ گرفت‌. "},
	{32, "32  و به‌ آن‌ سنگها مذبحی‌ به‌ نام‌ یهُوَه‌ بنا كرد و گرداگرد مذبح‌ خندقی‌ كه‌ گنجایش‌ دو پیمانه‌ بَزر داشت‌، ساخت‌. "},
	{33, "33  و هیزم‌ را ترتیب‌ داد و گاو را قطعه‌ قطعه‌ نموده‌، آن‌ را بر هیزم‌ گذاشت‌. پس‌ گفت‌: چهار خُمْ از آب‌ پر كرده‌، آن‌ را بر قربانی‌ سوختنی‌ و هیزم‌ بریزید. "},
	{34, "34  پس‌ گفت‌: بار دیگر بكنید؛ و گفت‌: بار سوم‌ بكنید. و بار سوم‌ كردند. "},
	{35, "35  و آب‌ گرداگرد مذبح‌ جاری‌ شد و خندق‌ نیز از آب‌ پر گشت‌. "},
	{36, "36  و در وقت‌ گذرانیدن‌ هدیۀ عصری‌، ایلیای‌ نبی‌ نزدیك‌ آمده‌، گفت‌: ای‌ یهُوَه‌، خدای‌ ابراهیم‌ و اسحاق‌ و اسرائیل‌، امروز معلوم‌ بشود كه‌ تو در اسرائیل‌ خدا هستی‌ و من‌ بندۀ تو هستم‌ و تمام‌ این‌ كارها را به‌ فرمان‌ تو كرده‌ام‌. "},
	{37, "37  مرا اجابت‌ فرما ای‌ خداوند ! مرا اجابت‌ فرما تا این‌ قوم‌ بدانند كه‌ تو یهُوَه‌ خدا هستی‌ و اینكه‌ دل‌ ایشان‌ را باز پس‌ گردانیدی‌. "},
	{38, "38  آنگاه‌ آتشِ یهُوَه‌ افتاده‌، قربانی‌ سوختنی‌ و هیزم‌ و سنگها و خاك‌ را بلعید و آب‌را كه‌ در خندق‌ بود، لیسید. "},
	{39, "39  و تمامی‌ قوم‌ چون‌ این‌ را دیدند، به‌ روی‌ خود افتاده‌، گفتند: یهُوَه‌، او خداست‌! یهُوَه‌ او خداست‌! "},
	{40, "40  و ایلیا به‌ ایشان‌ گفت‌: انبیای‌ بَعْل‌ را بگیرید و یكی‌ از ایشان‌ رهایی‌ نیابد. پس‌ ایشان‌ را گرفتند و ایلیا ایشان‌ را نزد نهر قیشون‌ فرود آورده‌، ایشان‌ را در آنجا كشت‌. "},
	{41, "41  و ایلیا به‌ اَخاب‌ گفت‌: برآمده‌، اكل‌ و شرب‌ نما زیرا كه‌ صدای‌ بارانِ بسیار می‌آید. "},
	{42, "42  پس‌ اَخاب‌ برآمده‌، اكل‌ و شرب‌ نمود. و ایلیا بر قلۀ كَرْمَل‌ برآمد و به‌ زمین‌ خم‌ شده‌، روی‌ خود را به‌ میان‌ زانوهایش‌ گذاشت‌. "},
	{43, "43  و به‌ خادم‌ خود گفت‌: بالا رفته‌، به‌ سوی‌ دریا نگاه‌ كن‌. و او بالا رفته‌، نگریست‌ و گفت‌ كه‌ چیزی‌ نیست‌ و او گفت‌: هفت‌ مرتبه‌ دیگر برو. "},
	{44, "44  و در مرتبه‌ هفتم‌ گفت‌ كه‌ اینك‌ ابری‌ كوچك‌ به‌ قدر كف‌ دست‌ آدمی‌ از دریا برمی‌آید. او گفت‌: برو و به‌ اَخاب‌ بگو كه‌ ارابۀ خود را ببند و فرود شو مبادا باران‌ تو را مانع‌ شود. "},
	{45, "45  و واقع‌ شد كه‌ در اندك‌ زمانی‌، آسمان‌ از ابرِ غلیظ‌ و باد، سیاه‌ فام‌ شد، و باران‌ سخت‌ بارید و اَخاب‌ سوار شده‌، به‌ یزْرَعِیل‌ آمد. "},
	{46, "46  و دست‌ خداوند بر ایلیا نهاده‌ شده‌، كمر خود را بست‌ و پیش‌ روی‌ اَخاب‌ دوید تا به‌ یزْرَعِیل‌ رسید. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
