static void view12() {
struct fa12 poems[] = {
	{1, "1  ای‌ خداوند تو عادل ‌تر هستی‌ از اینكه ‌من‌ با تو محاجّه‌ نمایم‌. لیكن‌ درباره‌ احكامت‌ با تو سخن‌ خواهم‌ راند. چرا راه‌ شریران‌ برخوردار می‌شود و جمیع‌ خیانتكاران‌ ایمن‌ می‌باشند؟ "},
	{2, "2  تو ایشان‌ را غرس‌ نمودی‌ پس‌ ریشه‌ زدند و نموّ كرده‌، میوه‌ نیز آوردند. تو به‌ دهان‌ ایشان‌ نزدیكی‌، امّا از قلب‌ ایشان‌ دور. "},
	{3, "3  اما تو ای‌ خداوند مرا می‌شناسی‌ و مرا دیده‌، دل‌ مرا نزد خود امتحان‌ كرده‌ای‌. ایشان‌ را مثل‌ گوسفندان‌ برای‌ ذبح‌ بیرون‌ كش‌ و ایشان‌ را به‌ جهت‌ روز قتل‌ تعیین‌ نما. "},
	{4, "4  زمین‌ تا به‌ كی‌ ماتم‌ خواهد نمود و گیاه‌ تمامی‌ صحرا خشك‌ خواهد ماند. حیوانات‌ و مرغان‌ به‌ سبب‌ شرارت‌ ساكنانش‌ تلف‌ شده‌اند زیرا می‌گویند كه‌ او آخرت‌ ما را نخواهد دید. "},
	{5, "5  اگر وقتی‌ كه‌ با پیادگان‌ دویدی‌، تو را خسته‌ كردند؟ پس‌ چگونه‌ با اسبان‌ می‌توانی‌ برابری‌ كنی‌؟ و هر چند در زمین‌ سالم‌، ایمن‌ هستی‌ در طغیان‌ اردنّ چه‌ خواهی‌ كرد؟ "},
	{6, "6  زیرا كه‌ هم‌ برادرانت‌ و هم‌ خاندان‌ پدرت‌ به‌ تو خیانت‌ نمودند و ایشان‌ نیز در عقب‌ تو صدای‌ بلند می‌كنند پس‌ اگر چه‌سخنان‌ نیكو به‌ تو بگویند ایشان‌ را باور مكن‌. "},
	{7, "7  من‌ خانه‌ خود را ترك‌ كرده‌، میراث‌ خویش‌ را دور انداختم‌. و محبوبه‌ خود را به‌ دست‌ دشمنانش‌ تسلیم‌ نمودم‌. "},
	{8, "8  و میراث‌ من‌ مثل‌ شیر جنگل‌ برای‌ من‌ گردید. و به‌ ضدّ من‌ آواز خود را بلند كرد از این‌ جهت‌ از او نفرت‌ كردم‌. "},
	{9, "9  آیا میراث‌ من‌ برایم‌ مثل‌ مرغ‌ شكاری‌ رنگارنگ‌ كه‌ مرغان‌ دور او را گرفته‌ باشند شده‌ است‌؟ بروید و جمیع‌ حیوانات‌ صحرا را جمع‌ كرده‌، آنها را بیاورید تا بخورند. "},
	{10, "10  شبانان‌ بسیار تاكستان‌ مرا خراب‌ كرده‌، میراث‌ مرا پایمال‌ نمودند. و میراث‌ مرغوب‌ مرا به‌ بیابان‌ ویران‌ مبدّل‌ ساختند. "},
	{11, "11  آن‌ را ویران‌ ساختند و آن‌ ویران‌ شده‌ نزد من‌ ماتم‌ گرفته‌ است‌. تمامی‌ زمین‌ ویران‌ شده‌، چونكه‌ كسی‌ این‌ را در دل‌ خود راه‌ نمی‌دهد. "},
	{12, "12  بر تمامی‌ بلندیهای‌ صحرا، تاراج‌كنندگان‌ هجوم‌ آوردند زیرا كه‌ شمشیر خداوند از كنار زمین‌ تا كنار دیگرش‌ هلاك‌ می‌كند و برای‌ هیچ‌ بشری‌ ایمنی‌ نیست‌. "},
	{13, "13  گندم‌ كاشتند و خار درویدند، خویشتن‌ را به‌ رنج‌ آورده‌، نفع‌ نبردند. و از محصول‌ شما به‌ سبب‌ حدّت‌ خشم‌ خداوند خجل‌ گردیدند. "},
	{14, "14  خداوند درباره‌ جمیع‌ همسایگان‌ شریر خود كه‌ ضرر می‌رسانند به‌ ملكی‌ كه‌ قوم‌ خود اسرائیل‌ را مالك‌ آن‌ ساخته‌ است‌ چنین‌ می‌گوید: اینك‌ ایشان‌ را از آن‌ زمین‌ برمی‌كَنم‌ و خاندان‌ یهودا را از میان‌ ایشان‌ برمی‌كَنم‌. "},
	{15, "15  و بعد از بركَندن‌ ایشان‌ رجوع‌ خواهم‌ كرد و بر ایشان‌ ترحّم‌ خواهم‌ نمود و هر كس‌ از ایشان‌ را به‌ ملك‌ خویش‌ و هر كس‌ را به‌ زمین‌ خود باز خواهم‌ آورد. "},
	{16, "16  و اگر ایشان‌ طریق‌های‌ قوم‌ مرا نیكو یاد گرفته‌، به‌ اسم‌ من‌ یعنی‌ به‌ حیات‌ یهوه‌ قسم‌ خورند چنانكه‌ ایشان‌ قوم‌ مرا تعلیم‌ دادند كه‌ به‌ بعل‌ قسم‌خورند، آنگاه‌ ایشان‌ در میان‌ قوم‌ من‌ بنا خواهند شد. "},
	{17, "17  اما اگر نشنوند آنگاه‌ آن‌ امّت‌ را بالكلّ بَركَنده‌، هلاك‌ خواهم‌ ساخت‌. كلام‌ خداوند این‌ است‌. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
