#include <map>
#include <string>
class Bible36
{
	struct fa1	{ int val; const char *msg; };
	struct fa2	{ int val; const char *msg; };
	struct fa3	{ int val; const char *msg; };
public:
static void view1() {
struct fa1 poems[] = {
	{1, "1  كلام‌ خداوند كه‌ در ایام‌ یوشّیا ابن‌ آمون‌،پادشاه‌ یهودا، بر صَفَنْیا ابن‌ كوشی‌ ابن‌ جَدَلْیا ابن‌ اَمَرْیا ابن‌ حِزْقِیا نازل‌ شد. "},
	{2, "2  خداوند می‌گوید كه‌ همه‌ چیزها را از روی‌ زمین‌ بالّكل‌ هلاك‌ خواهم‌ ساخت‌. "},
	{3, "3  انسان‌ و بهایم‌ را هلاك‌ می‌سازم‌. مرغان‌ هوا و ماهیان‌ دریا و سنگهای‌ مصادم‌ را با شریران‌ هلاك‌ می‌سازم‌. و انسان‌ را از روی‌ زمین‌ منقطع‌ می‌نمایم‌. قول‌ خداوند این‌ است‌. "},
	{4, "4  و دست‌ خود را بر یهودا و بر جمیع‌ سَكَنه‌ اورشلیم‌ دراز می‌نمایم‌. و بقیه‌ بَعْل‌ و اسمهای‌ مؤبدان‌ و كاهنان‌ را از این‌ مكان‌ منقطع‌ می‌سازم‌. "},
	{5, "5  و آنانی‌ را كه‌ لشكر آسمان‌ را بر بامها می‌پرستند، و آن‌ پرستندگان‌ را كه‌ به‌ یهوه‌ قَسَم‌ می‌خورند و آنانی‌ را كه‌ به‌ مَلْكوم‌ سوگند می‌خورند، "},
	{6, "6  و آنانی‌ را كه‌ از پیروی‌ یهوه‌ مُرتَدّ شده‌اند، و آنانی‌ را كه‌ خداوند را نمی‌طلبند و از او مسألت‌ نمی‌نمایند. "},
	{7, "7  به‌ حضور خداوند یهوه‌ خاموش‌ باش‌، زیرا كه‌ روز خداوند نزدیك‌ است‌، چونكه‌ خداوند قربانی‌ای‌ مهیا كرده‌ است‌ و دعوت‌شدگان‌ خود را تقدیس‌ نموده‌ است‌. "},
	{8, "8  و در روز قربانی خداوند واقع‌ خواهد شد كه‌ من‌ بر سروران‌ و پسران‌ پادشاه‌ و همه‌ آنانی‌ كه‌ لباس‌ بیگانه‌ می‌پوشند عقوبت‌ خواهم‌ رسانید. "},
	{9, "9  و در آن‌ روز بر همه‌ آنانی‌ كه‌ برآستانه‌ می‌جَهَند عقوبت‌ خواهم‌ رسانید و بر آنانی‌ كه‌ خانه‌ خداوند خود را از ظلم‌ و فریب‌ پر می‌سازند. "},
	{10, "10  و خداوند می‌گوید كه‌ در آن‌ روز صدای‌ نعره‌ای‌ از دروازه‌ ماهی‌ و وِلوِله‌ای‌ از محلّه‌ دوّم‌ و شكستگی‌ عظیمی‌ از تلّها مسموع‌ خواهد شد. "},
	{11, "11  ای‌ ساكنان‌ مَكْتیش‌ ولوله‌ نمایید زیرا كه‌ تمامی‌ قوم‌ كنعان‌ تلف‌ شده‌ و همه‌ آنانی‌ كه‌ نقره‌ را برمی‌دارند منقطع‌ گردیده‌اند. "},
	{12, "12  و در آنوقت‌ اورشلیم‌ را به‌ چراغها تفتیش‌ خواهم‌ نمود و بر آنانی‌ كه‌ بر دُرْدهای‌ خود نشسته‌اند و در دلهای‌ خود می‌گویند خداوند نه‌ نیكویی‌ می‌كند و نه‌ بدی‌، عقوبت‌ خواهم‌ رسانید. "},
	{13, "13  بنابراین‌، دولت‌ ایشان‌ تاراج‌ و خانه‌های‌ ایشان‌ خراب‌ خواهد شد؛ و خانه‌ها بنا خواهند نمود، امّا در آنها ساكن‌ نخواهند شد و تاكستانها غرس‌ خواهند كرد، امّا شراب‌ آنها را نخواهند نوشید. "},
	{14, "14  روز عظیم‌ خداوند نزدیك‌ است‌، نزدیك‌ است‌ و بزودی‌ هرچه‌ تمام‌تر می‌رسد. آواز روز خداوند مسموع‌ است‌ و مرد زورآور در آن‌ به‌ تلخی‌ فریاد برخواهد آورد. "},
	{15, "15  آن‌ روز، روز غضب‌ است‌، روز تنگی‌ و اضطراب‌، روز خرابی‌ و ویرانی‌، روزِ تاریكی‌ و ظلمت‌، روز ابرها و ظلمتِ غلیظ‌، "},
	{16, "16  روز كَرِنّا و هنگامه‌ جنگ‌ به‌ ضدّ شهرهای‌ حصاردار و به‌ ضدّ برجهای‌ بلند. "},
	{17, "17  و مردمان‌ را چنان‌ به‌ تنگ‌ می‌آورم‌ كه‌ كورانه‌ راه‌ خواهند رفت‌ زیرا كه‌ به‌ خداوند گناه‌ ورزیده‌اند. پس‌ خون‌ ایشان‌ مثل‌ غبار و گوشت‌ ایشان‌ مانند سرگین‌ ریخته‌ خواهد شد. "},
	{18, "18  در روز غضبِ خداوند نه‌ نقره‌ و نه‌ طلای‌ ایشان‌ ایشان‌ را تواند رهانید و تمامی‌ جهان‌ از آتش‌ غیرت‌ او سوخته‌ خواهدشد، زیرا كه‌ بر تمامی‌ ساكنان‌ جهان‌ هلاكتی‌ هولناك‌ وارد خواهد آورد. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct fa2 poems[] = {
	{1, "1  ای‌ امّتی‌ كه‌ حیا ندارید، فراهم‌ آیید و جمع بشوید! "},
	{2, "2  قبل‌ از آنكه‌ حُكم‌ نتاج‌ بیاورد و آن‌ روز مثل‌ كاه‌ بگذرد؛ قبل‌ از آنكه‌ حدّت‌ خشم‌ خداوند بر شما وارد آید؛ قبل‌ از آنكه‌ روز خشم‌ خداوند بر شما برسد. "},
	{3, "3  ای‌ جمیع‌ حلیمانِ زمین‌ كه‌ احكام‌ او را بجا می‌آورید، عدالت‌ را بطلبید و تَواضع‌ را بجویید، شاید كه‌ در روز خشم‌ خداوند مستور شوید. "},
	{4, "4  زیرا كه‌ غَزَّه‌ متروك‌ می‌شود و اَشْقَلُون‌ ویران‌ می‌گردد و اَهل‌ اَشْدُود را در وقت‌ ظهر اخراج‌ می‌نمایند و عَقْرُوْن‌ از ریشه‌ كنده‌ می‌شود. "},
	{5, "5  وای‌ بر امّت‌ كَریتیان‌ كه‌ بر ساحل‌ دریا ساكنند. ای‌ كنعان‌، ای‌ زمین‌ فِلِسطینیان‌ كلام‌ خداوند به‌ ضدّ شما است‌ و من‌ تو را چنان‌ هلاك‌ می‌كنم‌ كه‌ كسی‌ در تو ساكن‌ نخواهد بود، "},
	{6, "6  و ساحل‌ دریا موضع‌ مرتع‌های‌ شبانان‌ و آغلهای‌ گوسفندان‌ خواهد بود، "},
	{7, "7  و ساحل‌ دریا برای‌ بقیه‌ خاندان‌ یهودا خواهد بود تا در آن‌ بچرند. شبگاهان‌ در خانه‌های‌ اَشْقَلون‌ خواهند خوابید زیرا یهوه‌ خدایشان‌ ازایشان‌ تفقّد نموده‌، اسیران‌ ایشان‌ را باز خواهد آورد. "},
	{8, "8  ملامت‌ موآب‌ و سرزنش‌ بنی‌عَمُّون‌ را شنیدم‌ كه‌ چگونه‌ قوم‌ مرا ملامت‌ می‌كنند و بر سرحدِّ ایشان‌ فخر می‌نمایند. "},
	{9, "9  بنابراین‌، یهوه‌ صبایوت‌ خدای‌ اسرائیل‌ می‌گوید: به‌ حیات‌ خودم‌ قَسم‌ كه‌ موآب‌ مثل‌ سَدوم‌ و بنی‌عَمّون‌ مثل‌ عَمّوره‌ خواهد شد. محّل‌ خارها و حفره‌های‌ نمك‌ و ویرانی‌ ابدی‌ خواهد شد. بقیۀ قوم‌ من‌ آنها را غارت‌ خواهند نمود و بقیه‌ امّت‌ من‌ ایشان‌ را به‌ تصرّف‌ خواهند آورد. "},
	{10, "10  این‌ به‌ سبب‌ تكبّر ایشان‌ بر ایشان‌ وارد خواهد آمد زیرا كه‌ قوم‌ یهوه‌ صبایوت‌ را ملامت‌ نموده‌، بر ایشان‌ فخر كردند. "},
	{11, "11  پس‌ خداوند به‌ ضدّ ایشان‌ مهیب‌ خواهد بود زیرا كه‌ تمامی‌ خدایان‌ جهان‌ را زایل‌ خواهد ساخت‌ و جمیع‌ جزایر امّت‌ها هر كدام‌ از جای‌ خود او را عبادت‌ خواهند كرد. "},
	{12, "12  و شما نیز ای‌ حَبَشیان‌ به‌ شمشیر من‌ كشته‌ خواهید شد. "},
	{13, "13  و دست‌ خود را بر زمین‌ شمال‌ دراز كرده‌، آشور را هلاك‌ خواهد كرد و نینوی‌ را به‌ ویرانی‌ و به‌ زمین‌ خشك‌ مثل‌ بیابان‌ مبدّل‌ خواهد نمود. "},
	{14, "14  و گله‌ها و تمامی‌ حیوانات‌ امّت‌ها در میانش‌خواهند خوابید و مرغ‌ سقّا و خارپشت‌ بر تاجهای‌ ستونهایش‌ منزل‌ خواهند گرفت‌ و آواز سراینده‌ از پنجره‌هایش‌ مسموع‌ خواهد شد و خرابی‌ بر آستانه‌هایش‌ خواهد بود زیرا كه‌ چوب‌ سرو آزادش‌ را برهنه‌ خواهد كرد. "},
	{15, "15  این‌ است‌ شهر فرحناك‌ كه‌ در اطمینان‌ ساكن‌ می‌بود و در دل‌ خود می‌گفت‌: من‌ هستم‌ و غیر از من‌ دیگری‌ نیست‌؛ من‌ هستم‌ و غیر از من‌ دیگری‌ نیست‌. چگونه‌ خراب‌ شد! خوابگاه‌ حیوانات‌ گردیده‌ است‌! هر كه‌ از آن‌ عبور كند بر آن‌ سخریه‌ كرده‌، دست‌ خود را خواهد جنبانید! "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct fa3 poems[] = {
	{1, "1  وای‌ بر شهر فتنه‌ انگیزِ نجسِ ظلم‌ كننده‌! "},
	{2, "2  آواز را نمی‌شنود و تأدیب‌ را نمی‌پذیرد و بر خداوند توكّل‌ نمی‌نماید و بر خدای‌ خود تقرّب‌ نمی‌جوید. "},
	{3, "3  سرورانش‌ در اندرونش‌ شیران‌ غرّان‌ و داورانش‌ گرگان‌ شب‌ كه‌ چیزی‌ تا صبح‌ باقی‌ نمی‌گذارند. "},
	{4, "4  انبیایش‌ مغرور و خیانتكارند. كاهنانش‌ قدس‌ را نجس‌ می‌سازند و به‌ شریعت‌ مخالفت‌ می‌ورزیدند. "},
	{5, "5  خداوند در اندرونش‌ عادل‌ است‌ و بی‌انصافی‌ نمی‌نماید. هر بامداد حكم‌ خود را روشن‌ می‌سازد و كوتاهی‌ نمی‌كند، امّا مرد ظالم‌ حیا را نمی‌داند. "},
	{6, "6  امّت‌ها را منقطع‌ ساخته‌ام‌ كه‌ برجهای‌ ایشان‌ خراب‌ شده‌ است‌ و كوچه‌های‌ ایشان‌ را چنان‌ ویران‌ كرده‌ام‌ كه‌ عبور كننده‌ای‌ نباشد. شهرهای‌ ایشان‌ چنان‌ منهدم‌ گردیده‌ است‌ كه‌ نه‌ انسانی‌ و نه‌ ساكنی‌ باقی‌ مانده‌ است‌. "},
	{7, "7  و گفتم‌: كاش‌ كه‌ از من‌ می‌ترسیدی‌ و تأدیب‌ را می‌پذیرفتی‌. تا آنكه‌ مسكن‌ او معدوم نمی‌شد، موافق‌ هر آنچه‌ بر او تعیین‌ نموده‌ بودم‌. لكن‌ ایشان‌ صبح‌ زود برخاسته‌، اعمال‌ خود را فاسد گردانیدند. "},
	{8, "8  بنابراین‌ خداوند می‌گوید: برای‌ من‌ منتظر باشید تا روزی‌ كه‌ به‌ جهت‌ غارت‌ برخیزم‌ زیرا كه‌ قصد من‌ این‌ است‌ كه‌ امّت‌ها را جمع‌ نمایم‌ و ممالك‌ را فراهم‌ آورم‌ تا غضب‌ خود و تمامی‌ حدّت‌ خشم‌ خویش‌ را بر ایشان‌ بریزم‌ زیرا كه‌ تمامی‌ جهان‌ به‌ آتش‌ غیرت‌ من‌ سوخته‌ خواهد شد. "},
	{9, "9  زیرا كه‌ در آن‌ زمان‌، زبان‌ پاك‌ به‌ امّت‌ها باز خواهم‌ داد تا جمیع‌ ایشان‌ اسم‌ یهوه‌ را بخوانند و به‌ یك‌ دل‌ او را عبادت‌ نمایند. "},
	{10, "10  از ماورای‌ نهرهای‌ حَبَش‌ پرستندگانم‌ یعنی‌ دختر پراكندگانم‌ هدیه‌ای‌ برای‌ من‌ خواهند آورد. "},
	{11, "11  در آن‌ روز از همه‌ اعمالت‌ كه‌ به‌ من‌ عصیان‌ ورزیده‌ای‌ خجل‌ نخواهی‌ شد زیرا كه‌ در آن‌ زمان‌ آنانی‌ را كه‌ از تكبّر تو مسرورند، از میانت‌ دور خواهم‌ كرد و بار دیگر در كوه‌ مقدّس‌ من‌ تكبّر نخواهی‌ نمود. "},
	{12, "12  امّا در میان‌ تو قومی‌ ذلیل‌ و مسكین‌ باقی‌ خواهم‌ گذاشت‌ و ایشان‌ بر اسم‌ خداوند توكّل‌ خواهند نمود. "},
	{13, "13  و بقیه‌ اسرائیل‌ بی‌انصافی‌ نخواهند نمود و دروغ‌ نخواهند گفت‌ و در دهان‌ ایشان‌ زبان‌ فریبنده‌ یافت‌ نخواهد شد زیرا كه‌ ایشان‌ چرا كرده‌، به‌ آرامی‌ خواهند خوابید و ترساننده‌ای‌ نخواهد بود. "},
	{14, "14  ای‌ دختر صهیون‌ ترنّم‌ نما! ای‌ اسرائیل‌ آواز شادمانی‌ بده‌! ای‌ دختر اورشلیم‌ به‌ تمامی‌ دل‌ شادمان‌ شو و وجد نما ! "},
	{15, "15  خداوند عقوبت‌های‌ تو را برداشته‌ و دشمنانت‌ را دور كرده‌ است‌. یهوه‌ پادشاه‌ اسرائیل‌ در میان‌ تو است‌ پس‌ بار دیگر بلارا نخواهی‌ دید. "},
	{16, "16  در آن‌ روز به‌ اورشلیم‌ گفته‌ خواهد شد كه‌ مترس‌! ای‌ صهیون‌ دستهای‌ تو سُست‌ نشود! "},
	{17, "17  یهوه‌ خدایت‌ در میان‌ تو قدیر است‌ و نجات‌ خواهد داد. او بر تو شادی‌ بسیار خواهد نمود و در محبّت‌ خود آرامی‌ خواهد یافت‌ و با سرودها بر تو شادی‌ خواهد نمود. "},
	{18, "18  آنانی‌ را كه‌ به‌ جهت‌ عیدها محزون‌ می‌باشند و از آن‌ تو هستند، جمع‌ خواهم‌ نمود كه‌ عار بر ایشان‌ بار سنگین‌ می‌بود. "},
	{19, "19  اینك‌ در آن‌ زمان‌ برهمه‌ آنانی‌ كه‌ بر تو ظلم‌ می‌كردند، مكافات‌ خواهم‌ رسانید و لنگان‌ را خواهم‌ رهانید و رانده‌ شدگان‌ را جمع‌ خواهم‌ كرد و آنانی‌ را كه‌ عار ایشان‌ در تمامی‌ زمین‌ می‌بود محّل‌ تسبیح‌ و اسم‌ خواهم‌ گردانید. "},
	{20, "20  در آن‌ زمان‌ شما را در خواهم‌ آورد و در آن‌ زمان‌ شما را جمع‌ خواهم‌ كرد زیرا خداوند می‌گوید: حینی‌ كه‌ اسیران‌ شما را بنظر شما باز آورم‌، آنگاه‌ شما را در میان‌ تمامی‌ قوم‌های‌ زمین‌ محلّ اسم‌ و تسبیح‌ خواهم‌ گردانید. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};