#include <map>
#include <string>
class Bible61
{
	struct fa1	{ int val; const char *msg; };
	struct fa2	{ int val; const char *msg; };
	struct fa3	{ int val; const char *msg; };
public:
static void view1() {
struct fa1 poems[] = {
	{1, "1  شمعون پطرس، غلام و رسول عیسی مسیح، به آنانی که ایمان گرانبها را به مساویِ ما یافته‌اند، در عدالت خدای ما و عیسی مسیح نجات دهنده: "},
	{2, "2  فیض و سلامتی در معرفت خدا، و خداوند ما عیسی بر شما افزون باد. "},
	{3, "3  چنانکه قوّت الهیهٔ او همهٔ چیزهایی را که برای حیات و دینداری لازم است، به ما عنایت فرموده است، به معرفت او که ما را به جلال و فضیلت خود دعوت نموده: "},
	{4, "4  که بوساطت آنها وعده‌های بی‌نهایت عظیم و گرانبها به ما داده شد: تا شما به اینها شریکِ طبیعتِ الٰهی گردید، و از فسادی که از شهوت در جهان است، خلاصی یابید: "},
	{5, "5  و به همین جهت، کمال سعی نموده، در ایمان خود فضیلت پیدا نمایید؛ "},
	{6, "6  و در فضیلت، علم و در علم، عفّت و در عفّت، صبر و در صبر، دینداری؛ "},
	{7, "7  و در دینداری، محبّت برادران و در محبّت برادران، محبّت را. "},
	{8, "8  زیرا هرگاه اینها در شما یافت شود و بیفزاید، شما را نمی‌گذارد که در معرفت خداوند ما عیسی مسیح کاهل یا بی‌ثمر بوده باشید. "},
	{9, "9  زیرا هرکه اینها را ندارد، کور و کوتاه نظر است و تطیهرِ گناهانِ گذشتهٔ خود را فراموش کرده است. "},
	{10, "10  لهذا ای برادران، بیشتر جدّ و جهد کنید تا دعوت و برگزیدگی خود را ثابت نمایید: زیرا اگر چنین کنید، هرگز لغزش نخواهید خورد: "},
	{11, "11  و همچنین دخول در ملکوت جاودانی خداوند و نجات‌دهندهٔ ما عیسی مسیح به شما به دولتمندی داده خواهد شد. "},
	{12, "12  لهذا از پیوسته یاد دادن شما از این امور غفلت نخواهم ورزید، هرچند آنها را می‌دانید، و در آن راستی که نزد شما است استوار هستید. "},
	{13, "13  لکن، این را صواب می‌دانم، مادامی که در این خیمه هستم، شما را به یاد آوری برانگیزانم؛ "},
	{14, "14  چونکه می‌دانم که وقت بیرون کردن خیمهٔ من نزدیک است، چنانکه خداوند ما عیسی مسیح نیز مرا آگاهانید. "},
	{15, "15  و برای این نیز کوشش می‌کنم تا شما در هر وقت بعد از رحلت من بتوانید این امور را یاد آورید. "},
	{16, "16  زیرا که در پی افسانه‌های جعلی نرفتیم، چون از قوّت و آمدن خداوند ما عیسی مسیح شما را اعلام دادیم، بلکه کبریایی او را دیده بودیم. "},
	{17, "17  زیرا از خدای پدر اکرام و جلال یافت، هنگامی که آوازی از جلال کبریایی به او رسید که، این است پسر حبیب من، که از وی خشنودم. "},
	{18, "18  و این آواز را ما زمانی که با وی در کوه مقدّس بودیم شنیدیم، که از آسمان آورده شد. "},
	{19, "19  و کلام انبیا را نیز محکمتر داریم؛ که نیکو می‌کنید اگر در آن اهتمام کنید، مثل چراغی درخشنده در مکان تاریک، تا روز بشکافد و ستارهٔ صبح در دلهای شما طلوع کند: "},
	{20, "20  و این را نخست بدانید که، هیچ نبوّتِ کتاب از تفسیر خود نبی نیست. "},
	{21, "21  زیرا که نبوّت به ارادهٔ انسان هرگز آورده نشد: بلکه مردمان به روح‌القدس مجذوب شده، از جانب خدا سخن گفتند. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct fa2 poems[] = {
	{1, "1  لکن در میان قوم، انبیای کَذَبِه نیز بودند، چنانکه در میان شما هم معلّمان کَذَبَه خواهند بود که بدعتهای مهلک را خُفیةً خواهند آورد، و آن آقایی را که ایشان را خرید انکار خواهند نمود، و هلاکت سریع را بر خود خواهند کشید. "},
	{2, "2  و بسیاری فجور ایشان را متابعت خواهند نمود؛ که به‌سبب ایشان طریق حق، مورد ملامت خواهد شد. "},
	{3, "3  و از راه طمع به سخنان جعلی شما را خرید و فروش خواهند کرد: که عقوبت ایشان از مدّت مدید تأخیر نمی‌کند، و هلاکت ایشان خوابیده نیست. "},
	{4, "4  زیرا هرگاه خدا بر فرشتگانی که گناه کردند، شفقت ننمود، بلکه ایشان را به جهنّم انداخته، به زنجیرهای ظلمت سپرد، تا برای داوری نگاه داشته شوند؛ "},
	{5, "5  و بر عالمِ قدیم شفقت نفرمود، بلکه نوح، واعظِ عدالت را با هفت نفر دیگر محفوظ داشته، طوفان را بر عالم بی‌دینان آورد؛ "},
	{6, "6  و شهرهای سدوم و عموره را خاکستر نموده، حکم به واژگون شدن آنها فرمود و آنها را برای آنانی که بعد از این بی‌دینی خواهند کرد، عبرتی ساخت؛ "},
	{7, "7  و لوطِ عادل را که از رفتار فاجرانهٔ بی‌دینان رنجیده بود رهانید. "},
	{8, "8  زیرا که آن مرد عادل در میانشان ساکن بوده، از آنچه می‌دید و می‌شنید، دل صالح خود را به کارهای قبیح ایشان هرروزه رنجیده می‌داشت. "},
	{9, "9  پس خداوند می‌داند که عادلان را از تجربه رهایی دهد و ظالمان را تا به روز جزا در عذاب نگاه دارد: "},
	{10, "10  خصوصاً آنانی که در شهوات نجاست در پی جسم می‌روند و خداوندی را حقیر می‌دانند. اینها جسور و متکبّرند و از تهمت زدن بر بزرگان نمی‌لرزند. "},
	{11, "11  و حال آنکه فرشتگانی که در قدرت و قوّت افضل هستند، پیش خداوند بر ایشان حکم افترا نمی‌زنند. "},
	{12, "12  لکن اینها، چون حیوانات غیرناطق، که برای صید و هلاکت طبعاً متولّد شده‌اند، ملامت می‌کنند بر آنچه نمی‌دانند و در فساد خود هلاک خواهند شد؛ "},
	{13, "13  و مزد ناراستی خود را خواهند یافت، که عیش و عشرت یک روزه را سرور خود می‌دانند. لکّه‌ها و عیبها هستند، که در ضیافت‌های محبّتانهٔ خود عیش و عشرت می‌نمایند وقتی که با شما شادی می‌کنند؛ "},
	{14, "14  چشمهای پر از زنا دارند که از گناه بازداشته نمی‌شود؛ و کسان ناپایدار را به دام می‌کشند: ابنای لعنت که قلب خود را برای طمع ریاضت داده‌اند: "},
	{15, "15  و راه مستقیم را ترک کرده، گمراه شدند، و طریق بَلعام بن بَصور را که مزد ناراستی را دوست می‌داشت، متابعت کردند؛ "},
	{16, "16  لکن او از تقصیر خود توبیخ یافت: که حمار گنگ به زبان انسان متنطّق شده، دیوانگیِ نبی را توبیخ نمود. "},
	{17, "17  اینها چشمه‌های بی‌آب، و مِه‌های رانده‌شده به بادِ شدید هستند، که برای ایشان ظلمتِ تاریکیِ جاودانی، مقرّر است. "},
	{18, "18  زیرا که سخنان تکبّرآمیز و باطل می‌گویند، و آنانی را که از اهل ضلالتْ تازه رستگار شده‌اند، در دام شهوات به فجور جِسمی می‌کشند. "},
	{19, "19  و ایشان را به آزادی وعده می‌دهند، و حال آنکه خود غلام فساد هستند: زیرا هرچیزی که بر کسی غلبه یافته باشد، او نیز غلام آن است. "},
	{20, "20  زیرا هرگاه به معرفت خداوند و نجات‌دهندهٔ ما عیسی مسیح از آلایش دنیوی رستند، و بعد از آن، بار دیگر گرفتار و مغلوب آن گشتند، اواخر ایشان از اوایل بدتر می‌شود. "},
	{21, "21  زیرا که برای ایشان بهتر می‌بود که راه عدالت را ندانسته باشند، از اینکه، بعد از دانستن بار دیگر از آن حکم مقدّس که بدیشان سپرده شده بود، برگردند. "},
	{22, "22  لکن معنی مَثَل حقیقی بر ایشان راست آمد که، سگ به قی خود رجوع کرده است؛ و خَنزیرِ شسته شده، به غلطیدن در گِل. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct fa3 poems[] = {
	{1, "1  این رساله دوّم را ای حبیبان، الآن به شما می‌نویسم؛ که به این هردو، دلِ پاک شما را به طریق یادگاری برمی‌انگیزانم: "},
	{2, "2  تا بخاطر آرید کلماتی که انبیای مقدّس پیش گفته‌اند، و حکم خداوند و نجات دهنده را که به رسولان شما داده شد: "},
	{3, "3  و نخست این را می‌دانید، که در ایّام آخر مُستَهزئین با استهزا ظاهر خواهند شد، که بر وفق شهوات خود رفتار نموده، "},
	{4, "4  خواهند گفت، کجاست وعدهٔ آمدن او؟ زیرا از زمانی که پدران به خواب رفتند، هرچیز به همینطوری که از ابتدای آفرینش بود، باقی است. "},
	{5, "5  زیرا که ایشان عمداً از این غافل هستند، که به کلام خدا آسمانها از قدیم بود، و زمین از آب و به آب قائم گردید: "},
	{6, "6  و به این هر دو، عالمی که آن وقت بود در آب غرق شده، هلاک گشت: "},
	{7, "7  لکن آسمان و زمین الآن به همان کلام برای آتش ذخیره شده و تا روز داوری و هلاکت، مردم بی‌دین نگاه داشته شده‌اند. "},
	{8, "8  لکن ای حبیبان، این یک چیز از شما مخفی نماند که یک روز نزد خدا چون هزار سال است، و هزار سال چون یک روز. "},
	{9, "9  خداوند در وعدهٔ خود تأخیر نمی‌نماید، چنانکه بعضی تأخیر می‌پندارند، بلکه بر شما تحمّل می‌نماید، چون نمی‌خواهد که کسی هلاک گردد، بلکه همه به توبه گرایند. "},
	{10, "10  لکن روز خداوند چون دزد خواهد آمد؛ که در آن آسمانها به صدای عظیم زایل خواهند شد و عناصر سوخته شده، از هم خواهد پاشید، و زمین و کارهایی که در آن است سوخته خواهد شد. "},
	{11, "11  پس چون جمیع اینها متفرّق خواهند گردید، شما چطور مردمان باید باشید، در هر سیرتِ مقدّس و دینداری؟ "},
	{12, "12  و آمدن روز خدا را انتظار بکشید و آن را بشتابانید، که در آن آسمانها سوخته شده، از هم متفرّق خواهند شد و عناصر از حرارت گداخته خواهد گردید. "},
	{13, "13  ولی بحسب وعدهٔ او، منتظر آسمانهای جدید و زمین جدید هستیم، که در آنها عدالت ساکن خواهد بود. "},
	{14, "14  لهذا ای حبیبان، چون انتظار این چیزها را می‌کشید، جدّ و جهد نمایید تا نزد او بی‌داغ و بی‌عیب در سلامتی یافت شوید. "},
	{15, "15  و تحمّلِ خداوندِ ما را نجات بدانید؛ چنانکه برادر حبیب ما پولُس نیز برحسب حکمتی که به وی داده شد، به شما نوشت؛ "},
	{16, "16  و همچنین در سایر رساله‌های خود این چیزها را بیان می‌نماید؛ که در آنها بعضی چیزهاست که فهمیدن آنها مشکل است، و مردمان بی‌علم و ناپایدار آنها را مثل سایر کتب تحریف می‌کنند، تا به هلاکت خود برسند. "},
	{17, "17  پس شما ای حبیبان، چون این امور را از پیش می‌دانید، باحذر باشید که مبادا به گمراهی بی‌دینان ربوده شده، از پایداریِ خود بیفتید. "},
	{18, "18  بلکه در فیض و معرفت خداوند و نجات‌دهندهٔ ما عیسی مسیح ترقّی کنید، که او را از کنون تا ابدالآباد جلال باد. آمین. "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};