#include <map>
#include <string>
class Bible61
{
	struct gr1	{ int val; const char *msg; };
	struct gr2	{ int val; const char *msg; };
	struct gr3	{ int val; const char *msg; };
public:
static void view1() {
struct gr1 poems[] = {
	{1, "1 Συμεών Πέτρος, δούλος και απόστολος του Ιησού Χριστού, προς τους όσοι έλαχον ισότιμον με ημάς πίστιν εις την δικαιοσύνην του Θεού ημών και Σωτήρος Ιησού Χριστού·"},
	{2, "2 χάρις και ειρήνη πληθυνθείη εις εσάς διά της επιγνώσεως του Θεού και του Ιησού του Κυρίου ημών."},
	{3, "3 Καθώς η θεία δύναμις αυτού εχάρισεν εις ημάς πάντα τα προς ζωήν και ευσέβειαν διά της επιγνώσεως του καλέσαντος ημάς διά της δόξης αυτού και αρετής,"},
	{4, "4 διά των οποίων εδωρήθησαν εις ημάς αι μέγισται και τίμιαι επαγγελίαι, ίνα διά τούτων γείνητε κοινωνοί θείας φύσεως, αποφυγόντες την εν τω κόσμω υπάρχουσαν διά της επιθυμίας διαφθοράν."},
	{5, "5 Και δι' αυτό δε τούτο καταβαλόντες πάσαν σπουδήν, προσθέσατε εις την πίστιν σας την αρετήν, εις δε την αρετήν την γνώσιν,"},
	{6, "6 εις δε την γνώσιν την εγκράτειαν, εις δε την εγκράτειαν την υπομονήν, εις δε την υπομονήν την ευσέβειαν,"},
	{7, "7 εις δε την ευσέβειαν την φιλαδελφίαν, εις δε την φιλαδελφίαν την αγάπην."},
	{8, "8 Διότι, εάν ταύτα υπάρχωσιν εις εσάς και περισσεύωσι, σας καθιστώσιν ουχί αργούς ουδέ ακάρπους εις την επίγνωσιν του Κυρίου ημών Ιησού Χριστού·"},
	{9, "9 επειδή εις όντινα δεν υπάρχουσι ταύτα, τυφλός είναι, μυωπάζει και ελησμόνησε τον καθαρισμόν των παλαιών αυτού αμαρτιών."},
	{10, "10 Διά τούτο, αδελφοί, επιμελήθητε περισσότερον να κάμητε βεβαίαν την κλήσιν και την εκλογήν σας· διότι ταύτα κάμνοντες δεν θέλετε πταίσει ποτέ."},
	{11, "11 Διότι ούτω θέλει σας δοθή πλουσίως η είσοδος εις την αιώνιον βασιλείαν του Κυρίου ημών και Σωτήρος Ιησού Χριστού."},
	{12, "12 Όθεν δεν θέλω αμελήσει να σας υπενθυμίζω πάντοτε περί τούτων, καίτοι ειδότας και εστηριγμένους εις την παρούσαν αλήθειαν."},
	{13, "13 Στοχάζομαι όμως δίκαιον, εφ' όσον είμαι εν τούτω τω σκηνώματι, να σας διεγείρω διά της υπενθυμίσεως,"},
	{14, "14 επειδή εξεύρω ότι εντός ολίγον θέλω αποθέσει το σκήνωμά μου, καθώς και ο Κύριος ημών Ιησούς Χριστός μοι εφανέρωσε."},
	{15, "15 Θέλω όμως επιμεληθή, ώστε σεις και μετά την αποβίωσίν μου να δύνασθε πάντοτε να ενθυμήσθε αυτά."},
	{16, "16 Διότι σας εγνωστοποιήσαμεν την δύναμιν και παρουσίαν του Κυρίου ημών Ιησού Χριστού, ουχί μύθους σοφιστικούς ακολουθήσαντες, αλλ' αυτόπται γενόμενοι της εκείνου μεγαλειότητος."},
	{17, "17 Διότι έλαβε παρά Θεού Πατρός τιμήν και δόξαν, ότε ήλθεν εις αυτόν τοιαύτη φωνή υπό της μεγαλοπρεπούς δόξης, Ούτος είναι ο Υιός μου ο αγαπητός, εις τον οποίον εγώ ευηρεστήθην·"},
	{18, "18 και ταύτην την φωνήν ημείς ηκούσαμεν εξ ουρανού ελθούσαν, όντες μετ' αυτού εν τω όρει τω αγίω."},
	{19, "19 Και έχομεν βεβαιότερον τον προφητικόν λόγον, εις τον οποίον κάμνετε καλά να προσέχητε ως εις λύχνον φέγγοντα εν σκοτεινώ τόπω, εωσού έλθη η αυγή της ημέρας και ο φωσφόρος ανατείλη εν ταις καρδίαις υμών·"},
	{20, "20 τούτο πρώτον εξεύροντες, ότι ουδεμία προφητεία της γραφής γίνεται εξ ιδίας του προφητεύοντος διασαφήσεως·"},
	{21, "21 διότι δεν ήλθε ποτέ προφητεία εκ θελήματος ανθρώπου, αλλ' υπό του Πνεύματος του Αγίου κινούμενοι ελάλησαν οι άγιοι άνθρωποι του Θεού."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct gr2 poems[] = {
	{1, "1 Υπήρξαν όμως και ψευδοπροφήται μεταξύ του λαού, καθώς και μεταξύ σας θέλουσιν είσθαι ψευδοδιδάσκαλοι, οίτινες θέλουσι παρεισάξει αιρέσεις απωλείας, αρνούμενοι και τον αγοράσαντα αυτούς δεσπότην, επισύροντες εις εαυτούς ταχείαν απώλειαν·"},
	{2, "2 και πολλοί θέλουσιν εξακολουθήσει εις τας απωλείας αυτών, διά τους οποίους η οδός της αληθείας θέλει βλασφημηθή·"},
	{3, "3 και διά πλεονεξίαν θέλουσι σας εμπορευθή με πλαστούς λόγους, των οποίων η καταδίκη έκπαλαι δεν μένει αργή, και η απώλεια αυτών δεν νυστάζει."},
	{4, "4 Διότι εάν ο Θεός δεν εφείσθη αγγέλους αμαρτήσαντας, αλλά ρίψας αυτούς εις τον τάρταρον δεδεμένους με αλύσεις σκότους, παρέδωκε διά να φυλάττωνται εις κρίσιν,"},
	{5, "5 και εάν τον παλαιόν κόσμον δεν εφείσθη, αλλά φέρων κατακλυσμόν επί τον κόσμον των ασεβών εφύλαξεν όγδοον τον Νώε, κήρυκα της δικαιοσύνης,"},
	{6, "6 και κατέκρινεν εις καταστροφήν τας πόλεις των Σοδόμων και της Γομόρρας και ετέφρωσε, καταστήσας παράδειγμα των μελλόντων να ασεβώσι,"},
	{7, "7 και ηλευθέρωσε τον δίκαιον Λωτ καταθλιβόμενον υπό της ασελγούς διαγωγής των ανόμων·"},
	{8, "8 διότι ο δίκαιος, κατοικών μεταξύ αυτών, δι' οράσεως και ακοής, εβασάνιζεν από ημέρας εις ημέραν την δικαίαν αυτού ψυχήν διά τα άνομα έργα αυτών·"},
	{9, "9 εξεύρει ο Κύριος να ελευθερόνη εκ του πειρασμού τους ευσεβείς, τους δε αδίκους να φυλάττη εις την ημέραν της κρίσεως, διά να κολάζωνται,"},
	{10, "10 μάλιστα δε τους οπίσω της σαρκός ακολουθούντας με επιθυμίαν ακαθαρσίας και καταφρονούντας την εξουσίαν. Είναι τολμηταί, αυθάδεις, δεν τρέμουσι βλασφημούντες τα αξιώματα,"},
	{11, "11 ενώ οι άγγελοι, μεγαλήτεροι όντες εις ισχύν και δύναμιν, δεν φέρουσι κατ' αυτών βλάσφημον κρίσιν ενώπιον του Κυρίου."},
	{12, "12 Ούτοι όμως, ως άλογα φυσικά ζώα γεγεννημένα διά άλωσιν και φθοράν, βλασφημούσι περί πραγμάτων τα οποία αγνοούσι, και θέλουσι καταφθαρή εν τη ιδία αυτών διαφθορά,"},
	{13, "13 και θέλουσι λάβει τον μισθόν της αδικίας αυτών· στοχάζονται ηδονήν την καθημερινήν τρυφήν, είναι σπίλοι και μώμοι, εντρυφώσιν εν ταις απάταις αυτών, συμποσιάζουσι με σας,"},
	{14, "14 έχουσιν οφθαλμούς μεστούς μοιχείας και μη παυομένους από της αμαρτίας, δελεάζουσι ψυχάς αστηρίκτους, έχουσι την καρδίαν γεγυμνασμένην εις πλεονεξίας, είναι τέκνα κατάρας·"},
	{15, "15 αφήσαντες την ευθείαν οδόν, επλανήθησαν και ηκολούθησαν την οδόν του Βαλαάμ υιού του Βοσόρ, όστις ηγάπησε τον μισθόν της αδικίας,"},
	{16, "16 ηλέγχθη όμως διά την ιδίαν αυτού παρανομίαν, άφωνον υποζύγιον με φωνήν ανθρώπου λαλήσαν ημπόδισε την παραφροσύνην του προφήτου."},
	{17, "17 Ούτοι είναι πηγαί άνυδροι, νεφέλαι υπό ανεμοστροβίλου ελαυνόμεναι, διά τους οποίους το ζοφερόν σκότος φυλάττεται εις τον αιώνα."},
	{18, "18 Διότι λαλούντες υπερήφανα λόγια ματαιότητος, δελεάζουσι με τας επιθυμίας της σαρκός, με τας ασελγείας εκείνους οίτινες τωόντι απέφυγον τους εν πλάνη ζώντας,"},
	{19, "19 επαγγελλόμενοι εις αυτούς ελευθερίαν, ενώ αυτοί είναι δούλοι της διαφθοράς· διότι από όντινα νικάταί τις, τούτου και δούλος γίνεται."},
	{20, "20 Επειδή εάν αφού απέφυγον τα μολύσματα του κόσμου διά της επιγνώσεως του Κυρίου και Σωτήρος Ιησού Χριστού, ενεπλέχθησαν πάλιν εις ταύτα και νικώνται, έγειναν εις αυτούς τα έσχατα χειρότερα των πρώτων."},
	{21, "21 Επειδή καλήτερον ήτο εις αυτούς να μη γνωρίσωσι την οδόν της δικαιοσύνης, παρά αφού εγνώρισαν να επιστρέψωσιν εκ της παραδοθείσης εις αυτούς αγίας εντολής."},
	{22, "22 Συνέβη δε εις αυτούς το της αληθινής παροιμίας, Ο κύων επέστρεψεν εις το ίδιον αυτού εξέρασμα, και, Ο χοίρος λουσθείς επέστρεψεν εις το κύλισμα του βορβόρου."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct gr3 poems[] = {
	{1, "1 Δευτέραν ήδη ταύτην την επιστολήν σας γράφω, αγαπητοί, με τας οποίας διεγείρω δι' υπενθυμίσεως την ειλικρινή σας διάνοιαν,"},
	{2, "2 διά να ενθυμηθήτε τους λόγους τους προλαληθέντας υπό των αγίων προφητών και την παραγγελίαν ημών των αποστόλων του Κυρίου και Σωτήρος·"},
	{3, "3 τούτο πρώτον γνωρίζοντες, ότι θέλουσιν ελθεί εν ταις εσχάταις ημέραις εμπαίκται, περιπατούντες κατά τας ιδίας αυτών επιθυμίας"},
	{4, "4 και λέγοντες· Που είναι η υπόσχεσις της παρουσίας αυτού; διότι αφ' ης ημέρας οι πατέρες εκοιμήθησαν, τα πάντα διαμένουσιν ούτως απ' αρχής της κτίσεως."},
	{5, "5 Διότι εκουσίως αγνοούσι τούτο, ότι με τον λόγον του Θεού οι ουρανοί έγειναν έκπαλαι και η γη συνεστώσα εξ ύδατος και δι' ύδατος,"},
	{6, "6 διά των οποίων ο τότε κόσμος απωλέσθη κατακλυσθείς υπό του ύδατος·"},
	{7, "7 οι δε σημερινοί ουρανοί και η γη διά του αυτού λόγου είναι αποτεταμιευμένοι, φυλαττόμενοι διά το πυρ εις την ημέραν της κρίσεως και της απωλείας των ασεβών ανθρώπων."},
	{8, "8 Εν δε τούτο ας μη σας λανθάνη, αγαπητοί, ότι παρά Κυρίω μία ημέρα είναι ως χίλια έτη και χίλια έτη ως ημέρα μία."},
	{9, "9 Δεν βραδύνει ο Κύριος την υπόσχεσιν αυτού, ως τινές λογίζονται τούτο βραδύτητα, αλλά μακροθυμεί εις ημάς, μη θέλων να απολεσθώσι τινές, αλλά πάντες να έλθωσιν εις μετάνοιαν."},
	{10, "10 Θέλει δε ελθεί η ημέρα του Κυρίου ως κλέπτης εν νυκτί, καθ' ην οι ουρανοί θέλουσι παρέλθει με συριγμόν, τα στοιχεία δε πυρακτούμενα θέλουσι διαλυθή, και η γη και τα εν αυτή έργα θέλουσι κατακαή."},
	{11, "11 Επειδή λοιπόν πάντα ταύτα διαλύονται, οποίοι πρέπει να ήσθε σεις εις πολίτευμα άγιον και ευσέβειαν,"},
	{12, "12 προσμένοντες και σπεύδοντες εις την παρουσίαν της ημέρας του Θεού, καθ' ην οι ουρανοί πυρούμενοι θέλουσι διαλυθή και τα στοιχεία πυρακτούμενα θέλουσι χωνευθή;"},
	{13, "13 Κατά δε την υπόσχεσιν αυτού νέους ουρανούς και νέαν γην προσμένομεν, εν οις δικαιοσύνη κατοικεί."},
	{14, "14 Διά τούτο, αγαπητοί, ταύτα προσμένοντες, σπουδάσατε να ευρεθήτε άσπιλοι και αμώμητοι ενώπιον αυτού εν ειρήνη,"},
	{15, "15 και νομίζετε σωτηρίαν την μακροθυμίαν του Κυρίου ημών, καθώς και ο αγαπητός ημών αδελφός Παύλος έγραψε προς εσάς κατά την δοθείσαν εις αυτόν σοφίαν,"},
	{16, "16 ως και εν πάσαις ταις επιστολαίς αυτού, λαλών εν αυταίς περί τούτων, μεταξύ των οποίων είναι τινά δυσνόητα, τα οποία οι αμαθείς και αστήρικτοι στρεβλόνουσιν, ως και τας λοιπάς γραφάς προς την ιδίαν αυτών απώλειαν."},
	{17, "17 Σεις λοιπόν, αγαπητοί, προγνωρίζοντες ταύτα φυλάττεσθε, διά να μη παρασυρθήτε με την πλάνην των ανόμων και εκπέσητε από τον στηριγμόν σας,"},
	{18, "18 αυξάνεσθε δε εις την χάριν και εις την γνώσιν του Κυρίου ημών και Σωτήρος Ιησού Χριστού· εις αυτόν έστω η δόξα και νυν και εις ημέραν αιώνος· αμήν."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};