#include <map>
#include <string>
class Bible61
{
	struct hin1	{ int val; const char *msg; };
	struct hin2	{ int val; const char *msg; };
	struct hin3	{ int val; const char *msg; };
public:
static void view1() {
struct hin1 poems[] = {
	{1, "1 शमौन पतरस की और से जो यीशु मसीह का दास और प्रेरित है, उन लोगों के नाम जिन्हों ने हमारे परमेश्वर और उद्धारकर्ता यीशु मसीह की धार्मिकता से हमारा सा बहुमूल्य विश्वास प्राप्त किया है।"},
	{2, "2 परमेश्वर के और हमारे प्रभु यीशु की पहचान के द्वारा अनुग्रह और शान्ति तुम में बहुतायत से बढ़ती जाए।"},
	{3, "3 क्योंकि उसके ईश्वरीय सामर्थ ने सब कुछ जो जीवन और भक्ति से सम्बन्ध रखता है, हमें उसी की पहचान के द्वारा दिया है, जिस ने हमें अपनी ही महिमा और सद्गुण के अनुसार बुलाया है।"},
	{4, "4 जिन के द्वारा उस ने हमें बहुमूल्य और बहुत ही बड़ी प्रतिज्ञाएं दी हैं: ताकि इन के द्वारा तुम उस सड़ाहट से छूटकर जो संसार में बुरी अभिलाषाओं से होती है, ईश्वरीय स्वभाव के समभागी हो जाओ।"},
	{5, "5 और इसी कारण तुम सब प्रकार का यत्न करके, अपने विश्वास पर सद्गुण, और सद्गुण पर समझ।"},
	{6, "6 और समझ पर संयम, और संयम पर धीरज, और धीरज पर भक्ति।"},
	{7, "7 और भक्ति पर भाईचारे की प्रीति, और भाईचारे की प्रीति पर प्रेम बढ़ाते जाओ।"},
	{8, "8 क्योंकि यदि ये बातें तुम में वर्तमान रहें, और बढ़ती जाएं, तो तुम्हें हमारे प्रभु यीशु मसीह के पहचानने में निकम्मे और निष्फल न होने देंगी।"},
	{9, "9 और जिस में ये बातें नहीं, वह अन्धा है, और धुन्धला देखता है, और अपने पूर्वकाली पापों से धुलकर शुद्ध होने को भूल बैठा है।"},
	{10, "10 इस कारण हे भाइयों, अपने बुलाए जाने, और चुन लिये जाने को सिद्ध करने का भली भांति यत्न करते जाओ, क्योंकि यदि ऐसा करोगे, तो कभी भी ठोकर न खाओगे।"},
	{11, "11 बरन इस रीति से तुम हमारे प्रभु और उद्धारकर्ता यीशु मसीह के अनन्त राज्य में बड़े आदर के साथ प्रवेश करने पाओगे।"},
	{12, "12 इसलिये यद्यपि तुम ये बातें जानते हो, और जो सत्य वचन तुम्हें मिला है, उस में बने रहते हो, तौभी मैं तुम्हें इन बातों की सुधि दिलाने को सर्वदा तैयार रहूंगा।"},
	{13, "13 और मैं यह अपने लिये उचित समझता हूं, कि जब तक मैं इस डेरे में हूं, तब तक तुम्हें सुधि दिलाकर उभारता रहूं।"},
	{14, "14 क्योंकि यह जानता हूं, कि मसीह के वचन के अनुसार मेरे डेरे के गिराए जाने का समय शीघ्र आनेवाला है।"},
	{15, "15 इसलिये मैं ऐसा यत्न करूंगा, कि मेरे कूच करने के बाद तुम इन सब बातों को सर्वदा स्मरण कर सको।"},
	{16, "16 क्योंकि जब हम ने तुम्हें अपने प्रभु यीशु मसीह की सामर्थ का, और आगमन का समाचार दिया था तो वह चतुराई से गढ़ी हुई कहानियों का अनुकरण नहीं किया था बरन हम ने आप ही उसके प्रताप को देखा था।"},
	{17, "17 कि उस ने परमेश्वर पिता से आदर, और महिमा पाई जब उस प्रतापमय महिमा में से यह वाणी आई कि यह मेरा प्रिय पुत्रा है जिस से मैं प्रसन्न हूं।"},
	{18, "18 और जब हम उसके साथ पवित्रा पहाड़ पर थे, तो स्वर्ग से यही वाणी आते सुना।"},
	{19, "19 और हमारे पास जो भविष्यद्वक्ताओं का वचन है, वह इस घटना से दृढ़ ठहरा है और तुम यह अच्छा करते हो, कि जो यह समझकर उस पर ध्यान करते हो, कि वह एक दीया है, जो अन्धियारे स्थान में उस समय तक प्रकाश देता रहता है जब तक कि पौ न फटे, और भोर का तारा तुम्हारे हृदयों में न चमक उठे।"},
	{20, "20 पर पहिले यह जान लो कि पवित्रा शास्त्रा की कोई भी भविष्यद्वाणी किसी के अपने ही विचारधारा के आधार पर पूर्ण नहीं होती।"},
	{21, "21 क्योंकि कोई भी भविष्यद्वाणी मनुष्य की इच्छा से कभी नहीं हुई पर भक्त जन पवित्रा आत्मा के द्वारा उभारे जाकर परमेश्वर की ओर से बोलते थे।।"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct hin2 poems[] = {
	{1, "1 और जिस प्रकार उन लोगों में झूठे भविष्यद्वक्ता थे उसी प्रकार तुम में भी झूठे उपदेशक होंगे, जो नाश करनेवाले पाखण्ड का उद्घाटन छिप छिपकर करेंगे और उस स्वामी का जिस ने उन्हें मोल लिया है इन्कार करेंगे और अपने आप को शीघ्र विनाश में डाल देंगे।"},
	{2, "2 और बहुतेरे उन की नाई लुचपन करेंगे, जिन के कारण सत्य के मार्ग की निन्दा की जाएगी।"},
	{3, "3 और वे लोभ के लिये बातें गढ़कर तुम्हें अपने लाभ का कारण बनाएंगे, और जो दण्ड की आज्ञा उन पर पहिले से हो चुकी है, उसके आने में कुछ भी देर नहीं, और उन का विनाश ऊंघता नहीं।"},
	{4, "4 क्योंकि जब परमेश्वर ने उन स्वर्गदूतों को जिन्हों ने पाप किया नहीं छोड़ा, पर नरक में भेजकर अन्धेरे कुण्डों में डाल दिया, ताकि न्याय के दिन तक बन्दी रहें।"},
	{5, "5 और प्रथम युग के संसार को भी न छोड़ा, बरन भक्तिहीन संसार पर महा जल- प्रलय भेजकर धर्म के प्रचारक नूह समेत आठ व्यक्तियों को बचा लिया।"},
	{6, "6 और सदोम और अमोरा के नगरों को विनाश का ऐसा दण्ड दिया, कि उन्हें भस्म करके राख में मिला दिया ताकि वे आनेवाले भक्तिहीन लोगों की शिक्षा के लिये एक दृष्टान्त बनें।"},
	{7, "7 और धर्मी लूत को जो अधर्मियों के अशुद्ध चाल- चलन से बहुत दुखी था छुटकारा दिया।"},
	{8, "8 ( क्योंकि वह धर्मी उन के बीच में रहते हुए, और उन के अधर्म के कामों को देख देखकर, और सुन सुनकर, हर दिन अपने सच्चे मन को पीड़ित करता था)।"},
	{9, "9 तो प्रभु के भक्तों को परीक्षा में से निकाल लेना और अधर्मियों को न्याय के दिन तक दण्ड की दशा में रखना भी जानता है।"},
	{10, "10 निज करके उन्हें जो अशुद्ध अभिलाषाओं के पीछे शरीर के अनुसार चलते, और प्रभुता को तुच्छ जानते हैं: वे ढीठ, और हठी हैं, और ऊंचे पदवालों को बुरा भला कहने से नहीं डरते।"},
	{11, "11 तौभी स्वर्गदूत जो शक्ति और सामर्थ में उन से बड़े हैं, प्रभु के साम्हने उन्हें बुरा भला कहकर दोष नहीं लगाते।"},
	{12, "12 पर ये लोग निर्बुद्धि पशुओं ही के तुल्य हैं, जो पकड़े जाने और नाश होने के लिये उत्पन्न हुए हैं; और जिन बातों को जानते ही नहीं, उन के विषय में औरों को बुरा भला कहते हैं, वे अपनी सड़ाहट में आप ही सड़ जाएंगे।"},
	{13, "13 औरों का बुरा करने के बदले उन्हीं का बुरा होगा: उन्हें दिन दोपहर सुख- विलास करना भला लगता है; यह कलंक और दोष है-  जब वे तुम्हारे साथ खाते पीते हैं, तो अपनी ओर से प्रेम भोज करके भोग- विलास करते हैं।"},
	{14, "14 उन ही आंखों में व्यभिचारिणी बसी हुई है, और वे पाप किए बिना रूक नहीं सकते: वे चंचल मनवालों को फुसला लेते हैं; उन के मन को लोभ करने का अभ्यास हो गया है, वे सन्ताप के सन्तान हैं।"},
	{15, "15 वे सीधे मार्ग को छोड़कर भटक गए हैं, और बओर के पुत्रा बिलाम के मार्ग पर हो लिए हैं; जिस ने अधर्म की मजदूरी को प्रिय जाना।"},
	{16, "16 पर उसके अपराध के विषय में उलाहना दिया गया, यहां तक कि अबोल गदही ने मनुष्य की बोली से उस भविष्यद्वक्ता को उसके बावलेपन से रोका।"},
	{17, "17 ये लोग अन्धे कुंए, और आन्धी के उड़ाए हुए बादल हैं, उन के लिये अनन्त अन्धकार ठहराया गया है।"},
	{18, "18 वे व्यर्थ घमण्ड की बातें कर करके लुचपन के कामों के द्वारा, उन लोगों को शारीरिक अभिलाषाओं मे फंसा लेते हैं, जो भटके हुओं में से अभी निकल ही रहे हैं।"},
	{19, "19 वे उन्हें स्वतंत्रा होने की प्रतिज्ञा तो देते हैं, पर आप ही सड़ाहट के दास हैं, क्योंकि जो व्यक्ति जिस से हार गया है, वह उसका दास बन जाता है।"},
	{20, "20 और जब वे प्रभु और उद्धारकर्ता यीशु मसीह की पहचान के द्वारा संसार की नाना प्रकार की अशुद्धता से बच निकले, और फिर उन में फंसकर हार गए, तो उन की पिछली दशा पहिली से भी बुरी हो गई है।"},
	{21, "21 क्योंकि धर्म के मार्ग में न जानना ही उन के लिये इस से भला होता, कि उसे जानकर, उस पवित्रा आज्ञा से फिर जाते, जो उन्हें सौंपी गई थी। उन पर यह कहावत ठीक बैठती है,"},
	{22, "22 कि कुत्ता अपनी छांट की ओर और धोई हुई सुअरनी कीचड़ में लोटने के लिये फिर चली जाती है।।"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct hin3 poems[] = {
	{1, "1 हे प्रियो, अब मैं तुम्हें यह दूसरी पत्री लिखता हूं, और दोनों में सुधि दिलाकर तुम्हारे शुद्ध मन को उभारता हूं।"},
	{2, "2 कि तुम उन बातों को, जो पवित्रा भविश्यद्वक्ताओं ने पहिले से कही हैं और प्रभु, और उद्धारकर्ता की उस आज्ञा को स्मरण करो, जो तुम्हारे प्रेरितों के द्वारा दी गई थी।"},
	{3, "3 और यह पहिले जान लो, कि अन्तिम दिनों मे हंसी ठट्ठा करनेवाले आएंगे, जो अपनी ही अभिलाषाओं के अनुसार चलेंगे।"},
	{4, "4 और कहेंगे, उसके आने की प्रतिज्ञा कहां गई? क्योंकि जब से बाप- दादे सो गए हैं, सब कुछ वैसा ही है, जैसा सृष्टि के आरम्भ से था?"},
	{5, "5 वे तो जान बूझकर यह भूल गए, कि परमेश्वर के वचन के द्वारा से आकाश प्राचीन काल से वर्तमान है और पृथ्वी भी जल में से बनी और जल में स्थिर है।"},
	{6, "6 इन्हीं के द्वारा उस युग का जगत जल में डूब कर नाश हो गया।"},
	{7, "7 पर वर्तमान काल के आकाश और पृथ्वी उसी वचन के द्वारा इसलिये रखे हैं, कि जलाए जाएं; और वह भक्तिहीन मनुष्यों के न्याय और नाश होने के दिन तक ऐसे ही रखे रहेंगे।।"},
	{8, "8 हे प्रियों, यह एक बात तुम से छिपी न रहे, कि प्रभु के यहां एक दिन हजार वर्ष के बराबर है, और हजार वर्ष एक दिन के बराबर हैं।"},
	{9, "9 प्रभु अपनी प्रतिज्ञा के विषय में देर नहीं करता, जैसी देर कितने लोग समझते हैं; पर तुम्हारे विषय में धीरज धरता है, और नहीं चाहता, कि कोई नाश हो; बरन यह कि सब को मन फिराव का अवसर मिले।"},
	{10, "10 परन्तु प्रभु का दिन चोर की नाईं आ जाएगा, उस दिन आकाश बड़ी हड़हड़ाहट के शब्द से जाता रहेगा, और तत्व बहुत ही तप्त होकर पिघल जाएंगे, और पृथ्वी और उस पर के काम जल जाएेंगे।"},
	{11, "11 तो जब कि ये सब वस्तुएं, इस रीति से पिघलनेवाली हैं, तो तुम्हें पवित्रा चालचलन और भक्ति में कैसे मनुष्य होना चाहिए।"},
	{12, "12 और परमेश्वर के उस दिन की बाट किस रीति से जोहना चाहिए और उसके जल्द आने के लिये कैसा यत्न करना चाहिए; जिस के कारण आकाश आग से पिघल जाएंगे, और आकाश के गण बहुत ही तप्त होकर गल जाएंगे।"},
	{13, "13 पर उस की प्रतिज्ञा के अनुसार हम एक नए आकाश और नई पृथ्वी की आस देखते हैं जिन में धार्मिकता वास करेगी।।"},
	{14, "14 इसलिये, हे प्रियो, जब कि तुम इन बातों की आस देखते हो तो यत्न करो कि तुम शान्ति से उसके साम्हने निष्कलंक और निर्दोष ठहरो।"},
	{15, "15 और हमारे प्रभु के धीरज को उद्धार समझो, जैसे हमारे प्रिय भाई पौलुस न भी उस ज्ञान के अनुसार जो उसे मिला, तुम्हें लिखा है।"},
	{16, "16 वैसे ही उस ने अपनी सब पत्रियों में भी इन बातों की चर्चा की है जिन में कितनी बातें ऐसी है, जिनका समझना कठिन है, और अनपढ़ और चंचल लोग उन के अर्थों को भी पवित्रा शास्त्रा की और बातों की नाईं खींच तानकर अपने ही नाश का कारण बनाते हैं।"},
	{17, "17 इसलिये हे प्रियो तुम लोग पहिले ही से इन बातों को जानकर चौकस रहो, ताकि अधर्मियों के भ्रम में फंसकर अपनी स्थिरता को हाथ से कहीं खो न दो।"},
	{18, "18 पर हमारे प्रभु, और उद्धारकर्ता यीशु मसीह के अनुग्रह और पहचान में बढ़ते जाओ। उसी की महिमा अब भी हो, और युगानुयुग होती रहे। आमीन।।"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};