struct hu29 poems[] = {
	{1, "1 Azután monda Dávid király az egész gyülekezetnek: [Ím látjátok, ]hogy egyedül az én fiamat Salamont választotta Isten, a ki még gyermek és gyenge; a munka pedig nagy, mert nem emberé lészen az a ház, hanem az Úr Istené."},
	{2, "2 Én pedig teljes tehetségem szerint az én Istenem háza számára [bõségesen] szereztem aranyat az arany [szerszámok]ra, []ezüstöt az ezüst [szerszámok]ra, rezet a rézre, vasat a vasra, fákat a fákra, ónyxköveket, foglalni való köveket, veres köveket, különb-különbszínû köveket; mindenféle drágaköveket; márványköveket is bõséggel."},
	{3, "3 Ezenfölül, mivel nagy kedvem van az én Istenem házához, a mi kincsem, aranyam és ezüstöm van, oda adom az én Istenem házának szükségére, azok mellett, a melyeket szereztem a szentház számára."},
	{4, "4 Háromezer tálentom aranyat Ofir aranyából és hétezer tálentom tiszta ezüstöt, a házak falainak beborítására."},
	{5, "5 Aranyat az arany [szerszámok]ra, ezüstöt az ezüstre és minden szükségre, a mesteremberek kezéhez. És ha valaki [még] adni akar, szabad akaratja szerint, töltse meg az õ kezét ma és [adjon, a mit akar] az Úrnak."},
	{6, "6 Azért szabad akaratjok szerint adának [ajándékokat] az atyák fejedelmei, az Izráel nemzetségeinek fejedelmei, az ezredesek, századosok és a király dolgainak fejedelmei."},
	{7, "7 És adának az Isten házának szükségére ötezer tálentom aranyat és tízezer dárikot; tízezer tálentom ezüstöt és tizennyolczezer tálentom rezet és százezer tálentom vasat."},
	{8, "8 És valakinél [drága]kövek találtatának, adák az Úr házának kincséhez, a Gersoniták közül való Jéhiel kezébe."},
	{9, "9 És örvendeze a sokaság, hogy szabad akaratjokból adának; mert tiszta szívökbõl adakozának az Úrnak; Dávid király is nagy örömmel örvendezett."},
	{10, "10 Hálákat ada azért Dávid az Úrnak az egész gyülekezet elõtt, és monda Dávid: Áldott vagy te, Uram, Izráelnek, a mi atyánknak Istene, öröktõl fogva mind örökké!"},
	{11, "11 Oh Uram, tied a nagyság, hatalom, dicsõség, örökkévalóság és méltóság, sõt minden, valami a mennyben és a földön van, [tied]! Tied, oh Uram, az ország, te magasztalod fel magadat, hogy légy minden fejedelmek felett!"},
	{12, "12 A gazdagság és a dicsõség mind te tõled [vannak,] és te uralkodol mindeneken; a te kezedben van mind az erõsség és mind a birodalom; a te kezedben van mindeneknek felmagasztaltatása és megerõsíttetése."},
	{13, "13 Most azért, oh mi Istenünk, vallást teszünk elõtted, és dícsérjük a te dicsõséges nevedet;"},
	{14, "14 Mert micsoda vagyok én, és micsoda az én népem, hogy erõnk lehetne a szabad akarat szerint való [ajándék] adására, a mint tettünk? Mert tõled van minden, és a miket a te kezedbõl [vettünk, ]azokat adtuk most néked."},
	{15, "15 Mert mi csak jövevények vagyunk te elõtted és zsellérek, a mint a mi atyáink is egyenként; a mi [életünk] napjai olyanok e földön, mint az árnyék, melyben állandóság nincsen."},
	{16, "16 Oh mi Urunk Istenünk! mind ez a gazdagság, a mit gyûjtöttünk, hogy néked és a te szent nevednek házat építsünk, a te kezedbõl való és mindazok tiéid!"},
	{17, "17 Jól tudom, oh én Istenem, hogy te a szívet vizsgálod és az igazságot szereted. Én mindezeket tiszta szívembõl, nagy jó kedvvel adtam, s látom, hogy a te néped is, a mely itt jelen van, nagy örömmel, szabad akaratja szerint adta ezeket néked."},
	{18, "18 Oh Uram, Ábrahámnak, Izsáknak és Izráelnek, a mi atyáinknak Istene, tartsd meg mindörökké ezt az érzést a te néped szívében, és irányítsd az õ szívöket te feléd!"},
	{19, "19 Salamonnak pedig, az én fiamnak adj tökéletes szívet, hogy a te parancsolatidat, bizonyságtételeidet és rendelésidet megõrizhesse, s hogy mindazokat megcselekedhesse, s felépíthesse azt a házat, a melyet én megindítottam."},
	{20, "20 És szóla Dávid az egész gyülekezethez: Kérlek, áldjátok az Urat, a ti Isteneteket. Áldá azért az egész gyülekezet az Urat, az õ atyáik Istenét; s leborulván, tisztelék az Urat és a királyt."},
	{21, "21 És áldozának az Úrnak áldozatokkal, és áldozának egészen égõáldozatokkal az Úrnak másnapon, ezer tulkot, ezer kost, ezer bárányt italáldozataikkal, és sokféle áldozatokkal az egész Izráelért."},
	{22, "22 S evének és ivának az Úr elõtt azon a napon nagy vígasságban. Azután Salamont, a Dávid fiát másodszor is királylyá tevék és felkenék õt az Úrnak, hogy lenne fejedelem; Sádókot is fölkenék a fõpapságra."},
	{23, "23 És üle Salamon az Úr székébe, mint király az õ atyjának, Dávidnak helyében, és [mindenben] szerencsés vala, mert engedelmeskedék néki az egész Izráel."},
	{24, "24 A fejedelmek és a hatalmasok, sõt a Dávid király fiai is mindnyájan kezet adának, hogy Salamon királynak engedelmeskedni fognak."},
	{25, "25 És igen felmagasztalá az Úr Salamont mind az egész Izráel népe elõtt, és oly királyi méltóságot szerzett néki, a melyhez hasonló egy királynak sem volt õ elõtte Izráelben."},
	{26, "26 Így uralkodott Dávid, az Isai fia az egész Izráel felett."},
	{27, "27 Az idõ pedig, a melyben uralkodék Izráel felett, negyven esztendõ. Hebronban uralkodék hét esztendeig, Jeruzsálemben pedig harminczháromig uralkodék."},
	{28, "28 És meghala jó vénségben, életével, gazdagságával és minden dicsõségével megelégedve. És uralkodék az õ fia, Salamon helyette."},
	{29, "29 Dávid királynak pedig úgy elsõ, mint utolsó dolgai, ímé megirattak a Sámuel próféta könyvében és a Nátán próféta könyvében és a Gád próféta könyvében;"},
	{30, "30 Az õ egész országlásával, hatalmasságával és mind azzal, a mi az õ idejében történt vele, Izráellel és a föld minden országaival."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
