struct hu1 poems[] = {
	{1, "1 Jojakim, Júda királya uralkodásának harmadik esztendejében jöve Nabukodonozor, a babiloni király Jeruzsálemre, és megszállá azt."},
	{2, "2 És kezébe adá az Úr Jojakimot, a Júda királyát, és az Isten háza edényeinek egy részét; és vivé azokat Sineár földére, az õ istenének házába, és az edényeket bevivé az õ istenének kincsesházába."},
	{3, "3 És mondá a király Aspenáznak, az udvarmesterek fejedelmének, hogy hozzon az Izráel fiai közül és királyi magból való s elõkelõ származású ifjakat,"},
	{4, "4 A kikben semmi fogyatkozás nincsen, hanem a kik ábrázatra nézve szépek, minden bölcseségre eszesek, és ismeretekkel bírnak és értenek a tudományokhoz, és a kik alkalmatosak legyenek arra, hogy álljanak a király palotájában; és tanítsák meg azokat a Káldeusok írására és nyelvére."},
	{5, "5 És rendele nékik a király mindennapi szükségletül a királyi ételbõl és a borból, melybõl õ iszik vala, hogy így nevelje õket három esztendeig, és azután álljanak a király elõtt."},
	{6, "6 Valának pedig ezek között a Júda fiai közül: Dániel, Ananiás, Misáel és Azariás."},
	{7, "7 És az udvarmesterek fejedelme neveket ada nékik; tudniillik elnevezé Dánielt Baltazárnak, Ananiást Sidráknak, Misáelt Misáknak, Azariást Abednegónak."},
	{8, "8 De Dániel eltökélé az õ szívében, hogy nem fertõzteti meg magát a király ételével és a borral, a melybõl az iszik vala, és kéré az udvarmesterek fejedelmét, hogy ne kelljen magát megfertõztetnie."},
	{9, "9 És az Isten kegyelemre és irgalomra [méltóvá] tevé Dánielt az udvarmesterek fejedelme elõtt;"},
	{10, "10 És mondá az udvarmesterek fejedelme Dánielnek: Félek én az én uramtól, a királytól, a ki megrendelte a ti ételeteket és italotokat; minek lássa, hogy a ti orczátok hitványabb amaz ifjakénál, a kik egykorúak veletek? és így bûnbe kevernétek az én fejemet a királynál."},
	{11, "11 És mondá Dániel a felügyelõnek, a kire az udvarmesterek fejedelme bízta vala Dánielt, Ananiást, Misáelt és Azariást:"},
	{12, "12 Tégy próbát, kérlek, a te szolgáiddal tíz napig, és adjanak nékünk zöldségféléket, hogy azt együnk, és vizet, hogy azt igyunk."},
	{13, "13 Azután mutassák meg néked a mi ábrázatunkat és amaz ifjak ábrázatát, a kik a király ételével élnek, és a szerint cselekedjél majd a te szolgáiddal."},
	{14, "14 És engede nékik ebben a dologban, és próbát tõn velük tíz napig."},
	{15, "15 És tíz nap mulva szebbnek látszék az õ ábrázatuk, és testben kövérebbek valának mindazoknál az ifjaknál, a kik a király ételével élnek vala."},
	{16, "16 Elvevé azért a felügyelõ az õ ételöket és az õ italokul rendelt bort, és ad vala nékik zöldségféléket."},
	{17, "17 És ada az Isten ennek a négy gyermeknek tudományt, minden írásban való értelmet és bölcseséget; Dániel pedig értett mindenféle látomáshoz és álmokhoz [is.]"},
	{18, "18 Miután pedig elmúlt az idõ, a mikorra meghagyta vala a király, hogy [eléje] vigyék õket, bevivé õket az udvarmesterek fejedelme Nabukodonozor elé."},
	{19, "19 És szóla velök a király, és mindnyájok között sem találtaték olyan, mint Dániel, Ananiás, Misáel és Azariás, és állának a király elõtt."},
	{20, "20 És minden bölcs és értelmes dologban, a mely felõl a király tõlök tudakozódék, tízszerte okosabbaknak találá õket mindazoknál az írástudóknál és varázslóknál, a kik egész országában valának."},
	{21, "21 És [ott] vala Dániel a Cyrus király elsõ esztendejéig."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
