#include <map>
#include <string>
class Bible30
{
	struct hy1	{ int val; const char *msg; };
	struct hy2	{ int val; const char *msg; };
	struct hy3	{ int val; const char *msg; };
	struct hy4	{ int val; const char *msg; };
	struct hy5	{ int val; const char *msg; };
	struct hy6	{ int val; const char *msg; };
	struct hy7	{ int val; const char *msg; };
	struct hy8	{ int val; const char *msg; };
	struct hy9	{ int val; const char *msg; };
public:
static void view1() {
struct hy1 poems[] = {
	{1, "1 Ամոսի պատգամները Թեկուա Ակկարիմում, որոնք նրան յայտնուեցին մի տեսիլքում Երուսաղէմի մասին, երկրաշարժից երկու տարի առաջ Հրէաստանի Օզիա արքայի եւ Իսրայէլի արքայ Յովասի որդի Յերոբովամի ժամանակ։ "},
	{2, "2 Նա ասաց. Տէրը խօսեց Սիոն լեռից, եւ ձայն տուեց Երուսաղէմից. չորացան հօտերի արօտները եւ ցամաքեցին Կարմէլի բարձունքները։ "},
	{3, "3 Տէրն ասում է. Դամասկոսի գործած երեք անօրէնութիւններիեւ մանաւանդ չորրորդի համար,քանի որ նրանք գաղաադացիների յղիներին կտրատում էին երկաթէ սղոցներով, ես պիտի պատժեմ նրանց։ "},
	{4, "4 Կրակ պիտի գցեմ Ազայէլի տունը, եւ այն պիտի ոչնչացնի Ադերի որդու տան հիմքերը։ "},
	{5, "5 Պիտի խորտակեմ Դամասկոսի նիգերը։ Պիտի ոչնչացնեմ Ովնի դաշտի բնակիչներին։ Պիտի կոտորեմ Խառանի տոհմը։ Գերի պիտի ընկնի ասորիների ընտիր ժողովուրդը, - ասում է Տէրը։ "},
	{6, "6 Այսպէս է ասում Տէրը. Գազայի երեք ամբարշտութիւնների եւ մանաւանդ չորրորդի համար, քանի որ գերեցին Սողոմոնի ամբողջ ժողովուրդը եւ արգելափակեցին Եդոմում, ես պիտի պատժեմ նրանց։ "},
	{7, "7 Կրակ պիտի գցեմ Գազայի պարիսպները, եւ այն պիտի լափի նրա հիմքերը։ "},
	{8, "8 Պիտի ոչնչացնեմ Ազոտի բնակիչներին, եւ պիտի վերանայ ժողովուրդը Ասկաղոնից, պիտի հարուածեմ Ակկարոնը, եւ պիտի կորչեն այլազգիների մնացորդները, - ասում է Տէրը։ "},
	{9, "9 Այսպէս է ասում Տէրը. Տիւրոսի երեք ամբարշտութիւնների եւ մանաւանդ չորրորդի համար, քանի որ նրանք Եդոմի մէջ արգելափակեցին Սողոմոնի գերիներին եւ չյիշեցին եղբայրների ուխտը, ես պիտի պատժեմ նրանց։ "},
	{10, "10 Կրակ պիտի գցեմ Տիւրոսի պարիսպները, եւ այն պիտի լափի նրա հիմքերը։ "},
	{11, "11 Այսպէս է ասում Տէրը. Եդոմի երեք ամբարշտութիւնների եւ մանաւանդ չորրորդի համար, քանի որ նա սրով հալածեց իր եղբօրը, անգութ գտնուեց եղբօր նկատմամբ երկրում, բորբոքեց իր բարկութիւնը եւ նրա հանդէպ պահեց իր թշնամանքի յանդգնութիւնը, ես պիտի պատժեմ նրան։ "},
	{12, "12 Կրակ պիտի գցեմ Թեմանը եւ կրակը պիտի լափի նրա պարիսպների հիմքերը։ "},
	{13, "13 Այսպէս է ասում Տէրը. Ամոնացիների երեք ամբարշտութիւնների եւ մանաւանդ չորրորդի համար, քանի որ նրանք ճեղքեցին գաղաադացիների յղի կանանց փորը, որպէսզի ընդարձակեն իրենց սահմանները, ես պիտի պատժեմ նրանց։ "},
	{14, "14 Ռաբի պարիսպների վրայ կրակ պիտի բորբոքեմ, եւ այն պիտի լափի նրա հիմքերը պատերազմի օրուայ աղաղակի ձայնով։ Եւ նա պիտի սասանուի իր վախճանի օրը։ "},
	{15, "15 Նրանց թագաւորները գերի պիտի տարուեն նրանց քրմերով ու իշխաններով հանդերձ, - ասում է Տէր Աստուածը։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct hy2 poems[] = {
	{1, "1 Այսպէս է ասում Տէրը. Մովաբի երեք ամբարշտութիւնների եւ մանաւանդ չորրորդի համար ես պիտի պատժեմ նրանց, քանի որ նրանք այրեցին եդոմայեցիների թագաւորների ոսկորները,մինչեւ որ դրանք մոխիր դարձան։ "},
	{2, "2 Կրակ պիտի գցեմ Մովաբի վրայ, եւ կրակը պիտի լափի նրանց քաղաքների հիմքերը. Մովաբը պիտի մեռնի տկարութեան մէջ՝ աղաղակով եւ փողի ձայնով։ "},
	{3, "3 Պիտի սպանեմ նրա դատաւորինեւ նրա հետ պիտի կոտորեմ բոլոր իշխաններին, - ասում է Տէրը։ "},
	{4, "4 Այսպէս է ասում Տէրը. Յուդայի երկրի որդիների երեք ամբարշտութիւններիեւ մանաւանդ չորրորդի համարես պիտի պատժեմ նրանց, քանի որ նրանք մերժեցին Տիրոջ օրէնքներն ու չկատարեցին նրա հրամանները, մոլորեցրին նրանց իրենց ստեղծած սնոտի կուռքերով, որոնց յետեւից գնացին նրանց հայրերը։ "},
	{5, "5 Կրակ պիտի գցեմ Հրէաստանի վրայ,եւ այն պիտի լափի Երուսաղէմի հիմքերը։ "},
	{6, "6 Այսպէս է ասում Տէրը. Իսրայէլի երեք ամբարշտութիւնների եւ մանաւանդ չորրորդի համար ես պիտի պատժեմ նրանց,քանի որ նրանք արդարին ծախեցին արծաթով եւ տնանկին՝ մի զոյգ կօշիկի դիմաց, "},
	{7, "7 նրանց, որ տրորում են երկրի հողը, որ կռփահարում էին աղքատների գլուխները, շեղում խոնարհների ճանապարհը, եւ հայր ու որդի մի բոզի մօտ էին մտնում, որպէսզի պղծեն իրենց Աստծու անունը։ "},
	{8, "8 Նրանք իրենց զգեստները որպէս ծածկոց էին գցում իրենց վրայ եւ զոհասեղանին եւ բռնագրաւուած գինի էին խմում իրենց Աստծու տան մէջ։ "},
	{9, "9 Բայց ես նրանց առաջից վերացրի Ամորհացուն, որի հասակը մայրու բարձրութիւն ունէր, եւ նա հզօր էր կաղնու պէս, վերացրի նրա պտուղները՝ վերեւից, եւ նրա արմատները՝ ներքեւից։ "},
	{10, "10 Ես հանեցի ձեզ Եգիպտացիների երկրիցեւ քառասուն տարի շրջեցրի ձեզ անապատում՝ ժառանգել տալու համար ամորհացիների երկիրը։ "},
	{11, "11 Ես մարգարէներ վերցրի ձեր որդիներիցեւ ուխտաւորներ՝ ձեր երիտասարդներից։ Մի՞թէ դա այդպէս չի եղել, Իսրայէլի՛ որդիներ, - ասում է Տէրը։ - "},
	{12, "12 Դուք Իմ ուխտաւորներին գինի էիք խմեցնումեւ մարգարէներին պատուիրում, որ չմարգարէանան։ "},
	{13, "13 Դրա համար էլ ահա ես ձեզ պիտի ճզմեմ տեղում, ինչպէս ճզմում է օրանով լի սայլը իր անցած տեղը։ "},
	{14, "14 Փախչողին լքելու է նրա ուժը, զօրաւորը զօրեղ չի մնայ իր զօրութեամբ, եւ պատերազմողը չի փրկի ինքն իրեն։ "},
	{15, "15 Աղեղնաւորը չի կարող դէմ կանգնել, արագավազը չի փրկուի, հեծեալն էլ չի ազատի իր անձը։ "},
	{16, "16 Սիրտը չի գտնի իր զօրութիւնը,մերկն էլ կը հալածուի այն օրը, - ասում է Տէրը։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct hy3 poems[] = {
	{1, "1 Լսեցէ՛ք այս պատգամը, որ Տէրը խօսեց Իսրայէլի ձեր այդ տանեւ բոլոր այն տոհմերի դէմ, որոնց հանեց Եգիպտացիների երկրից։ "},
	{2, "2 Երկրի բոլոր ազգերից միայն ձե՛զ ընտրեցի, այդ պատճառով էլ ձե՛զ պիտի պատժեմ ձեր բոլոր չարիքների համար։ "},
	{3, "3 Երկու մարդ միասին կը գնա՞ն երբեւէ, եթէ չճանաչեն իրար։ "},
	{4, "4 Առիւծը կը մռնչի՞ անտառում, եթէ նա որս չի արել, առիւծի կորիւնը ձայն կը տա՞յ իր որջից, եթէ նա ոչինչ յափշտակած չլինի։ "},
	{5, "5 Թռչունը գետին կ՚ընկնի՞ առանց թռչնորսի, գետնի վրայ թակարդը կը շրխկա՞յ, եթէ ոչինչ չի բռնուել։ "},
	{6, "6 Կը լինի՞, որ քաղաքում փող հնչի, եւ ժողովուրդը չխռովուի. քաղաքում չարիք կը պատահի՞, եթէ Տէրը չի արել այն։ "},
	{7, "7 Դրա համար Տէրը ոչինչ չի անի, եթէ իր գաղտնի խորհուրդը չյայտնի իր ծառայ մարգարէներին։ "},
	{8, "8 Եթէ առիւծը մռնչայ, ո՞վ է որ չի սարսռայ։ Եթէ Տէր Աստուածը խօսեց, ո՞վ է, որ չի մարգարէանայ։ "},
	{9, "9 Յայտնեցէ՛ք Ասորեստանի գաւառներում,Եգիպտացիների երկրի գաւառներում եւ ասացէ՛ք. Հաւաքուեցէ՛ք Սամարիայի լերան վրայեւ տեսէ՛ք բազում զարմանալի գործերն ու բռնութիւնը, որ կատարւում են այնտեղ։ "},
	{10, "10 Նրանք չիմացան իրենց պատահելիք եղեռնը, - ասում է Տէրը, - քանի որ նրանք անօրէնութիւն եւ թշուառութիւն էին գանձում իրենց գաւառներում։ "},
	{11, "11 Դրա համար այսպէս է ասում Տէրը Տիւրոսին. Այն երկիրը, որ քո շուրջն է, կ՚աւերուի, կը խորտակուի քո ամբողջ զօրութիւնը, եւ կը յափշտակուեն քո գաւառները։ "},
	{12, "12 Այսպէս է ասում Տէրը. Ինչպէս հովիւը կը կարողանայ առիւծի բերանից ազատել երկու սրունք կամ ականջի մի բլթակ, այնպէս պիտի փրկուեն իսրայէլացիները, որոնք ապրում են Սամարիայումնրանց ազգի դէմ դիմաց եւ դամասկացիների մէջ։ "},
	{13, "13 Քահանանե՛ր, լսեցէ՛ք եւ վկայութիւն տուէք Յակոբի այդ տանը, - ասում է Ամենակալ Տէրը, "},
	{14, "14 քանի որ այն օրը, երբ Իսրայէլի ամբարշտութեան պատճառով պիտի պատժեմ նրան, վրէժ պիտի առնեմ նաեւ Բեթէլի զոհասեղանից, պիտի փշրուեն զոհասեղանի եղջիւրները եւ գետին ընկնեն։ "},
	{15, "15 Ես պիտի խորտակեմ ընդարձակ ապարանքներն ու ամարանոցները,պիտի անհետանան փղոսկրեայ ապարանքները, եւ շատ ուրիշ ապարանքներ պիտի ընկնեն, - ասում է Տէրը։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct hy4 poems[] = {
	{1, "1 Լսեցէ՛ք Տիրոջ խօսքը, Բասանի՛ երինջներ, որ Սամարիայի լեռներում էք, որ զրկում էք աղքատներին եւ ոտքի տակ էք գցում տնանկին, դուք որ ասում էիք ձեր ամուսիններին՝ Գինի՛ մատուզեցէք մեզ, որ խմենք։ "},
	{2, "2 Տէրը երդւում է իր սրբութեամբ, որ ահա օրեր պիտի գան ձեզ վրայ, եւ ձեզ զէնքով պիտի քշեն, եւ նրանց, որ ձեզ հետ են, պիտի նետեն եռացած կաթսաների մէջ։ "},
	{3, "3 Դուք մերկ դուրս պիտի գաք միմեանց դէմ եւ պիտի ընկնէք Ռեմանի լերան վրայ, - ասում է Տէրը։ "},
	{4, "4 Մտէ՛ք Բեթէլ, ամբարշտացէ՛ք,գնացէ՛ք Գաղգաղա եւ շատացրէ՛ք ամբարշտութիւնը, առաւօտեան մատուցեցէ՛ք ձեր զոհերըեւ երեք օրը մէկ՝ ձեր տասանորդները։ "},
	{5, "5 Ընթերցում էին արտաքին օրէնքները եւ հրաւիրում խոստովանութեան. յայտնեցէ՛ք, որ իսրայէլացիները սիրեցին սա, - ասում է Տէրը։ "},
	{6, "6 Ես ձեր բոլոր քաղաքներում ատամի ցաւ պատճառեցի ձեզ եւ հացի կարօտ թողեցի ձեր բոլոր բնակավայրերում, բայց դուք էլի չդարձաք դէպի ինձ, - ասում է Տէրը։ "},
	{7, "7 Եւ ձեզ անձրեւից զրկեցի բերքահաւաքից երեք ամիս առաջ. մի քաղաքում անձրեւ տեղացրի,իսկ միւս քաղաքում չտեղացրի. մի մասը, ուր անձրեւ տեղացրի, խոնաւ պիտի մնայ,իսկ միւսը, ուր չտեղացրի, պիտի ցամաքի։ "},
	{8, "8 Երկու ու երեք քաղաքներ պիտի հաւաքուեն մի քաղաքում՝ ջուր խմելու, եւ չեն յագենալու, բայց դուք էլի չէք դառնայ դէպի ինձ, - ասում է Տէրը։ "},
	{9, "9 Հարուածեցի ձեզ տապով եւ դալկութիւն բերի ձեր բերքի վրայ։ Գնացիք ձեր պարտէզները եւ այգիները, ձեր թզենիներն ու ձեր ձիթենիները. թրթուրը կերաւ, սակայն եւ այնպէս դուք չդարձաք դէպի ինձ, - ասում է Տէրը։ "},
	{10, "10 Եգիպտոսի ճանապարհին մահ ուղարկեցի ձեզ վրայեւ կոտորեցի ձեր երիտասարդներին՝ գերի տարուած ձեր երիվարների հետ, սպառեցի ձեր բանակները իմ բարկութեան հրով, սակայն եւ այնպէս դուք չդարձաք դէպի ինձ, - ասում է Տէրը։ "},
	{11, "11 Կործանեցի ձեզ, ինչպէս Աստուած կործանեց Սոդոմն ու Գոմորը, եւ դուք կրակից փրկուած փայտի պէս եղաք, սակայն եւ այնպէս չդարձաք դէպի ինձ, - ասում է Տէրը։ "},
	{12, "12 Դրա համար էլ ես այսպէս պիտի անեմ քեզ, Իսրայէ՛լ, բայց քանի որ այսպէս պիտի անեմ քեզ, պատրա՛ստ եղիր կանչելու քո Աստծու անունը, Իսրայէ՛լ։ "},
	{13, "13 Ահաւասիկ ես եմ, որ հաստատում եմ երկինքը, հաստատում եմ հողմերը, ես եմ նա, որ իր օծեալին ի յայտ է բերում մարդկանց մէջ, ստեղծում է առաւօտն ու մէգըեւ ելնում է երկրի բարձունքների վրայ.Ամենակալ Տէր Աստուած է նրա անունը։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view5() {
struct hy5 poems[] = {
	{1, "1 Լսեցէ՛ք Տէր Աստծու խօսքը, որով ես պիտի ողբամ ձեզ վրայ։ Ընկա՜ւ Իսրայէլի տունը եւ այլեւս չի կարողանայ ոտքի կանգնել։ "},
	{2, "2 Իսրայէլի կոյսը կործանուեց իր երկրում, եւ չկայ մէկը, որ կանգնեցնի նրան։ "},
	{3, "3 Այսպէս է ասում Տէր Աստուածը. Այն քաղաքից, որից հազար մարդ էր դուրս գալիս, պիտի մնայ հարիւրը, եւ որից հարիւրն էր ելնում, պիտի մնայ տասը Իսրայէլի այդ տնից։ "},
	{4, "4 Ահա այսպէս է ասում Տէրը Իսրայէլի այդ տանը. Փնտռեցէ՛ք ինձ, եւ դուք կ՚ապրէք. "},
	{5, "5 մի՛ փնտռէք Բեթէլը եւ Գաղգաղա մի՛ մտէք, Երդման ջրհորից մի՛ ելնէք, քանի որ Գաղգաղան գերուելով պիտի գերուի, եւ Բեթէլը կարծես թէ չի եղել։ "},
	{6, "6 Փնտռեցէ՛ք Տիրոջը, եւ դուք կ՚ապրէք, որպեսզի կրակի պէս չբորբոքուի Յովսէփի տունը, կրակը չուտի այն եւ ոչ ոք չի լինի, որ հանգցնի Իսրայէլի այդ տունը։ "},
	{7, "7 Նա, ով իրաւունք է հաստատում բարձունքներում արդարութիւն է դրել երկրում, "},
	{8, "8 ով ստեղծում է ամէն ինչ եւ նորոգում, ով մահուան ստուերը այգ է դարձնում եւ ցերեկը մթնեցնում ինչպէս գիշեր, ով վերեւ է կանչում ծովի ջրերը եւ հեղում երկրի երեսին, "},
	{9, "9 Ամենակալ Տէր է նրա անունը. նա, որ կոտրում է ուժեղի զօրութիւնը եւ թշուառութիւն է բերում ամրոցների վրայ։ "},
	{10, "10 Դռների առաջ նրանք ատեցին յանդիմանողներին եւ սուրբ խօսքերը պիղծ դարձրին։ "},
	{11, "11 Դրա համար աղքատներին նեղելով եւ նրանցից ընտրութեամբ կաշառք առնելով՝ շքեղազարդ ապարանքներ պիտի շինէք, բայց չէք բնակուի նրանց մէջ, գեղեցիկ այգիներ պիտի տնկէք, սակայն նրանց գինին չէք խմի, "},
	{12, "12 քանի որ ես իմացայ ձեր բազում ամբարշտութիւնները, եւ սարսափելի են ձեր մեղքերը։ Արդարին ոտնակոխ էիք անում՝ կաշառք առնելով եւ աղքատին դռնէ դուռ էիք գցում։ "},
	{13, "13 Ահա թէ ինչու նա, ով իմաստուն է, այդ ժամանակ կը լռի, քանի որ ժամանակը չար է։ "},
	{14, "14 Բարի՛ն փնտռեցէք եւ ոչ թէ չարը, որպէսզի ապրէք, եւ ձեզ հետ կը լինի Ամենակալ Տէր Աստուածը.ինչպէս որ ասում էիք, թէ՝ Ատեցինք չարը եւ սիրեցինք բարին։ "},
	{15, "15 Իրաւո՛ւնք հաստատեցէք ձեր դռանը, որպէսզի Տէր Աստուածը ողորմի Յովսէփի մնացորդներին։ "},
	{16, "16 Դրա համար այսպէս է ասում Ամենակալ Տէր Աստուածը. Բոլոր հրապարակներում կոծ կը լինի եւ բոլոր ճանապարհներին վա՜յ, վա՜յ կ՚ասուի. "},
	{17, "17 մշակը կը կանչուի սուգի ու կոծի եւ ձայնարկու կը դառնայ, բոլոր ճանապարհներին կոծ կը լինի, որովհետեւ ես պիտի անցնեմ քո միջով, - ասում է Տէրը։ "},
	{18, "18 Վա՜յ Տիրոջ օրը ցանկացողին. ձեր ինչի՞ն է պէտք Տիրոջ օրը,այն խաւար է եւ ոչ թէ լոյս։ "},
	{19, "19 Որպէս թէ մի մարդ փախչի առիւծից, մի արջ պատահի նրան, նա տուն փախչի, ձեռքը պատին յենի եւ օձը խայթի նրան։ "},
	{20, "20 Չէ՞ որ խաւար է Տիրոջ օրը եւ ոչ լուսաւոր, աղջամղջային է, եւ լոյսի ճաճանչ չկայ նրա մէջ։ "},
	{21, "21 Ատեցի, մերժեցի ձեր տօները եւ ձեր տարեկան հաւաքների հոտը չեմ շնչի. "},
	{22, "22 եւ եթէ ձեր ողջակէզներն ու զոհերը մատուցէք, չեմ ընդունի դրանք եւ չեմ նայի ձեր փրկութեան նշաններին։ "},
	{23, "23 Հեռացրո՛ւ ինձնից քո երգերի ձայնը, քո սրինգների նուագը ես չեմ լսի։ "},
	{24, "24 Թող յորդի իրաւունքը ինչպէս ջուր,եւ արդարութիւնը՝ ինչպէս անհուն հեղեղ։ "},
	{25, "25 Միթէ ողջակէզ եւ զոհեր մատուցեցի՞ք ինձ այնտեղ,անապատում, քառասուն տարի, ո՛վ տունդ Իսրայէլի։ "},
	{26, "26 Սակայն դուք վերցրիք Մողոքի խորանը, ձեր Ռեմփա աստծու աստղը եւ նրանց պատկերները, որ պատրաստեցիք ձեզ համար։ "},
	{27, "27 Ես ձեզ պիտի փոխադրեմ Դամասկոսից այն կողմ, - ասում է Տէրը,որի անունն է Աստուած Ամենակալ։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view6() {
struct hy6 poems[] = {
	{1, "1 Վա՛յ նրանց, ովքեր արհամարհում են Սիոնը եւ յոյս են դրել Սամարիայի լերան վրայ։ Հաւաքեցին ազգերի իշխաններին, եւ Իսրայէլի տունը մտաւ նրանց մէջ։ "},
	{2, "2 Բոլորդ անցէ՛ք Քաղանէից եւ տեսէ՛ք, այնտեղից անցէ՛ք մեծ Եմաթ Ռաբա եւ իջէ՛ք օտարազգիների Գէթ քաղաքը,որն աւելի զօրաւոր է, քան այդ բոլոր քաղաքները. միթէ նրանց սահմաններն աւելի մե՞ծ կը լինեն, քան ձեր սահմանները։ "},
	{3, "3 Դուք, որ գալիս էք դէպի չարիքների օրը, մօտենում էք հասնելու ստութիւնների շաբաթին, "},
	{4, "4 դուք, որ ննջում էք փղոսկրեայ գահաւորակներում,մեղկանում ձեր անկողիններում, հօտերից ուլեր էք ուտում եւ նախիրներից՝ պարարտ հորթեր, "},
	{5, "5 դուք, որ պարում էք սրինգների նուագի տակ, եւ դրանք մնայուն բան համարեցիք եւ ոչ՝ անցաւոր, "},
	{6, "6 դուք, որ խմում էք անխառն գինի եւ օծւում անուշ իւղերովեւ ցաւ չէիք զգում Յովսէփի տան կործանման համար, "},
	{7, "7 ահա դրա համար էլ այժմ այժմ գերի պիտի տարուէք բռնակալների իշխանութեան կողմից, եւ պիտի կտրուի Եփրեմի տան երիվարների խրխինջը։ "},
	{8, "8 Տէրը երդուեց իր անձով եւ այսպէս է ասում Ամենակալ Տէր Աստուածը. Ես գարշելի պիտի դարձնեմ Յակոբի տան ամբողջ հպարտութիւնը, նրա երկիրը պիտի ատեմեւ պիտի վերացնեմ նրա քաղաքներն իրենց բոլոր բնակիչներով։ "},
	{9, "9 Եւ եթէ լինի, որ տասը մարդ էլ մնայ մի տանը, նրանք պիտի մեռնեն, "},
	{10, "10 մնացողները պիտի վերցնեն իրենց հարազատներին եւ իջնեն հանելու նրանց ոսկորներն այդ տնից եւ ասեն տան առաջ կանգնածներին, թէ՝Ուրիշ մի բան մնա՞ց այնտեղ, քեզ մօտ։ Նա կ՚ասի, թէ՝ ոչ։ "},
	{11, "11 Չի ասի, թէ՝ լո՛ւռ կաց։Եւ դա նրա համար որ Տիրոջ անունը չէին տալիս։ "},
	{12, "12 Ահա Տէրը հրաման պիտի տայ եւ հարուածի մեծ ապարանքները՝ կործանելով, եւ փոքր ապարանքները՝ աւերելով։ "},
	{13, "13 Միթէ երիվարները կ՚արշաւե՞ն ժայռերի վրայ, կամ կը հանդարտուե՞ն մատակ ձիերի մէջ։ Դուք, որ իրաւունքը ցասման փոխարկեցիք, արդարութեան պտուղը դարձրիք լեղի եւ ուրախանում էիք ոչինչ բաներով, "},
	{14, "14 դուք, որ ասում էիք, թէ՝ Մեր ուժով չէ՞, որ մենք հզօր եղջիւրներ ունեցանք, "},
	{15, "15 ահա դրա համար էլ, ո՛վ Իսրայէլի տուն, ձեր դէմ մի ազգ պիտի հանեմ, որը պիտի նեղի ձեզ, որպէսզի այլեւս Եմաթ չմտնէք ու չհասնէք մինչեւ արեւմտեան հեղեղատը, - ասում է Զօրութիւնների Տէրը։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view7() {
struct hy7 poems[] = {
	{1, "1 Այսպիսի տեսիլք ցոյց տուեց ինձ Տէր Աստուածը,ահա մորեխի մի պարս էր գալիս արեւելքից առաւօտեան դէմ.եւ ահա մի ջորեակ՝ մի Գոգ արքայ։ "},
	{2, "2 Եւ երբ այնպէս եղաւ, որ դրանք ուտելով վերջացրին երկրի խոտը, ես ասացի. Տէ՛ր, Տէ՛ր, խնայի՛ր, որովհետեւ ո՞վ կը վերականգնի Յակոբի տունը, քանի որ նա սակաւաթիւ է։ "},
	{3, "3 Գթա՛ այդ բանի համար, Տէ՛ր։ Դա այլեւս չի լինի, - ասում է Տէրը։ "},
	{4, "4 Այսպիսի մի տեսիլք ցոյց տուեց ինձ Տէր Աստուածը. ահա կրակով դատաստան արեց Տէր Աստուածը, կրակը լափեց շատ խորութիւններ եւ կերաւ երկրի մի մասը։ "},
	{5, "5 Ես ասացի. Տէ՛ր Աստուած, դադարեցրո՛ւ դա, որովհետեւ ո՞վ է, որ ոտքի կը կանգնեցնի Յակոբի տունը, քանի որ նա սակաւաթիւ է։ "},
	{6, "6 Գթա՛ այդ բանի համար, Տէ՛ր։Դա այլեւս չի լինի, - ասում է Տէրը։ "},
	{7, "7 Այսպիսի մի տեսիլք ցոյց տուեց ինձ Տէր Աստուածը. ահա մի մարդ կանգնած էր ադամանդեայ պարսպի վրայ, ձեռքին՝ մի ադամանդ։ "},
	{8, "8 Եւ ասաց ինձ Տէրը. Ի՞նչ ես տեսնում, Ամո՛ս։ Ես ասացի՝ ադամանդ։ Տէրն ասաց ինձ. Ահաւասիկ ես ադամանդ պիտի հաստատեմ Իսրայէլի իմ ժողովրդի մէջ եւ այլեւս ոչ մի յանցանք չեմ ներելու նրան։ "},
	{9, "9 Պիտի ապականուեն նրանց ուրախութեան բագինները, պիտի աւերուեն Իսրայէլի մեհեանները, եւ ես սուր պիտի բարձրացնեմ Յերոբովամի տան վրայ։ "},
	{10, "10 Բեթէլի քուրմ Ամասիան մարդ ուղարկեց Իսրայէլի Յերոբովամ արքայի մօտ եւ ասաց. Ամոսը քո դէմ խռովութիւններ է յարուցում Իսրայէլի տանը, եւ երկիրը չի կարող տանել նրա բոլոր խօսքերը, "},
	{11, "11 քանի որ այսպէս է ասում Ամոսը. Յերոբովամը սրով պիտի վախճանուի, եւ Իսրայէլը գերուելով պիտի գերուի իր երկրից։ "},
	{12, "12 Իսկ Ամասիան ասաց Ամոսին. Ով դու գուշակդ, թո՛ղ այս տեղդ, գնա՛ Հրէաստան երկիրը, այնտե՛ղ ապրիր եւ այնտե՛ղ մարգարէացիր, "},
	{13, "13 այլեւս չշարունակես մարգարէանալ Բեթէլում, որովհետեւ սա թագաւորի սրբութիւնն է եւ թագաւորութեան տաճարը։ "},
	{14, "14 Պատասխան տուեց Ամոսը եւ ասաց Ամասիային. Ես ո՛չ մարգարէ էի եւ ո՛չ մարգարէի որդի, այլ հովիւ էի եւ թութ էի քաղում. "},
	{15, "15 Տէրը վերցրեց ինձ հօտից եւ ասաց ինձ. Դու գնա՛ եւ մարգարէացի՛ր Իսրայէլի իմ ժողովրդի մասին։ "},
	{16, "16 Արդ, լսի՛ր Տիրոջ պատգամները. դու ասում ես՝ Մի՛ մարգարէացիր Իսրայէլի մասին, մարդ մի՛ հաւաքիր ու մի՛ խօսիր Յակոբի տան դէմ։ "},
	{17, "17 Դրա համար այսպէս է ասում Տէր Աստուածը. Քո կինը այդ քաղաքում պիտի պոռնկանայ, եւ քո որդիներն ու դուստրերը սրով պիտի ընկնեն, քո երկիրը լարերով պիտի բաժանուի, ինքդ պիտի վախճանուես պիղծ երկրում, եւ Իսրայէլը գերուելով պիտի գերուի իր երկրից։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view8() {
struct hy8 poems[] = {
	{1, "1 Այսպիսի մի տեսիլք էլ ցոյց տուեց ինձ Տէր Աստուածը. թռչնորսների մի գործիք էր։ Տէրն ասաց. Ի՞նչ ես տեսնում, Ամո՛ս։ Ես ասացի՝ թռչնորսների գործիք։ "},
	{2, "2 Եւ Տէրն ասաց ինձ. Հասել է Իսրայէլի իմ ժողովրդի վախճանը, եւ ես այլեւս ոչ մի յանցանք չեմ ներելու նրան։ "},
	{3, "3 Տաճարի յարկերի տակ այդ օրը ողբեր պիտի հնչեն, - ասում է Տէրը, - շատերը պիտի ընկնեն, եւ ամէն տեղ լռութիւն պիտի հաստատեմ։ "},
	{4, "4 Լսեցէ՛ք սա, դուք, որ առաւօտեան նեղում էիք տնանկներին,երեկոյեան զրկում աղքատներին "},
	{5, "5 եւ ասում էիք. Երբ պիտի անցնի այս ամիսը, որ շահ անենք, եւ այս շաբաթը, որ բացենք պահեստները, փոքրացնենք չափը եւ մեծացնենք կշիռը, սուտ կշռաքարեր սարքենք, "},
	{6, "6 ծախու առնենք աղքատներին արծաթով,տնանկներին՝ մի զոյգ կօշիկով, եւ շահ ստանանք մեր բոլոր վաճառքներից։ "},
	{7, "7 Տէրը երդւում է Յակոբի հպարտութեամբ՝ ասելով, թէ ձեր գործերը իսպառ չեն մոռացուի, "},
	{8, "8 եւ որ դրանց պատճառով երկիրը պիտի խռովուի, նրա բոլոր բնակիչները սուգ պիտի անեն, նրա վախճանը պիտի գայու եգիպտացիների գետի պէս ելնի ու իջնի։ "},
	{9, "9 Այդ օրը, - ասում է Տէր Աստուածը, - այնպէս պիտի լինի, որ արեգակը մայր պիտի մտնի միջօրէին,եւ երկրում լոյսը պիտի խաւարի ցերեկով, "},
	{10, "10 ձեր տօնը սուգ պիտի դարձնեմ, ձեր բոլոր երգերը՝ ողբեր,ամէն տարիքի մարդկանց վրայ քուրձ պիտի գցեմ եւ կնտութիւն՝ բոլոր գլուխներին, այդ օրը պիտի դարձնեմ սիրելիի սգի օր, եւ բոլոր նրանց համար, որ նրա հետ կը լինեն՝ ցաւերի օր։ "},
	{11, "11 Ահա օրեր պիտի գան, - ասում է Տէր Աստուածը, - եւ ես սով պիտի ուղարկեմ այդ երկիրը, ոչ թէ հացի սով կամ ջրի ծարաւ, այլ Տիրոջ խօսքը լսելու սով։ "},
	{12, "12 Ջրերը պիտի յուզուեն ծովից մինչեւ ծով եւ հիւսիսից մինչեւ արեւելք. նրանք պիտի գնան Տիրոջ խօսքը փնտռելու եւ չեն գտնելու։ "},
	{13, "13 Այդ օրը գեղեցիկ կոյսերն ու երիտասարդները ծարաւից պիտի թուլանան։ "},
	{14, "14 Նրանք, որ երդւում էին Սամարիայի սրբութիւն սրբոցով եւ ասում էին՝ Կենդանի է քո Աստուածը՝ Դանը, կենդանի է քո Աստուածը՝ Բերսաբէէն,պիտի կործանուեն եւ այլեւս ոտքի չեն կանգնելու։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view9() {
struct hy9 poems[] = {
	{1, "1 Տեսայ Տիրոջը. նա զոհասեղանի վրայ էր եւ ասաց. Հարուածի՛ր այդ զոհասեղանին,եւ թող շարժուեն դրանդիները. կտրիր գցի՛ր դրանք բոլորի գլուխներին,իսկ մնացածներին ես սրով պիտի կոտորեմ.նրանցից փախածը չի փրկուի,եւ չի ազատուի նրանցից ազատուածը։ "},
	{2, "2 Թէկուզ նրանք դժոխքում էլ թաքնուեն, իմ ձեռքը այնտեղից էլ դուրս կը բերի նրանց. եթէ երկինք բարձրանան, ես այնտեղից էլ կ՚իջեցնեմ նրանց, իսկ եթէ թաքնուեն Կարմէլի բարձունքներում, այնտեղ էլ կը փնտռեմ եւ կը բռնեմ նրանց։ "},
	{3, "3 Եթէ իմ աչքից խոյս տան եւ պահուեն ծովի խորքերում, այնտեղ էլ վիշապին կը հրամայեմ, որ խայթի նրանց։ "},
	{4, "4 Եթէ գերի տարուեն իրենց թշնամիների կողմից, ես այնտեղ հրաման կը տամ սրին, որ ոչնչացնի նրանց։ Իմ աչքը նրանց վրայ կը լինի՝ չարութեան եւ ոչ թէ բարութեան համար։ "},
	{5, "5 Երբ Ամենակալ Տէր Աստուածը դիպչի երկրին եւ շարժի այն, պիտի սգան նրա բոլոր բնակիչները, նրա վախճանը պիտի գայու եգիպտացիենրի գետի պէս ելնի ու իջնի։ "},
	{6, "6 Նա, ով պատրաստում է իր համբառնալը դէպի երկինք,ով իր աւետիսն է հաստատում երկրում, ով վերեւ է կանչում ծովի ջրերը եւ հեղում երկրի երեսին, Ամենակալ Տէր է նրա անունը։ "},
	{7, "7 Միթէ ինձ համար եթովպացիների որդիների պէս չէ՞ք դուք,Իսրայէլի՛ որդիներ, - ասում է Տէրը. - իսրայէլին չհանեցի Եգիպտացիների երկրից, այլազգիներին՝ Գամրից եւ ասորիներին՝ Բոթորից։ "},
	{8, "8 Ահա Տիրոջ աչքը մեղաւորների այդ թագաւորութեան վրայ է.Ես դա պիտի վերացնեմ երկրի երեսից, սակայն չեմ ջնջի Յակոբի տունը, - ասում է Տէրը։ - "},
	{9, "9 Ահա ես հրաման եմ տալիս ձեզ,ո՛վ Իսրայէլի տուն, ես պիտի ցրեմ ձեզ բոլոր ազգերի մէջ այնպէս,ինչպէս հեծանոցով ցորենն են զատում,եւ ոչ մի մանրուքը գետին չի ընկնի։ "},
	{10, "10 Սրով պիտի վախճանուեն իմ ժողովրդի բոլոր մեղաւորները, որոնք ասում էին, թէ՝ չարիքները չեն մօտենայ մեզ, չեն հասնի մեզ վրայ։ "},
	{11, "11 Այդ օրը պիտի կանգնեցնեմ Դաւթի խորանը,պիտի վերականգնեմ նրա աւերակները, գետնին հաւասարուած նրա տեղերը վերստին պիտի կանգնեցնեմ եւ պիտի վերաշինեմ այն հին օրերի նման, "},
	{12, "12 որպէսզի մնացած մարդիկ եւ բոլոր ազգերը, որոնց վրայ կայ իմ անունը, փնտռեն ինձ - ասում է Տէրը, որ անում է սա։ "},
	{13, "13 Ահա օրեր են գալիս, - ասում է Տէրը, -երբ կալերը պիտի հասնեն բերքահաւաքին,խաղողը պիտի հասնի հնձանների համար, լեռները քաղցրութիւն պիտի բխեցնեն, եւ բոլոր բլուրները անտառախիտ պիտի դառնան։ "},
	{14, "14 Ես պիտի վերադարձնեմ Իսրայէլի իմ ժողովրդին գերութիւնից,նրանք պիտի շինեն աւերուած քաղաքներն ու պիտի բնակուեն այնտեղ, այգիներ պիտի տնկեն եւ պիտի խմեն դրանց գինին, պարտէզներ պիտի աճեցնեն եւ ուտեն դրանց պտուղները։ "},
	{15, "15 Ես նրանց պիտի հաստատեմ իրենց երկրում, եւ այլեւս նրանք չեն հեռացուի իրենց երկրից, որ ես տուեցի նրանց, - ասում է Ամենակալ Տէր Աստուածը։ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};