#include <map>
#include <string>
class Bible32
{
	struct it1	{ int val; const char *msg; };
	struct it2	{ int val; const char *msg; };
	struct it3	{ int val; const char *msg; };
	struct it4	{ int val; const char *msg; };
public:
static void view1() {
struct it1 poems[] = {
	{1, "1 La parola dell’Eterno fu rivolta Giona, figliuolo di Amittai, in questi termini:"},
	{2, "2 \"Lèvati, va’ a Ninive, la gran città, e predica contro di lei; perché la loro malvagità è salita nel mio cospetto\"."},
	{3, "3 Ma Giona si levò per fuggirsene a Tarsis, lungi dal cospetto dell’Eterno; e scese a Giaffa, dove trovò una nave che andava a Tarsis; e, pagato il prezzo del suo passaggio, s’imbarcò per andare con quei della nave a Tarsis, lungi dal cospetto dell’Eterno."},
	{4, "4 Ma l’Eterno scatenò un gran vento sul mare, e vi fu sul mare una forte tempesta, sì che la nave minacciava di sfasciarsi."},
	{5, "5 I marinari ebbero paura, e ognuno gridò al suo dio e gettarono a mare le mercanzie ch’erano a bordo, per alleggerire la nave; ma Giona era sceso nel fondo della nave, s’era coricato, e dormiva profondamente."},
	{6, "6 Il capitano gli si avvicinò, e gli disse: \"Che fai tu qui a dormire? Lèvati, invoca il tuo dio! Forse Dio si darà pensiero di noi e non periremo\"."},
	{7, "7 Poi dissero l’uno all’altro: \"Venite, tiriamo a sorte, per sapere a cagione di chi ci capita questa disgrazia\". Tirarono a sorte, e la sorte cadde su Giona."},
	{8, "8 Allora essi gli dissero: \"Dicci dunque a cagione di chi ci capita questa disgrazia! Qual è la tua occupazione? donde vieni? qual è il tuo paese? e a che popolo appartieni?\""},
	{9, "9 Egli rispose loro: \"Sono Ebreo, e temo l’Eterno, l’Iddio del cielo, che ha fatto il mare e la terra ferma\"."},
	{10, "10 Allora quegli uomini furon presi da grande spavento, e gli dissero: \"Perché hai fatto questo?\" Poiché quegli uomini sapevano ch’egli fuggiva lungi dal cospetto dell’Eterno, giacché egli avea dichiarato loro la cosa."},
	{11, "11 E quelli gli dissero: \"Che ti dobbiam fare perché il mare si calmi per noi?\" Poiché il mare si faceva sempre più tempestoso."},
	{12, "12 Egli rispose loro: \"Pigliatemi e gettatemi in mare, e il mare si calmerà per voi; perché io so che questa forte tempesta vi piomba addosso per cagion mia\"."},
	{13, "13 Nondimeno quegli uomini davan forte nei remi per ripigliar terra; ma non potevano, perché il mare si faceva sempre più tempestoso e minaccioso."},
	{14, "14 Allora gridarono all’Eterno, e dissero: \"Deh, o Eterno, non lasciar che periamo per risparmiar la vita di quest’uomo, e non ci mettere addosso del sangue innocente; poiché tu, o Eterno, hai fatto quel che ti è piaciuto\"."},
	{15, "15 Poi presero Giona e lo gettarono in mare; e la furia del mare si calmò."},
	{16, "16 E quegli uomini furon presi da un gran timore dell’Eterno; offrirono un sacrifizio all’Eterno, e fecero dei voti."},
	{17, "17 (H2-1) E l’Eterno fece venire un gran pesce per inghiottir Giona; e Giona fu nel ventre del pesce tre giorni e tre notti."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct it2 poems[] = {
	{1, "1 (H2-2) E Giona pregò l’Eterno, il suo Dio, nel ventre del pesce, e disse:"},
	{2, "2 (H2-3) Io ho gridato all’Eterno dal fondo della mia distretta, ed egli m’ha risposto; dalle viscere del soggiorno dei morti ho gridato, e tu hai udito la mia voce."},
	{3, "3 (H2-4) Tu m’hai gettato nell’abisso, nel cuore del mare; la corrente mi ha circondato e tutte le tue onde e tutti i tuoi flutti mi son passati sopra."},
	{4, "4 (H2-5) E io dicevo: Io son cacciato via lungi dal tuo sguardo! Come vedrei io ancora il tuo tempio santo?"},
	{5, "5 (H2-6) Le acque m’hanno attorniato fino all’anima; l’abisso m’ha avvolto; le alghe mi si son attorcigliate al capo."},
	{6, "6 (H2-7) Io son disceso fino alle radici dei monti; la terra con le sue sbarre mi ha rinchiuso per sempre; ma tu hai fatto risalir la mia vita dalla fossa, o Eterno, Dio mio!"},
	{7, "7 (H2-8) Quando l’anima mia veniva meno in me, io mi son ricordato dell’Eterno, e la mia preghiera è giunta fino a te, nel tuo tempio santo."},
	{8, "8 (H2-9) Quelli che onorano le vanità bugiarde abbandonano la fonte della loro grazia;"},
	{9, "9 (H2-10) ma io t’offrirò sacrifizi, con canti di lode; adempirò i voti che ho fatto. La salvezza appartiene all’Eterno."},
	{10, "10 (H2-11) E l’Eterno diè l’ordine al pesce, e il pesce vomitò Giona sull’asciutto."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct it3 poems[] = {
	{1, "1 E la parola dell’Eterno fu rivolta a Giona per la seconda volta, in questi termini:"},
	{2, "2 \"Lèvati, va’ a Ninive, la gran città e proclamale quello che io ti comando\"."},
	{3, "3 E Giona si levò, e andò a Ninive, secondo la parola dell’Eterno. Or Ninive era una grande città dinanzi a Dio, di tre giornate di cammino."},
	{4, "4 E Giona cominciò a inoltrarsi nella città per il cammino di una giornata, e predicava e diceva: \"Ancora quaranta giorni, e Ninive sarà distrutta!\""},
	{5, "5 E i Niniviti credettero a Dio, bandirono un digiuno, e si vestirono di sacchi, dai più grandi ai più piccoli."},
	{6, "6 Ed essendo la notizia giunta al re di Ninive, questi s’alzò dal trono, si tolse di dosso il manto, si coprì d’un sacco, e si mise a sedere sulla cenere."},
	{7, "7 E per decreto del re e dei suoi grandi, fu pubblicato in Ninive un bando di questo tenore: \"Uomini e bestie, armenti e greggi, non assaggino nulla; non si pascano e non bevano acqua;"},
	{8, "8 uomini e bestie si coprano di sacchi e gridino con forza a Dio; e ognuno si converta dalla sua via malvagia, e dalla violenza perpetrata dalle sue mani."},
	{9, "9 Chi sa che Dio non si volga, non si penta, e non acqueti l’ardente sua ira, sì che noi non periamo\"."},
	{10, "10 E Dio vide quel che facevano, vide che si convertivano dalla loro via malvagia, e si pentì del male che avea parlato di far loro: e non lo fece."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view4() {
struct it4 poems[] = {
	{1, "1 Ma Giona ne provò un gran dispiacere, e ne fu irritato; e pregò l’Eterno, dicendo:"},
	{2, "2 \"O Eterno, non è egli questo ch’io dicevo, mentr’ero ancora nel mio paese? Perciò m’affrettai a fuggirmene a Tarsis; perché sapevo che sei un Dio misericordioso, pietoso, lento all’ira, di gran benignità, e che ti penti del male minacciato."},
	{3, "3 Or dunque, o Eterno, ti prego, riprenditi la mia vita; perché per me val meglio morire che vivere\"."},
	{4, "4 E l’Eterno gli disse: \"Fai tu bene a irritarti così?\""},
	{5, "5 Poi Giona uscì dalla città, e si mise a sedere a oriente della città; si fece quivi una capanna, e vi sedette sotto, all’ombra, stando a vedere quello che succederebbe alla città."},
	{6, "6 E Dio, l’Eterno, per guarirlo dalla sua irritazione, fece crescere un ricino, che montò su di sopra a Giona, per fargli ombra al capo; e Giona provò una grandissima gioia a motivo di quel ricino."},
	{7, "7 Ma l’indomani, allo spuntar dell’alba, Iddio fece venire un verme, il quale attaccò il ricino, ed esso si seccò."},
	{8, "8 E come il sole fu levato, Iddio fece soffiare un vento soffocante d’oriente, e il sole picchiò sul capo di Giona, sì ch’egli venne meno, e chiese di morire, dicendo: \"Meglio è per me morire che vivere\"."},
	{9, "9 E Dio disse a Giona: \"Fai tu bene a irritarti così a motivo del ricino?\" Egli rispose: \"Sì, faccio bene a irritarmi fino alla morte\"."},
	{10, "10 E l’Eterno disse: \"Tu hai pietà del ricino per il quale non hai faticato, e che non hai fatto crescere, che è nato in una notte e in una notte è perito:"},
	{11, "11 e io non avrei pietà di Ninive, la gran città, nella quale si trovano più di centoventimila persone che non sanno distinguere la loro destra dalla loro sinistra, e tanta quantità di bestiame?\""},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};