struct kk8 poems[] = {
	{1, "1 Сүлеймен патша Жаратқан Иенің Келісім сандығын Сион төбесіндегі Дәуіттің шаһарынан киелі үйге көшіруге арналған салтанатқа қатысуға Исраил халқының ақсақалдарын, барлық рубасылары мен әулет басшыларын Иерусалимге шақырды."},
	{2, "2 Содан бүкіл Исраил халқының өкілдері қазан айындағы Күркелер мейрамы кезінде Сүлеймен патшаның қасына жиналды."},
	{3, "3 Елдің барлық ақсақалдары жиналғаннан кейін діни қызметкерлер Жаратқан Иенің Келісім сандығын тұрған орнынан көтеріп алып кетті."},
	{4, "4 Олар Жаратқан Иенің сандығымен бірге кездесу шатырын және соның ішіндегі барлық киелі жиһаз-жабдықтарды да көтеріп алып, жаңа ғибадатханаға қарай өрлеп бара жатты. Бұларды діни қызметкерлер мен өзге леуіліктер алып жүрді."},
	{5, "5 Сүлеймен патша мен қасына жиналған күллі исраилдіктер Келісім сандығының алдында ешкім санап шыға алмайтындай көптеген қой-ешкі мен мүйізді ірі қара малдарды құрбандыққа шалып ұсынды."},
	{6, "6 Осылайша діни қызметкерлер Жаратқан Иенің Келісім сандығын киелі үйдің ең қасиетті бөлмесіндегі өз орнына — екі керуб мүсінінің қанаттарының астына алып барып, орналастырды."},
	{7, "7 Екі керуб мүсіні Келісім сандығы тұратын орынның үстінен қанаттарын жайып, Келісім сандығының өзін және оны көтеріп алып жүретін сырықтарын жауып тұрды."},
	{8, "8 Сырықтардың ұзын болғаны сонша, олардың ұштары ең қасиетті бөлменің алдынан көрініп тұратын еді. Алайда киелі үйдің сыртынан оларды ешкім көре алмайтын. Сырықтар әлі күнге дейін сонда."},
	{9, "9 Сандықтың ішінде Мұса пайғамбар Хориб тауында салған екі тас тақташа ғана болды. Жаратқан Ие сол жерде Мысырдан көшіп шыққан исраилдіктермен Келісім жасаған еді."},
	{10, "10 Діни қызметкерлер Жаратқан Иенің киелі үйінен шыққан кезде киелі үй бір қалың бұлтқа кенелді."},
	{11, "11 Жаратқан Иенің нұрлы салтанаты осылайша Жаратқан Иенің киелі үйін кенелтіп жібергендіктен, діни қызметкерлер оның ішіндегі қызметтерін жалғастыра алмай қалды."},
	{12, "12 Сол кезде Сүлеймен былай деді: Жаратқан Ие қалың бұлт ішін мекендейтінін айтқан болатын."},
	{13, "13 Уа, Ием, Сенің мәңгі тұруың үшін Саған арнап осы салтанатты мекенді салдым."},
	{14, "14 Мұнан кейін патша артқа қарай бұрылды да, қарсы алдында жиналып тұрған Исраилдің бүкіл қауымына батасын беріп,"},
	{15, "15 былай деді: — Әкем Дәуіт марқұмға берген уәдесін іс жүзінде орындаған Исраилдің Құдайы — Жаратқан Ие мадақталсын! Ол мынаны айтқан болатын:"},
	{16, "16 Мен халқымды Мысырдан құтқарып, бастап алып шыққан күннен бері өз есімімнің орнығуы үшін киелі үй салынсын деп Исраилдің ешбір руындағы бірде-бір қаланы таңдамадым. Ал халқымды басқаруға Дәуітті таңдап алдым! —"},
	{17, "17 Сүлеймен сөзін жалғастырып былай деді: — Исраилдің Құдайы Жаратқан Иенің есіміне арнап киелі үй салуға әкем марқұм шын жүректен ниеттенген болатын."},
	{18, "18 Ал Жаратқан Ие әкеме былай деді: Менің есіміме арнап киелі үй салғың келгені өте дұрыс."},
	{19, "19 Алайда сол үйді салатын адам сен емессің, ол — болашақта дүниеге келетін өз ұлың. Менің есіміме арнап киелі үйді салатын сол болады."},
	{20, "20 Жаратқан Ие осы айтқандарын жүзеге асырды да. Оның уәдесіне сай, мен әкемнің орнын басып, Исраилдің патшалық тағына отырдым. Енді Исраилдің Құдайы Жаратқан Иенің құрметіне киелі үй салғыздым."},
	{21, "21 Соның ішінде мен Жаратқан Иенің сандығына арнайы орын жасадым. Сол сандықтың ішінде біздің ата-бабаларымызды Мысырдан көшіріп алып шыққан кезде Жаратқан Иенің олармен жасасқан Келісімінің айғақтары сақталып жатыр."},
	{22, "22 Содан соң Сүлеймен Жаратқан Иеге арналған құрбандық үстелінің алдында тұрып, жиналған бүкіл Исраил қауымының көз алдында қолдарын аспанға қарай жайып былай деп мінажат етті:"},
	{23, "23 Уа, Исраилдің Құдайы Жаратқан Ие! Жоғарыдағы көкте де, төмендегі жерде де Сен сияқты ешбір құдай жоқ! Жасаған Келісіміңе адалсың, шын жүректен тура жолыңмен жүрген қызметшілеріңе рақымың мол."},
	{24, "24 Сен әкем марқұмға уәде еткеніңдей, оған сенімді болып, рақымыңды көрсетіп келдің. Бұрын Өзің сөз беріп, уәде еткеніңді бүгін іс жүзінде орындап отырсың."},
	{25, "25 Ал енді, уа, Исраилдің Құдайы Жаратқан Ие, Өзіңнің қызметшің Дәуітке Егер ұрпақтарың сен сияқты Менің көз алдымда таза жүретін болса, онда әрдайым сенің ұрпақтарыңның біреуі Исраилдің патшалық тағында отыратын болады! деп берген уәдеңді іске асыра гөр."},
	{26, "26 Сонымен, уа, Исраилдің Құдайы, Өзіңе қызмет еткен әкем марқұмға айтқан сөздеріңді орындай гөр!"},
	{27, "27 Алайда Құдай шынымен-ақ жер бетінде тұратын бола ма?! Тіпті ұлан-ғайыр көктің өзі Сені сыйғыза алмағанда, мен салған мына үй қайдан сыйғызсын?!"},
	{28, "28 Солай болса да, уа, Құдайым Жаратқан Ие, бүгінгі күні Сенің алдыңда бой ұрып сиынып жасаған өтінішіме назар аударып, жалбарына еткен мінажатыма мейіріммен құлақ сала гөр!"},
	{29, "29 Сен Мына киелі үйді өз атыммен атаймын деп сөз бергендіктен, оған күні-түні қамқор бола гөр. Уа, Ием, алдыңдағы қызметшіңнің бұл жерге қарай Өзіңе бағыштаған мінажатына құлақ аса гөр."},
	{30, "30 Осы пендең және меншікті Исраил халқың бұл жерге қарай Саған сиынып дұға еткен кезде тұрағыңнан өтінішімізге құлақ салып, күнәларымызды кешіре гөр."},
	{31, "31 Егер болашақта бір кісі басқа біреуге жамандық жасапты деп айыпталып, осы киелі үйдегі құрбандық үстелінің алдында ант беруге мәжбүр етілсе,"},
	{32, "32 онда Сен көктен құлақ асып, пенделеріңе төрелік ете гөр. Сол кісі кінәлі болса, істеген қылмысын мойнына арта гөр, ал кінәсіз болса, әділдігін әшкерелеп, ақтай гөр."},
	{33, "33 Егер де халқың Исраил Сенің алдыңда күнәға батқандықтан, жауларынан жеңіліп қалса, ал кейін Саған қайтып, Құдайлық билігіңе мойынсұнса, әрі осы киелі үйіңе қарай сиынып, рақым көрсетуіңді сұрап жалынса,"},
	{34, "34 онда Сен көктен оларға құлақ сала гөр. Халқың Исраилдің күнәларын кешіріп, ата-бабаларына сыйлаған осы елге қайтара гөр."},
	{35, "35 Егер олар көз алдыңда күнәлі болғанда Сен өздерін жазалап, аспанды жаңбыр жауғызбай жауып тастасаң, әрі олар бұл жерге қарай Саған сиынып, билігіңе мойынсұнып, күнәларынан бас тартса,"},
	{36, "36 онда Сен көктен олардың дұғаларына құлақ сала гөр. Қызметшілеріңнің және бүкіл халқың Исраилдің күнәларын кешіре гөр. Оларға тура жолмен қалай жүру керектігін үйретіп, Өзің халқыңа мұралық меншікке берген осы еліңе қайтадан жаңбыр жібере гөр!"},
	{37, "37 Болашақта елді ашаршылық не індет жайлап, егінді өсімдік таты басып немесе көгеріп шірісе, құрт-шегіртке жалмап, не болмаса елге жау шауып, қалаларын талан-таражға салса, яки халықтың арасында қандай да бір апат болып не ауру-сырқау жайылса,"},
	{38, "38 онда олардың Өзіңе сиынып еткен мінажаттарына құлақ сала гөр. Азап шегіп жатқан жеке біреу болсын не бүкіл халқың Исраил болсын, егер осы киелі үйіңе қарай қолдарын жайып Саған сиынып жалбарынса,"},
	{39, "39 онда Сен көктегі тұрағыңнан құлақ сала гөр. Оларды кешіріп, әрқайсысына өзінің ой-ниеті мен істеріне қарай лайықтысын бере гөр. Әр адамның жүрегінде не бар екенін Сен ғана жақсы білесің ғой."},
	{40, "40 Өзің осы елді ата-бабаларымызға сыйлаған едің. Ендеше меншікті халқың бұл жерде тұрған барлық уақытта Сені терең қастерлеп тура жолыңмен жүретін болсын."},
	{41, "41 Тіпті халқың Исраилге жатпайтын жат жерлік адам алыс елден"},
	{42, "42 ұлы есімің мен зор құдіретің туралы естігендіктен осында келіп, Саған бұл киелі үйге қарай сиынса,"},
	{43, "43 онда Сен көктегі мекеніңнен құлақ салып, оның барлық өтініштерін орындай гөр. Осылайша жер жүзінің күллі халықтары Сенің Құдай екеніңді мойындасын. Халқың Исраил сияқты олар да Сені терең қастерлеп, мен салғызған осы ғибадатхананың Өзіңе бағышталып, есіміңмен аталғанын білсін."},
	{44, "44 Халқың Сен жіберген бір жаққа қарай жауларына қарсы соғысқа шыққан кезде, Өзіңнің таңдап алған қалаңа және мен Сенің есіміңе арнап салғызған осы киелі үйге қарай Саған, уа, Жаратқан Ие, сиынып мінажат етсе,"},
	{45, "45 онда Сен көктен олардың мінажаттары мен жалбарыныштарына құлақ салып, оларды қолдап-қорғай гөр."},
	{46, "46 Егер олар Сенің алдыңда күнә жасаса, себебі күнә жасамайтын пенде жоқ, әрі Сен оларға қаһарланып, жау қолына бере салып, жау оларды өзінің не алыс, не жақын бір еліне айдап әкетсе,"},
	{47, "47 ал олар тұтқында жүрген сол жау елінде өкініп, теріс жолдарынан қайтып, Біз күнәға баттық, жасаған істеріміз бұрыс та жексұрын болды! — деп Сенің алдыңда мойынсұнса,"},
	{48, "48 әрі тұтқындап, жер аударған жауларының елінде Саған шын жүректен бет бұрып қайтып келсе, Өзің ата-бабаларына сыйлаған еліңе, таңдап алған қалаңа және мен Сенің құрметіңе салғызған осы ғибадатханаңа қарай Саған сиынса,"},
	{49, "49 онда көктегі тұрағыңнан олардың мінажаты мен жалбарынышына құлақ асып, оларды қолдап-қорғай гөр!"},
	{50, "50 Олардың Өзіңе қарсы шығып, күнәға батып, жасаған теріс қылықтарының бәрін де кешіре гөр. Жеңіп шыққан жауларының жүректерін оларға мейіріммен қарайтындай етіп жібіте гөр."},
	{51, "51 Өйткені олар Өзіңнің меншікті халқың ғой, Сен Өзің оларды темір балқытатын көрік іспетті Мысыр елінен бастап алып шықтың."},
	{52, "52 Уа, Ием, мендей қызметшіңнің және өз халқың Исраилдің жалбарыныштарына назар аудара көрші! Олар Саған сиынып Сенен көмек сұраған кездердің бәрінде дұғаларына жауап бере көрші!"},
	{53, "53 Сен оларды жер бетіндегі басқа барлық халықтардың арасынан Өзіңнің меншікті халқың болуға таңдап алғансың. Біздің ата-бабаларымызды Мысырдан бастап алып шыққан кезіңді осыны өз қызметшің Мұса арқылы айтқансың, уа, Жаратушы Тәңір Ие!"},
	{54, "54 Жаратқан Иеге бағышталған құрбандық үстелінің алдында қолдарын көкке көтеріп, тізерлеп отырған Сүлеймен Жаратқан Иеге арнаған осы мінажаты мен өтініш-тілектерінің бәрін айтып бітірген соң, орнынан тұрды."},
	{55, "55 Сүлеймен сол жерде жиналған күллі исраилдіктерге қарап, қатты дауыспен бата беріп былай деді:"},
	{56, "56 Берген уәдесі бойынша өз халқы Исраилге тыныштық сыйлаған Жаратқан Ие мадақталсын! Оның өз қызметшісі Мұса арқылы берген ізгі уәделерінің барлығы да орындалды."},
	{57, "57 Құдайымыз Жаратқан Ие ата-бабаларымызбен бірге жүріп жар болғанындай, бізге де жар болып, ешқашан бізді тастап та кетпесін, жалғыз да қалдырмасын!"},
	{58, "58 Ол жүрегімізге әсер етіп, Өзіне жақын тартып, ата-бабаларымызға табыстаған өз өсиеттерін, ережелері мен шешімдерін ұстануға құштарлық сыйлай көрсін!"},
	{59, "59 Біздің Құдайымыз Жаратқан Иенің алдында сиынып айтқан осы сөздеріме Ол күні-түні ықылас бөле көрсін! Соның арқасында Жаратқан Ие мендей қызметшісінің және халқы Исраилдің мүддесін қорғап, күнделікті мұқтаждықтарын қамтамасыз ете көрсін!"},
	{60, "60 Осылайша бүкіл жер бетінің халықтары Жаратқан Иенің шынымен Құдай екенін, Одан басқа ешбір құдайдың жоқ екенін білетін болсын!"},
	{61, "61 Ал сендер Құдайымыз Жаратқан Иеге толықтай бағышталған болыңдар! Өздерің қазір істеп жүргендей, әрқашан да Оның ережелері бойынша жүріп, өсиеттерін ұстанатын болыңдар!"},
	{62, "62 Мұнан кейін патша мен жиналып тұрған барлық исраилдіктер Жаратқан Иеге арнап құрбандықтар шалды."},
	{63, "63 Сүлеймен Жаратқан Иемен қарым-қатынасына ризашылық білдіретін құрбандық үшін жиырма екі мың мүйізді ірі қара мен жүз жиырма мың қой-ешкіні ұсынды. Осылайша патша мен күллі исраилдіктер киелі үйді Жаратқан Иеге бағыштау рәсімін орындады."},
	{64, "64 Сол күні өртеліп ұсынылатын тартулар мен құрбандықтардың көп болғаны сонша, киелі үйдің алдындағы қола құрбандық үстелі олардың барлығын сыйғыза алмай қалды. Сондықтан патша Жаратқан Иенің киелі үйінің алдындағы ауланың ортаңғы бөлігіне ерекше тазарту рәсімін жасатып, оны да сол мақсатқа бағыштады. Осылай бұл жерде де толығымен өртелетін құрбандықтар, астық тартулары және ризашылық білдіру үшін шалынған құрбандықтардың майлы бөліктері өртеліп ұсынылды."},
	{65, "65 Осы кезде Сүлеймен бір үлкен той жасады. Бүкіл Исраил халқы: солтүстіктегі Лебо-Хамат қаласынан бастап, Мысырмен шекарадағы өзеннің аңғарына дейінгі зор аралықтағы барлық жерден жиналған қалың қауым Құдайымыз Жаратқан Иенің алдында он төрт күн бойы Сүлейменмен бірге той тойлады: жеті күн ғибадатхананы Құдайға бағыштау рәсімін және тағы жеті күн Күркелер мейрамын өткізуге арналған болатын."},
	{66, "66 Сегізінші күні ол халықты үйлеріне қайтарды. Олар кетпей тұрып патшаға да ізгі тілек айтып баталарын берді. Бәрі Жаратқан Иенің өз қызметшісі Дәуітке және өз халқы Исраилге жасаған барлық игіліктеріне қуанып жүректері шаттанған күйі үйлеріне қайтып кетті."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
