struct kk29 poems[] = {
	{1, "1 Езекия патша болған кезде жасы жиырма бесте еді. Ол Иерусалимде жиырма тоғыз жыл билік жүргізді. Оның шешесінің аты-жөні Зәкәрия қызы Әбия болатын."},
	{2, "2 Езекия түп атасы Дәуіт істегендей Жаратқан Иеге ұнамды әділ істер істеді."},
	{3, "3 Патшалық құруының бірінші жылының бірінші айында Езекия Жаратқан Иенің киелі үйінің есіктерін ашып, оларды жөндетті."},
	{4, "4 Содан ол діни қызметкерлер мен леуіліктерді шақыртып, киелі үйдің шығыс жағындағы алаңға жинап алып,"},
	{5, "5 оларға былай деп тіл қатты: — Уа, леуіліктер, сөзіме құлақ салыңдар! Ата-бабаларыңның сиынған Құдайы Жаратқан Иеге бағышталып тазару рәсімінен өтіңдер. Содан кейін Оның ғибадатханасын тазартыңдар. Осы киелі орынды арамдайтын нәрселердің бәрін де шығарып тастаңдар!"},
	{6, "6 Біздің аталарымыз опасыздық жасап, Құдайымыз Жаратқан Иеден бас тартып, Оған жек көрінішті істер істеді. Олар Жаратқан Иенің жер бетіндегі тұрағынан беттерін бұрып, безіп кетті."},
	{7, "7 Аталарымыз осы ғибадатхананың кіреберісінің есіктерін бекітіп тастап, іштегі шамдарды сөндіріп, Исраилдің Құдайының киелі орнында хош иісті заттарды түтетпей, шалынып, өртелетін құрбандықтарды да ұсынбай қойды."},
	{8, "8 Сол себепті Жаратқан Ие Яһуда мен Иерусалимге қаһарланды. Өз көздеріңмен көріп тұрғандай, Иеміз оларды қорқынышты көрініске айналдырып, басқа халықтардың ысқырып табалауына қалдырды."},
	{9, "9 Қараңдаршы, сол үшін қарияларымыз семсер жүзінен қаза тауып, ұл-қыздарымыз бен әйелдеріміз тұтқында жүр!"},
	{10, "10 Ал енді, Исраилдің сиынған Құдайы Жаратқан Ие бізге мейірімін көрсетіп, ашуын басуы үшін Онымен Келісім жасаспақ ниетім бар."},
	{11, "11 Уа, халайық, міндеттеріңе немқұрайды қарамаңдар! Жаратқан Ие сендерді Өзінің алдында тұрып құрбандықтар мен сый-тартулар ұсынатын қызметкерлер болуға таңдап алған еді."},
	{12, "12 Сонда мына леуіліктер алға шықты: Қохат ұрпақтарынан Амасай ұлы Махат, Азариях ұлы Жоел, Мерари ұрпақтарынан Ғабди ұлы Қиш, Ехәлелел ұлы Азариях; Герсон ұрпақтарынан Зиммах ұлы Жоғах, Жоғах ұлы Ғадан;"},
	{13, "13 Елісапан ұрпақтарынан Шимри мен Еиел; Асаф ұрпақтарынан Зәкәрия мен Маттаниях;"},
	{14, "14 Хеман ұрпақтарынан Ехиел мен Шимей, Едутун ұрпақтарынан Шемаях пен Ұзиел."},
	{15, "15 Олар бауырларын жинап алып, бәрі тазару рәсімінен өтті. Бұдан кейін патша әмір еткендей, Жаратқан Иенің еркіне сай Оның ғибадатханасын тазартуға кірісті."},
	{16, "16 Діни қызметкерлер Жаратқан Иенің киелі үйін тазарту үшін оның ішіне кірді. Олар ғибадатханадан тапқан арам нәрселердің барлығын оның ауласына шығарды. Леуіліктер соларды көтеріп алып, (қаланың сыртындағы) Қидрон сайына апарып тастады."},
	{17, "17 Олар тазарту жұмысын көктемде, бірінші айдың бірінші күні, бастап, сол айдың сегізінші күні Жаратқан Иенің киелі үйінің кіреберісіне енді. Діни қызметкерлер сегіз күн бойы қасиетті бөлмені тазартып, бірінші айдың он алтыншы күні жұмыстарын аяқтады."},
	{18, "18 Содан соң олар патшаға: — Біз Жаратқан Иенің киелі үйін, шалынған құрбандықтарды өртейтін үстелді және оған қажетті барлық құрал-саймандарын, нан тартулары қойылатын үстелді әрі оның барлық ыдыс-аяқтарын түгелдей тазартып шықтық."},
	{19, "19 Ахаз патша билік жүргізген кезінде Құдайға опасыздық жасап, менсінбей аяқасты еткен бұйымдарды қайтадан дайындап, тазарттық. Олар енді Жаратқан Иенің құрбандық үстелінің жанында тұр, — деп есеп берді."},
	{20, "20 Ертеңіне Езекия патша ерте тұрып, қаланың барлық әкімдерін Жаратқан Иенің үйіне ертіп алып келді."},
	{21, "21 Патшалық әулет пен Яһуда халқының күнәларын өтеп, киелі үйді күнәдан арылту үшін олар сол жерге жеті-жетіден жас бұқа, қошқар, тоқты, текелер ала келді. Патша Һароннан тараған діни қызметкерлерге оларды құрбандыққа шалып, Жаратқан Иеге бағышталған құрбандық үстелінде өртеп ұсынуға бұйырды."},
	{22, "22 Алдымен жас бұқалар құрбандыққа шалынды. Діни қызметкерлер солардың қанын құрбандық үстеліне бүркіп шашты. Мұнан соң қошқарлар, кейін тоқтылар шалынып, олардың да қаны құрбандық үстеліне себілді."},
	{23, "23 Осыны бітірген соң діни қызметкерлер күнәні өтеу құрбандығы етіп шалмас бұрын текелерді патша мен бүкіл жамағаттың алдына алып келді. Олар текелердің үстеріне қолдарын қойды."},
	{24, "24 Діни қызметкерлер текелерді шалып, қанын бүкіл Исраил халқын күнәларының арамдығынан тазарту үшін құрбандық үстеліне бүркіп шашты. Себебі шалынып, өртелетін және күнәнің құнын өтейтін құрбандықтар бүкіл Исраил елі үшін ұсынылсын деп патша алдын ала бұйырып қойған болатын."},
	{25, "25 Ол леуіліктерге даңғыра, шертер, жетіген аспаптарын беріп, оларды Жаратқан Иенің ғибадатханасына тұрғызып қойды. Езекияның бұл істегені Дәуіт патшаның берген нұсқауларына, әрі Дәуітке Құдайдың хабарын жеткізген Ғад пен Натан пайғамбарлардың Жаратқан Иеден алған нұсқауларына сай болды."},
	{26, "26 Осылайша леуіліктер Дәуіттің саз аспаптарын, ал діни қызметкерлер кернейлерін ұстап тұрды."},
	{27, "27 Мұнан кейін Езекия Тәңір Иеге арнап тағы басқа да құрбандықтар шалынып, өртелсін деп бұйырды. Олар құрбандық үстелінде өртеле бастаған кезде халық Жаратқан Иеге арналған мадақтау жырларын айта бастады. Исраил патшасы Дәуіттің жасатқан сазды аспаптары және кернейлер де тартылып жатты."},
	{28, "28 Құрбандықтар шалынып, өртеліп біткенше, жиналған бүкіл халық ғибадат етіп, әншілер мадақтау жырларын шырқап, кернейшілер кернейлерін тартып тұрды."},
	{29, "29 Құрбандықтар өртеліп ұсынылған соң патша мен қасындағы жамағаттың барлығы Жаратқан Иенің алдында иіліп, Оған ғибадат етті."},
	{30, "30 Езекия патша және оның әміршілері леуіліктерге: Дәуіт пен Асаф пайғамбар жазып қалдырған жырларымен Жаратқан Иені мадақтай беріңдер! — деп бұйырған еді. Олар Жаратқан Иеге шаттана мадақтау жырларын айтып, бастарын иіп ғибадат етті."},
	{31, "31 Содан соң Езекия халыққа қарап: Енді сендер Жаратқан Иеге бағышталдыңдар. Қазір Оның киелі үйіне жақындап, құрбандықтар шалып, шүкірлік білдіретін сый-тартуларыңды алып келіңдер! — деді. Мұны естіп, бүкіл жамағат құрбандықтар мен сый-тартулар алып келді. Бұларға қоса халайық өз жүрек қалауларына сай шалынып, өртелетін құрбандықтар да ұсынды."},
	{32, "32 Олардың Жаратқан Иеге арнап шалынып, өртелуге әкелген құрбандықтарының жалпы саны жетпіс сиыр, жүз қошқар, екі жүз тоқты болды."},
	{33, "33 Бұлардан басқа тағы да алты жүз бұқа мен үш мың қой-ешкі Құдайға бағышталды."},
	{34, "34 Ал діни қызметкерлер аз болып, олар шалынып, өртелетін құрбандықтардың терілерін сыдырып үлгіре алмады. Сондықтан басқа діни қызметкерлер тазару рәсімінен өтіп болғанға дейін, рулас бауырлары, леуілік қызметшілер, оларға жұмыстарын бітіргенше көмектесе тұрды. Бұлар тазару рәсімінен өтуге діни қызметкерлерден гөрі көбірек көңіл бөлетін еді."},
	{35, "35 Сонымен ағыл-тегіл мол құрбандықтар шалынып, үстеріне арнайы сұйық тартулар құйылып, өртелді. Ризашылық құрбандықтарының майлы бөліктері де өртеліп ұсынылды. Осылай Жаратқан Иенің киелі үйінің қызметі қайтадан қалпына келтірілді."},
	{36, "36 Құдайдың өз халқы үшін жасағандарына Езекия мен бүкіл жұрт қатты қуанды. Себебі бұлардың бәрі күтпеген жерден тез жүзеге асқан еді."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
