struct kk36 poems[] = {
	{1, "1 Езекияның патшалық құрған он төртінші жылы Ассур патшасы Сеннахирим Яһуда елінің (Иерусалимнен өзге) барлық қамалды қалаларына қарсы аттанып, оларды басып алды."},
	{2, "2 Сол кезде Ассур патшасы Лахыштан өзінің бас уәзірін қалың қолмен Иерусалимдегі Езекия патшаға жіберді. Бас уәзір Жоғарғы тоғанға қарай аққан арықтың бойында, мата ағартушы шеберлердің даласына апаратын жолда тоқтады."},
	{3, "3 Сонда Яһуда патшасының сарай басқарушысы Хелкия ұлы Елиәкім, хатшысы Себна және жылнамашысы Асаф ұлы Жоғах қаладан шығып, оның алдына келді."},
	{4, "4 Ассур патшасының бас уәзірі бұларға тіл қатып былай деді: — Езекияға мына сәлемді жеткізіңдер: Ассур елінің ұлы патшасы мынаны айтады: Сен не нәрсеге арқа сүйейсің?"},
	{5, "5 Менде соғысуға ақыл-кеңес пен күш-қуат бар дейсің. Алайда мұның тек бос сөздер ғана. Маған қарсы бас көтеретіндей сен енді кімге сүйенесің?!"},
	{6, "6 Біліп ал, сен сүйеніп келген Мысыр сынық қамыс іспетті. Егер адам сынық қамысқа сүйенсе, сол оның қолына кіріп, алақанын тесіп шықпай ма? Сол сияқты, Мысыр патшасы перғауын да өзіне сүйенгендердің бәріне осылай істейді."},
	{7, "7 Ал егер маған Сиынатын Құдайымыз Жаратқан Иеге сеніп отырмыз десеңдер, мынаны ойлаңдар: Езекия Оны қатты ренжітті емес пе? Ол Жаратқан Иеге арналған биіктеу жерлердегі табыну орындары мен құрбандық үстелдерін қиратып, Яһуда мен Иерусалим халқына Тек (Иерусалимдегі) осы құрбандық үстелінің алдында ғана ғибадат етіңдер! деп бұйырған жоқ па?!"},
	{8, "8 Ал енді, менің тақсыр ием — Ассур патшасымен келісім жасаңдар: мен сендерге екі мың ат беремін, егер де араларыңнан соншама атқа мінер жігіт шығара алсаңдар!"},
	{9, "9 Сендер тіпті Мысырға, оның соғыс күймелері мен атты әскеріне сүйене отырып та, тақсыр иемнің нөкерлерінің арасынан ең кіші деген әкіміне де қарсы тұра алмайсыңдар!"},
	{10, "10 Мұның үстіне мені осы жерді талқандауға Жаратқан Иенің еркінсіз келді дейсіңдер ме? Маған: Сол елге өрлеп барып, оны құртып жібер! деген — Жаратқан Иенің Өзі!"},
	{11, "11 Сонда Елиәкім, Себна және Жоғах Ассур патшасының бас уәзірінен: — Біздей құлдарыңызға арамейше сөйлей беріңіз, біз оны да түсінеміз. Яһудилердің тілінде сөйлемей-ақ қойыңыз. Әйтпесе қамал басындағы халық естіп қояр, — деп өтінді."},
	{12, "12 Бірақ бас уәзір жауап қайырып: — Тақсыр ием мені осы сөздерді тек сендердің иелерің мен өздеріңе ғана айту үшін жіберді дейсіңдер ме? Мені мына қабырғаның үстінде отырған адамдарға да жіберді емес пе? Олар әлі де сендермен бірге өз нәжістері мен зәрлерін ішіп-жейтін болады! — деді."},
	{13, "13 Содан бас уәзір отырған орнынан тұрып, бар даусымен айқайлап яһудилердің тілінде былай деді: — Ұлы патша — Ассур патшасының сөзін тыңдаңдар!"},
	{14, "14 Патша мынаны айтады: Езекияның еліктіруіне алданбаңдар! Ол сендерді менің қолымнан құтқарып қала алмайды."},
	{15, "15 Езекияның Бізді Жаратқан Ие шүбәсіз құтқарады, осы қала Ассур патшасының қолына түспейді деп сендерді Жаратқан Иеге нандыруына жол бермеңдер!"},
	{16, "16 Езекияны тыңдамаңдар! Өйткені Ассур патшасы мынаны айтады: Менімен бітім жасап, қаладан шығып келіңдер! Сонда әркім өз жүзім бұтасы мен інжір ағашының жемісін жеп, өз құдығынан су ішетін болады."},
	{17, "17 Кейін мен мұнда қайтып келіп, сендерді осы елдерің сияқты басқа бір елге көшіремін. Онда нан мен жүзім шырыны, егіндіктер мен жүзімдіктер мол болады."},
	{18, "18 Тек Езекия Жаратқан Ие бізді құтқарады деп сендерді еліктірмесін! Әлде басқа халықтардың қандай да бір тәңірі өз елін Ассур патшасының қолынан құтқара алып па еді?!"},
	{19, "19 Хамат пен Арпадтың тәңірлері қайда? Сепар-Уаймның тәңірлері қайда? Әлде бірде-бір тәңір Самарияны менің қолымнан аман алып қала алды ма?"},
	{20, "20 Осы елдердің барлық тәңірлерінің қайсысы өз елін менің қолымнан құтқарып қалған еді? Ендеше Жаратқан Ие Иерусалимді менің қолымнан құтқарып алмақшы ма?!"},
	{21, "21 Ал халық еш жауап қайтармай, үндемей тұра берді. Себебі Езекия патша: Оған жауап қайырмаңдар! деген жарлық шығарған болатын."},
	{22, "22 Мұнан кейін сарай басқарушысы Хелкия ұлы Елиәкім, бас хатшы Себна және жылнамашы Асаф ұлы Жоғах Езекияға қайта оралды. Олар қатты күйзеліп үстеріндегі көйлектерінің өңірін айыра жыртып, патшаға Ассур патшасының бас уәзірінің сөздерін жеткізді."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
