static void view11() {
struct kk11 poems[] = {
	{1, "1 Мен де Миқайлды мидиялық Дарийдің билік құрған бірінші жылынан бастап қолдап-қорғап келемін."},
	{2, "2 Ал енді, мен саған болашақта шынымен орын алатын оқиғаларды көрсетемін. Персияда тағы үш патша билік құрады, ал төртіншісі бұрынғылардан әлдеқайда бай болады. Ол сол байлығымен күш жинап, барлық адамдарын Грекия патшалығына қарсы аттандырады."},
	{3, "3 Бұдан кейін (Грекияда) бір ерекше мықты патша пайда болады. Ол зор күшпен билік құрып, өз қалауымен іс-әрекет жасайды."},
	{4, "4 Алайда әбден күшейген кезде оның патшалығы түп-тамырымен жұлынып, ыдырап, төртке бөлініп кетеді. Оның билігі басқаларға беріліп, өзінің ұрпақтарына бұйырмайды. Патшалықтың бұрынғы күш-қуаты да қалмайды."},
	{5, "5 Сол тұста оңтүстіктегі елдің (яғни Мысырдың) патшасы күшейеді. Бірақ (өлген грек патшасының) бір қолбасшысы Мысыр патшасынан да күшті болып, оның билігі әлдеқайда үлкейіп кетеді."},
	{6, "6 Бірнеше жылдан соң екеуі одақтасып, келісімдерін бекіту үшін Мысыр патшасы өз қызын солтүстіктің патшасына ұзатады. Бірақ сол ханым ықпалынан айырылып, күйеуінің өзі де, оның күш-қуаты да ұзаққа жетпейді. Сол кездерде ханымның өзі мен еріп келген нөкерлері, оның әкесі және ханымды жақтаған күйеуі де бақытсыздыққа ұшырайды."},
	{7, "7 Кейін ханымның әулетінен шыққан біреу Мысырға билік құрады. Ол солтүстіктің патшасының әскеріне қарсы аттанып, оның қамалына басып кіріп, жеңіске жетеді."},
	{8, "8 Мысыр патшасы солтүстік патшалығының жалған тәңірлерін, қола мүсіндерін және бағалы алтын, күміс бұйымдарын олжалап, өз еліне алып кетеді. Содан ол бірнеше жыл бойы солтүстік патшасына тыныштық береді."},
	{9, "9 Ал кейінірек солтүстіктің патшасы Мысыр патшалығына шабуыл жасайды, бірақ өз еліне қайта оралуға мәжбүр болады."},
	{10, "10 Оның ұлдары да соғысқа дайындалып, үлкен қарулы күш жасақтайды. Сол әскер асып-тасыған судай елді басып алып, Мысыр патшасының қорғанына дейін жетеді."},
	{11, "11 Қатты қаһарланған Мысыр патшасы қарсы аттанып, солтүстіктің патшасына бас салады. Оның жинаған үлкен әскері Мысыр патшасының қолына түседі."},
	{12, "12 Мысыр патшасы сол әскерді қолға түсіріп, айдап әкеткеніне өркөкіректеніп, сан мыңдаған адамдарды құртады. Бірақ оның жеңісі ұзаққа созылмайды."},
	{13, "13 Себебі солтүстіктің патшасы бұрынғыдан да үлкен әскер жинап, бірнеше жылдан кейін толықтай жарақтанған қуатты әскерімен оған қайтадан шабуыл жасамақ."},
	{14, "14 Сол кездері көп адамдар Мысыр патшасына қарсы бас көтереді. Сонда сенің халқыңның арасынан да кейбір қатал бүлікшілер көріністі жүзеге асыру үшін бас көтереді. Бірақ олар жеңіледі."},
	{15, "15 Ал солтүстіктің патшасы әскерімен аттанып келіп, Мысырдағы жақсы бекінген қаланы қоршауға алып, топырақтан биік етіп үймек жасап, қаланы басып алады. Мысыр әскерінің, тіпті ең таңдаулы бөлімдерінің де жауға тойтарыс беретін еш қауқары қалмайды."},
	{16, "16 Осылайша басқыншы қалағандарын істеп, оған қарсы тұратын ешбір адам болмайды. Ол көрікті елге қосын тігіп, зорлық-зомбылық көрсетеді."},
	{17, "17 Солтүстіктің патшасы бүкіл мемлекетінің күш-қуатымен Мысырға аттануға бел буып, сол елмен одақтасу шарттарын алға қойып, жүзеге асырады. Мысыр патшалығын тәуелсіздігінен біржолата айыру үшін оның патшасына өз қызын береді. Бірақ бұл жоспары табыссыз болып, онысынан еш нәтиже шықпайды."},
	{18, "18 Кейін солтүстіктің патшасы теңіз жағалауларындағы елдерге аттанып, көп адамды қолға түсіреді. Алайда бір қолбасшы патшаның көрсеткен зорлық-зомбылығын тоқтатып, солардың есесін қайтарады."},
	{19, "19 Содан солтүстіктің патшасы өз елінің бекіністеріне қарай беттейді, бірақ ол жеңіліп, із-түзсіз құрып кетеді."},
	{20, "20 Оның мұрагері таққа отырғаннан кейін, патшалық салтанатқа қаржы жетерлік болу үшін алым-салық жинайтын қанаушыны қол астындағы елдерін аралатып жібереді. Ал азғана уақыттан кейін патша не соғыста емес, не халықтың қаһарынан емес, күтпеген жерден мерт болады."},
	{21, "21 Оның орнына патшалық қадір-құрмет бұйырылмаған бір жексұрын адам солтүстік патшалыққа билік құрады. Ол бейбіт кезде барып, патшалықты айлакерлікпен қолына түсіріп алады."},
	{22, "22 Оған ұлан-ғайыр қарулы күштер қарсы тұра алмай, олар да, онымен келісім жасасқан билеуші де күйреп, ғайып болады."},
	{23, "23 Сол жексұрын патша келісім жасағанмен, қулық-сұмдықпен әрекет етеді, әрі азғана адамдардың қолдауымен күшейіп алады."},
	{24, "24 Патша ата-бабалары істеп көрмеген зұлымдықты істеп, бейбіт жатқан құнарлы, бай аймақтарға кенеттен басып кіреді. Олжалап қолға түсірген дүние-мүлікті нөкерлеріне үлестіріп береді. Ол қамалды қалаларға қарсы айлалы жоспар құрса да, онысы ұзаққа созылмайды."},
	{25, "25 Кейінірек ол батылы барып, Мысырдың патшасына қарсы үлкен әскер аттандырады. Мысыр патшасы әлдеқайда үлкен де қуатты әскерімен аттанып, соғысса да, оның тойтарыс беретін шамасы болмайды. Себебі өзіне қарсы астыртын әрекеттер жасалмақ."},
	{26, "26 Дастарқанынан дәм татқан өз адамдары оған сатқындық жасайды. Содан Мысыр патшасының әскері күйреп, көптеген жасақшысы қаза табады."},
	{27, "27 Бұдан кейін екі патша бір дастарқанның басында отырып, іштей бір-біріне жамандық ойлап, жалғандық айтады. Бірақ бұдан еш нәтиже шықпайды. Себебі Құдай белгілеген уақытта істің соңы болады."},
	{28, "28 Солтүстіктің патшасы еліне көп байлықпен қайта оралады. Жолшыбай ол Құдай қасиетті Келісімін жасаған Исраил халқына қастық ойлап, сонысын іске асырады да, өз еліне қайтады."},
	{29, "29 Кейін уақыты келгенде солтүстіктің патшасы қайтадан Мысырға аттанып, басып кіреді. Бірақ бұл жорығы бұрынғысындай табысты болмайды."},
	{30, "30 Себебі теңіздің батыс жағалауларындағы елден кемелер оған қарсы шығады. Содан солтүстіктің патшасы батылдығынан айырылып, еліне қарай кері шегінеді. Қатты ашуланған ол Құдаймен қасиетті Келісім жасаған Исраил халқына қаһарын төкпек болады. Сондықтан ол өз еліне қайта оралып бара жатқанда, қасиетті Келісімнен бас тартқандарға ықылас танытып, дем береді."},
	{31, "31 Бірақ оның жіберген жасақшылар бөлімі қамалмен бекінген киелі үйге басып кіріп, оны арамдайды. Олар Құдайға арналған күнделікті құрбандық шалуды тоқтатып, киелі үйде оны қаңыратып бос қалдыратын жексұрын нәрсені орнатады."},
	{32, "32 Солтүстіктің патшасы қасиетті Келісімді бұзғандарға жағымпазданып, оларды арамдыққа бұрынғыдан бетер еліктіреді. Алайда Құдайға сенімділер нығая түсіп, оған қарсы тұрады."},
	{33, "33 Халық арасындағы даналар көп адамға түсінік береді. Бірақ едәуір уақыт бойы олар қатты қудаланып, семсердің жүзінен не өрттен шейіт болады немесе құлдыққа түсіп не тоналып жүреді."},
	{34, "34 Олар сондай азап шеккенде адамдардан біраз көмек көрсе де, өздеріне қосылғандардың көбінің ниеттері адал болмайды."},
	{35, "35 Ал (Құдайға сенімді) даналардың арасындағы бірнешеуі шейіт болады. Сол арқылы олар сынақтан өтіп, осы ауыр кезеңнің соңына дейін тазарып, ақталады. Себебі сол кезеңнің Құдай белгілеген соңы келгенше әлі де біраз уақыт бар."},
	{36, "36 Солтүстіктің әлгі патшасы өз бетімен жүріп, тым паңданады. Сонда ол өзін әрбір тәңірден де жоғарымын деп санап, тіпті тәңір атаулының үстіндегі Құдайдың Өзіне қарсы небір балағат сөздер айтады. Құдай қаһарлы үкімін жүзеге асырғанша бұл патша онысымен табысты болады. Себебі сол алдын ала белгіленген оқиғалар орындалуға тиіс."},
	{37, "37 Патша не өз ата-бабалары сиынған (жалған) тәңірлерге, не әйелдер ұнатқан біреуіне, не басқа да тәңірге құрмет көрсетпей, өзін-өзі бәрінен де жоғары қоятын болады."},
	{38, "38 Олардың орнына патша қамалдардың тәңіріне табынып, ата-бабалары танымаған сол тәңірсымақты алтын, күміс, асыл тас және өзге де әр түрлі қымбат сыйлықтармен дәріптейді."},
	{39, "39 Патша ең қуатты қамалдарды сол бөтен тәңірсымақтың көмегімен басып алады. Өзіне мойынсұнғандарға ерекше сый беріп, оларды көптеген халықтың үстінен басқарушы етіп қойып, жерді ақшаға сататын болады."},
	{40, "40 Сол заманның ақырында Мысыр патшасы оған қарсы соғысуға аттанады. Бірақ солтүстіктің патшасы өзінің жарақталған соғыс күймелерімен, атты әскерімен және көптеген әскери кемесімен оған қарсы құйындай шүйіледі де, көптеген елдерді жаулап алып, топан судай жайпап өтеді."},
	{41, "41 Ол көрікті елге де басып кіреді. Көп халық жеңіледі, ал Едом мен Моаб және Аммонның әміршілері оның қолынан сытылып кетеді."},
	{42, "42 Солтүстіктің патшасы тағы басқа елдерге де билік құрып, Мысыр патшалығы да одан құтыла алмайды."},
	{43, "43 Патша Мысырдың алтын-күмісіне және өзге де бүкіл қазыналарына ие болып, либиялықтар мен құштықтар да оған мойынсұнып, соңынан еретін болады."},
	{44, "44 Бірақ патшалығының солтүстік және шығыс аймақтарынан келген суық хабарлар оны алаңдатады. Содан патша долданған күйі көптеген адамдарды құртып, ойрандау үшін сонда кетеді."},
	{45, "45 Жолшыбай ол өзінің патшалық шатырын Жерорта теңізі мен көрікті де қасиетті Сион тауының арасына тігеді. Бірақ патшаның дәурені өтіп, оған қол ұшын беретін ешкім де болмайды."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view12() {
struct kk12 poems[] = {
	{1, "1 Сол кезде сенің халқыңды қорғаушы бас періште Миқайл шығады. Бұл, халықтар алғаш пайда болғалы ешқашан болып көрмеген қиын-қыстау кезең болады. Сонда сенің халқың, яғни есімдері арнайы кітапқа енгізілгендердің барлығы, құтқарылады."},
	{2, "2 Жер қойнында жатқан сансыз көп өлгендер қайта тіріледі. Олардың бірқатары мәңгілік өмірге ие болады, ал қалғандары мәңгілік ұят-масқараға қалады."},
	{3, "3 Даналар аспан кеңістігі сияқты нұрланады, көптеген адамды әділдік жолына бастап алып келген олар жұлдыздар сияқты мәңгі бақи жарқырап тұратын болады."},
	{4, "4 Ал өзің, Даниял, осы сөздерді жақсылап сақтап, сол заманның соңына дейін кітапты мөрлеп бекітіп қой. Көптеген адам оны мұқият қарап шығып, білімдері молая түсетін болады. — (Маған келген хабаршы айтқандарын осылай тамамдады.)"},
	{5, "5 Бұдан кейін мен, Даниял, қарасам, басқа екі хабаршы пайда болыпты: біреуі өзеннің бір жағасында, екіншісі қарама-қарсы жағасында тұр екен."},
	{6, "6 Солардың біреуі үстіне ақ бәтес киім киген. Ол өзен суының үстінде тұрған басқа хабаршыдан: — Осы таңғаларлық ғажап оқиғалар жүзеге асқанға дейін қанша уақыт өтуге тиіс? — деп сұрады."},
	{7, "7 Ақ бәтес киінген, өзен суының үстінде тұрған әлгі хабаршы екі қолын да аспанға қарай көтеріп, мәңгі тірі Хақ Тағаланың атымен ант етіп былай деді: — Бұның бәрі бір кезеңге, екі кезеңге және жарты кезеңге созылады! Құдайға бағышталған халықтың күші түбегейлі күйрегенде осы оқиғалар аяқталады!"},
	{8, "8 Мен оның айтқанын естісем де, түсінбедім. Сондықтан мен одан: — Мырзам, мұның бәрінің соңы не болар екен? — деп сұрадым."},
	{9, "9 Хабаршының жауабы мынау болды: — Даниял, өз жолыңмен жүре бер. Бұл хабар осы заманның соңына дейін жабылып, сақталып әрі мөрленіп бекітілген."},
	{10, "10 Көптеген адам осы ауыр сынақтардан өтіп, тазарып, мінсіз болады. Алайда зұлымдар күнә жасауларын жалғастыра береді. Солардың ешқайсысы хабарды түсінбейді, тек даналар ғана оның мағынасын ұғатын болады."},
	{11, "11 Күнделікті құрбандық ұсыну доғартылып, киелі үйді арамдап бос қалдыратын жексұрын нәрсе сол жерде орнатылғаннан кейін бір мың екі жүз тоқсан күн өтеді."},
	{12, "12 Ал бір мың үш жүз отыз бес күн күтіп, солар өткенше шыдамдылық танытқан әркім бақытты болады!"},
	{13, "13 Өзің соңына дейін өз жолыңмен жүре бер. Сен дүниеден өтіп, тынығасың. Ақыр заманда қайта тіріліп, (Құдайдың Патшалығындағы) өзіңе дайындалып қойылған үлесіңді иемденесің!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
