static void view1() {
struct kk1 poems[] = {
	{1, "1 Мұнда Жаратқан Иенің Петуел ұлы Жоелге арнаған сөзі берілген."},
	{2, "2 Тыңдаңдар мынаны, уа, ақсақалдар, құлақ салыңдар, уа, елдің күллі тұрғындары! Өз бастарыңнан өткен күндерде не ата-бабаларыңның заманында мұндай нәрсе болып па еді?"},
	{3, "3 Бұл туралы хабарды балаларыңа айтыңдар, балаларың немерелеріңе, ал немерелерің шөберелеріңе жеткізсін!"},
	{4, "4 Шегірткенің құрттарынан қалғанды шегірткелер жеді, шегірткелерден қалғанды басқа құрттар жеді, ал одан қалғанды қоңыздар жеп тауысты."},
	{5, "5 Ояныңдар, уа, маскүнемдер, еңіреп жылаңдар! Ей, шарап ішетіндердің бәрі де, тәтті шарапты жоқтап зарлаңдар, өйткені енді содан құр қалдыңдар!"},
	{6, "6 Себебі Менің еліме бір халық басып кірді, олар күшті әрі қисапсыз көп, ал тістері — арыстанның тістеріндей, тырнақтары — ұрғашы арыстанның тырнақтарындай күшті де өткір."},
	{7, "7 Сол халық Менің жүзім бұталарымды ойрандады, інжір ағаштарымның бұтақтарын сындырып, жемістерін жұлып тастап, бұтақтары ағарып қалды."},
	{8, "8 Атастырылған күйеуін жоқтап, азалы киім киіп тұрған қыздай зарлап жылаңдар!"},
	{9, "9 Жаратқан Иенің киелі үйінде жұрт астық әрі сұйық тартуларын ұсынбайтын болды. Соған Жаратқан Иеге ғибадат етуші діни қызметкерлер қайғыруда."},
	{10, "10 Егіндіктер ойрандалған, қара жер қайғырып жатыр, өйткені қуаңшылықтан егін құрып, жүзімдер суалып, зәйтүн жемістері піспей солып қалды."},
	{11, "11 Күдерлеріңді үзіңдер, уа, егіншілер, зарлаңдар, уа, жүзімшілер! Өйткені далада жинайтын өнім қалмады."},
	{12, "12 Жүзім бұталары қурап, інжір ағаштары солып қалды, анар, құрма, алма, даладағы барлық ағаштар да қурап қалды. Содан адамдар да қуаныштан ада болды."},
	{13, "13 Уа, діни қызметкерлер, азалы киім киіп жылаңдар! Құрбандық үстелінің алдында қызмет етуші сендер, зарлаңдар! Азалы киімдеріңді түнде де шешпей ұйықтаңдар, уа, Құдайымның қызметкерлері! Өйткені сендердің Құдайыңның үйінде енді астық әрі сұйық тартулары ұсынылмайтын болды!"},
	{14, "14 Құдайға арнап ораза ұстайтын уақытты белгілеңдер! Салтанатты жиналыс жариялап, елдің ақсақалдары мен басқа да тұрғындарын Құдайларың Жаратқан Иенің киелі үйіне жинап алыңдар! Иемізді көмектесуге жалбарына шақырыңдар!"},
	{15, "15 Жаратқан Иенің күні таяп қалды. Бұл қандай қасіретті күн болмақ! Сол күні құдіреті шексіз Құдай елді талқандамақ."},
	{16, "16 Біздің көз алдымыздан азық-түлік алшақтап кетіп, Құдайымыздың киелі үйінен қуаныш пен шат-шадыман әндер ғайып болады емес пе?"},
	{17, "17 Себілген тұқым топырақ кесектерінің астында құрғап қалды. Содан егін шықпай, қамбалар бос тұр, астық қоймалары бұзылған."},
	{18, "18 Малдың ыңырсуын-ай! Ірі қара жайылым болмай айналаны кезіп адасып жүр, ұсақ мал да қиналуда."},
	{19, "19 Уа, Жаратқан Ие, Саған жалбарынамын! От иен даладағы жайылымдарды жалмап, жалындары жеміс ағаштарын құртып жіберді!"},
	{20, "20 Даладағы жабайы аңдар да Саған жалбарынуда, өйткені бұлақтардың арналары кеуіп, елсіз даладағы жайылымдарды өрт жалмады."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct kk2 poems[] = {
	{1, "1 Сионда керней тартыңдар, киелі тауымда дабыл қағыңдар! Бүкіл елдің халқы қорқып қалтырасын, өйткені Жаратқан Иенің күні келеді, міне, ол таяп та қалды!"},
	{2, "2 Сол күн — қараңғылық пен қара түнектің, бұлттар мен қою тұманның күні! Таң ата жарықтың тау бастарына біртіндеп жайылатынындай, бір үлкен де қуатты әскер елді қаптап келе жатыр. Ол сияқты ежелде ештеңе болмаған, келер замандарда да болмайды."},
	{3, "3 Әскердің алдында — бәрін жалмайтын от, артында — лаулаған жалын! Алдындағы жер Едемдегі пейіш бағы сияқты болып тұрады, ал әскер өткеннен кейін қаңыраған қу дала болып шыға келеді. Олардан ештеңе де құтыла алмайды."},
	{4, "4 Бұл жаулардың түрі аттардың түріндей, ал алға ұмтылулары — атты әскердің шабысындай."},
	{5, "5 Олардың даусы тау бастарында гүрілдеген соғыс күймелеріндей, сабанды сатырлатып жалмайтын оттай, әрі шайқасқа дайындалып сап құрған қуатты әскердей естіледі."},
	{6, "6 Бұларды көргенде халықтар қорқып қалтырап, барлық адамның өңдері қуаңқы тартады."},
	{7, "7 Сол әскер батырдай алға басады, жауынгердей қабырғаға өрмелейді! Барлығы бірдей алға басып, алған бағыттарынан таймайды."},
	{8, "8 Бір-бірімен кақтығыспай, әрқайсысы өз жолымен жүреді. Жау қаруының арасын жарып өткенде де, оларды ештеңе тоқтата алмайды."},
	{9, "9 Олар қалаға бас салып, оның қабырғасының үстінде жүгіреді, үйлеріне өрмелеп, ұры сияқты терезе арқылы кіреді."},
	{10, "10 Олардың алдында жер сілкініп, аспан шайқалады, күн мен ай қараңғыланып, жұлдыздар да сөніп қалады."},
	{11, "11 Жаратқан Ие сол әскерінің алдында дауысын шығарады. Оның жинаған қолы қандай орасан зор! Оның әмірін орындайтындар өте көп. Шын мәнінде Жаратқан Иенің соты күні ұлы да қатты қорқынышты! Оған кім шыдай алады?"},
	{12, "12 Тіпті қазірдің өзінде Жаратқан Ие мынаны нық айтады: Шын жүректен Маған қайтыңдар, ораза ұстап, жылап қайғырыңдар!"},
	{13, "13 Қайғыларыңды білдіру үшін тек киімдеріңнің өңірін жыртып қана қоймаңдар, істегендеріңе шын жүректен өкініңдер! Құдайларың Жаратқан Иеге қайтып келіңдер! Өйткені Ол рақымды да қайырымды, тез қаһарланбайды, берік мейірімі мол, әрі жазалаймын деген райынан қайтуға дайын."},
	{14, "14 Кім біледі, мүмкін, Ол ашуы басылып, сендерді мүсіркейтін шығар? Сонда Құдайларың Жаратқан Ие сендердің Өзіне арнап астық әрі сұйық тартулар ұсынуларыңа жететіндей мол жарылқауын жаудырар."},
	{15, "15 Сионда керней тартыңдар! Ораза ұстайтын уақытты белгілеп, салтанатты жиналыс жариялаңдар!"},
	{16, "16 Халықты жинаңдар, әрқайсысы тазару рәсімінен өтіп келсін! Бұған ақсақалдар мен балаларды да, тіпті емшектегі нәрестелерді де жинап әкеліңдер! Күйеу мен келін де отауларынан шығып, жиналысқа келсін!"},
	{17, "17 Жаратқан Иеге ғибадат етуші діни қызметкерлер киелі үйдің кіреберісі мен құрбандық үстелінің арасындағы жерде қайғырып жылап, былай деп жалынсын: — Уа, Жаратқан Ие, өз халқыңды аяй гөр, меншікті халқыңның масқаралануына жол бере көрме! Басқа халықтардың Олардың сиынған Құдайы қайда екен? деген мазағына қалдырмашы!"},
	{18, "18 Сонда Жаратқан Ие Өзінің елін жан сала қорғап, өз халқын аяйтын болады."},
	{19, "19 Иә, Иеміз оларға жауап қатып былай дейді: — Мен сендерге астық, жүзім шырынын және зәйтүн майын жіберемін, сендер соларға тойынасыңдар. Бұдан былай Мен сендерді басқа халықтарға мазақтатып қоймаймын!"},
	{20, "20 Солтүстіктен баса-көктеп келген әскерді сендерден аластатамын. Оны басы шығыстағы Өлі теңізден басталып, аяғы батыстағы Жерорта теңізімен аяқталатын қылып, қуаңшылық басқан, қаңыраған бос елге жіберемін. Сол жерде шіріп сасып, олардан жағымсыз сасық иіс тарайтын болады. Себебі олардың істегендері ғаламат жауыздық болатын!"},
	{21, "21 Уа, елім, қорықпа, қуан да шаттан, өйткені Жаратқан Ие ұлы істер істеді!"},
	{22, "22 Ей, жабайы аңдар, қорықпаңдар! Иен даланың жайылымдары жасыл, ағаштар жемісін уақытында береді, інжір ағашы мен жүзім бұтасы да молынан өнім береді."},
	{23, "23 Сендер де, уа, Сион халқы, Құдайларың Жаратқан Иеге қуанып, шат-шадыман болыңдар! Өйткені Ол сендерді ақтағанының белгісі ретінде күзгі жаңбырды жаудырды. Бұдан былай Ол бұрынғысынша күзгі және көктемгі жаңбырларды уақытында жіберетін болады."},
	{24, "24 Қырмандар астыққа толып, сыққыштарыңнан алынатын жүзім шырыны мен зәйтүн майы мол болады."},
	{25, "25 Сол жылдары шегіртке, құрт, қоңыз және шегіртке құрттарының, яғни Өзім араларыңа жіберген ғаламат әскерімнің, жегендерінің орнын сендер үшін толтырамын."},
	{26, "26 Осылай жейтін тамақтарың мол болып тойынасыңдар. Маған, Құдайларың Жаратқан Иеге, шүкірлік айтасыңдар, себебі өздерің үшін істейтіндерім сондай ғажап болмақ! Осылай Менің халқым бұдан былай масқараға қалмайды."},
	{27, "27 Сонда сендер Менің Исраилдің арасында тұратынымды және өздеріңнің Құдайларың Жаратқан Ие екенімді, әрі Менен басқа ешқандай Құдайдың жоқ екенін білетін боласыңдар."},
	{28, "28 Осылардан кейін Мен өз Рухымды бүкіл адамзаттың үстіне құямын. Ұл-қыздарың пайғамбарлық сөз айтады, қарттарың түс, жас жігіттерің аян көреді."},
	{29, "29 Сол күндері Мен тіпті құл-күңдердің үстіне де Рухымды төгемін."},
	{30, "30 Мен аспан мен жерде керемет істер істеймін: қан, от және бұрқыраған түтін пайда болады."},
	{31, "31 Жаратқан Иенің ұлы да қорқынышты (соты) күні келердің алдында күн — қара түнек, ай — қан қызыл болады."},
	{32, "32 Ал Иеміздің атын атап Оған сиынған әркім құтқарылады. Жаратқан Иенің айтқанындай, құтқарылғандар Иерусалимдегі Сион тауында тұратын болады. Олар Жаратқан Иенің шақырып алып, аман қалдырғандарының арасында болады."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct kk3 poems[] = {
	{1, "1 Біліп алыңдар, Мен сол оқиғалар болатын заманда Яһуда елі мен Иерусалимнің тұтқында жүрген халқын қайтарып, игілікке кенелтемін."},
	{2, "2 Сонда Мен халықтардың барлығын жинап, Жаратқан Иенің Соттау аңғарына алып барамын. Сол жерде Мен осы халықтарды Өзімнің меншікті халқым Исраилді басқа елдердің арасына шашыратып жібергендері үшін соттап жазалаймын. Олар Менің елімді өзара бөлісті,"},
	{3, "3 халқымды да жеребе тастап бөлісіп алды: ұл балаларды жезөкшенің ақысына айырбастап, бойжеткендерді шарапқа сатып, ішіп қойып жүрді."},
	{4, "4 Ал сендер, уа, Тир мен Сидон және філістір елінің аймақтары, Маған не істейін деп жатырсыңдар? Сендер Менен кек алғыларың келе ме? Егер солай ететін болсаңдар, Мен істегендеріңді өз бастарыңа лезде-ақ қайтарамын."},
	{5, "5 Өйткені сендер Менің алтын-күмісім мен қымбат бағалы бұйымдарымды тартып алып, өз ғибадатханаларыңа апарып қойдыңдар."},
	{6, "6 Сонымен қатар өз елдерінен аластатып, басқа жаққа жіберу үшін Яһуда мен Иерусалимнің адамдарын гректерге саттыңдар."},
	{7, "7 Біліп қойыңдар, Мен оларды өздерің сатып жіберген елдерден жетелеп алып келемін. Ал оларға істегендеріңді өз бастарыңа түсіремін."},
	{8, "8 Өздеріңнің ұл-қыздарыңды Яһуданың адамдарына сатамын, олар соларды алыста тұратын Сәба халқына сатып жібереді. — Жаратқан Ие осыны нық айтады."},
	{9, "9 Халықтардың арасында мынаны жариялаңдар: Соғысқа дайындалып, батырларыңды шақырыңдар! Барлық жасақшыларың аттанып, осында өрлеп келсін!"},
	{10, "10 Соқаларыңнан семсер, орақтарыңнан найза жасаңдар! Әлсіздерің Мен мықтымын! дейтін болсын."},
	{11, "11 Барлық халықтар, тездетіп жан-жақтан келіңдер, бәрің де сол жерге жиналыңдар! — Уа, Жаратқан Ие, өз батырларыңды алып келе гөр!"},
	{12, "12 — Халықтар тұрып, Жаратқан Иенің Соттау аңғарына өрлеп келсін! Мен сонда отырып, айналадағы барлық халықтарды соттайтын боламын."},
	{13, "13 Орақтарыңмен орыңдар, өйткені өнім дайын. Келіп, жүзімді басып сығыңдар, өйткені сыққыш өнімге толып тұр, енді күбілер шырынға толмақ. Себебі сол адамдардың зұлымдығы шектен асып кетті."},
	{14, "14 Соттау аңғарында сан мыңдаған адамдар күтіп тұр! Себебі жуырда Жаратқан Иенің күні Соттау аңғарында айқын көрінеді."},
	{15, "15 Күн мен ай қараңғыланып, жұлдыздар да сөніп қалады."},
	{16, "16 Сонда Жаратқан Ие Сионнан ақырып, Иерусалимнен дауысын шығарады. Сол кезде аспан мен жер шайқалады. Бірақ Жаратқан Ие — өз халқының бас сауғалайтын панасы, Исраилдің үрім-бұтағының қамал іспетті берік қорғаушысы!"},
	{17, "17 Сол кезде сендер Менің, өз Құдайларың Жаратқан Иенің, киелі тауым Сионда тұратынымды білетін боласыңдар. Иерусалим қаласы Өзіме бағышталып қасиетті болады. Енді оны ешбір жат жұрттық басып ала алмайды."},
	{18, "18 Сол күні таулардан тәтті шырын тамшылап, төбелерден сүт ағатын болады, Яһуданың барлық жылғаларында су ағады. Жаратқан Иенің киелі үйінен басталған бұлақ Аққараған аңғарын суға кенелтеді."},
	{19, "19 Яһуданың халқына жасаған қиянаттары үшін және өз жерінде отырған халықтың жазықсыз қанын төккендері үшін Мысыр елі қаңырап бос қалып, Едом елі де қу медиенге айналады."},
	{20, "20 Ал Яһуда бұдан былай бос қалмайды. Иерусалимді де ұрпақтан ұрпаққа мекендейтін болады."},
	{21, "21 Мен олардың әлі кешірілмеген, қан төгіп жасаған күнәларын кешіремін. Сонымен Жаратқан Ие Сионда тұратын болады."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
