#include <map>
#include <string>
class Bible31
{
	struct kk1	{ int val; const char *msg; };
public:
static void view1() {
struct kk1 poems[] = {
	{1, "1 Бұл — Абдидің көрген көрінісі. Мұнда Жаратушы Тәңір Иенің Едом елі туралы айтқаны берілген. Жаратқан Иеден жеткен бір хабарды естідік, халықтардың арасына бір шабарман жіберілді. Ол: Жиналыңдар! Едомға қарсы шайқасуға шығыңдар! — деп жариялайды."},
	{2, "2 (Жаратқан Ие Едом еліне былай деп ескертеді:) Біліп қой, Мен сені ұлттардың арасында шағын әрі әбден елеусіз қыламын."},
	{3, "3 Сен жартастағы қуыста тұрып, биік қыраттарды мекендеп келесің. Сондықтан Кім мені бұл жерден құлатып түсіре алады?! деп кеуде көтеріп, соған алданып кеттің."},
	{4, "4 Алайда тіпті бүркіттей самғап, ұяңды жұлдыздардың арасында салсаң да, бәрібір Мен сені сол жақтан да құлатып түсіремін! — деп нық айтады Жаратқан Ие."},
	{5, "5 Саған ұры түсіп, түнде қарақшы бас салса, олар өздеріне керегінше ғана ұрлап әкетпес пе еді? Жүзімшілер жүзімді жинағанда біразын (кедейлерге) қалдырмас па еді? Бірақ сені күтіп тұрған қырғынды айтсаңшы!"},
	{6, "6 Есау халқының тінтілетіні-ай, тығылған көмбелерінің де тоналатыны-ай!"},
	{7, "7 Одақтастарыңның барлығы сені шекараға тықсырады, достарың сені тақырға отырғызып, күштеп әлімжеттік жасайды. Дастарқандас болып наныңды жеген достарың саған тұзақ құрып: Ол тіпті де түсінбейді дейтін болады."},
	{8, "8 Жаратқан Ие сол күні мынаны айтады: Мен міндетті түрде Едомның данышпандарын, Есаудың тауларындағы түсінігі мол адамдарын құртамын!"},
	{9, "9 Уа, Теман қаласы, сенің жауынгерлерің үрейленіп, Есаудың тауларында тұрғандардың барлығы қырғынға ұшырайды."},
	{10, "10 Өзіңнің бауырлас елің Жақыпқа жасаған зорлығың үшін сен масқара болып, біржола құрисың."},
	{11, "11 Жау Исраил еліне басып кірген күні сен қарап тұра бердің. Жат жерліктер оның әскерін айдап әкетіп, шетелдіктер Иерусалимнің қақпаларына кіріп, жеребе тастап оның байлығын бөлісіп жатқанда, сен солардың бірі сияқты болып тұрдың."},
	{12, "12 Бауырыңның басына күн туғанда сен қарап тұра бермеуің керек еді! Яһуда елі қырғынға ұшырағанда оны табалап мәз болмауың керек еді! Олар қатты қиналған күндері сенің мақтанбауың керек еді!"},
	{13, "13 Халқым сондай апатқа ұшырағанда, соның қақпасына басып кірмеуің керек еді! Халқым азап шеккен күні бақытсыздығына қарап мәз болмауың керек еді! Сол қиын-қыстау күні халқымның дүние-мүлкін талан-таражға салмауың керек еді!"},
	{14, "14 Жол айырықтарында тұрып, босқындарын өлтірмеуің, тірі қалғандарын жау қолына ұстап бермеуің керек еді!"},
	{15, "15 Жаратқан Иенің барлық халықтарды соттайтын күні таяп қалды! (Уа, Едом,) сен не істеген болсаң, саған да сол нәрсе істелінеді. Осылайша істегендерің өз басыңа түспек."},
	{16, "16 Сендер Менің қасиетті тауымда тойлап ішіп алғандарың сияқты, енді барлық халықтар да Менің қаһарыма толы тостағанымнан ішетін болады! Сонда сендер (сол тостағаннан) ішіп, ішкендеріңді жұтып, ешқашан болмағандай жоғалып кетесіңдер."},
	{17, "17 Бірақ Сион тауы қашқындарға бас сауғалайтын пана болады. Ол киелі болмақ. Жақыптың халқы өзінің мұралық жерін қайтадан иемденеді."},
	{18, "18 Сол кезде Жақыптың халқы — от, Жүсіптің ұрпақтары — жалын іспетті болады. Ал Есаудан тарағандар сабанға ұқсайды. Әлгілер сабанға от қойып, от оны жалмап қояды. Содан Есаудан тарағандардан ешкім аман қалмайды. — Жаратқан Ие осыны кесіп айтады."},
	{19, "19 Сонда оңтүстіктегі Негев даласында тұратындар Есаудың тауларын иемденіп, батыстағы төменгі қыраттардың тұрғындары філістірлер еліне ие болады. Бұлар Ефрем мен Самарияның жерлерін, ал Бунямин руы Ғилақад аймағын иемденбек."},
	{20, "20 Қанахандағы исраилдік жер аударылғандардың осы тобы Сарептаға дейінгі жерге ие болып, Иерусалимнен Сефарадқа жер аударылғандар қайтып келіп, Негевтің қалаларына иелік етеді."},
	{21, "21 Елді азат еткендер Сион тауына шығып, Есаудың тауларына билік құрады. Ал Патшалық Жаратқан Иенікі болады."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};