struct kk8 poems[] = {
	{1, "1 Бұдан кейін Иса қалалар мен ауылдарды аралап, халыққа уағыз айтып Құдайдың Патшалығы туралы Ізгі хабарды жеткізді. Оған он екі елшісі"},
	{2, "2 және көптеген әйелдер еріп жүрді. Әйелдердің бірнешеуін Иса қинаған жындарынан арылтып, ауруларынан сауықтырған болатын. Олар — жеті жынынан арылған мағдалалық Мәриям,"},
	{3, "3 Иродтың сарай басқарушысы Қузаның әйелі Жоханна, Сузанна және көптеген басқалары. Олар қолдарындағы барымен Исаға және Оның шәкірттеріне қызмет көрсетті."},
	{4, "4 Көптеген қалалардың тұрғындары Исаның қасына келіп, үлкен топ жиналған еді. Иса оларға мына астарлы әңгімені айтып берді:"},
	{5, "5 — Бір диқан тұқым себуге шығыпты. Ол сеуіп жүргенде, дәндердің кейбіреулері жол бойына түсіп, тапталып қалады. Құстар ұшып келіп, шоқып, жеп қояды."},
	{6, "6 Біраз тұқым тастақ жерге түседі. Алайда көктеп шыққаннан кейін ылғал жетіспегендіктен олар қурап қалады."},
	{7, "7 Кейбір дәндер тікен арасына түседі. Бірге өскен тікендер оларды тұншықтырып, өсірмей тастайды."},
	{8, "8 Ал кейбіреулері құнарлы топыраққа түседі. Бұлар көктеп шығып, өсіп, жүз еседен өнім береді. — Мұны айтқаннан кейін Иса дауыстап: — Құлағы барлар жақсылап тыңдап алсын! — деп ескертті."},
	{9, "9 Сонда шәкірттері Исадан: — Бұл астарлы әңгіменің мағынасы қандай? — деп сұрады."},
	{10, "10 Иса оларға жауап беріп былай деді: — Құдай өз Патшалығы туралы сырларды білуді сендерге бұйырған. Бірақ басқаларға олар астарлы әңгімелер арқылы айтылады. Осылайша олар көріп тұрса да, шынымен көрмесін, естіп тұрса да, бәрібір түсінбесін."},
	{11, "11 Ал әңгіменің мағынасы мынадай: тұқым Құдайдың Ізгі хабарын білдіреді."},
	{12, "12 Жол бойына түскендері Ізгі хабарды былай тыңдайтын адамдарды бейнелейді: оларға әзәзіл шайтан келіп, сенбесін, құтқарылмасын деп, жүректеріне себілген бұл сөздерді ұрлап алып кетеді."},
	{13, "13 Тастақ жерге түскендері мынаны көрсетеді: адамдар Ізгі хабарды естіген бойда оны қуанышпен қабылдайды, бірақ тамыр жаймағандықтан уақытша ғана сенеді. Олар сынақтан өтіп жүрген кезде сенімдерінен безіп кетеді."},
	{14, "14 Тікен арасына түскен дәндер болса, мынаны білдіреді: адамдар Ізгі хабарды естиді. Алайда былай шыға бере, тұрмыс қамын ойлап абыржиды, байлық пен ләззаттың қызығына алданады. Осылайша Ізгі хабар ескерусіз қалып тұншығып, өнімі пісіп үлгермейді."},
	{15, "15 Ал құнарлы топыраққа себілген тұқымдар мынаны бейнелейді: бірсыпыра адамдар Құдайдың хабарын тыңдап, ізгі де таза жүректерінде сақтап, шыдамдылықпен өнім береді."},
	{16, "16 Ешкім шам жаққан соң оны ыдыспен бүркемейді, не төсек астына да қоймайды. Қайта, оны шам қойғышқа қояды, сонда ол үйге кіргендерге жарығын түсіреді."},
	{17, "17 Сол сияқты, мәлім болмайтын ешбір сыр да жоқ, жарыққа шығып, жария болмайтын ешбір құпия да жоқ."},
	{18, "18 Сондықтан қалай тыңдайтындарыңа назар аударыңдар! Өйткені кімде біраз түсінік бар болса, соған тағы көбірек беріледі, ал кімде жоқ болса, оның өзімде бар дегені де одан алынып қойылады, — деп қорытты Иса."},
	{19, "19 Исаның шешесі мен туған бауырлары келіп, халықтың көптігінен Оған жақындай алмады."},
	{20, "20 Біреу Исаға: — Сыртта шешеңіз бен бауырларыңыз келіп, Сізбен жолығамыз деп тұр — деді."},
	{21, "21 Алайда Иса: — Құдайдың сөзін тыңдап, орындайтындар Маған шеше мен бауырлар іспетті жақын! — деп жауап қайырды."},
	{22, "22 Бір күні Иса шәкірттерін ертіп қайыққа мініп: Көлдің арғы бетіне өтейік, — деді. Осылай олар жағадан аттанды."},
	{23, "23 Олар көлден өтіп бара жатқанда, Иса ұйықтап қалды. Сол кезде тау жақтан қатты дауыл көтеріліп, көлге соқты. Қайық толқынның астында қалып, суға тола бастады. Оларға зор қауіп төнді."},
	{24, "24 Шәкірттері Исаға жақындап, Оны оятып: — Ұстаз! Ұстаз! Құритын болдық! — деп айқайлады. Иса орнынан тұрып, жел мен толқындардың долдануына тыйым салды. Олар басылып, тыныштық орнады."},
	{25, "25 Иса шәкірттеріне: — Сенімдерің қайда? — деді. Олар үрейленіп, аң-таң болып, біріне-бірі: Тіпті жел мен суға да бұйрық беретін Бұл кім Өзі? Олар Оған бағынады ғой! — десті."},
	{26, "26 Осылай Иса мен шәкірттері көлден өтіп, Ғалилея аймағының қарсы бетіндегі Ғадара өңіріне жетті."},
	{27, "27 Жағаға шығысымен-ақ Исаға сол қалалық бір кісі кездесе кетті. Жын соққан осы адам көп уақыттан бері киім-кешек кимей, үйде емес, қабірге арналған үңгірлерде тұрып жүрген болатын."},
	{28, "28 Ол Исаны көрген бойда айқай сала аяғына жығылды да, қатты дауыстап: — Құдай Тағаланың рухани Ұлы Иса, менде нең бар? Саған жалынамын: азаптай көрмеші мені! — деп жалынды."},
	{29, "29 Себебі Иса жынға: Шық бұл кісіден! — деп бұйырған болатын. Жын сол адамды көптен бері қинап келген еді. Жұрт оны шынжырлармен, кісендермен бұғаулап тастаса да, ол бұғауларын үзіп, жын айдап елсіз далаға кететін еді."},
	{30, "30 Иса одан: — Атың кім? — деп сұрағанда, ол: — Үлкен топ, — деді. Себебі сол адамға көптеген жындар кіріп алған болатын."},
	{31, "31 Олар Исадан: Бізге тұңғиық шыңырауға түсуге бұйыра көрме, — деп өтінді."},
	{32, "32 Осы жердегі қырат беткейінде үлкен бір топ шошқа жайылып жүрген болатын. Жындар соларға енуге рұқсат сұрады. Иса жол берген соң,"},
	{33, "33 жындар кісіден шығып, әлгі хайуандарға енді. Сонда олар тік жардан көлге қарғып, суға батып, қырылып қалды."},
	{34, "34 Мұны көріп тұрған бақташылары қашып кетіп, болған оқиғаны қала мен ауылдарда айтып берді."},
	{35, "35 Халық соны көру үшін жолға шығып, Исаға келді. Олар жындары шыққан кісінің ақыл-есі кіріп, киініп, Исаның аяқ жағында отырғанын көргенде қорқып кетті."},
	{36, "36 Болған оқиғаны өз көздерімен көрген куәгерлер оларға бұрын жын қинаған кісінің қалай азат етілгені туралы әңгімелеп берді."},
	{37, "37 Сонда Ғадара төңірегінің бүкіл халқы зәрелері ұшып, Исадан: Біздің жерімізден кетіңіз, — деп өтінді. Ол қайыққа мініп, қайтып кетті."},
	{38, "38 Жындарынан арылған кісі Исадан: — Сізбен бірге болайыншы, — деп сұраған еді. Бірақ Иса:"},
	{39, "39 — Сен үйіңе қайтып, ондағыларға Құдайдың өзіңе не істегенін айтып бер! — деп, оны үйіне жіберді. Әлгі адам барып, Исаның өзіне не істегенін бүкіл қалаға жариялады."},
	{40, "40 Иса көлдің батыс жағына қайта оралғанда, халық Оны қарсы алды. Өйткені бәрі Оны күтіп тұрған еді."},
	{41, "41 Кенеттен Жайыр есімді бір ер адам сол жерге келіп, Исаның аяғына жығылды да, үйіне кіруін өтінді. Жайыр мәжілісхананың жетекшісі еді."},
	{42, "42 Ол кісінің он екі жас шамасындағы жалғыз қызы бар еді, сол өлім аузында жатқан болатын. Иса Жайырдың үйіне бара жатқанда, халық Оны жан-жақтан қаумалап қыспақтады."},
	{43, "43 Халықтың арасында он екі жыл бойы қан кетіп ауырып келген бір әйел де бар еді. Ол бар мал-мүлкін дәрігерлерге жұмсағанмен, біреуі де оны емдеп жаза алмады."},
	{44, "44 Әйел Исаның арт жағынан жақындап келіп, Оның шапанының шетіне қолын тигізді. Сол сәтте-ақ қаны тоқтады."},
	{45, "45 Иса: — Маған қолын тигізген кім? — деп сұрады. Ешкім де мойындамаған соң, Петір мен оның қасындағылар: — Ұстаз! Халық Сізді қаумалап қыспақтап тұр ғой. Ал Сіз: Маған қолын тигізген кім? дейсіз бе?! — деді."},
	{46, "46 Бірақ Иса: — Біреу Маған қолын тигізді! Өйткені Менен біраз сауықтыру құдіретінің шыққанын сездім, — деп кесіп айтты."},
	{47, "47 Әлгі әйел істеген ісін жасыра алмайтынын білген соң қорыққанынан қалтырап, Исаның қасына келіп, аяғына жығылды. Сосын өзінің қандай себеппен қолын тигізгенін және сол сәтте-ақ қалай сауығып кеткенін бүкіл халықтың алдында айтып берді."},
	{48, "48 Иса оған: — Қарындас, сенімің арқылы құтқарылдың. Аман-есен қайта бер, — деді."},
	{49, "49 Иса осыны айтып тұрғанда, мәжілісхана жетекшісінің үйінен біреу келіп, оған: — Қызыңыздан айырылып қалдық. Енді Ұстазды әурелемей-ақ қойыңыз, — деп хабарлады."},
	{50, "50 Бірақ мұны естіген Иса Жайырға: — Қорықпа, тек сенімің болсын, сонда қызың аман қалады, — деді."},
	{51, "51 Жайырдың үйіне жеткенде Иса Петір, Жохан, Жақып және қыздың әке-шешесінен басқа ешкімнің үйге кіруіне рұқсат бермеді."},
	{52, "52 Жұрттың бәрі жылап, жоқтау айтып тұрған еді. Ал Иса: Жыламаңдар, ол өлген жоқ, ұйықтап жатыр, — деді."},
	{53, "53 Қыздың өлгенін білетін олар Исаның айтқанына күлді."},
	{54, "54 Бірақ Ол (солардың бәрін сыртқа шығарды да) қыздың қолынан ұстап тұрып, дауыстап: Қызым, тұра ғой! — деді."},
	{55, "55 Сонда қыздың жаны қайтып кіріп, ол дереу орнынан тұрды! Иса: Оған тамақ беріңдер, — деп бұйырды."},
	{56, "56 Қыздың әке-шешесі қатты қайран қалды. Ал Иса оларға бұл оқиға жайлы ешкімге тіс жармауды ескертті."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
