struct kk12 poems[] = {
	{1, "1 Ата-бабаларың сиынған Құдай Ие сендерге иемденуге берген елде қоныс теуіп, өмір сүрген кездеріңде сақтап орындауларыңа тиісті ережелер мен шешімдер мыналар:"},
	{2, "2 Сендер қуып шығарғалы отырған халықтар биік тау-төбелерде және әрбір саялы ағаштың түбінде өздерінің жалған тәңірлеріне құлшылық етеді. Сол ғибадат орындарының барлығын түгелдей бұзып тастауларың керек."},
	{3, "3 Олардың құрбандық үстелдерін қиратып, тас мүсіндерін сындырып, Ашераға бағышталған ағаш бағаналарын өртеп жіберіңдер! Сол халықтар табынып келген жалған тәңірлерінің мүсіндерін де бөлшектеп, жалған тәңірлердің аттарын сол орындардан есте қалмастай етіп өшіріңдер!"},
	{4, "4 Құдайларың Жаратқан Иеге сол халықтардың (жексұрын) дәстүрлері бойынша құлшылық етпеңдер!"},
	{5, "5 Керісінше, Құдай Иеге Өзінің есімі құрметтелетін жер бетіндегі тұрағы болуға сендердің күллі руларыңның арасынан бір орынды таңдап алатын болады. Оған сиынып ғибадат етуге сол жерге баруларың қажет."},
	{6, "6 Құдайға арнап өртелетін құрбандықтарың мен сый-тартуларыңды, өнімнің он бөлігінің бірін әрі арнайы, не беруге серт еткен, не өз еріктеріңмен ұсынған сый-тартуларыңды, сондай-ақ малдарыңның тұңғыш төлдерін де сол жерге алып барыңдар!"},
	{7, "7 Құрбандық асын отбасыларыңмен бірге сол жерде Құдай Иеміздің алдында ішіп-жеңдер. Осылайша қол тигізген барлық істеріңнің Құдайларың Жаратқан Иенің жарылқауымен жемісті болғанын тойлап шаттаныңдар."},
	{8, "8 Бүгін біздің істегеніміздей, болашақта әркім өзіне дұрыс болып көрінгенді істемесін."},
	{9, "9 Себебі Құдайларың Жаратқан Ие сендерге тыныштық сыйлайтын орынға, өздеріңнің меншікті жерлеріңе әлі жеткен жоқсыңдар."},
	{10, "10 Ал сендер Иордан өзенінен өтіп, Құдай Ие сендерге иемденуге беріп отырған елде қоныстанған кезде Ол қауіпсіздікте тұруларың үшін сендерге айналадағы жауларыңның бәрінен қорғап, тыныштық сыйлайтын болады."},
	{11, "11 Содан соң Құдайларың Жаратқан Ие өз есімін орнату үшін қандай жерді таңдап алса, сол жерге мен сендерге тапсырып отырған нәрселердің бәрін әкеліңдер: өртелетін сый-тартуларыңды, шалынып өртелетін құрбандықтарыңды, өнімдеріңнің он бөлігінің бірін, арнайы тартуларыңды және өздерің Жаратқан Иеге ант етіп бағыштаған бағалы заттарыңды алып келіңдер!"},
	{12, "12 Сол жерде өздерің, ұл-қыздарың, қызметшілерің және қалаларыңда тұрып жатқан, мұралық меншікке жер үлесі бөлінбеген леуіліктер, бәрің бірге Құдайларың Жаратқан Иенің алдында қуана тойлаңдар."},
	{13, "13 Өртелетін құрбандықтарыңды өздерің қалаған әр жерде шалып, өртеуден сақ болыңдар!"},
	{14, "14 Тек Жаратқан Ие руларыңның арасынан тағайындаған орында ғана құрбандықтарды шалып, мен өздеріңе тапсырған ережелердің бәрін орындаңдар!"},
	{15, "15 Бірақ өз елді мекендеріңнің кез келген жерінде малдарыңды азыққа сойып, етін Құдай Иенің берген жарылқауына сай қалағандарыңша жеулеріңе болады. Аң аулап, соғып алған елік немесе бұғының еті іспетті сол етті де Жаратқан Иенің құрбандық орнына апармай-ақ жей аласыңдар."},
	{16, "16 Алайда оның қанын ішіп-жемеңдер, оны су іспетті жерге төгіп тастаңдар!"},
	{17, "17 Ал жиналған астықтың, жүзім шырынының және зәйтүн майының (Жаратқан Иеге тиісті) он бөлігінің бірін, малдарыңның алғашқы төлдерін, сондай-ақ Жаратқан Иеге ұсынуға серт еткен не өз еркіңмен ұсынып жатқан немесе арнайы берген сый-тартуларыңды да өздерің тұрып жатқан елді мекендеріңде жеулеріңе болмайды."},
	{18, "18 Керісінше, бұларды Құдайларың Жаратқан Ие тағайындайтын орында Оның алдында өздерің, ұл-қыздарың, қызметшілерің және елді мекендеріңде бірге тұратын леуіліктер, бәрің бірге ішіп-жеңдер. Осылайша қолға алған барлық істеріңнің жемісті болғанына қуанып Құдай Иелеріңнің алдында тойлаңдар."},
	{19, "19 Елде тұрып жатқан уақыттың барлығында леуіліктерді қамқорлықсыз қалдырмаңдар!"},
	{20, "20 Құдайларың Жаратқан Ие жерлеріңді өз уәдесіне сай кеңейтіп берген соң, етке аңсарларың ауғанда оны қанша жегілерің келсе, сонша жей аласыңдар."},
	{21, "21 Егер Құдай Ие өз есімін орнатуға тағайындайтын орын сендердің тұратын жерлеріңнен тым алыс болса, онда өздеріңе өсиет еткенімдей, Жаратқан Иенің жарылқауымен өскен малдарыңнан бір малды сойып, етін елді мекендеріңде қалағандарыңша жеулеріңе болады."},
	{22, "22 Сол етті еліктің не бұғының етін жегендей жей аласыңдар. Тазару рәсімінен өткен не өтпеген адамдарың да соны жей алады."},
	{23, "23 Алайда оның қанын ішіп-жеуден сақ болыңдар! Себебі қан дегеніміз — жан. Етті жанмен қосып жеулеріңе болмайды. Қайта, қанын су іспетті жерге төгіп тастаңдар!"},
	{24, "25 Қанды еш ішіп-жемеңдер! Сонда өздерің және болашақ ұрпақтарың игілікті өмір сүретін боласыңдар. Себебі осы бұйрықты орындағандарың Жаратқан Иеге ұнамды."},
	{25, "26 Ал Жаратқан Иеге бағышталған және Оған тарту етемін деп серт еткен барлық құрбандықтарыңды Жаратқан Ие тағайындайтын жерге алып барыңдар."},
	{26, "27 Өртеп ұсынылатын құрбандықтарың Құдайларың Жаратқан Иеге арналған құрбандық үстелінде қанымен және етімен қоса ұсынылсын. Бұлардан өзге құрбандықтарыңның қаны да сол құрбандық үстелінің жанында төгілуі керек, ал солардың етін жей берулеріңе болады."},
	{27, "28 Өздеріңе беріп отырған осы бұйрықтарымның бәрін мұқият тыңдап, орындайтын болыңдар! Сонда өздерің және болашақ ұрпақтарың үшін игілікті өмір сүретін боласыңдар. Себебі осылай еткендерің Құдай Иемізге ұнамды және дұрыс."},
	{28, "29 Құдайларың Жаратқан Ие өздерің кіріп, иемденейін деп жатқан елдегі халықтарды алдарыңнан аластатып, сендер оларды елден қуып шығып, сонда қоныстанып алғандарыңнан кейін,"},
	{29, "30 өздерің азғырылып қалмаңдар! Солардың жалған тәңірлеріне ынтығып, Осы халықтар өз тәңірлеріне қалай ғана құлшылық етті екен? Біз де солай істейікші! деп олардың торына түсіп қалып жүрмеңдер!"},
	{30, "31 Сендер Құдайларың Жаратқан Иеге олардан үйренген жолдармен табынушы болмаңдар! Себебі сол халықтар жалған тәңірлеріне табынған кезде Жаратқан Иеге жексұрын да жиренішті нәрселердің әр түрін жасайды. Олар сол тәңірлеріне арнап тіпті ұл-қыздарын да құрбан етіп өртеп жібереді."},
	{31, "32 Сонымен мен айтқан бұйрықтардың барлығын мұқият орындаңдар, оларға еш нәрсені не қосып, не алып тастаушы болмаңдар!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
