struct kk1 poems[] = {
	{1, "1 Құтқарушымыз Құдайдың және үмітіміздің Бастауы — Иса Мәсіхтің бұйрығымен Иса Мәсіхтің елшісі болған Пауылдан"},
	{2, "2 сеніміміздегі шынайы ұлым іспетті Тімотеге дұғай сәлем. Лайым Құдай Әкеміз және Иеміз Мәсіх Иса саған рақымы мен қайырымын жаудырып, тыныштығын сыйлай берсін!"},
	{3, "3 Македонияға барарда өтініп сұрағанымдай, сен Ефес қаласында қалып, кейбіреулердің басқа ілімдерді үйретулеріне"},
	{4, "4 және аңыздармен, ұлан-ғайыр шежірелермен шұғылдануларына тыйым сал. Бұлар сенім арқылы білінетін Құдайдың ісіне қызмет етудің орнына көп дау-жанжал тудырады."},
	{5, "5 Осы бұйрықтың мақсаты — пәк жүрек, таза ар-ұждан мен шынайы сенімнен бастау алатын сүйіспеншілікті ояту."},
	{6, "6 Кейбір адамдар бұлардан бас тартып, көп мылжыңдыққа түсіп, адасып кетті."},
	{7, "7 Олар Таурат заңының ұстаздары болғылары келеді, бірақ өз айтқандарын әрі табандылықпен білдіріп жүрген пікірлерін өздері дұрыстап түсінбейді."},
	{8, "8 Ал тиісінше қолданылса, Таурат заңының игілікті болатынын білеміз."},
	{9, "9 Оның Құдай алдында ақталған біздерге емес, заң мен билікке бағынбайтындарға, зұлымдар мен күнәкарларға, қасиетсіздер мен дінсіздерге, ата-анасын не басқа біреуді өлтірушілерге,"},
	{10, "10 бұзылғандар мен жыныстық азғындыққа түскендерге, кісі ұрлаушыларға, өтірікшілер мен жалған ант беретіндерге, яки дұрыс ілімге қайшы әрекет ететіндердің бәріне тыйым салатынын түсінеміз."},
	{11, "11 Осы ілім мадақталуға тиісті Құдайдың ұлы Ізгі хабарында мәлімденген. Құдай соны уағыздауды маған аманаттады."},
	{12, "12 Міндетімді атқаруға күш-қуат беріп келе жатқан Иеміз Мәсіх Исаға ризашылығымды білдіремін. Өйткені Ол мені сенімді болады деп бағалап, Өзіне қызмет етуге тағайындаған."},
	{13, "13 Бұдан бұрын Оған тіл тигізіп, қауымдарын қудалап, зорлық-зомбылық көрсеткеніммен, Иеміз маған қайырымды болды. Себебі менде әлі де шынайы сенім болмағандықтан, не істеп жүргенімді білмедім."},
	{14, "14 Осылай маған Иеміздің рақымы молынан төгіліп, Иса Мәсіхтен келетін сенім мен сүйіспеншілікке кенелдім."},
	{15, "15 Иса Мәсіх осы дүниеге келді Құтқару үшін күнәкар кісілерді! Бұл — дәл айтылған шынайы сөз, оны түгелдей қабылдау қажет. Күнәкарлардың ішіндегі нағыз күнәкар мен едім."},
	{16, "16 Ал Иса Мәсіх алдымен мен арқылы Өзінің төзімділігін толығымен көрсету үшін маған қайырымдылық танытты. Сөйтіп мені болашақта Иса Мәсіхке сеніп, мәңгілік өмірге ие болатын адамдардың бәріне үлгі етті."},
	{17, "17 Мәңгілік Патша — өлмес, көзге көрінбес бірден-бір Құдай мәңгі құрметтеліп, ұлылығы мадақтала берсін! Аумин."},
	{18, "18 Тімоте, балам менің, саған мына өсиетті аманаттаймын, ол бұдан бұрын өзің туралы Құдай Рухы білдірген хабарларға сәйкес: ізгі күресте сол хабарларға сай әрекет жасап,"},
	{19, "19 сенімің мен ар-ұжданыңды таза ұста! Кейбіреулер ар-ұждандарынан безіп, сенімдерін құртты."},
	{20, "20 Олардың араларында Үменей мен Ескендір бар. Құдайға тіл тигізбеуге үйренсін деп, екеуін (қауымнан шығарып) шайтанның қолына тапсырдым."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
