struct lt4 poems[] = {
	{1, "1 “Klausyk, Izraeli, įstatymų ir paliepimų, kurių jus mokau, kad juos vykdydami gyventumėte ir užėmę paveldėtumėte žemę, kurią Viešpats, jūsų Dievas, jums duoda."},
	{2, "2 Nieko nepridėkite prie mano įstatymų ir nieko iš jų neatimkite. Laikykitės Viešpaties, jūsų Dievo, įsakymų, kuriuos jums skelbiu."},
	{3, "3 Jūsų akys matė, ką Viešpats darė dėl Baal Peoro, kaip išnaikino visus jo garbintojus, buvusius tarp jūsų."},
	{4, "4 Jūs gi, kurie laikėtės Viešpaties, jūsų Dievo, išlikote gyvi iki šios dienos."},
	{5, "5 Štai aš mokiau jus nuostatų ir įstatymų, kaip Viešpats, mano Dievas, man įsakė, kad jūs vykdytumėte juos žemėje, kurią paveldėsite."},
	{6, "6 Laikykitės jų ir vykdykite juos, nes tai jūsų išmintis ir protas tautų akyse. Jos išgirs jūsų nuostatus ir sakys: ‘Iš tiesų ši didelė tauta yra protinga ir išmintinga’."},
	{7, "7 Iš tikrųjų nėra kitos tokios didingos tautos, kuriai Dievas būtų taip arti, kaip mūsų Dievas, kai Jo šaukiamės."},
	{8, "8 Kuri kita didelė tauta turi nuostatus ir įsakymus tokius teisingus, kaip šis įstatymas, kurį šiandien skelbiu jums?"},
	{9, "9 Saugokis ir rūpestingai saugok savo sielą, kad neužmirštum to, ką matei savo akimis, ir tepasilieka tai tavo širdyje per visas tavo dienas. Jų mokykite savo vaikus ir vaikaičius."},
	{10, "10 Tą dieną, kai stovėjai Viešpaties, savo Dievo, akivaizdoje prie Horebo, Jis man kalbėjo: ‘Surink prie manęs tautą, kad išgirstų mano žodžius ir bijotų manęs, kol gyvens žemėje, ir mokytų to paties savo vaikus’."},
	{11, "11 Jūs priartėjote prie kalno pašlaitės, iš kur liepsnos kilo į dangų. Jis buvo apsuptas tamsa ir debesimis."},
	{12, "12 Viešpats kalbėjo jums iš ugnies. Jūs girdėjote Jo žodžius, bet nematėte Jo pavidalo; girdėjote tik balsą."},
	{13, "13 Jis jums paskelbė savo sandorą ir įsakė ją vykdyti. Jis užrašė dešimt įsakymų dviejose akmeninėse plokštėse"},
	{14, "14 ir įsakė man mokyti jus vykdyti tuos įsakymus ir paliepimus žemėje, kurią paveldėsite."},
	{15, "15 Gerai įsidėmėkite, kad tą dieną, kai Viešpats jums kalbėjo Horebe iš ugnies, jūs nematėte jokio pavidalo,"},
	{16, "16 kad kartais nedirbtumėte sau drožinių, panašių į vyrą ar moterį,"},
	{17, "17 panašių į kurį nors gyvulį, esantį žemėje, ar skraidantį padangėje paukštį,"},
	{18, "18 ar šliaužiantį gyvį, ar žuvį, plaukiojančią vandenyse."},
	{19, "19 Kad kartais, pakėlęs akis į dangų ir pamatęs saulę, mėnulį, žvaigždes, nepaklystum, jų negarbintum ir nesilenktum tiems, kuriuos Viešpats, jūsų Dievas, sutvėrė tarnauti visoms tautoms."},
	{20, "20 Jus gi Viešpats išvedė iš geležinės krosnies, Egipto vergijos, kad būtumėte Jo tauta."},
	{21, "21 Dėl jūsų Viešpats užsirūstino ant manęs ir prisiekė, kad nepereisiu Jordano ir neįeisiu į tą gerą žemę, kurią Viešpats, tavo Dievas, duoda tau paveldėti."},
	{22, "22 Aš turėsiu mirti šioje žemėje ir nepereisiu Jordano; tačiau jūs pereisite ir užimsite tą gerą žemę."},
	{23, "23 Neužmirškite Viešpaties, savo Dievo, sandoros, kurią Jis su jumis padarė ir nedarykite sau jokio drožinio ar atvaizdo, nes Viešpats tai uždraudė."},
	{24, "24 Viešpats, tavo Dievas, yra naikinanti ugnis, pavydus Dievas."},
	{25, "25 Jei gyvendami žemėje, kai susilauksite vaikų ir vaikaičių, suteršite save ir pasidarysite drožinių ar atvaizdų, ir darysite pikta Viešpaties, savo Dievo, akyse, Jį supykdydami,"},
	{26, "26 aš šaukiu šiandien liudytoju dangų ir žemę, kad, taip elgdamiesi, jūs žūsite krašte, kurį, perėję per Jordaną, paveldėsite. Negyvensite jame ilgai, bet būsite visai sunaikinti."},
	{27, "27 Viešpats jus išsklaidys tarp tautų, ir jūsų liks labai mažai tarp pagonių, pas kuriuos būsite išvesti."},
	{28, "28 Tenai tarnausite dievams, kurie žmonių rankomis padaryti: medžiui ir akmeniui, kurie nemato ir negirdi, nevalgo ir nieko neužuodžia."},
	{29, "29 Bet jei ten ieškosi Viešpaties, savo Dievo, visa širdimi ir siela, tu atrasi Jį."},
	{30, "30 Patyręs vargą, paskutinėmis dienomis sugrįši prie Viešpaties, savo Dievo, ir klausysi Jo balso."},
	{31, "31 Nes Viešpats, tavo Dievas, yra gailestingas Dievas; Jis nepaliks tavęs ir nesunaikins, neužmirš sandoros, padarytos su tavo tėvais."},
	{32, "32 Paklausk praeities dienų, pradedant nuo tos dienos, kai Dievas sutvėrė žmogų žemėje, ir dangus nuo vieno krašto iki kito, ar įvyko kada nors toks didingas dalykas, ar girdėta tai?"},
	{33, "33 Ar kuri nors tauta girdėjo Dievo balsą, kalbantį iš ugnies, kaip tu girdėjai, ir išliko gyva?"},
	{34, "34 Ar kada nors Dievas yra atėjęs pasiimti tautos, esančios vergijoje, bandymais, ženklais, stebuklais, kova, galinga ranka ir baisiais siaubais, kaip Viešpats, jūsų Dievas, padarė dėl jūsų Egipte, jums visa tai matant savo akimis?"},
	{35, "35 Jis tai parodė tau, kad žinotum, jog Viešpats yra Dievas ir kito nėra."},
	{36, "36 Jis tau leido išgirsti balsą iš dangaus, kad pamokytų tave, žemėje parodė tau didelę ugnį, ir tu girdėjai Jo žodžius iš ugnies."},
	{37, "37 Jis mylėjo tavo tėvus, todėl išsirinko jų palikuonis ir išvedė iš Egipto savo didžia galia,"},
	{38, "38 kad išvytų už tave didesnes ir galingesnes tautas, ir tave įvestų paveldėti jų žemę."},
	{39, "39 Suprask tad šiandien ir palaikyk visa tai savo širdyje, kad Viešpats yra dangaus ir žemės Dievas ir jokio kito nėra."},
	{40, "40 Laikykis Jo įsakymų ir paliepimų, kuriuos tau skelbiu šiandien, kad tau ir tavo vaikams gerai sektųsi ir ilgai gyventum žemėje, kurią Viešpats, tavo Dievas, tau duoda visiems amžiams”."},
	{41, "41 Mozė paskyrė tris miestus šioje Jordano pusėje,"},
	{42, "42 kad juose rastų prieglaudą ir išliktų gyvas žmogžudys, netyčia užmušęs savo artimą, kuris nebuvo jo priešas."},
	{43, "43 Iš Rubeno giminės paskyrė Becerą dykumos lygumoje, iš Gado giminės­Ramot Gileadą ir iš Manaso giminės­Golaną Bašane."},
	{44, "44 Tai įstatymas, Mozės duotas izraelitams."},
	{45, "45 Šitie įsakymai, nuostatai ir paliepimai buvo paskelbti izraelitams, jiems išėjus iš Egipto,"},
	{46, "46 šioje Jordano pusėje, slėnyje, prieš Bet Peoro miestą, krašte amoritų karaliaus Sihono, kuris gyveno Hešbone ir buvo Mozės ir izraelitų nugalėtas, kai jie išėjo iš Egipto."},
	{47, "47 Jie apsigyveno Sihono ir Bašano karaliaus Ogo žemėse, dviejų amoritų karalių, buvusių į rytus nuo Jordano."},
	{48, "48 Šis kraštas tęsėsi nuo Aroero miesto, Arnono upelio slėnyje, ligi Siono kalno, kuris kitaip vadinamas Hermonu,"},
	{49, "49 per visą lygumą nuo Jordano į rytus iki lygumos jūros ir Pisgos kalno šlaitų."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
