static void view12() {
struct lt12 poems[] = {
	{1, "1 Efraimai susirinko, atėjo į Cafoną ir tarė Jeftei: “Kodėl, eidamas į karą prieš amonitus, nepakvietei mūsų? Mes sudeginsime tavo namus ir tave”."},
	{2, "2 Jeftė jiems atsakė: “Aš ir mano žmonės smarkiai susikivirčijome su amonitais. Aš jus šaukiau, bet jūs nepadėjote."},
	{3, "3 Matydamas, kad jūs nepadedate, aš stačiau savo gyvybę į pavojų ir ėjau prieš amonitus. Viešpats atidavė juos į mano rankas. Kodėl šiandien atėjote kariauti prieš mane?”"},
	{4, "4 Jeftė, surinkęs visus Gileado vyrus, kariavo su Efraimu ir jį nugalėjo. Mat efraimai sakė gileadiečiams: “Jūs esate pabėgėliai iš Efraimo ir gyvenate tarp Efraimo ir Manaso”."},
	{5, "5 Gileadiečiai užėmė Jordano brastas, vedančias į Efraimą. Kai efraimas bėglys prašydavo praleisti, gileadiečiai jį klausdavo: “Ar tu esi efraimas?” Jam atsakius: “Ne!”,"},
	{6, "6 jie liepdavo ištarti “šibolet!” Jis atsakydavo “sibolet”, nes negalėdavo teisingai ištarti. Tada jie nužudydavo jį prie Jordano brastų. Taip tuo metu žuvo keturiasdešimt du tūkstančiai efraimų."},
	{7, "7 Jeftė teisė Izraelį šešerius metus. Po to gileadietis Jeftė mirė ir buvo palaidotas viename Gileado mieste."},
	{8, "8 Po jo Izraelį teisė Ibcanas iš Betliejaus."},
	{9, "9 Jis turėjo trisdešimt sūnų ir trisdešimt dukterų. Savo dukteris jis išleido svetur, o jo sūnūs parvedė trisdešimt mergaičių iš kitų kraštų. Jis teisė Izraelį septynerius metus."},
	{10, "10 Po to Ibcanas mirė ir buvo palaidotas Betliejuje."},
	{11, "11 Po jo dešimt metų Izraelį teisė Elonas iš Zabulono giminės."},
	{12, "12 Elonui mirus, jis buvo palaidotas Ajalone, Zabulono krašte."},
	{13, "13 Po jo Izraelį teisė Hilelio sūnus Abdonas iš Piratono."},
	{14, "14 Jis turėjo keturiasdešimt sūnų ir trisdešimt sūnėnų, jodinėjusių ant septyniasdešimties asilų. Jis teisė Izraelį aštuonerius metus."},
	{15, "15 Hilelio sūnui Abdonui mirus, jis buvo palaidotas Piratone, Efraimo krašte, amalekiečių kalnyne."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view13() {
struct lt13 poems[] = {
	{1, "1 Izraelitai darė pikta Viešpaties akivaizdoje, ir Viešpats atidavė juos į filistinų rankas keturiasdešimčiai metų."},
	{2, "2 Coroje gyveno vienas vyras iš Dano giminės, vardu Manoachas. Jo žmona buvo nevaisinga ir negimdė."},
	{3, "3 Viešpaties angelas pasirodė jai ir tarė: “Tu esi nevaisinga, tačiau pastosi ir pagimdysi sūnų."},
	{4, "4 Negerk vyno nė stipraus gėrimo ir nevalgyk nieko nešvaraus."},
	{5, "5 Tu pastosi ir pagimdysi sūnų. Skustuvas tegul nepaliečia jo galvos, nes berniukas bus pašvęstas Dievui nuo pat gimimo, jis pradės išlaisvinti Izraelį iš filistinų”."},
	{6, "6 Moteris atėjusi tarė savo vyrui: “Dievo vyras atėjo pas mane, jo išvaizda buvo kaip angelo, ir aš labai išsigandau. Aš jo nepaklausiau, iš kur jis, o jis savo vardo man nepasakė."},
	{7, "7 Jis tik pasakė: ‘Tu pastosi ir pagimdysi sūnų. Žiūrėk, negerk nei vyno, nei stipraus gėrimo ir nevalgyk nieko nešvaraus, nes berniukas bus Dievui pašvęstas nuo pat gimimo iki savo mirties’ ”."},
	{8, "8 Manoachas meldė Viešpatį: “Viešpatie! Leisk, meldžiu, angelui, kurį buvai siuntęs, vėl ateiti pas mus ir mus pamokyti, kaip auklėti berniuką, kuris užgims”."},
	{9, "9 Dievas išklausė Manoachą. Dievo angelas vėl atėjo pas moterį, jai sėdint lauke. Jos vyro nebuvo šalia jos."},
	{10, "10 Moteris, skubiai nubėgusi, pranešė savo vyrui: “Man pasirodė tas vyras, kuris aną dieną buvo atėjęs pas mane!”"},
	{11, "11 Pakilęs Manoachas sekė žmoną ir, atėjęs pas tą vyrą, jam tarė: “Ar tu esi vyras, kuris kalbėjo su mano žmona?” Jis atsakė: “Taip”."},
	{12, "12 Manoachas klausė: “Kai tavo žodžiai išsipildys, ką mes turime daryti su vaiku ir kaip elgtis su juo?”"},
	{13, "13 Viešpaties angelas atsakė Manoachui: “Moteris turi saugotis to, ką sakiau."},
	{14, "14 Jai nevalia valgyti vynmedžio vaisių, gerti vyno ar stipraus gėrimo, taip pat valgyti, kas nešvaru; ji turi vykdyti, ką įsakiau”."},
	{15, "15 Manoachas tarė Viešpaties angelui: “Prašau, pasilik pas mus, iki prirengsiu tau ožiuką”."},
	{16, "16 Viešpaties angelas atsakė Manoachui: “Jei mane ir sulaikysi, aš vis tiek nevalgysiu tavo valgio; o jei prirengsi deginamąją auką, tai aukok ją Viešpačiui”. Manoachas nežinojo, kad jis buvo Viešpaties angelas."},
	{17, "17 Jis klausė Viešpaties angelo: “Kuo tu vardu? Mes tave pagerbsime, kai tavo žodis išsipildys”."},
	{18, "18 Viešpaties angelas jam atsakė: “Kodėl klausi mano vardo? Tai yra paslaptis”."},
	{19, "19 Manoachas aukojo Viešpačiui ožiuką ir duonos auką ant uolos. Angelas padarė stebuklą jo ir jo žmonos akivaizdoje:"},
	{20, "20 kai liepsna kilo nuo aukuro aukštyn į dangų, Viešpaties angelas pakilo aukštyn aukuro liepsnoje. Tai matydami, jie puolė veidais į žemę."},
	{21, "21 Viešpaties angelas daugiau jiems nebepasirodė. Manoachas suprato, kad tai buvo Viešpaties angelas,"},
	{22, "22 ir tarė žmonai: “Mes mirsime, nes matėme Dievą”."},
	{23, "23 Bet jo žmona atsakė jam: “Jei Viešpats būtų norėjęs mus nužudyti, jis nebūtų priėmęs mūsų deginamosios ir duonos aukos ir nebūtų mums parodęs to, ką matėme, ir pasakojęs to, ką girdėjome”."},
	{24, "24 Moteris pagimdė sūnų ir jį pavadino Samsonu. Berniukas augo, ir Viešpats jį laimino."},
	{25, "25 Viešpaties Dvasia pradėjo veikti jame Mahane Dano stovykloje, kuri buvo tarp Coros ir Eštaolio."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view14() {
struct lt14 poems[] = {
	{1, "1 Samsonas, nuėjęs į Timną, pamatė vieną filistinų mergaitę."},
	{2, "2 Sugrįžęs jis sakė tėvams: “Mačiau Timnoje filistinų mergaitę. Leiskite man ją vesti”."},
	{3, "3 Jo tėvai atsakė: “Nejaugi tarp tavo giminių ir visoje tautoje nėra mergaitės, kad nori vesti iš neapipjaustytų filistinų?” Bet Samsonas atsakė: “Leiskite man ją vesti, nes ji man labai patinka”."},
	{4, "4 Tėvai nežinojo, kad Viešpats taip padarė, ieškodamas progos prieš filistinus. Tuo metu filistinai valdė Izraelį."},
	{5, "5 Samsonas su tėvais ėjo į Timną. Prie Timnos vynuogynų jis sutiko jauną liūtą."},
	{6, "6 Viešpaties Dvasia galingai nužengė ant jo, ir jis sudraskė jį kaip ožiuką plikomis rankomis, bet viso to nepapasakojo tėvams."},
	{7, "7 Nuėjęs pas mergaitę, kalbėjosi, ir ji patiko Samsonui."},
	{8, "8 Po kurio laiko jis grįžo jos paimti. Eidamas jis pasuko iš kelio, norėdamas pamatyti liūto dvėselieną. Ir štai liūto dvėselienoje bičių spiečius ir medus."},
	{9, "9 Jis paėmė medaus ir ėjo toliau valgydamas. Parėjęs pas savo tėvus, jis davė ir jiems to medaus, ir jie valgė. Bet jis nepasakė jiems, kad tą medų jis buvo ėmęs iš liūto dvėselienos."},
	{10, "10 Jo tėvas nuėjo pas tą mergaitę, ir Samsonas suruošė ten puotą; taip darydavo jaunikiai."},
	{11, "11 Filistinai, jį pamatę, atvedė trisdešimt pabrolių, kad jie būtų su juo."},
	{12, "12 Samsonas tarė: “Aš užminsiu jums mįslę. Jei ją įminsite per septynias puotos dienas, aš jums duosiu trisdešimt apatinių ir trisdešimt išeiginių drabužių,"},
	{13, "13 o jei neįminsite, tai jūs man duosite trisdešimt apatinių ir trisdešimt išeiginių drabužių”. Jie atsakė jam: “Užmink mums tą mįslę, norime ją išgirsti”."},
	{14, "14 Jis tarė jiems: “Iš ėdiko išėjo maistas, iš stipruolio­saldumas”. Jie negalėjo įminti mįslės per tris dienas."},
	{15, "15 Ketvirtą dieną jie tarė Samsono žmonai: “Išgauk iš savo vyro mįslės įminimą. Jei neišgausi, sudeginsime tave ir tavo tėvo namus. Argi mus čia pasikvietėte apiplėšti?”"},
	{16, "16 Samsono žmona verkdama kalbėjo: “Tu nemyli manęs. Tu užminei mįslę mano tautos sūnums, o man jos nesakai”. Jis jai tarė: “Aš jos nepasakiau nei savo tėvui, nei motinai, kodėl turėčiau tau ją pasakyti?”"},
	{17, "17 Ji verkė septynias dienas, kol vyko puota. Septintą dieną, netekęs kantrybės, jis pasakė mįslės įminimą. Ji viską pasakė savo tautos sūnums."},
	{18, "18 Septintą dieną, prieš saulės nusileidimą, miesto vyrai Samsonui tarė: “Kas saldesnis už medų? Ir kas stipresnis už liūtą?” Jis jiems atsakė: “Jei nebūtumėte arę su mano telyčia, nebūtumėt įminę mįslės”."},
	{19, "19 Tada Viešpaties Dvasia nužengė ant jo ir jis, nuėjęs į Aškeloną, užmušė trisdešimt vyrų, paėmė jų drabužius ir atidavė juos tiems, kurie įminė mįslę. Po to, labai supykęs, jis sugrįžo į savo tėvo namus."},
	{20, "20 Samsono žmona buvo atiduota vienam iš pabrolių."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view15() {
struct lt15 poems[] = {
	{1, "1 Po kurio laiko, kviečių pjūties metu, Samsonas atėjo aplankyti savo žmonos ir atnešė jai ožiuką. Samsonas sakė: “Aš noriu įeiti į kambarį pas savo žmoną”. Bet jos tėvas jo neleido,"},
	{2, "2 tardamas: “Aš tikrai maniau, kad tu jos nekenti, todėl ją atidaviau tavo pabroliui. Argi jos jaunesnioji sesuo nėra gražesnė už ją? Imk ją vietoj anos”."},
	{3, "3 Tada Samsonas atsakė: “Šį kartą nenusikalsiu, atkeršydamas filistinams”."},
	{4, "4 Samsonas, sugavęs tris šimtus lapių, surišo jas po porą uodegomis ir tarp uodegų įrišo po deglą."},
	{5, "5 Uždegęs deglus, jis paleido lapes į filistinų javus. Taip jis padegė javų pėdus, nepjautus javus, vynuogynus ir alyvmedžių sodus."},
	{6, "6 Filistinai klausė: “Kas tai padarė?” Jiems atsakė: “Samsonas, timniečio žentas, keršydamas už žmonos atidavimą pabroliui”. Atėję filistinai sudegino ją ir jos tėvą."},
	{7, "7 Samsonas jiems tarė: “Nors jūs tai padarėte, nurimsiu tik tada, kai jums atkeršysiu”."},
	{8, "8 Jis smarkiai puolė juos ir, sulaužęs jų blauzdų ir šlaunų kaulus, nuėjo į Etamą ir apsigyveno ant uolos."},
	{9, "9 Tada filistinai atėję pasistatė stovyklas Judo žemėje iki Lehio."},
	{10, "10 Judas klausė: “Kodėl išėjote prieš mus?” Tie atsakė: “Samsono surišti ir padaryti jam taip, kaip jis mums padarė”."},
	{11, "11 Tada trys tūkstančiai Judo vyrų atėjo prie Etamo uolos ir klausė Samsoną: “Argi nežinai, kad mus valdo filistinai? Kodėl taip padarei?” Jis atsakė: “Kaip jie man padarė, taip aš jiems padariau”."},
	{12, "12 Judo vyrai tarė jam: “Mes atėjome tavęs surišti ir atiduoti filistinams”. Samsonas atsakė: “Prisiekite man, kad jūs patys manęs nenužudysite”."},
	{13, "13 Jie jam atsakė: “Mes tave tvirtai surišime ir atiduosime filistinams, bet patys tavęs nenužudysime”. Jie surišo jį dviem naujomis virvėmis ir nusivedė."},
	{14, "14 Prie Lehio filistinai pasitiko jį šūkaudami iš džiaugsmo. Viešpaties Dvasia galingai nužengė ant jo, ir virvės ant jo rankų sutrūko kaip sudegę linai ir nukrito nuo jo."},
	{15, "15 Jis susirado šviežią asilo žandikaulį ir, paėmęs jį, užmušė juo tūkstantį filistinų."},
	{16, "16 Tuomet Samsonas tarė: “Asilo žandikauliu nužudžiau tūkstantį vyrų ir suverčiau į krūvas”."},
	{17, "17 Taip pasakęs, jis nusviedė žandikaulį. Tą vietą pavadino Ramat Lehiu."},
	{18, "18 Labai ištroškęs, jis šaukėsi Viešpaties: “Tu davei man šitokią pergalę, o dabar mirštu iš troškulio ir pateksiu į neapipjaustytųjų rankas!”"},
	{19, "19 Dievas atvėrė daubą Lehyje, ir iš jos tekėjo vanduo. Atsigėręs jis atsigaivino ir atgavo jėgas. Tą vietą pavadino En Korės versme; ji tebėra ten iki šios dienos."},
	{20, "20 Filistinų laikais Samsonas teisė Izraelį dvidešimt metų."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view16() {
struct lt16 poems[] = {
	{1, "1 Kartą Samsonas, nuvykęs į Gazą, pamatė paleistuvę ir užėjo pas ją."},
	{2, "2 Gazos gyventojai, sužinoję, kad Samsonas atėjo į miestą, visą naktį laukė jo prie miesto vartų, sakydami: “Kai prašvis rytas, nužudysime jį”."},
	{3, "3 Samsonas buvo pas paleistuvę iki vidurnakčio. Atsikėlęs vidurnaktį, jis nutvėrė miesto vartus su staktomis, iškėlė juos kartu su sklende, užsidėjo ant pečių ir nunešė ant kalno, esančio prie Hebrono."},
	{4, "4 Vėliau jis pamilo Soreko slėnyje moterį, vardu Delila."},
	{5, "5 Atėję filistinų kunigaikščiai tarė jai: “Sužinok iš Samsono, kur glūdi didelės jo jėgos paslaptis ir kokiu būdu jį galėtume nugalėti ir surišti. Mes tau duosime kiekvienas po tūkstantį šimtą sidabrinių”."},
	{6, "6 Delila klausė Samsoną: “Pasakyk man, kur glūdi tavo didelės jėgos paslaptis ir kuo galima tave surišti, kad neištrūktum?”"},
	{7, "7 Samsonas jai atsakė: “Jei mane surištų septyniomis virvėmis iš gyslų, kurios dar nėra išdžiūvusios, tai aš būčiau toks, kaip bet kuris kitas žmogus”."},
	{8, "8 Filistinų kunigaikščiai atnešė jai septynias virves iš gyslų, kurios dar nebuvo išdžiūvusios, ir ji jomis surišo Samsoną."},
	{9, "9 Jos kambaryje laukė pasislėpę vyrai. Delila sušuko: “Samsonai, filistinai puola!” Jis sutraukė virves, kaip sutrūksta pakuliniai siūlai nuo ugnies. Iš kur jo jėga­nepaaiškėjo."},
	{10, "10 Delila tarė Samsonui: “Tu pasityčiojai iš manęs, meluodamas man. Dabar, prašau tavęs, pasakyk man, kuo galima tave surišti?”"},
	{11, "11 Jis atsakė: “Jei mane surištų visiškai naujomis virvėmis, kurios dar nebuvo naudotos, tai aš turėčiau tokią jėgą, kaip bet kuris kitas žmogus”."},
	{12, "12 Delila, surišusi naujomis virvėmis Samsoną, tarė: “Samsonai, filistinai puola!” Tuo metu pasislėpę vyrai sėdėjo kambaryje. Jis sutraukė virves kaip siūlus."},
	{13, "13 Delila tarė Samsonui: “Lig šiol tu tyčiojaisi iš manęs ir man melavai. Pasakyk, kuo tave būtų galima surišti?” Jis atsakė jai: “Jei supinsi septynias mano galvos garbanas į kasas ir prikalsi vinimi, aš tapsiu toks silpnas, kaip bet kuris kitas žmogus”."},
	{14, "14 Jam užmigus, ji supynė septynias jo galvos garbanas į kasas, prikalė jas vinimi ir tarė: “Samsonai, filistinai puola!” Pabudęs iš miego, jis ištraukė vinį su plaukais."},
	{15, "15 Delila supykus tarė: “Kaip tu gali sakyti: ‘Aš myliu tave’, kai tavo širdis nėra su manimi? Jau tris kartus pasityčiojai iš manęs ir nepasakei, kur glūdi tavo didelės jėgos paslaptis”."},
	{16, "16 Ji nedavė jam ramybės savo kalbomis ir, primygtinai klausinėdama, taip jam įkyrėjo,"},
	{17, "17 kad jis pasakė jai visą tiesą: “Mano galva niekados nebuvo skusta, nes aš esu pašvęstas Dievui nuo pat gimimo. Jei man nuskustų galvą, tai netekčiau jėgos ir tapčiau silpnas, kaip bet kuris kitas žmogus”."},
	{18, "18 Delila pamatė, kad jis atvėrė jai širdį, ir pasišaukė filistinų kunigaikščius, sakydama: “Dar kartą ateikite, nes jis man atvėrė savo širdį”. Filistinų kunigaikščiai atėjo pas ją ir atnešė pinigus."},
	{19, "19 Delila užmigdė Samsoną ant savo kelių ir, pasišaukusi vyrą, liepė nukirpti Samsonui septynias galvos garbanas. Jo jėga dingo."},
	{20, "20 Ji tarė: “Samsonai, filistinai puola!” Pabudęs iš miego, jis galvojo, kad bus taip, kaip anksčiau. Bet jis nežinojo, kad Viešpats pasitraukė nuo jo."},
	{21, "21 Filistinai, nutvėrę jį, išdūrė jam akis, nusivedė į Gazą ir, sukaustę varinėmis grandinėmis, pristatė jį sukti girnas kalėjime."},
	{22, "22 Tuo laiku jo galvos plaukai pradėjo ataugti."},
	{23, "23 Filistinų kunigaikščiai susirinko aukoti gausių aukų savo dievui Dagonui ir pasidžiaugti laimėjimu. Jie sakė: “Mūsų dievas atidavė į mūsų rankas Samsoną, mūsų priešą”."},
	{24, "24 Žmonės, jį matydami, garbino savo dievą: “Mūsų dievas atidavė į mūsų rankas mūsų priešą, kuris nusiaubė mūsų šalį ir daug mūsiškių išžudė”."},
	{25, "25 Įsilinksminę jie tarė: “Pašaukite Samsoną, kad jis mus palinksmintų”. Jie atvedė Samsoną iš kalėjimo ir jį pastatė tarp kolonų, kad juos linksmintų."},
	{26, "26 Samsonas sakė jaunuoliui, kuris vedė jį už rankos: “Privesk mane prie kolonų, ant kurių laikosi pastatas, kad galėčiau atsiremti į jas”."},
	{27, "27 Pastatas buvo pilnas vyrų ir moterų, ten buvo ir visi filistinų kunigaikščiai. Ant stogo buvo apie tris tūkstančius vyrų bei moterų, kurie žiūrėjo, kaip Samsonas juos linksmino."},
	{28, "28 Samsonas šaukėsi Viešpaties: “Viešpatie Dieve, prašau, atsimink mane! Sustiprink mane, Viešpatie, dar kartą, kad atkeršyčiau filistinams vienu smūgiu už savo akis!”"},
	{29, "29 Samsonas įsirėmė į abi vidurines kolonas, kurios laikė pastatą, į vieną­dešine, į kitą­kaire ranka"},
	{30, "30 ir tarė: “Mirštu drauge su filistinais!” Ir iš visų jėgų pastūmė. Pastatas griuvo ant kunigaikščių ir ant visų jame buvusiųjų. Žuvusiųjų, kuriuos jis užmušė mirdamas, buvo daugiau negu tų, kuriuos užmušė gyvendamas."},
	{31, "31 Po to atėjo jo broliai ir visi giminės. Jie, pasiėmę jį, nunešė ir palaidojo jo tėvo Manoacho kape tarp Coros ir Eštaolio. Samsonas teisė Izraelį dvidešimt metų."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
