struct lv1 poems[] = {
	{1, "1 Kad pēc Saula nāves Dāvids bija pārnācis mājās no kaujas pret Amaleku un bija pavadījis divas dienas Ciklagā, "},
	{2, "2 tad trešajā dienā nāca kāds vīrs no Saula karotāju nometnes, un viņa drēbes bija saplēstas, un zeme bija uz viņa galvas; un, tiklīdz kā tas bija piegājis pie Dāvida, viņš noliecās līdz zemei un nometās uz sava vaiga. "},
	{3, "3 Un Dāvids tam sacīja: No kurienes tu nāc? Un tas viņam atbildēja: Es esmu izbēdzis no Israēla nometnes. "},
	{4, "4 Tad Dāvids viņam sacīja: Kā tur iet? Lūdzu, pastāsti man! Un tas teica: Tauta ir bēgusi no kaujas lauka, un daudzi no karotāju saimes ir krituši un miruši, arī Sauls un viņa dēls Jonatāns ir miruši. "},
	{5, "5 Un Dāvids vēl prasīja šim jauneklim, kas viņam bija šo ziņu atnesis: Kā tu zini, ka Sauls un viņa dēls Jonatāns ir miruši? "},
	{6, "6 Tad tas jauneklis, kas bija viņam ziņu atnesis, sacīja: Pilnīgi nejauši es nonācu Gilboas kalnos; un redzi, Sauls bija atspiedies uz sava šķēpa, un redzi, rati un jātnieki tam lauzās virsū. "},
	{7, "7 Kad viņš apgriezās apkārt un ieraudzīja mani, tad viņš mani sauca, un es atbildēju: lūk, še es esmu! "},
	{8, "8 Un tad viņš man jautāja: kas tu tāds esi? - Un es viņam atbildēju: es esmu amalekietis. "},
	{9, "9 Un viņš man sacīja: nāc šurp pie manis un nonāvē mani! Gan reibonis ir mani pārņēmis, tomēr dzīvība līdz šim pašam laikam vēl ir manī. "},
	{10, "10 Tad es pie viņa piegāju un to nonāvēju, jo es zināju, ka viņš pēc tādas savas krišanas nevar vairs dzīvot. Un tad es paņēmu diadēmu, kas bija uz viņa galvas, un roku sprādzes, kas bija ap viņa elkoņiem, un es atnesu tās šeit manam pavēlniekam. "},
	{11, "11 Tad Dāvids sagrāba savu apģērbu un to saplēsa; un tāpat to darīja vīri, kas bija pie viņa. "},
	{12, "12 Un tie sēroja un raudāja, un gavēja līdz vakaram par Saulu un par viņa dēlu Jonatānu, un par Tā Kunga tautu, un par Israēla namu, jo tie bija krituši no zobena. "},
	{13, "13 Un Dāvids vēl prasīja tam jauneklim, kas viņam bija to paziņojis: No kurienes tu esi? Un viņš atbildēja: Es esmu kāda šeit piemītoša amalekieša dēls. "},
	{14, "14 Tad Dāvids viņam vaicāja: Kā gan tu neesi izjutis bailes, paceldams savu roku, lai Tā Kunga svaidītam ņemtu dzīvību? "},
	{15, "15 Un Dāvids pasauca vienu no jaunekļiem un sacīja: Nāc šurp! Nokauj viņu! Tad tas viņam cirta, ka viņš nomira. "},
	{16, "16 Bet Dāvids vēl viņam uzsauca: Tavas asinis lai paliek uz tavas paša galvas, jo tava paša mute tevi nodeva, liecinādama: es esmu nogalinājis Tā Kunga svaidīto. "},
	{17, "17 Tad Dāvids dziedāja šo raudu dziesmu par Saulu un par viņa dēlu Jonatānu "},
	{18, "18 un pavēlēja, lai to māca Jūdas bērniem - Saula sēru dziesmu; un redzi, tā ir uzrakstīta Varoņu grāmatā: "},
	{19, "19 Tavs krāšņums, ak, Israēl, ir kritis tavos kalnos, ak, kā ir krituši varoņi! "},
	{20, "20 Tikai nepaudiet to Gātā, nesludiniet to kā prieka vēsti Aškalonas ielās, lai filistiešu meitas nepriecājas, lai neapgraizīto meitas negavilē! "},
	{21, "21 Jūs, Gilboas kalni, lai nekrīt uz jums ne rasa, ne lietus, lai uz jums nav tīrumu, no kurienes nāk augļu pirmaju upuru dāvanas, jo tur guļ nomesti varoņu vairogi, arī Saula vairogs, ko neviens vairs nesvaidīs ar eļļu. "},
	{22, "22 Bez caururbto asinīm un varoņu miesu taukiem nekad neatlaidās Jonatāna stops, un nekad neatgriezās bez uzvaras laupījuma mājās Saula zobens. "},
	{23, "23 Sauls un Jonatāns - tik mīlami un dārgi - dzīvībā un nāvē tie netika šķirti; viņi bija ātrāki nekā ērgļi un stiprāki nekā lauvas! "},
	{24, "24 Ak, Israēla meitas! Raudiet par Saulu, kas jūs tērpa dārgā purpurā un greznoja jūsu tērpus ar mirdzoša zelta rotājumiem! "},
	{25, "25 Ak, kā šie varoņi ir krituši cīņas karstumā! Uz taviem kalniem nodurts guļ Jonatāns! "},
	{26, "26 Man tevis žēl, mans brāli Jonatān! Tu biji man ļoti mīļš! Tava mīlestība man bija daudzkārt dārgāka nekā sievu mīlestība! "},
	{27, "27 Ak, kā šie varoņi ir krituši, un kā kara ieroči ir cīņā gājuši bojā!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
