#include <map>
#include <string>
class Bible39
{
	struct lv1	{ int val; const char *msg; };
	struct lv2	{ int val; const char *msg; };
	struct lv3	{ int val; const char *msg; };
public:
static void view1() {
struct lv1 poems[] = {
	{1, "1 Šis ir spriedums par Israēlu, ko Tas Kungs runājis caur pravieti Maleahiju. "},
	{2, "2 Es esmu jūs mīlējis, saka Tas Kungs, un tad jūs prasāt: kā tad Tu esi parādījis pret mums Savu mīlestību? - Vai Ēsavs nav Jēkaba brālis? - saka Tas Kungs, un tomēr Es mīlu Jēkabu "},
	{3, "3 un ienīstu Ēsavu, un Es esmu viņa kalnāju pārvērtis par neauglīgu kailatni un viņa mantojuma tiesu par tuksnesi šakāļiem. "},
	{4, "4 Un kaut arī Edoms sacītu: mēs esam postā likti, bet mēs gribam izpostīto atkal atjaunot! - tad Tas Kungs Cebaots saka tā: ja jūs sāksit celt, tad Es uzcelto atkal sagraušu, un tai zemei būs saukties par bezdievības zemi, un tās tautai būs būt tādai, pret kuru Tas Kungs dusmo mūžīgi. "},
	{5, "5 To jūs redzēsit paši savām acīm, un tad jūs teiksit: Tas Kungs ir dižens un spēcīgs Israēla robežās un arī ārpus tām! - "},
	{6, "6 Dēlam būs godāt savu tēvu un kalpam savu kungu. Ja nu Es esmu tēvs, kur tad nu ir Man parādāmais gods? Ja Es esmu Kungs, kur tad ir pret Mani sajūtamā bijība? - tā saka Tas Kungs Cebaots jums, priesteri, kas jūs nicināt Manu Vārdu, - un tad jūs sakāt: kā tad mēs Tavu Vārdu nicinām? "},
	{7, "7 Jūs to darāt, nesdami uz Mana altāra upurim nešķīstu maizi. Bet jūs tad turpināt: kā tad mēs upurējam Tev kaut ko nešķīstu? - Ar to, ka jūs sakāt: Tā Kunga galds ir nicināms! "},
	{8, "8 Un, kad jūs upurējat kādu aklu dzīvnieku, vai tad tas nav ļaunums, un, ja jūs nesat ziedam ko klibu vai neveselu, vai arī tas nav ļaunums? Ziedo to taču reiz sava apgabala pārvaldniekam. Vai tad viņam būs prieks par to, un vai tad viņš tevi laipni uzlūkos? - tā saka Tas Kungs Cebaots. "},
	{9, "9 Tad nu lūdziet Dievu, lai Viņš mums būtu žēlīgs, - tādas lietas no jūsu puses tiešām ir notikušas. Un vai tad Viņš jūs laipni uzlūkos? - saka Tas Kungs Cebaots. "},
	{10, "10 Kaut jel kāds no jums būtu tūliņ aizslēdzis Tempļa durvis, lai jūs nededzinātu velti uguni uz Mana altāra! Man nav nekādas patikas pret jums, saka Tas Kungs Cebaots, un upuru devas no jūsu rokām Man nav pieņemamas! "},
	{11, "11 Bet no saules lēkta līdz saules rietam Mans Vārds būs augsts un varens tautu starpā, un visās malās Mans Vārds tiks godināts ar kvēpināmo un šķīstu ēdamo upuri, jo Mans Vārds kļūs augsts un varens tautu starpā, saka Tas Kungs Cebaots. "},
	{12, "12 Bet jūs to apgānāt, sacīdami: Tā Kunga galds, tas ir nešķīsts, un tā augļi ir nicināmi. "},
	{13, "13 Un jūs sakāt: redziet, viss tas tikai veltas pūles, - un jūs laižat to vienkārši pār galvu, saka Tas Kungs Cebaots, un jūs ņemat to, kas salaupīts, kas klibs un nevesels, un pienesat to Tam Kungam kā ēdamo upuri. Un Es lai pieņemtu to no jums? - saka Tas Kungs Cebaots. "},
	{14, "14 Nē, lai nolādēts ir krāpnieks, kam viņa ganāmā pulkā ir veselīgs pieaudzis auns, bet kas, dodams Tam Kungam solījumu, ziedo Viņam kādu mazvērtīgu dzīvnieku! Jo Es esmu liels un varens valdnieks, saka Tas Kungs Cebaots, un Mans Vārds ir bijājams tautu vidū!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct lv2 poems[] = {
	{1, "1 Un tagad, priesteri, šis aizrādījums ar pamācību domāts tieši jums: "},
	{2, "2 ja jūs negribat klausīt, nedz arī paši uz to raudzīties, lai jūs godātu Manu Vārdu, saka Tas Kungs Cebaots, tad Es raidīšu Savu lāstu pār jums un pārvērtīšu par lāstiem jūsu pašu svētības vārdus, jo jūs nemaz neņemat pie sirds un neliekat vērā Manus vārdus. "},
	{3, "3 Redzi, Es norāšu visu jūsu sēklu un sviedīšu mēslus jums sejā, jūsu svētku ziedojumu mēslus, un jūs kopā ar tiem tapsit aizslaucīti! "},
	{4, "4 Tad jūs arī izzināsit un sapratīsit, ka tieši Es esmu pieņēmis par jums šādu lēmumu, lai Mana derība ar Leviju varētu pastāvēt, saka Tas Kungs Cebaots, "},
	{5, "5 jo Mana derība ar viņu bija dzīvības un miera derība; kā vienu, tā otru Es piešķīru viņam, lai viņš tiešām Mani bītos un sajustu godbijību Mana Vārda priekšā. "},
	{6, "6 Patiesības likums bija viņa mutē, un nekāda ļaunuma, nedz netaisnības nebija uz viņa lūpām. Mierīgi un ar atklātu sirdi viņš virzījās Man pa priekšu un atgrieza daudzus no grēkiem. "},
	{7, "7 Jo priestera lūpām būs glabāt īsto atziņu, lai viņa mutes vārdos varētu meklēt īsto Dieva bauslību; ne velti taču priesteris ir Dieva, Tā Kunga Cebaota, sūtnis. "},
	{8, "8 Bet jūs esat novērsušies no pareizā ceļa un radāt daudzos neapmierinātību ar savu bauslības pildīšanu un skaidrošanu, un esat Levija derību lauzuši, saka Tas Kungs Cebaots, "},
	{9, "9 tādēļ arī Es esmu pret jums rīkojies tā, lai jūs tiktu visas tautas nicināti un visu ļaužu priekšā par necienīgiem turēti, jo jūs nestaigājat Manis norādītos ceļus un augstāk par bauslību izceļat atsevišķus cilvēkus. "},
	{10, "10 Vai mums visiem nav viens tēvs? Vai mūs visus nav viens Dievs radījis? Kāpēc tad mēs cits citu neieredzam un laupām svētumu Dieva slēgtajai derībai ar mūsu tēviem? "},
	{11, "11 Tiešām, Jūda ir kļuvis vārda lauzējs, un šausmu lietas notiek Jeruzālemē un visā Israēlā, jo Jūda pazemo to, kas Tam Kungam ir svēts un kas Viņam labpatīk, un ir padarījis par savu sievu kāda sveša dieva meitu. "},
	{12, "12 Bet Tas Kungs iznīdēs to, kas tā dara, no Jēkaba nama, abus, tiklab ierosinātāju, kā arī ierosināto un tāpat arī to, kas Tam Kungam Cebaotam nes ēdamo upuri! "},
	{13, "13 Un tālāk jūs darāt arī tā. Jūs nemitīgi apslakāt Tā Kunga altāri ar asarām, jūs liekat atskanēt pār to bezgalīgi daudz raudām un vaidiem, tā ka Es nevaru vairs mierīgi uzlūkot ēdamo upuri, nedz arī saņemt ko patīkamu no jūsu rokām. "},
	{14, "14 Un tomēr jūs prasāt: kāpēc tas tā? - Tāpēc, ka Tas Kungs ir bijis par liecinieku tev un tavu jaunības gadu sievai, kuru tu tagad esi atstūmis un ienīsti, kaut viņa bija tava līdzgaitniece un tavas paša derības sieva. "},
	{15, "15 Tā nerīkojās arī viņš, tas vienīgais, jūs sakāt, liela gara cilvēks. - Bet ko tad darīja šis vienīgais? Viņš meklēja viņam Dieva apsolīto sēklu! Tāpēc esiet nomodā par savu garu, un lai neviens nenonicina savu jaunības gados apņemto sievu! "},
	{16, "16 Bet, kas uz viņu dusmo un padzen viņu, saka Tas Kungs, Israēla Dievs, tas aptraipa savu tērpu ar varmācības negodu, saka Tas Kungs Cebaots, tādēļ esiet nomodā par savu garu un neesiet neuzticīgi pret viņu - šādu sievu! "},
	{17, "17 Jūs noskaņojat pret sevi To Kungu nelabvēlīgi, jūs apkaitināt Viņu ar savām runām. Un tomēr jūs vēl jautājat: ar ko tad mēs Viņā radām šo nelabvēlību? - Ar to, ka jūs runājat: katrs, kas dara ļaunu, ir labs Tā Kunga acīs, un uz tādiem Viņam ir labs prāts, - vai: kur tad ir Dievs, kas sodītu?"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct lv3 poems[] = {
	{1, "1 Redzi, Es sūtīšu Savu sūtni, kam būs sataisīt Manu ceļu Manā priekšā. Un tad drīz ieradīsies Savā namā Tas Kungs, kuru jūs kārojat. Raugi, Viņš jau nāk! saka Tas Kungs Cebaots. "},
	{2, "2 Bet kas varēs izturēt Viņa atnākšanas dienu, un kas varēs pastāvēt, kad Viņš ieradīsies? - Jo Viņš ir kā zeltkaļa kausētāja uguns un kā veļas mazgātājas sārms. "},
	{3, "3 Viņš sēdēs un kausēs un dzidrinās sudrabu, un Viņš šķīstīs Levija bērnus un pārkausēs viņus pamazām kā zeltu un sudrabu arvien tīrākus. Tad viņi nesīs Tam Kungam ēdamo upuri īstā taisnīgumā, "},
	{4, "4 un Tam Kungam tad labi patiks Jūdas un Jeruzālemes ēdamais upuris kā senajos laikos un priekš ilgiem gadiem. "},
	{5, "5 Un tad Es nākšu pie jums un jūs tiesāšu, un Es nodošu uz vietas Savu taisno liecību pret burvjiem, laulības pārkāpējiem, zvērestu lauzējiem un tiem, kas izturas varmācīgi un netaisni pret algādžiem, atraitnēm un bāriņiem, kas nomāc ienācējus svešiniekus un Manis nebīstas! saka Tas Kungs Cebaots. "},
	{6, "6 Jo Es esmu Tas Kungs, un Es nepārveidojos, un jums, Jēkaba bērniem, vēl nebūs būt pagalam. "},
	{7, "7 Jau kopš savu tēvu laikiem jūs arvien esat novērsušies no Maniem baušļiem un neesat tos ievērojuši. Tad nu atgriezieties tagad pie Manis, un arī Es gribu atgriezties pie jums, saka Tas Kungs Cebaots, tad jūs atkal sakāt: kā tad mēs lai atgriežamies? "},
	{8, "8 Vai ir pareizi, ka cilvēks krāpj Dievu, kā jūs Mani krāpjat? Jūs tad sakāt: kā tad mēs Tevi krāpjam? - ar desmito tiesu un labprātīgiem upuriem. "},
	{9, "9 Tādēļ jau jūs esat nolādēti, tā ka viss krīt jums no rokām un iet jūsu rokās bojā, jo jūs visi vienā kopā cits pār citu Mani krāpjat! "},
	{10, "10 Bet atnesiet katrs savu desmito tiesu pilnā vērtībā Manā klētī tā, lai arī Manā mājā būtu barība, un pārbaudiet tad Mani šai ziņā, saka Tas Kungs Cebaots, vai Es arī neatvēršu debess logus un nelikšu svētībai pa tiem pārpilnībā nolīt pār jums! "},
	{11, "11 Un Es padzīšu, jums palīdzēdams, rijīgo postītāja kukaini no jūsu labības laukiem, lai viņš tos neizposta un lai koks jūsu vīna dārzā jums nebūtu neauglīgs, saka Tas Kungs Cebaots, "},
	{12, "12 lai arī visas tautas jūs daudzinātu par svētlaimīgiem, jo jums pašiem būs lielu labumu un augstas cieņas pilnai zemei būt, saka Tas Kungs Cebaots, "},
	{13, "13 jūs vēršat pret Mani nepiekāpīgu valodu, saka Tas Kungs, un jūs atkal jautājat: ko tad mēs runājam Tev pretī? "},
	{14, "14 Jau ar to vien, ka jūs sakāt: ir velti, ja cilvēks kalpo Dievam, un kāds labums mums no tā, ka mēs turam Viņa baušļus un bēdu pilni panesam visdažādākās dzīves grūtības Tā Kunga Cebaota priekšā? "},
	{15, "15 Tāpēc mēs daudzinām laimīgus esam likuma nievātājus; bezdievīgo skaits pieaug, viņi kārdina Dievu, un tomēr viss viņiem iziet labi! "},
	{16, "16 Bet tie, kas bīstas Dieva, savā starpā mierina cits citu: Tas Kungs visu dzird un ievēro, un Viņa priekšā ir rakstīta piemiņas grāmata tiem, kas to Kungu bīstas un domā par Viņa Vārdu. "},
	{17, "17 Tai dienā, saka Tas Kungs Cebaots, ko Es izraudzīšu, viņiem būs būt Manam īpašumam, un Es viņus saudzēšu, kā vīrs saudzē savu dēlu, kas viņam kalpo. "},
	{18, "18 Un jums savukārt būs jāredz, kāda ir starpība starp taisno un bezdievīgo un starp to, kas Dievam kalpo, un to, kas Viņam nekalpo. "},
	{19, "19 Jo redzi, nāks diena, kas kvēlos kā karsti nokurināta krāsns; tad visi pārgalvīgie nievātāji un ļaundari būs kā rugāji, un šī nākamā diena tiem pielaidīs uguni, saka Tas Kungs Cebaots, un neatstās no viņiem pāri ne saknes, ne stiebrus! "},
	{20, "20 Bet jums, kas jūs Manu Vārdu bīstaties, uzlēks taisnības saule, un jūsos ieplūdīs dziedinājums no šīs saules spārnu gaismas, un jūs iziesit un lēkāsit kā barojamie teļi. "},
	{21, "21 Jūs samīsit bezdievīgos, jo viņiem būs kļūt par pīšļiem zem jūsu kājām tai dienā, kuru Es būšu nolicis, saka Tas Kungs Cebaots, "},
	{22, "22 atcerieties Mozus, Mana kalpa, likumus, ko Es viņam kādreiz pavēlēju izveidot visa Israēla labā Horeba kalnā kopā ar visiem likumiem un tiesām. "},
	{23, "23 Redzi, Es sūtīšu pie jums pravieti Ēliju, iekāms nāks Tā Kunga lielā un šausmu pilnā diena. "},
	{24, "24 Viņam būs jāpiegriež tēvu sirdis bērniem un bērnu sirdis saviem tēviem, lai Man nebūtu jānāk un nebūtu jāapkrauj visa zeme ar Savu lāstu smagumu!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};