struct lv11 poems[] = {
	{1, "1 Kad nu Hacoras ķēniņš Jabīns to dzirdēja, tad viņš sūtīja sūtņus pie Madonas ķēniņa Jobāba un pie Šimronas ķēniņa, un pie Akšafas ķēniņa, "},
	{2, "2 un pie tiem ķēniņiem, kuri dzīvoja ziemeļos, kalnos, kā arī dienvidu klajumos, Kinnerotas ielejā, un rietumos, Doras kāpās, "},
	{3, "3 tāpat arī pie kānaāniešiem austrumos un rietumos, pie amoriešiem un pie hetiešiem, pie ferisiešiem un pie jebusiešiem kalnos un pie hīviešiem Micpas zemē, Hermona kalna pakājē. "},
	{4, "4 Tā viņi izgāja cīņā un visas viņu karapulku nometnes līdz ar tiem un varen daudz ļaužu, kā smiltis jūras malā, un daudz zirgu un kara ratu. "},
	{5, "5 Visi šie ķēniņi sapulcējās un nāca un kopā nometnē uzcēla teltis pie Meromas ūdeņiem, lai karotu pret Israēlu. "},
	{6, "6 Tad Tas Kungs sacīja uz Jozuu: Nebīsties no viņiem! Jo rītdien ap šo laiku Es visus viņus nolikšu nokautus Israēlam pie kājām; tad tu varēsi pārgriezt viņu zirgiem pakaļkāju dzīslas un viņu cīņas ratus sadedzināt ugunī. "},
	{7, "7 Tad Jozua un visa viņa karavīru saime negaidot parādījās pie Meromas ūdeņiem un pēkšņi viņiem uzbruka. "},
	{8, "8 Un Tas Kungs tos nodeva Israēla rokā, un viņi tos kāva un vajāja viņus līdz Sidonai, šai lielajai pilsētai, un līdz Šerapot-Majimai, arī līdz Micpas ielejai austrumos, un tos sakāva, tā ka neviens no tiem nepalika dzīvs. "},
	{9, "9 Un Jozua tiem darīja, kā Tas Kungs viņam bija pavēlējis: viņu zirgus viņš padarīja tizlus, un viņu kara ratus viņš sadedzināja ugunī. "},
	{10, "10 Un Jozua tanī laikā atgriezās atpakaļ un ieņēma Hacoru, un tās ķēniņu viņš sagūstīja un nokāva ar zobena asmeni; jo Hacora bija pirms tam visām šīm ķēniņu valstīm galvaspilsēta. "},
	{11, "11 Un tie nokāva visus iedzīvotājus, kas tur bija, viņus pilnīgi iznīcinādami; un tur nepalika pāri neviena dvēsele, un Hacora viņš sadedzināja ugunī. "},
	{12, "12 Un Jozua ieņēma visas šo ķēniņu pilsētas, sagūstīja visus viņu ķēniņus un nokāva tos ar zobena asmeni, viņus pilnīgi iznīcinādams, kā Mozus, Tā Kunga kalps, bija pavēlējis. "},
	{13, "13 Tomēr tās pilsētas, kas bija nocietinātas kalnos, Israēls nenodedzināja, izņemot vienīgi Hacoru, kurai Jozua lika aiziet bojā liesmās. "},
	{14, "14 Bet visu šais pilsētās gūto laupījumu un visus lopus Israēla bērni paturēja paši sev, cilvēkus turpretī visus nogalināja ar zobena asmeni, tos pilnīgi iznīcinādami; tie neatstāja pāri nevienas dzīvas dvēseles. "},
	{15, "15 Kā Tas Kungs Savam kalpam Mozum bija pavēlējis, tā Mozus pavēlēja Jozuam, un tā arī Jozua darīja; viņš itin neko neatstāja neizpildītu no visa tā, ko Tas Kungs bija Mozum pavēlējis. "},
	{16, "16 Un tā Jozua ieņēma visu šo zemi - gan kalnus, gan visu dienvidu zemi, gan visu Gošenes zemi un ieleju un klajumu un Israēla kalnus un to ielejas, "},
	{17, "17 no Halaka kalna, kas paceļas pretī Seīra kalnam, līdz Baal-Gadai, kas atrodas Libanona ielejā Hermona pakājē,- viņš sagūstīja visus to ķēniņus, tos sita un nogalināja. "},
	{18, "18 Ilgus gadus Jozua karoja pret šiem ķēniņiem. "},
	{19, "19 Tur nebija nevienas pilsētas, kas ar labu būtu padevusies Israēlam, izņemot tos hīviešus, kas dzīvoja Gibeonā; visas pārējās viņš ieņēma ar kauju, "},
	{20, "20 jo tas bija Tā Kunga nolikts, ka viņu sirdis tika nocietinātas karam pret Israēlu, lai iznīcības lāsts tiktu piepildīts un lai tiem netiktu nekāda žēlastība parādīta, bet lai viņi tiktu izdeldēti, kā Tas Kungs Mozum bija pavēlējis. "},
	{21, "21 Tanī laikā Jozua nāca un iznīcināja anakiešus kalnājā ap Hebronu, Debīru, Anābu, kā arī Jūdas un visos Israēla kalnu novados, un Jozua tos pilnīgi iznīcināja kopā ar viņu pilsētām. "},
	{22, "22 Neviena no anakiešiem neatlika Israēla bērnu zemē, un tikai Gazā, Gatā un Ašdodā tie vēl palika. "},
	{23, "23 Tā Jozua ieņēma visu zemi, kā Tas Kungs to Mozum bija pavēlējis, un Jozua to nodeva par īpašumu Israēlam, to dalīdams pēc viņu ciltīm; tad zeme varēja atpūsties no kariem."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
