struct mk1 poems[] = {
	{1, "1 По смртта на Ахава, Моав се одметна од Израилот."},
	{2, "2 А Охозиј падна низ прозорската решетка на својата горна соба, која е во Самарија, и се разболе. Па испрати пратеници и им рече: 'Одете, прашајте го Веелзевула, акаронското божество: ке оздравам ли од оваа болест?' И тие отидоа да прашаат."},
	{3, "3 Тогаш Ангел Господов му кажа на Илија Тесвитецот: 'Стани, пресретни ги пратениците на самарискиот цар и кажи им: ‘Зар во Израилот нема Бог, та сте тргнале да го прашате Веелзевула, акаронското божество?‘"},
	{4, "4 Затоа вака вели Господ: ‘Од постелата на која си легнал, нема да станеш, туку ке умреш‘'. И отиде Илија и им кажа."},
	{5, "5 Се вратија пратениците при Озохија. И тој ги праша: 'Зошто се вративте?'"},
	{6, "6 Тие му одговорија: 'Не пресретна еден човек и ни рече: 'Одете, вратете се при царот, кој ве испратил, и кажете му: вака вели Господ: ‘зар во Израилот нема Бог, та испракаш да го прашаш Веелзевула, акаронското божество?‘ Затоа од посталата на која си легнал, нема да станеш, туку ке умреш.'"},
	{7, "7 Тој ги праша: 'Каков беше на изглед оној човек, што ве пресретна и ви ги кажа тие зборови?'"},
	{8, "8 Тие му одговорија: 'Тој човек целиот беше со влакна и беше опашан преку половината со кожен појас.' Тој рече: 'Тоа е Илија Тесвитецот.'"},
	{9, "9 Тогаш испрати царот при него педесетник со неговите педесетмина. И тој се искачи при него, кога Илија седеше на врвот од гората, и му рече: 'Човеку Божји, царот нареди да слезеш.'"},
	{10, "10 Одговори Илија и му рече на педесетникот: 'Ако сум јас човек Божји, нека падне оган од небото и да те изгори тебе и твоите педесетмина.' И падна оган од небото и го изгори него и неговите педесетмина."},
	{11, "11 Царот испрати друг педесетник со неговите педесетмина. И тој му проговори и рече: 'Човеку Божји, вака вели царот: ‘Слези поскоро!‘'"},
	{12, "12 Илија одговори и му рече: 'Ако сум јас човек Божји, нека падне оган од небото и да те изгори тебе и твоите педесетмина'. И падна оган Божји од небото и го изгоре него и неговите педесетмина."},
	{13, "13 Царот испрати по третпат педесетник со неговите педесетмина. Третиот педесетник стана, та дојде, и падна на коленици пред Илија, го молеше и му зборуваше: 'Човеку Божји, да не биде презрена душата моја и душата на твоите слуги - овие педесетмина - пред твоите очи;"},
	{14, "14 Ете, падна оган од небото и ги изгоре двајцата претходни педесетници со нивните педесетмина; но сега да не биде презрена душата моја пред твоите очи!'"},
	{15, "15 Тогаш му рече Ангелот Господов на Илија: 'Оди со него и не плаши се од него.' И тој стана, та отиде со него при царот."},
	{16, "16 И му рече: 'Вака вели Господ: ‘Поради тоа што испрати пратеници да го прашаат Веелзевула, акаронското божество, како да немаше во Израилот Бог да Го прашаш за словото Негово, - ти нема да станеш од леглото, на кое си легнал, туку ке умреш.'"},
	{17, "17 И тој умре според словото Господово, што го изрече Илија. И стана цар Јорам, братот на Охозија, место него, во втората година на Јорама, син Јосафатов, цар јудејски, бидејки оној немаше син."},
	{18, "18 Другото за Охозија, она што го извршил тој, е опишано во летописите на израилските цареви."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
