#include <string>
class Bible37
{
	struct ro1	{ int val; const char *msg; };
	struct ro2	{ int val; const char *msg; };
public:
static void view1() {
struct ro1 poems[] = {
	{1, "1 În anul al doilea al împăratului Dariu, în ziua întîi a lunii a şasea, Cuvîntul Domnului a vorbit prin proorocul Hagai către Zorobabel, fiul lui Şealtiel, dregătorul lui Iuda, şi către Iosua, fiul lui Ioţadac, marele preot, astfel:"},
	{2, "2 'Aşa vorbeşte Domnul oştirilor: ,Poporul acesta zice: ,N'a venit încă vremea pentru zidirea din nou a Casei Domnului!`"},
	{3, "3 De aceea Cuvîntul Domnului le -a vorbit prin proorocul Hagai astfel:"},
	{4, "4 'Dar pentru voi a venit oare vremea să locuiţi în case căptuşite cu tavan, cînd Casa aceasta este dărîmată?'"},
	{5, "5 Aşa vorbeşte acum Domnul oştirilor: 'Uitaţi-vă cu băgare de seamă la căile voastre!"},
	{6, "6 Sămănaţi mult, şi strîngeţi puţin, mîncaţi, şi tot nu vă săturaţi, beţi, şi tot nu vă potoliţi setea, vă îmbrăcaţi, şi tot nu vă este cald; şi cine cîştigă o simbrie, o pune într'o pungă spartă!"},
	{7, "7 Aşa vorbeşte Domnul oştirilor: ,Uitaţi-vă cu băgare de seamă la căile voastre!"},
	{8, "8 Suiţi-vă pe munte, aduceţi lemne, şi zidiţi Casa! Eu Mă voi bucura de lucrul acesta, şi voi fi proslăvit, zice Domnul.'"},
	{9, "9 'Vă aşteptaţi la mult, şi iată că aţi avut puţin; l-aţi adus acasă, dar Eu l-am suflat. Pentruce? zice Domnul oştirilor. Din pricina Casei Mele, care stă dărîmată, pe cînd fiecare din voi aleargă pentru casa lui."},
	{10, "10 De aceea cerurile nu v'au dat roua, şi pămîntul nu şi -a dat roadele."},
	{11, "11 Am chemat seceta peste ţară, peste munţi, peste grîu, peste must, peste untdelemn, peste tot ce poate aduce pămîntul, peste oameni şi peste vite, şi peste tot lucrul mînilor voastre.'"},
	{12, "12 Zorobabel, fiul lui Şealtiel, Iosua, fiul lui Ioţadac, marele preot, şi toată rămăşiţa poporului, au ascultat glasul Domnului, Dumnezeului lor, şi cuvintele proorocului Hagai, fiindcă Domnul, Dumnezeul lor, îl trimesese. Şi poporul s'a temut de Domnul."},
	{13, "13 Hagai, trimesul Domnului, a zis poporului, după porunca Domnului: 'Eu sînt cu voi, zice Domnul.'"},
	{14, "14 Domnul a trezit duhul lui Zorobabel, fiul lui Şealtiel, dregătorul lui Iuda, şi duhul lui Iosua, fiul lui Ioţadac, marele preot, şi duhul întregei rămăşiţe a poporului. Aşa că ei au venit..., şi s'au pus pe lucru în Casa Domnului oştirilor, Dumnezeul lor,"},
	{15, "15 în ziua a douăzeci şi patra a lunii a şasea, în anul al doilea al împăratului Dariu."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct ro2 poems[] = {
	{1, "1 În a douăzeci şi una zi a lunii a şaptea, Cuvîntul Domnului a vorbit prin proorocul Hagai, astfel:"},
	{2, "2 'Vorbeşte lui Zorobabel, fiul lui Şealtiel, dregătorul lui Iuda, lui Iosua, fiul lui Ioţadac, marele preot, şi rămăşiţei poporului, şi spune-le:"},
	{3, "3 ,Cine a mai rămas între voi din ceice au văzut Casa aceasta în slava ei dintîi? Şi cum o vedeţi acum? Aşa cum este, nu pare ea ca o nimica în ochii voştri?"},
	{4, "4 Acum fii tare, Zorobabel! zice Domnul. Fii tare şi tu, Iosua, fiul lui Ioţadac, marele preot! Fii tare şi tu, tot poporul din ţară, zice Domnul, şi lucraţi! Căci Eu sînt cu voi, zice Domnul oştirilor."},
	{5, "5 Eu rămîn credincios legămîntului pe care l-am făcut cu voi, cînd aţi ieşit din Egipt, şi Duhul Meu este în mijlocul vostru; nu vă temeţi!"},
	{6, "6 Căci aşa vorbeşte Domnul oştirilor: 'Încă puţină vreme, şi voi clătina încă odată cerurile şi pămîntul, marea şi ucatul;"},
	{7, "7 voi clătina toate neamurile; comorile tuturor neamurilor vor veni, şi voi umplea de slavă Casa aceasta, zice Domnul oştirilor.'"},
	{8, "8 'Al Meu este argintul, şi al Meu este aurul, zice Domnul oştirilor.'"},
	{9, "9 'Slava acestei Case din urmă va fi mai mare decît a celei dintîi, zice Domnul oştirilor; şi în locul acesta voi da pacea, zice Domnul oştirilor.'"},
	{10, "10 În a douăzeci şi patra zi a lunii a noua, în anul al doilea al lui Dariu, cuvîntul Domnului a vorbit prin proorocul Hagai, astfel:"},
	{11, "11 'Aşa vorbeşte Domnul oştirilor: ,Pune preoţilor următoarea întrebare asupra Legii:"},
	{12, "12 ,Dacă poartă cineva în poala hainei sale carne sfinţită, şi atinge cu haina lui pîne, bucate ferte, vin, untdelemn, sau o mîncare oarecare, lucrurile acestea vor fi sfinţite?` Preoţii au răspuns: ,Nu!`"},
	{13, "13 Şi Hagai a zis: ,Dacă se atinge cineva, spurcat prin atingerea de un trup mort, de toate aceste lucruri, vor fi ele spurcate oare?` Preoţii au răspuns: ,Vor fi spurcate.`"},
	{14, "14 Atunci Hagai, luînd iarăş cuvîntul, a zis: ,Tot aşa este şi poporul acesta, tot aşa este şi neamul acesta înaintea Mea, zice Domnul, aşa sînt toate lucrările mînilor lor: ce-Mi aduc ei acolo ca jertfă, este spurcat!"},
	{15, "15 Uitaţi-vă dar, cu băgare de seamă, la celece s'au întîmplat pînă în ziua de azi, pînă să se fi pus piatră pe piatră la Templul Domnului!"},
	{16, "16 Atunci, cînd veneau la o grămadă de douăzeci de măsuri, nu erau în ea decît zece; cînd veneau la teasc să scoată cincizeci de măsuri, nu erau în el decît douăzeci!"},
	{17, "17 V'am lovit cu rugină în grîu şi cu tăciune, şi cu grindină; am lovit tot lucrul mînilor voastre. Şi cu toate acestea..., tot nu v'aţi întors la Mine, zice Domnul.'"},
	{18, "18 'Uitaţi-vă cu băgare de seamă, la cele ce s'au petrecut pînă în ziua de azi, pînă în a douăzeci şi patra zi a lunii a noua, din ziua cînd a fost întemeiat Templul Domnului, uitaţi-vă cu băgare de seamă la ele."},
	{19, "19 Mai era sămînţă în grînare? Nici via, nici smochinul, nici rodiul, şi nici măslinul, n'au mai adus nimic. Dar din ziua aceasta, Îmi voi da binecuvîntarea Mea."},
	{20, "20 Cuvîntul Domnului a vorbit a doua oară lui Hagai, în a douăzeci şi patra zi a lunii, astfel:"},
	{21, "21 'Vorbeşte lui Zorobabel, dregătorul lui Iuda, şi spune: ,Voi clătina cerurile şi pămîntul;"},
	{22, "22 voi răsturna scaunul de domnie al împărăţiilor, voi nimici puterea împărăţiilor neamurilor, voi răsturna şi carăle de război şi pe ceice se suie în ele; caii şi călăreţii lor vor fi trîntiţi la pămînt, şi unul va peri ucis de sabia altuia."},
	{23, "23 În ziua aceea, zice Domnul oştirilor, pe tine, Zorobabele, fiul lui Şealtiel, robul Meu, -zice Domnul, -te voi lua şi te voi păstra ca pe o pecete; căci Eu te-am ales, zice Domnul oştirilor.`"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};