struct sq15 poems[] = {
	{1, "1 Atëherë Moisiu dhe bijtë e Izraelit i kënduan këtë kantik Zotit dhe folën duke thënë: Unë do t'i këndoj Zotit, sepse u lartësua shumë; hodhi në det kuaj dhe kalorës."},
	{2, "2 Zoti është forca ime dhe kantiku im, ka qënë shpëtimi im. Ky është Perëndia im, unë do ta përlëvdoj; është Perëndia i atit tim, unë do ta lartësoj."},
	{3, "3 Zoti është një luftëtar, emri i tij është Zoti."},
	{4, "4 Ai hodhi në det qerret e Faraonit dhe ushtrinë e tij, luftëtarët më të mirë të tij u mbytën në Detin e Kuq."},
	{5, "5 Ata mbulohen nga humnera; ranë në fund si një gur."},
	{6, "6 E djathta jote, o Zot, është e mrekullueshme në fuqinë e saj. E djathta jote, o Zot, i dërmoi armiqtë."},
	{7, "7 Me madhështinë e madhërisë sate, ti i përmbys ata që ngrihen kundër teje; ti nxjer jashtë zemërimin tënd, ai i prish ata sikur të ishin kallamishte."},
	{8, "8 Sa të shfrysh nga vrimat e hundëve, ujërat janë grumbulluar, valët janë ngritur si një mur, dhe dallgët janë përforcuar në zemrën e detit."},
	{9, "9 Armiku thoshte: Do t'i ndjek, do t'i arrij, do të ndaj kufomat, lakmia ime do të ngopet mbi ta; do të zhvesh shpatën time, dora ime do t'i shfarosë."},
	{10, "10 Po ti dërgove jashtë frymën tënde dhe deti i mbuloi; u mbytën si plumbi në ujërat e fuqishme."},
	{11, "11 Kush barazohet me ty midis perëndive, o Zot? Kush barazohet me ty, i mrekullueshëm në shenjtëri, i madhërishëm në lëvdata, o çudibërës?"},
	{12, "12 Ti shtrive dorën e djathtë, dhe toka i gëlltiti."},
	{13, "13 Me mëshirën tënde, ke udhëhequr popullin që e ke shpenguar; me forcën tënde e ke çuar drejt banesës sate të shenjtë."},
	{14, "14 Popujt e kanë dëgjuar këtë dhe dridhen. Ankthi ka mbërthyer banorët e Filistinës."},
	{15, "15 Që tani krerët e Edomit e kanë humbur, të fuqishmit e Moabit i ka zënë frika, tërë banorët e Kanaanit po shkrihen."},
	{16, "16 Frika dhe tmerri do të bierë mbi ta. Falë forcës së krahut tënd do të bëhen të palëvizshëm si guri, deri sa populli yt, o Zot, të kalojë, deri sa të kalojë populli që ti ke blerë."},
	{17, "17 Ti do t'i futësh dhe do t'i vendosësh në malin e trashëgimisë sate, në vendin që ke përgatitur, o Zot, për banesën tënde, në shenjtëroren që duart e tua, o Perëndi, kanë vendosur."},
	{18, "18 Zoti do të mbretërojë gjithnjë, përjetë."},
	{19, "19 Pasi kuajt e Faraonit me qerret e tij dhe kalorësit e tij kishin hyrë në det, dhe Zoti kishte kthyer mbi ta ujërat e detit, por bijtë e Izraelit kishin ecur në mes të detit, në të thatë."},
	{20, "20 Atëherë profetesha Miriam, motra e Aaronit, mori në dorë dajren, dhe të gjitha gratë e ndoqën nga pas me dajre dhe me valle."},
	{21, "21 Dhe Miriami u këndonte atyre: Këndojini Zotit, sepse u lartësua shumë; hodhi në det kuaj dhe kalorës."},
	{22, "22 Pastaj Moisiu i nisi Izraelitët nga Deti i Kuq dhe këta u drejtuan nga shkretëtira e Shurit; ecën tri ditë në shkretëtirë dhe nuk gjetën ujë."},
	{23, "23 Kur arritën në Mara nuk mundën të pinë ujërat e Marës sepse ishin të hidhura; prandaj ky vend u quajt Mara."},
	{24, "24 Atëherë populli murmuriti kundër Moisiut, duke thënë: Ç'do të pimë?."},
	{25, "25 Kështu ai i bërtiti Zotit; dhe Zoti i tregoi një dru; ai e hodhi atë në ujë, dhe ujërat u bënë të ëmbla. Atje Zoti i dha popullit një ligj dhe një dekret, dhe e vuri kështu popullin në provë,"},
	{26, "26 dhe tha: Në qoftë se ti dëgjon me vëmëndje zërin e Zotit, Perëndisë tënd, dhe bën atë që është e drejtë në sytë e tij dhe dëgjon urdhërimet e tij dhe respekton tërë ligjet e tij, unë nuk do të jap  asnjë nga ato sëmundje që u kam dhënë  Egjiptasve, sepse unë jam Zoti që të shëron."},
	{27, "27 Kështu arritën në Elim, ku kishte dymbëdhjetë burime uji dhe shtatëdhjetë palma; dhe e ngritën kampin e tyre pranë ujërave."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
