struct sq30 poems[] = {
	{1, "1 Do të bësh edhe një altar për të djegur temjanin; dhe do ta bësh prej druri të akacies."},
	{2, "2 Do të jetë një kubit i gjatë dhe një kubit i gjerë; do të jetë katror dhe do të ketë një lartësi dy kubitësh; brirët e tij do të përbëjnë një pjesë të vetme me të."},
	{3, "3 Do ta veshësh me ar safi: pjesën e tij të sipërme, anët e tij rreth e qark dhe brirët e tij; do t'i bësh edhe një kurorë ari."},
	{4, "4 Do t'i bësh edhe dy unaza ari nën kurorë, në të dy krahët e tij; do t'i vësh në të dy krahët e tij për të kaluar shtizat, të cilat shërbejnë për ta mbartur."},
	{5, "5 Shtizat do të bëhen me dru të akacies dhe do t'i veshësh me ar."},
	{6, "6 Do ta vendosësh altarin përpara velit që ndodhet përpara arkës së dëshmisë, përballë pajtuesit që ndodhet mbi dëshminë, ku unë do të të takoj."},
	{7, "7 Mbi të Aaroni do të djegë temjan të parfumuar; do ta djegë çdo mëngjes, kur rregullon llambat."},
	{8, "8 Kur Aaroni ndez llambat në të ngrysur, do të djegë temjanin: një temjan i vazhdueshëm përpara Zotit, për brezat e ardhshëm."},
	{9, "9 Nuk do të ofroni mbi të as temjan të huaj, as olokaust, as blatim; dhe mbi të nuk do të derdhni libacione."},
	{10, "10 Dhe Aaroni do të bëjë një herë në vit shlyerjen e mëkatit mbi brirët e tij; me gjakun e flisë së shlyerjes së mëkatit do të bëjë mbi të shlyerjen një herë në vit, brez pas brezi. Do të jetë gjë shumë e shenjtë, e shenjtë për Zotin."},
	{11, "11 Zoti i foli akoma Moisiut, duke thënë:"},
	{12, "12 Kur do të bësh llogarinë e bijve të Izraelit, për regjistrimin e tyre, secili do t'i japë Zotit shpengimin e jetës së tij, kur do të numërohen, me qëllim që të mos goditen nga ndonjë plagë, kur do të bësh regjistrimin e tyre."},
	{13, "13 Kjo është ajo që do të japë secili nga ata që paguajnë taksë: gjysmë sikli, simbas siklit të shenjtërores, (sikli është baras me njëzet gere), një gjysmë sikli do të jetë oferta që duhet t'i bëhet Zotit."},
	{14, "14 Kushdo që do të përfshihet nga regjistrimi, njëzet vjeç e lart, do t'i japë Zotit këtë ofertë."},
	{15, "15 I pasuri nuk do të japë më tepër, as i varfëri më pak se gjysmë sikli, kur do t'i bëhet kjo ofertë Zotit për të shlyer fajet e jetës suaj."},
	{16, "16 Do të marrësh, pra, nga djemtë e Izraelit këto të holla të shërbimit dhe do t'i përdorësh për shërbimin e çadrës së mbledhjes: kjo do të jetë për bijtë e Izraelit një kujtim para Zotit për të bërë shlyerjen për jetën tuaj."},
	{17, "17 Zoti i foli akoma Moisiut, duke i thënë:"},
	{18, "18 Do të bësh edhe një legen prej bronzi, me bazën e tij prej bronzi, për t'u larë; do ta vendosësh midis çadrës së mbledhjes dhe altarit dhe do të vësh edhe ujë."},
	{19, "19 Dhe aty Aaroni dhe bijtë e tij do të lajnë duart dhe këmbët."},
	{20, "20 Kur do të hyjnë në çadrën e mbledhjes, do të lahen me ujë, që të mos vdesin; kështu edhe kur do t'i afrohen altarit për të shërbyer, për të tymosur një ofertë të bërë Zotit me anë të zjarrit."},
	{21, "21 Do të lajnë duart dhe këmbët e tyre, që të mos vdesin. Ky do të jetë një rregull i përjetshëm, për atë dhe për pasardhësit e tij, brez pas brezi."},
	{22, "22 Zoti i foli akoma Moisiut, duke i thënë:"},
	{23, "23 Gjej edhe aromat më të mira: pesëqind sikla mirrë të lëngët, dyqind e pesëdhjetë, domethënë gjysma, cinamon aromatik dhe dyqind e pesëdhjetë kanellë aromatike,"},
	{24, "24 pesëqind sikla, në bazë të siklit të shenjtërores, kasie dhe një hin vaj ulliri."},
	{25, "25 Me këto të përgatitësh një vaj për vajosjen e shenjtë, një parfum të prodhuar me artin e parfumierit: ky do të jetë vaji i vajosjes së shenjtë."},
	{26, "26 Me të do të vajosësh çadrën e mbledhjes dhe arkën e dëshmisë,"},
	{27, "27 tryezën dhe të gjitha veglat e tij, shandanin dhe të gjitha përdorëset e tij, altarin e temjanit,"},
	{28, "28 altarin e olokausteve dhe të gjitha përdorëset e tij, legenin dhe bazën e tij."},
	{29, "29 Do të shenjtërosh kështu këto gjëra dhe do të jenë shumë të shenjta; të gjitha sendet që u përkasin, do të jenë të shenjta."},
	{30, "30 Do të vajosësh edhe Aaronin dhe bijtë e tij dhe do t'i shenjtërosh, me qëllim që të më shërbejnë si priftërinj."},
	{31, "31 Pastaj do t'u flasësh bijve të Izraelit, duke u thënë: Ky do të jetë për mua një vaj për vajosjen e shenjtë, brez pas brezi."},
	{32, "32 Nuk do ta derdhni mbi mish njeriu dhe nuk do të përgatisni tjetër po ashtu; ai është i shenjtë, do të jetë i shenjtë edhe për ju."},
	{33, "33 Kushdo që përgatit të ngjashëm, apo kushdo që e vë mbi një të huaj, do të shfaroset nga populli i tij."},
	{34, "34 Zoti i tha akoma Moisiut: Gjej disa aroma, storaks, guaska erëmira, galban, aroma me temjan të kulluar, në doza të barabarta;"},
	{35, "35 do të përgatitësh me to një parfum simbas artit të parfumierit, një parfum të kripur, të pastër dhe të shenjtë;"},
	{36, "36 do ta kthesh një pjesë të tij në pluhur shumë të hollë dhe do të vendosësh pak nga ai përpara dëshmisë në çadrën e mbledhjes, ku unë do të të takoj: ai do të jetë për ju një gjë shumë e shenjtë."},
	{37, "37 Por nga temjani që ke për të bërë, nuk do të përgatisni për veten tuaj me të njëjtën përbërje; kjo do të jetë për ty një gjë e shenjtë kushtuar Zotit."},
	{38, "38 Kushdo që do të përgatisë një temjan të këtij lloji, do të shfaroset nga populli i tij."},
};
