struct sq6 poems[] = {
	{1, "1 Ky është urdhërimi, statutet dhe dekretet që Zoti, Perëndia juaj, ka urdhëruar t'ju mësojë, për t'i zbatuar në vendin në të cilin po hyni për ta marrë në zotërim;"},
	{2, "2 me qëllim që ti të kesh frikë nga Zoti, Perëndia yt, të respektosh gjatë gjithë ditëve të jetës sate, ti, yt bir dhe djali i birit tënd, të tëra statutet dhe të gjitha urdhërimet e tij që unë po të  jap, dhe me qëllim që ditët e jetës sate të  zgjaten."},
	{3, "3 Dëgjo, pra, Izrael, dhe ki kujdes t'i zbatosh në praktikë, me qëllim që të njohësh begatinë dhe të shumëzohesh me të madhe në vendin ku rrjedh qumësht dhe mjalt, ashtu si të ka thënë Zoti, Perëndia i etërve të tu."},
	{4, "4 Dëgjo, Izrael, Zoti, Perëndia ynë, është një i vetëm."},
	{5, "5 Ti do ta duash, pra, Zotin, Perëndinë tënd, me gjithë zemër, me gjithë shpirt dhe me tërë forcën tënde."},
	{6, "6 Dhe këto fjalë që sot po të urdhëroj do të mbeten në zemrën tënde;"},
	{7, "7 do t'ua ngulitësh bijve të tu, do të flasësh për to kur rri ulur në shtëpinë tënde, kur ecën rrugës, kur ke rënë në shtrat dhe kur çohesh."},
	{8, "8 Do t'i lidhësh si një shenjë në dorë, do të jenë si shirita midis syve,"},
	{9, "9 dhe do t'i shkruash mbi pllakat e shtëpisë sate dhe mbi portat e tua."},
	{10, "10 Kur pastaj Zoti, Perëndia yt, do të të fusë në vendin për të cilin iu betua etërve të tu, Abrahamit, Isakut dhe Jakobit, të të japë qytete të mëdha dhe të bukura që ti nuk i ke ndërtuar,"},
	{11, "11 dhe shtëpi plot me të mira që ti nuk i ke mbushur, puse të çelur që ti nuk i ke gërmuar, vreshta dhe ullishte që ti nuk i ke mbjellë; kur të kesh ngrënë dhe të jesh ngopur,"},
	{12, "12 ki kujdes të mos harrosh Zotin që të nxori nga vendi i Egjiptit, nga shtëpia e skllavërisë."},
	{13, "13 Do të kesh frikë nga Zoti, Perëndia yt, do t'i shërbesh dhe do të betohesh për emrin e tij."},
	{14, "14 Nuk do të shkoni pas perëndive të tjera midis perëndive të popujve që ju rrethojnë,"},
	{15, "15 (sepse Zoti, Perëndia yt, që ndodhet në mes teje, është një Perëndi ziliqar); përndryshe zemërimi i Zotit, Perëndisë tënd, do të ndizej kundër teje dhe do të bënte që ti të zhdukesh nga faqja e dheut."},
	{16, "16 Nuk do të tundoni Zotin, ashtu si u përpoqët në Masa."},
	{17, "17 Do të respektoni me kujdes urdhërimet e Zotit, Perëndisë tuaj, rregullat dhe statutet e tij që ai ju ka urdhëruar."},
	{18, "18 Prandaj do të bësh atë që është e drejtë dhe e mirë në sytë e Zotit, që ti të përparosh dhe të hysh e të pushtosh vendin e mirë që Zoti iu betua t'u japë etërve të tu,"},
	{19, "19 duke dëbuar tërë armiqtë e tu, ashtu siç kishte premtuar Zoti."},
	{20, "20 Kur në të ardhmen biri yt do të të pyesë: Çfarë kuptimi kanë këto rregulla, statute dhe dekrete, që Zoti, Perëndia ynë, ju ka urdhëruar?,"},
	{21, "21 ti do t'i përgjigjesh birit tënd: Ishim skllevër të Faraonit në Egjipt dhe Zoti na nxori nga Egjipti me një dorë të fuqishme"},
	{22, "22 Përveç kësaj Zoti veproi para syve tona, kreu mrekulli të mëdha dhe të tmerrshme kundër Egjiptit, Faraonit dhe gjithë shtëpisë së tij."},
	{23, "23 Dhe na nxori që andej për të na çuar në vendin që u ishte betuar t'u jepte etërve tanë."},
	{24, "24 Kështu Zoti na urdhëroi të zbatonim në praktikë të gjitha këto statute duke pasur frikë nga Zoti, Perëndia ynë, që të kemi gjithnjë mirëqënie dhe që ai të na mbante të gjallë, siç po ndodh tani."},
	{25, "25 Dhe kjo do të jetë drejtësia jonë në rast se kujdesemi të zbatojmë në praktikë tërë urdhërimet e Zotit, Perëndisë tonë, ashtu siç na ka urdhëruar."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
