static void view11() {
struct sq11 poems[] = {
	{1, "1 Me anë të besimit ne kuptojmë se bota është ndërtuar me fjalën e Perëndisë, sa që ato që shihen nuk u bënë prej gjërave që shihen."},
	{2, "2 Me anë të besimit Abeli i ofroi Perëndisë flijim më të mirë nga ai i Kainit; me anë të tij ai mori dëshmimin se ishte i drejtë, sepse Perëndia dha dëshmin se i pranoi ofertat e tij; dhe me anë të tij, ndonëse vdiq, ai po flet ende."},
	{3, "3 Me anë të besimit Enoku u zhvendos që të mos shikonte vdekje dhe nuk u gjet më, sepse Perëndia e kishte zhvendosur; sepse para se të merrej, ai pati dëshmimin se i kishte pëlqyer Perëndisë."},
	{4, "4 Edhe pa besim është e pamundur t'i pëlqesh Atij, sepse ai që i afrohet Perëndisë duhet të besojë se Perëndia është, dhe se është shpërblenjësi i atyre që e kërkojnë atë."},
	{5, "5 Me anë të besimit Noeu, i lajmëruar hyjnisht për ato që ende nuk shiheshin, u frikësua dhe ndërtoi një arkë për shpëtimin e shtëpisë së tij, me anë të së cilës e dënoi botën dhe u bë trashëgimtar i drejtësisë që është me anë të besimit."},
	{6, "6 Me anë të besimit Abrahami, kur u thirr, u bind të shkojë në atë vend që do të merrte për trashëgim; dhe u nis pa ditur se ku po shkonte."},
	{7, "7 Me anë të besimit Abrahami banoi në tokën e premtuar, si në një dhe të huaj, dhe ndenji në çadra bashkë me Isakun dhe Jakobin, bashkëtrashëgimtarë të të nëjtit premtim,"},
	{8, "8 sepse priste qytetin që ka themelet, mjeshtër dhe ndërtues i të cilit është Perëndia."},
	{9, "9 Me anë të besimit edhe vetë Sara, ndonëse e kishte kaluar moshën, mori fuqi të bëhet me barrë dhe të lindë, sepse e konsideroi besnik atë që i kishte bërë premtimin."},
	{10, "10 Prandaj edhe prej një të vetmi, edhe ky gati për të vdekur, lindën pasardhës të shumtë sa yjet e qiellit dhe si rëra që është në breg të detit dhe që nuk numërohet dot."},
	{11, "11 Të gjithë këta vdiqën në besim, pa marrë premtimet e bëra, por i panë ato për se largu dhe u bindën dhe i përshëndetën ato me gëzim duke rrëfyer se janë të huaj dhe shtegtarë mbi dhe."},
	{12, "12 Sepse ata që flasin të tilla, tregojnë se kërkojnë atdhe."},
	{13, "13 Dhe, po ta kishin pasur në mend atë nga e cila kishin dalë, do të kishin gjetur kohë të ktheheshin atje."},
	{14, "14 Por ata tani dëshirojnë një atdhe më të mirë, domethënë qiellor; prandaj Perëndisë nuk i vjen turp të quhet Perëndi i tyre, sepse u bëri gati atyre një qytet."},
	{15, "15 Me anë të besimit Abrahami, pasi u vu në provë, ofroi Isakun dhe ai që kishte pritur premtimet ofroi birin e tij të vetëm,"},
	{16, "16 ndonëse Perëndia i pati thënë: Te Isaku do të quhet pasardhja jote (që do të mbajë emrin tënd),"},
	{17, "17 sepse Abrahami mendonte se Perëndia ishte i fuqishëm sa ta ringjallte edhe prej së vdekurish; prej të cilëve edhe e mori atë përsëri si një lloj figure."},
	{18, "18 Me anë të besimit Isaku bekoi Jakobin dhe Esaun, për gjërat që do të ndodhnin."},
	{19, "19 Me anë të besimit Jakobi, duke vdekur, bekoi secilin nga bijtë e Jozefit dhe adhuroi duke u mbështetur mbi majën e shkopit të tij."},
	{20, "20 Me anë të besimit Jozefi, duke vdekur, përmendi eksodin e bijve të Izraelit dhe dha porosi për eshtrat e tij."},
	{21, "21 Më anë të besimit Moisiu, si lindi, u fsheh nga prindërit e tij tre muaj, sepse ata panë se fëmija ishte i bukur dhe nuk u frikësuan nga urdhëri i mbretit."},
	{22, "22 Me anë të besimit Moisiu, si u bë i madh, nuk pranoi të thuhet i biri i së bijës së Faraonit,"},
	{23, "23 duke parapëlqyer të keqtrajtohet bashkë me popullin e Perëndisë se sa të gëzonte për një farë kohe dëfrimet e mëkatit,"},
	{24, "24 duke e çmuar dhunën e Krishtit si pasuri më të madhe nga thesaret e Egjiptit, sepse i drejtonte sytë nga shpërblimi."},
	{25, "25 Me anë të besimit e la Egjiptin pa pasur frikë mërinë e mbretit, sepse qëndroi i patundur sikur të shihte të padukshmin."},
	{26, "26 Me anë të besimit kremtoi Pashkën dhe bëri spërkatjen e gjakut, me qëllim që ai i cili i vriste të parëlindurit të mos ngiste ata të Izraelit."},
	{27, "27 Me anë të besimit kaluan nëpër Detin e Kuq si të ishte tokë e thatë, ndërsa kur Egjiptasit u përpoqën ta bëjnë, u mbytën."},
	{28, "28 Me anë të besimit ranë muret e Jerihos, pasi u suallën rreth tyre shtatë ditë."},
	{29, "29 Me anë të besimit Rahabi, një prostitutë, nuk humbi bashkë me ata që nuk besuan, sepse kishte pritur paqësisht zbuluesit."},
	{30, "30 Dhe ç'të them më? Sepse nuk do të më mjaftonte koha, po të doja të tregoja për Jedeonin, Barakun, Sansonin, Jeftin, Davidin, Samuelin dhe për profetët,"},
	{31, "31 të cilët, me anë të fesë nënshtruan mbretërira, realizuan drejtësinë, arritën ato që u premtuan, ua zunë grykën luanëve,"},
	{32, "32 fikën fuqinë e zjarrit, shpëtuan nga tehu i shpatës, nga të dobët u bënë të fuqishëm, u bënë të fortë në betejë, thyen ushtritë e huaja."},
	{33, "33 Gratë morën të vdekurit e tyre me anë të ringjalljes; e të tjerët u torturuan, sepse nuk pranuan çlirimin, për të fituar një ringjallje më të mirë."},
	{34, "34 Dhe të tjerë hoqën përqeshje dhe goditje, madje edhe pranga dhe burgime."},
	{35, "35 U vranë me gurë, me sharrë, u prenë, u vranë nga shpata, u endën të mbuluar me lëkurë dhënsh e dhish, nevojtarë, të pikë-lluar, të keqtrajtuar"},
	{36, "36 (bota nuk ishte e denjë për ta), u sollën nëpër shkretëtira e nëpër male, nëpër shpella dhe nëpër guva të dheut."},
	{37, "37 E pra, të gjithë këta, ndonëse morën dëshmim të mirë me anë të besimit, nuk morën atë që u ishte premtuar,"},
	{38, "38 sepse Perëndia kishte paraparë diçka më të mirë për ne, që ata të mos arrinin në përsosje pa ne."},
	{39, "39 Prandaj edhe ne, duke qenë të rrethuar nga një re kaq e madhe dëshmimtarësh, duke hedhur tej çdo barrë dhe mëkatin që na qarkon vazhdimisht duke na joshur, le të rendim me durim në udhën që është përpara nesh,"},
	{40, "40 duke i drejtuar sytë te Jezusi, kreu dhe plotësonjësi i besimit, i cili, për gëzimin që ishte përpara tij, duroi kryqin duke e përçmuar fyerjen dhe u ul në të djathtën e fronit të Perëndisë."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view12() {
struct sq12 poems[] = {
	{1, "1 Kujtoni, pra, atë që duroi një kundërshtim të tillë nga ana e mëkatarëve kundër tij, që të mos lodheni dhe të ligështoheni në shpirtin tuaj."},
	{2, "2 Ju akoma nuk keni qëndruar deri në gjak, duke luftuar kundër mëkatit,"},
	{3, "3 dhe keni harruar këshillën që ju drejtohet juve porsi bijve: Biri im, mos e përçmo qortimin e Perëndisë dhe mos e humb zemrën kur ai të qorton,"},
	{4, "4 sepse Perëndia ndreq atë që do dhe fshikullon çdo bir që i pëlqen."},
	{5, "5 Në qoftë se ju do ta duroni qortimin, Perëndia do t'ju trajtojë si bij; sepse cilin bir nuk e korigjon i ati?"},
	{6, "6 Por, po të mbeteni të pandrequr, ku të gjithë u bënë pjestarë, atëherë jeni kopila dhe jo bij."},
	{7, "7 Pastaj etërit tanë sipas mishit i patëm për të na ndrequr dhe i nderonim ata; a nuk do t'i nënshtrohemi edhe më tepër Atit të shpirtrave, për të jetuar?"},
	{8, "8 Sepse ata na ndreqën për pak ditë, ashtu siç u dukej më mirë, kurse ai na ndreq për të mirën tonë që të bëhemi pjestarë të shenjtërisë së tij."},
	{9, "9 Çdo ndreqje, pra, aty për aty, nuk duket se sjell gëzim, po hidhërim; por më pas u jep një fryt drejtësie atyre që janë ushtruar me anë të tij."},
	{10, "10 Prandaj forconi duart e kapitura dhe gjunjët e këputur,"},
	{11, "11 dhe bëni shtigje të drejta për këmbët tuaja, që ajo e cila çalon të mos ndrydhet, por më tepër të shërohet."},
	{12, "12 Kërkoni paqe me të gjithë dhe shenjtërim, pa të cilin askush nuk ka për të parë Perëndinë,"},
	{13, "13 duke u kujdesur fort që askush të mos mbetet pa hirin e Perëndisë dhe se mos mbijë ndonjë rrënjë e hidhur dhe t'ju turbullojë dhe me anë të saj të ndoten shumë njerëz;"},
	{14, "14 se mos ndodhet ndonjë kurvar ose profan, si Esau, i cili për një gjellë shiti të drejtën e tij të parëbirnisë."},
	{15, "15 Sepse e dini se, më pas, kur ai deshi të trashëgojë bekimin, iu refuzua, ndonëse e kërkoi me lot, sepse nuk gjeti vend pendimi."},
	{16, "16 Sepse ju nuk iu afruat malit që mund të kapet me dorë dhe që digjet me zjarr, as errësirës, as territ, as stuhisë,"},
	{17, "17 as zërit të borisë, as zërit të fjalëve, të cilin ata që e dëgjuan u lutën që atyre të mos u drejtohej më asnjë fjalë,"},
	{18, "18 sepse nuk mund të duronin urdhërin: Në se edhe një shtazë e prek malin, të vritet me gurë ose me shigjeta;"},
	{19, "19 dhe aq e llahtarshme ishte pamja, sa Moisiu tha: Unë jam i frikësuar dhe po dridhem i tëri."},
	{20, "20 Por ju iu afruat malit Sion dhe qytetit të Perëndisë së gjallë, që është Jeruzalemi qiellor, edhe morisë së engjëjve,"},
	{21, "21 kuvendit universal dhe kishës të të parëlindurve që janë shkruar në qiej, Perëndisë, gjykatësit të të gjithëve, shpirtërave të të drejtëve që u bënë të përsosur,"},
	{22, "22 dhe Jezusit, Ndërmjetësit të Besëlidhjes së re, dhe gjakut të spërkatjes, që flet më mirë se ai i Abelit."},
	{23, "23 Shikoni se mos refuzoni atë që flet, sepse në qoftë se nuk shpëtuan ata që refuzuan të dëgjojnë atë që fliste si orakull mbi dhe, aq më pak do të shpëtojmë ne, po të mos refuzojmë të dëgjojmë atë që flet prej qiellit,"},
	{24, "24 zëri i të cilit e drodhi atëherë dheun, por tashti bëri këtë premtim, duke thënë: Unë edhe një herë do të tund jo vetëm dheun, por edhe qiellin."},
	{25, "25 Dhe kjo fjalë edhe një herë tregon ndryshueshmërinë e gjërave, që tunden, si të bëra me dorë, që të mbeten ato që nuk tunden."},
	{26, "26 Prandaj, duke marrë mbretërinë të patundur, le ta ruajmë këtë hir me anë të të cilit i shërbejmë Perëndisë në një mënyrë të pëlqyeshme, me nderim e me frikë,"},
	{27, "27 sepse Perëndia ynë është një zjarr që konsumon."},
	{28, "28 Dashuria vëllazërore le të mbetet."},
	{29, "29 Mos harroni mikpritjen, sepse disa duke e praktikuar e priten pa ditur engjëj!"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view13() {
struct sq13 poems[] = {
	{1, "1 Kujtoni të burgosurit si të ishit të lidhur bashkë me ta, dhe ata që keqtrajtohen, sepse edhe ju vetë jeni në trup."},
	{2, "2 Martesa të nderohet nga të gjithë dhe shtrati martesor i papërlyer, sepse Perëndia do të gjykojë kurvarët dhe kurorëshkelësit."},
	{3, "3 Sjellja juaj të jetë pa lakmi paraje dhe kënaquni me atë që keni, sepse vetë Perëndia ka thënë: Nuk do të të lë, nuk do të të braktis."},
	{4, "4 Kështu mund të themi, me plot besim: Perëndia është ndihmuesi im dhe unë nuk do të kem frikë; ç'do të më bëjë njeriu?."},
	{5, "5 Kujtoni të parët tuaj, që ju shpallën fjalën e Perëndisë dhe, duke çmuar rezultatin e sjelljes së tyre, merrni si shembull besimin e tyre."},
	{6, "6 Krishti është i njëjtë dje, sot e përjetë."},
	{7, "7 Mos e lëshoni veten aty e këtu prej doktrinah të ndryshme dhe të huaja, sepse është mirë që zemra juaj të forcohet me anë të hirit dhe jo prej ushqimit, të cilat nuk u dhanë asnjë dobi atyre aq sa ecën."},
	{8, "8 Ne kemi një altar prej të cilit nuk kanë të drejtë të hanë ata që i shërbejnë tabernakullit."},
	{9, "9 Sepse trupat e atyre kafshëve, gjaku i të cilave është sjellë prej kryepriftit në shenjtëroren për mëkatin, digjen jashtë fushës."},
	{10, "10 Prandaj edhe Jezusi, për të shenjtëruar popullin me gjakun e vet, pësoi jashtë derës (së qytetit)"},
	{11, "11 Le të dalim, pra, drejt tij jashtë fushës, duke bartur poshtërimin e tij."},
	{12, "12 Sepse nuk kemi këtu qytet të përhershëm, por kërkojmë atë që ka për të ardhur."},
	{13, "13 Me anë të tij, pra, le t'i ofrojmë vazhdimisht Perëndisë një flijim lavdie, domethënë frytin e buzëve që rrëfejnë emrin e tij."},
	{14, "14 Dhe mos harroni bamirësinë dhe t'u jepni ndihmë të tjerëve, sepse Perëndisë i pëlqejnë flijime të tilla."},
	{15, "15 Dëgjoni të parët tuaj dhe nënshtrohuni atyre, sepse ata rrijnë zgjuar për shpirtërat tuaj, si ata që duhet të japin llogari; që ta bëjnë këtë me gëzim dhe jo me psheretima, sepse kjo nuk do t'ju sillte dobi."},
	{16, "16 Lutuni për ne, sepse ne besojmë se kemi ndërgjegje të mirë, duke dashur të sillemi mirë në çdo gjë."},
	{17, "17 Dhe ju bëj thirrje edhe më shumë ta bëni këtë, që unë të kthehem te ju sa më parë."},
	{18, "18 Dhe Perëndia e paqes, që e ngriti nga të vdekurit, me anë të gjakut të Besëlidhjes së amshuar, Bariun e madh të dhënve, Perëndinë tonë Jezu Krisht,"},
	{19, "19 ju bëftë të përsosur për çdo vepër të mirë, që të kryeni vullnetin e tij, duke punuar te ju atë që është e pëlqyer përpara tij, me anë të Jezu Krishtit, të cilit i qoftë lavdi në shekuj të shekujve. Amen."},
	{20, "20 Dhe ju lutem, o vëllezër, ta mbani këtë fjalë këshillimi, sepse ju shkrova shkurt."},
	{21, "21 Ta dini se vëllai Timoteu u lirua nga burgu. Nëse mbërrin së shpejti, unë do të vij te ju bashkë me të."},
	{22, "22 Përshëndetni të gjithë të parët tuaj dhe gjithë shenjtorët! Ju përshëndesin ata të Italisë."},
	{23, "23 Hiri qoftë mbi ju të gjithë! Amen."},
	{24, "24 Jakobi, shërbëtor i Perëndisë dhe i Zotit Jezu Krisht, të dymbëdhjetë fiseve të shpërndarë nëpër botë: përshëndetje!"},
	{25, "25 Ta konsideroni një gëzim të madh, o vëllezër të mi, kur ndodheni përballë sprovash nga më të ndryshmet,"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
