#include <map>
#include <string>
class Bible61
{
	struct sq1	{ int val; const char *msg; };
	struct sq2	{ int val; const char *msg; };
	struct sq3	{ int val; const char *msg; };
public:
static void view1() {
struct sq1 poems[] = {
	{1, "1 Duke qenë se fuqia e tij hyjnore na dhuroi të gjitha gjërat që i takojnë jetës dhe perëndishmërisë, me anë të njohjes së atij që na thirri me lavdinë dhe virtytin e vet,"},
	{2, "2 me anë të të cilave na u dhuruan premtimet e çmueshme dhe shumë të mëdha, që nëpërmjet tyre të bëheni pjestarë të natyrës hyjnore, duke i shpëtuar prishjes që është në botë për shkak të lakmisë."},
	{3, "3 Edhe ju, për të njëjtën arsye, duke bërë çdo përpjekje, t'i shtoni besimit tuaj virtytin, dhe virtytit diturinë,"},
	{4, "4 diturisë vetkontroll, vetkontrollit qëndresën, qëndresës perëndishmerinë,"},
	{5, "5 mëshirës dashurinë vëllazërore dhe dashurisë vëllazërore dashurinë."},
	{6, "6 Sepse nëse këto gjëra gjendën në ju me shumicë, nuk do t'ju lejojnë të bëheni përtacë dhe as të pafryt në njohjen e Zotit tonë Jezu Krisht."},
	{7, "7 Sepse ai që nuk i ka këto gjëra është i verbër dhe dritëshkurtër, sepse harroi pastrimin nga mëkatet e tij të vjetër."},
	{8, "8 Prandaj, vëllezër, përpiquni gjithnjë e më shumë ta përforconi thirrjen dhe zgjedhjen tuaj, sepse, duke bërë këto gjëra, nuk do të pengoheni kurrë."},
	{9, "9 Sepse kështu begatisht do t'ju hapet hyrja në mbretërinë e amshuar të Zotit dhe Shpëtimtarit tonë Jezu Krisht."},
	{10, "10 Prandaj nuk do të lë pas dore t'ju kujtoj vazhdimish këto gjëra, ndonëse ju i dini dhe jeni të patundur në këtë të vërtetë."},
	{11, "11 Por unë mendoj se është e drejtë, përderisa jam në këtë tendë, t'ju mbaj zgjuar duke ua sjellë këto gjëra ndër mend,"},
	{12, "12 duke ditur se, së shpejti më duhet ta lë këtë tendën time, sikurse ma tregoi Zoti ynë Jezu Krisht."},
	{13, "13 Por do të kujdesem që, edhe pas largimit tim, ju të mund t'i kujtoni gjithnjë këto gjëra."},
	{14, "14 Sepse nuk jua bëmë të njohur fuqinë dhe ardhjen e Zotit tonë Jezu Krisht, duke shkuar pas përralla të sajuara me mjeshtri, por sepse jemi dëshmitarë okular e madhështisë së tij."},
	{15, "15 Sepse ai mori nder dhe lavdi nga Perëndia Atë, kur i erdhi ky zë nga lavdia e madhërishme: Ky është Biri im i dashur, në të cilin jam kënaqur."},
	{16, "16 Dhe ne e dëgjuam këtë zë që vinte nga qielli, kur qemë bashkë me të në malin e shenjtë."},
	{17, "17 Ne e kemi edhe më të vërtetuar fjalën profetike, të cilën bëni mirë ti kushtoni kujdes, si një kandil që ndriçon në një vend të errët, derisa të zbardhë dita dhe ylli i dritës të lindë në zemrat tuaja,"},
	{18, "18 duke ditur së pari këtë: që asnjë profeci e Shkrimit nuk është objekt i interpretimeve të veçanta."},
	{19, "19 Sepse asnjë profeci nuk ka ardhur nga vullneti i njeriut, por njerëzit e shenjtë të Perëndisë kanë folur, të shtyrë nga Fryma e Shenjtë."},
	{20, "20 Por ka pasur edhe profetë të rremë midis popullit, ashtu si do të ketë midis jush mësues të rremë, të cilët do të fusin fshehurazi herezi shkatërruese dhe, duke mohuar Zotërinë që i bleu ata, do të tërheqin mbi vete një shkatërrim të shpejtë."},
	{21, "21 Dhe shumë vetë do të ndjekin doktrina shkatërruese të tyre dhe për shkak të tyre udha e së vërtetës do të shahet."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view2() {
struct sq2 poems[] = {
	{1, "1 Dhe në lakminë e tyre do t'ju shfrytëzojnë me fjalë të shpifura, por ndëshkimi i tyre që moti nuk vonon dhe shkatërrimi i tyre nuk po dremit."},
	{2, "2 Sepse, në qoftë se Perëndia nuk i kurseu ëngjëjt që mëkatuan, por i hodhi në tartar të lidhur me pranga terri që të ruhen për gjyq;"},
	{3, "3 dhe nuk kurseu botën e lashtë, por shpëtoi bashkë me shtatë të tjerë Noeun, predikues të drejtësisë, kur solli përmbytjen mbi botën e të pabesëve,"},
	{4, "4 edhe dënoi me shkatërrim qytetet e Sodomës dhe të Gomorës, duke i bërë hi, dhe i bëri shëmbull për ata që në të ardhmen do të jetonin pabesisht,"},
	{5, "5 dhe e liroi të drejtin Lot, që helmohej nga sjellja imorale e këtyre të çoroditurve"},
	{6, "6 (sepse ky i drejtë banonte në mes tyre e mundonte për ditë shpirtin e tij të drejtë duke shikuar dhe duke dëgjuar veprat e tyre të poshtra),"},
	{7, "7 Zoti di t'i shpëtojë nga tundimi të perëndishmit dhe t'i ruajë të padrejtët që të ndëshkohen ditën e gjyqit,"},
	{8, "8 sidomos ata që shkojnë pas mishit në lakminë e fëlliqësisë dhe e përbuzin pushtetin. Ata janë të paturpshëm, arrogant dhe nuk druajnë të blasfemojnë dinjitetet;"},
	{9, "9 ndërsa engjëjt, që janë më të lartë për nga forca e nga fuqia, nuk sjellin kundër atyre gjykime fyese përpara Zotit."},
	{10, "10 Por këta, si shtazë pa mend, të lindura nga natyra që të kapen dhe të shkatërrohen, flasin keq për gjërat që nuk i dinë dhe do të asgjësohen në prishjen e tyre, duke marrë kështu shpërblimin e paudhësisë së tyre."},
	{11, "11 Ata e konsiderojnë kënaqësi ta çojnë ditën ndër qejfe; janë njolla dhe fëlliqësi, dhe lëshohen pas mashtrimeve të tyre, kur marrin pjesë në gostitë tuaja."},
	{12, "12 Sytë i kanë plot me kurorëshkelje dhe nuk pushojnë së mëkatuari; mashtrojnë shpirtrat e paqëndrueshëm, kanë zemër të stërvitur në lakmime, dhe janë bij mallkimi."},
	{13, "13 Ata, pasi e lanë udhën e drejtë, devijuan duke ndjekur udhën e Balaamit, birit të Beorit, i cili e deshi pagën e paudhësisë,"},
	{14, "14 por u qortua për shkeljen e tij: një kafshë barre pa gojë, duke folur me zë njeriu, e ndaloi marrëzinë e profetit."},
	{15, "15 Këta janë burime pa ujë, re që shtyhen nga furtuna, të cilëve u rezervohet errësira e territ përjetë."},
	{16, "16 Sepse duke mbajtur ligjërata jashtë mase të fryra dhe të kota bëjnë për vete, me anë të pasioneve të mishit dhe të paturpësisë, ata që me të vërtetë kishin shpëtuar nga ata që jetojnë në gabim;"},
	{17, "17 ndërsa u premtojnë atyre liri, ata vetë janë skllevër të prishjes, sepse një bëhet skllav i atij që e mundi."},
	{18, "18 Sepse ata që shpëtuan nga ndyrësitë e botës me anë të njohjes së Zotit dhe Shpëtimtarit Jezu Krisht, nëse përsëri ngatërrohen nga këto dhe munden, gjendja e tyre e fundit është më e keqe se e para."},
	{19, "19 Sepse do të qe më mirë për ta të mos e kishin njohur udhën e drejtësisë, se sa, pasi e njohën, t'ia kthejnë shpinën urdhërimit të shenjtë që u qe dhënë."},
	{20, "20 Por atyre u ndodhi ç'ka thotë një fjalë e urtë e vërtetë: Qeni u kthye në të vjellat e veta, dhe dosa e larë u kthye të zhgërryhet në llucë."},
	{21, "21 Shumë të dashurit, kjo është e dyta letër që po ju shkruaj; në të cilat po e mbaj zgjuar mendjen tuaj të kthjellët duke iu drejtuar kujtesës suaj,"},
	{22, "22 që të mbani mend fjalët që u thanë më parë nga profetët e shenjtë dhe nga urdhërimi i vetë Zotit dhe Shpëtimtarit të transmetuar nga ne apostujt."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
static void view3() {
struct sq3 poems[] = {
	{1, "1 Së pari duhet të dini këtë, se në ditët e fundit do të vijnë disa përqeshës, të cilët do të ecin sipas dëshirave të veta,"},
	{2, "2 dhe do të thonë: Ku është premtimi i ardhjes së tij? Sepse, që kur etërit fjetën, të gjitha gjërat mbetën në po atë gjendje si në fillim të krijimit."},
	{3, "3 Sepse ata me dashje harruan se nëpërmjet fjalës së Perëndisë qiejt vinin në eksistencë shumë kohë më parë, dhe se toka doli nga uji dhe u sajua nëpërmjet ujit,"},
	{4, "4 për shkak të të cilit bota e atëhershme u mbulua nga uji dhe humbi,"},
	{5, "5 ndërsa qiejt dhe dheu i sotshëm janë ruajtur nga e njëjta fjalë për zjarrin, të rezervuar për ditën e gjyqit dhe të humbjes së njerëzve të pabesë."},
	{6, "6 Por, shumë të dashur, mos harroni këtë gjë: se për Zotin një ditë është si një mijë vjet, dhe një mijë vjet si një ditë."},
	{7, "7 Zoti nuk vonon plotësimin e premtimit të tij, siç disa besojnë që ai bën; por është i durueshëm ndaj nesh, sepse nuk do që ndokush të humbasë, por që të gjithë të vijnë në pendim."},
	{8, "8 Dhe dita e Zotit do të vijë si një vjedhës natën; atë ditë qiejt do të shkojnë me krismë, elementët do të shkrihen nga të nxehtit dhe veprat dhe toka që janë në të do të digjen krejt."},
	{9, "9 Përderisa të gjitha këto gjëra duhet të shkatërrohen, si të mos duhet të keni një sjellje të shenjtë dhe të perëndishme,"},
	{10, "10 duke pritur dhe shpejtuar ardhjen e ditës së Perëndisë, për shkak të së cilës qiejt marrin flakë do të treten, dhe elementët të konsumuar nga nxehtësia do të shkrihen?"},
	{11, "11 Por ne, sipas premtimit të tij, presim qiej të rinj dhe toka të re, në të cilët banon drejtësia."},
	{12, "12 Prandaj, shumë të dashur, duke i pritur këto gjëra, përpiquni të jeni përpara tij të papërlyer dhe të paqortueshëm, në paqe."},
	{13, "13 Dhe kini parasysh se durimi i Zotit tonë është në shërbim të shpëtimit, sikurse ju ka shkruar edhe vëllai ynë i dashur Pal, sipas diturisë që iu dha;"},
	{14, "14 Ashtu bën ai në të gjitha letrat e tij, ku flet për këto gjëra. Në to ka disa gjëra të vështira për t'u kuptuar, të cilat të paditurit dhe të paqëndrueshmit i shtrëmbërojnë, sikurse bëjnë me Shkrimet e tjera, për përhumbjen e tyre."},
	{15, "15 Ju pra, shumë të dashur, duke i ditur këto gjëra, ruajuni se mos ligështoheni në qëndresën tuaj, të tërhequr nga gabimi i të pabesëve."},
	{16, "16 Por rrituni, në hirin dhe në njohjen e Zotit dhe të Shpëtimtarit tonë Jezu Krisht. Atij i qoftë lavdia, tani dhe përjetë. Amen."},
	{17, "17 Atë që ishte nga fillimi, atë që dëgjuam, atë që pamë me sytë tanë, atë që vështruam dhe që duart tona e prekën lidhur me Fjalën e jetës"},
	{18, "18 (dhe jeta u shfaq dhe ne e pamë e dëshmojmë për të, dhe ju shpallim jetën e përjetshme që ishte pranë Atit dhe që na u shfaq neve),"},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
};