struct tr24 poems[] = {
	{1, "1 Saul Filistlileri kovalamaktan dönünce, Davutun Eyn-Gedi Çölünde olduğu haberini aldı."},
	{2, "2 Saul da Davutla adamlarını Dağ Keçisi Kayalığı dolaylarında arayıp bulmak için, bütün İsrailden üç bin seçme asker alıp yola çıktı."},
	{3, "3 Yolda koyun ağıllarına rastladı. Yakında bir de mağara vardı. Saul ihtiyacını gidermek için mağaraya girdi. Davutla adamları mağaranın en iç bölümünde kalıyorlardı."},
	{4, "4 Adamları, Davuta, ‹‹İşte RABbin sana, ‹Dilediğini yapabilmen için düşmanını eline teslim edeceğim› dediği gün bugündür›› dediler. Davut kalkıp Saulun cüppesinin eteğinden gizlice bir parça kesti."},
	{5, "5 Ama sonradan Saulun eteğinden bir parça kestiği için kendini suçlu buldu."},
	{6, "6 Adamlarına, ‹‹Efendime, RABbin meshettiği kişiye karşı böyle bir şey yapmaktan, el kaldırmaktan RAB beni uzak tutsun›› dedi, ‹‹Çünkü o RABbin meshettiği kişidir.››"},
	{7, "7 Davut bu sözlerle adamlarını engelledi ve Saula saldırmalarına izin vermedi. Saul mağaradan çıkıp yoluna koyuldu."},
	{8, "8 O zaman Davut da mağaradan çıktı. Saula, ‹‹Efendim kral!›› diye seslendi. Saul arkasına bakınca, Davut eğilip yüzüstü yere kapandı."},
	{9, "9 ‹‹Davut sana kötülük yapmak istiyor› diyenlerin sözlerini neden önemsiyorsun?›› dedi,"},
	{10, "10 ‹‹Bugün RABbin mağarada seni elime nasıl teslim ettiğini gözünle görüyorsun. Bazıları seni öldürmemi istedi. Ama ben seni esirgeyip, ‹Efendime el kaldırmayacağım, çünkü o RABbin meshettiği kişidir› dedim."},
	{11, "11 Ey baba, cüppenin eteğinden kesilmiş, elimdeki şu parçaya bak; evet, bak! Cüppenden bir parça kestim, ama seni öldürmedim. Bundan ötürü içimde kötülük ve başkaldırma düşüncesi olmadığını iyice bilesin. Sana kötülük yapmadığım halde sen beni öldürmeye çalışıyorsun."},
	{12, "12 RAB aramızda yargıç olsun ve benim öcümü senden O alsın. Ama ben elimi sana karşı kaldırmayacağım."},
	{13, "13 Eskilerin şu, ‹Kötülük kötü kişilerden gelir› deyişi uyarınca elim sana karşı kalkmayacaktır."},
	{14, "14 İsrail Kralı kime karşı çıkmış? Sen kimi kovalıyorsun? Ölü bir köpek mi? Bir pire mi?"},
	{15, "15 RAB yargıç olsun ve hangimizin haklı olduğuna O karar versin. RAB davama baksın ve beni savunup senin elinden kurtarsın.››"},
	{16, "16 Davut söylediklerini bitirince, Saul, ‹‹Davut oğlum, bu senin sesin mi?›› diye sordu ve hıçkıra hıçkıra ağlamaya başladı."},
	{17, "17 Sonra, ‹‹Sen benden daha doğru bir adamsın›› dedi, ‹‹Sana kötülük yaptığım halde sen bana iyilikle karşılık verdin."},
	{18, "18 Bugün bana iyi davrandığını kanıtladın: RAB beni eline teslim ettiği halde beni öldürmedin."},
	{19, "19 Düşmanını yakalayan biri onu güvenlik içinde salıverir mi? Bugün bana yaptığın iyiliğe karşılık RAB de seni iyilikle ödüllendirsin."},
	{20, "20 Şimdi anladım ki, sen gerçekten kral olacaksın ve İsrail Krallığı senin egemenliğin altında sürecek."},
	{21, "21 Benden sonra soyumu ortadan kaldırmayacağına, babamın ailesinden adımı silmeyeceğine dair RABbin önünde ant iç.››"},
	{22, "22 Davut Saul'un istediği gibi ant içti. Sonra Saul evine döndü. Davut'la adamları da sığınağa gittiler."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
