struct uk13 poems[] = {
	{1, "1 Рік був, як Саул зацарював, і два роки царював над Ізраїлем."},
	{2, "2 І вибрав собі Саул три тисячі з Ізраїля, дві тисячі були з Саулом у Міхмаші та на горі Бет-Елу, а тисяча були з Йонатаном у Веніяминовій Ґів'ї. А решту народу відпустив він кожного до наметів своїх."},
	{3, "3 І побив Йонатан филистимського намісника, що в Ґеві. І почули це филистимляни, а Саул засурмив у сурму по всьому Краю, говорячи: Нехай почують євреї!"},
	{4, "4 А ввесь Ізраїль чув, говорячи: Саул побив филистимського намісника, і тим Ізраїль став ненависним серед филистимлян. І скликаний був народ за Саулом до Ґілґалу."},
	{5, "5 А филистимляни були зібрані воювати з Ізраїлем, тридцять тисяч возів і шість тисяч верхівців, а народу, щодо численности, як піску на морському березі. І вийшли вони, і таборували в Міхмаші, на схід Бет-Авену."},
	{6, "6 А Ізраїльтянин побачив, що скрутно йому, що народ був пригноблений, і народ ховався по печерах, і по щілинах, і по скелях, і по льохах та по ямах."},
	{7, "7 А інші євреї перейшли Йордан до краю Ґада та до Ґілеаду. А Саул був ще у Ґілґалі, а ввесь народ із тривогою поспішав за ним."},
	{8, "8 І чекав він сім день умовленого часу, що призначив Самуїл, та Самуїл не прийшов до Ґілґалу, і народ став розбігатися від нього."},
	{9, "9 І сказав Саул: Приведіть до мене призначене на цілопалення та мирні жертви. І він приніс цілопалення."},
	{10, "10 І сталося, як скінчив він приносити цілопалення, то ось приходить Самуїл. І вийшов Саул, щоб зустріти його, щоб привітати його."},
	{11, "11 І сказав Самуїл: Що ти зробив? А Саул відказав: Бо я бачив, що народ розбігається від мене, а ти не прийшов на умовлений час тих днів. А филистимляни зібралися в Міхмаші."},
	{12, "12 І я сказав: Тепер филистимляни зійдуть до мене до Ґілґалу, а Господнього лиця я ще не вблагав. І я вирішив, і приніс цілопалення!"},
	{13, "13 І сказав Самуїл до Саула: Ти зробив нерозумне! Не послухав ти наказів Господа, Бога свого, що наказав був тобі, бо тепер Господь міцно поставив би аж навіки твоє царство над Ізраїлем."},
	{14, "14 А тепер царство твоє не буде стояти, Господь пошукав Собі мужа за серцем Своїм, і Господь наказав йому бути володарем над народом Своїм, бо ти не виконав, що наказав був тобі Господь."},
	{15, "15 І встав Самуїл, і зійшов із Ґілґалу до Веніяминової Ґів'ї. А Саул перелічив народ, що знаходився з ним, близько шости сотень чоловіка."},
	{16, "16 І Саул і син його Йонатан та народ, що знаходився з ним, сиділи в Веніяминовій Ґеві, филистимляни ж таборували в Міхмаші."},
	{17, "17 І вийшли руїнники з филистимського табору трьома відділами: один відділ звертається на офрійську дорогу до краю Шуал,"},
	{18, "18 і один відділ звертається на дорогу до Бет-Хорону, а один відділ звертається на дорогу до границі, що провадить від Ґе-Цевоїму до пустині."},
	{19, "19 А коваля не було по всім Ізраїлевім Краї, бо филистимляни сказали: Щоб не робили євреї меча чи списа!"},
	{20, "20 І сходив увесь Ізраїль до филистимлян гострити кожен свого плуга, і заступа свого, і сокиру свою, і серпа свого,"},
	{21, "21 коли тупилися вістря плугів, і заступів, і вил, і сокир, і мусіли сходити, щоб направити вістря рожна."},
	{22, "22 І сталося за днів війни, і не знайшлося ані меча, ані списа в руці всього народу, що був з Саулом та з Йонатаном, та був знайдений тільки для Саула та для сина його Йонатана."},
	{23, "23 І вийшла филистимська залога до переходу Міхмашу."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
