static void view3() {
struct ur3 poems[] = {
	{1, "1  خُونریز شہر پر افسوس ! وہ جھوٹ اور لُوٹ سے بالکل بھرا ہے وہ لوٹ مار سے باز نہیں آتا۔ "},
	{2, "2  سُنو! اچانک لی آواز اور پہیوں کی کھڑکھڑاہٹ اور گھوڑوں کا کودنا اور رتھوں کے ہچکولے۔ "},
	{3, "3  دیکھو ! سواروں کا حملہ اور تلواروں کی چمک اور بھالوں کی جھلک اور متقولوں کے ڈھیر اور لاشوں کے تُودے۔لاشوں کی انتہا نہیں۔لاشوں سے ٹھوکر کھاتے ہیں۔ "},
	{4, "4  یہ اس خُوب صورت جادوگرنی فاحشہ کی بدکاری کی کثرت کا نتیجہ ہے کیونکہ وہ قوموں کو اپنی بدکاری سےاور گھرانوں کو اپنی جادوگری سے بیچتی ہے۔ "},
	{5, "5  ربُ الافواج فرماتا ہے دیکھ میں تیرا مُخالف ہوں اور تیرے سامنے سے تیرا دامن اٹھادوں گا اور قوموں کو تیری برہنگی اور مملکتوں کو تیرا ستر دکھلاوں گا۔ہ "},
	{6, "6  اور نجاست تجھ پر ڈالوں گا اور تُجھے رُسوا کُروں گا ہاں تُجھے انگشت نُما کر دُوں گا۔ "},
	{7, "7  اور جو کوئی تجھ پر نگاہ کرے گا تُجھ سے بھاگے گا اور کہے گا کہ نینوہ ویران ہُوا۔ اس پر کون ترس کھاے گا؟ میں تیرے لے تسلی دینے والے کہاں سے لاؤں؟۔ "},
	{8, "8  کیا تو آمون سے بہتر ہے جو نہروں کے درمیان بسا تھا اور پانی اس کی چاروں طرف تھا جس کی شہر پناہ دریا نیل تھا اور جس کی فصیل پانی تھا؟۔ "},
	{9, "9  کُوش اور مصر اس کی بے انتہا توانائی تھے۔ فوط اور لُوبیم اس کے حماتیی تھے۔ (10 ) تو بھی جلا وطن اور اسیر ہُوا۔ اس کے بے سب کُوچوں میں پٹک دے گئے اور اُن کے شُرفا پر قُرعہ ڈالا گیا اور اس کے سب بُزرگ زنجیروں سے جکڑے گے۔ "},
	{10, "11  تو بھی مست ہو کر اپنے آپ کو چھپائے گا اور دشمن کے سامنے سے پناہ ڈھونڈے گا۔ "},
	{11, "12  تیرے سب قلعے انجیر کے درخت کی مانند ہیں جس پر پہلے پکے پھل لگے ہوں جس کو اگر کوئی ہلاے تو وہ کھانے والے کے مُنہ میں گھر پڑیں۔ "},
	{12, "13  دیکھ تیرے اندر تیرے مرد عورتیں بن گے۔ تیری مملکت کے پھاٹک تیرے دشمنوں کے سامنے کُھلے ہیں۔ آگ تیرے اڑبنگوں کو کھا گئی ۔ "},
	{13, "14  تو اپنے مُحاصرہ کے وقت کے لے پانی بھر لے اور اپنے قلعوں کو مضبوط کر۔ گڑھے میں اتر کر مٹی تیار کر اوع اینٹ کا سانچہ ہاتھ میں لے۔ "},
	{14, "15  وہاں آگ تُجھے کھا جائے گی۔ تلوار تُجھے کاٹ ڈالے گی۔ وہ ٹڈی کی طرح تُجھے چٹ کر جاے گی اگرچہ تو اپنے آپ کو چٹ کر جانے والی ٹڈیوں کی مانند فراوان کرے اور فوج ملخ کی مانند بے شُمار ہو جائے۔ "},
	{15, "16  تو نے اپنے سودا گروں کو آسمان کے ستاروں سے زیادہ فراوان کیا۔ چٹ کر جانے والی ٹڈی خراب کر کے اُڑ جاتی ہے۔ "},
	{16, "17  تیرے امراملخ اور تیرے سردار ٹڈیوں کا ہجوم ہیں جو سردی کے وقت جھاڑیوں میں رہتی ہیں اور آفتاب نکلتا ہے تو اُڑ جاتی ہیں اور ان کا مکان کوئی نہیں جانتا۔ "},
	{17, "18  اے شاہ اسور تیرے چرواہے سو گئے۔ سردار لیٹ گئے۔ تیری رعایا پہاڑوں پر پراگندہ ہوگئی ار اس کو فراہم کرنے والا کوئی نہیں۔ "},
	{18, "19  تیری شکستگی لاعلاج ہے۔ تیرا زخم کاری ہے۔ تیرا خال سُن کر سب تالی بجائیں گے کیونکہ کون ہے جس پر ہمشیہ تیری شرارت کا بار نہ تھا؟+ "},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
