static void view50() {
struct vi50 poems[] = {
	{1, "1 Giô-sép bèn cúi đầu xuống mặt cha mình, hôn người và khóc."},
	{2, "2 Ðoạn, Giô-sép biểu mấy thầy thuốc hầu việc mình, dùng thuốc thơm xông cho xác cha; thầy thuốc bèn xông cho Y-sơ-ra-ên."},
	{3, "3 Bốn mươi ngày xông thuốc hầu qua, vì ấy là hạn ngày dùng xông thuốc; dân Ê-díp-tô khóc người trong bảy mươi ngày."},
	{4, "4 Khi mãn tang rồi, Giô-sép bèn nói cùng quần thần Pha-ra-ôn rằng: Nếu tôi được ơn trước mặt các ngươi, xin hãy thuật lại lời nầy cho Pha-ra-ôn nghe:"},
	{5, "5 Cha tôi có biểu tôi thề mà rằng: Nầy, cha sẽ chết, con hãy chôn cha nơi mộ địa ta đã mua sẵn rồi trong xứ Ca-na-an. Vậy bây giờ, tôi phải lên đó chôn cha tôi, rồi sẽ trở xuống."},
	{6, "6 Pha-ra-ôn bèn phán rằng: Hãy trở lên chôn cha ngươi, y như lời người đã bắt thề đó."},
	{7, "7 Giô-sép dời xác cha mình trở lên chôn; quần thần Pha-ra-ôn, các bực trưởng lão trong đền, các bực trưởng lão trong xứ Ê-díp-tô,"},
	{8, "8 hết thảy nội nhà Giô-sép, các anh em và nội nhà cha mình đều đi lên theo đưa với người; trong Gô-sen chỉ còn những đứa trẻ và chiên, cùng bò của họ mà thôi."},
	{9, "9 Lại cũng có đem ngựa và xe theo lên nữa: thật là một đám xác rất đông thay!"},
	{10, "10 Khi đến sân đạp lúa của A-tát, ở bên kia sông Giô-đanh, thì họ làm lễ khóc than rất nên trọng thể tại đó; đoạn, Giô-sép để tang cha trong bảy ngày."},
	{11, "11 Dân Ca-na-an, tức là dân xứ ấy, thấy khóc than nơi sân đạp lúa A-tát, thì nói rằng: Ấy là một đám khóc than trọng thể của dân Ê-díp-tô đó! Bởi cớ ấy người ta gọi cái sân nầy tên là A-bên-Mích-ra-im ở bên kia sông Giô-đanh."},
	{12, "12 Vậy, các con trai Gia-cốp làm theo lời cha trối lại,"},
	{13, "13 dời xác người về xứ Ca-na-an, chôn trong hanh đá của đồng Mặc-bê-la ngang Mam-rê, mà Áp-ra-ham đã mua luôn với đồng ruộng Ép-rôn, người Hê-tít, để dùng làm mộ địa."},
	{14, "14 Chôn cha xong rồi, Giô-sép cùng các anh em và các người đi lên theo đưa xác cha, đều trở xuống xứ Ê-díp-tô."},
	{15, "15 Các anh Giô-sép thấy cha mình chết rồi, thì nói với nhau rằng: Có lẽ Giô-sép sẽ ganh ghét chúng ta, và trả thù việc ác chúng ta đã làm cho người chăng."},
	{16, "16 Các anh bèn sai người đến nói cùng Giô-sép rằng: Trước khi qua đời, cha em có trối rằng:"},
	{17, "17 Hãy nói lại cho Giô-sép như vầy: Ôi! xin hãy tha điều ác, tội phạm của các anh con đi, vì chúng nó đã lỗi cũng con đó; nhưng bây giờ cha xin con hãy tha tội kẻ tôi tớ của Ðức Chúa Trời cha. Nghe qua mấy lời nầy, Giô-sép bèn khóc."},
	{18, "18 Chánh các anh người đến sắp mình xuống dưới chơn mà nói rằng: Các anh đây thật là kẻ tôi tớ của em đó."},
	{19, "19 Giô-sép đáp rằng: Các anh đừng sợ chi, vì tôi há thay mặt Ðức Chúa Trời sao?"},
	{20, "20 Các anh toan hại tôi, nhưng Ðức Chúa Trời lại toan làm điều ích cho tôi, hầu cho cứu sự sống cho nhiều người, y như đã xảy đến ngày nay, và giữ gìn sự sống của dân sự đông đảo."},
	{21, "21 Vậy, đừng sợ, tôi sẽ cấp dưỡng các anh và con cái các anh. Ðoạn, Giô-sép an ủy các anh, và lấy lời êm dịu mà nói cùng họ."},
	{22, "22 Giô-sép cùng nhà cha mình kiều ngụ tại xứ Ê-díp-tô; người hưởng thọ được một trăm mười tuổi."},
	{23, "23 Người thấy được các con cháu Ép-ra-im đến đời thứ ba, và cũng có được nâng niu trên gối mình các con của Ma-ki, tức con trai của Ma-na-se, nữa."},
	{24, "24 Kế, Giô-sép nói cùng các anh em rằng: Em sẽ chết, nhưng Ðức Chúa Trời sẽ đến viếng các anh em thật; đem các anh em về xứ mà Ngài đã thề hứa cùng Áp-ra-ham, Y-sác, và Gia-cốp."},
	{25, "25 Giô-sép biểu các con trai của Y-sơ-ra-ên thề mà rằng: Quả thật, Ðức Chúa Trời sẽ đến viḀ¿ng các anh em; xin anh em hãy dời hài cốt tôi khỏi xứ nầy."},
	{26, "26 Ðoạn, Giô-sép qua đời, hưởng thọ được một trăm mười tuổi. Người ta xông thuốc thơm cho xác Giô-sép, và liệm trong một quan tài tại xứ Ê-díp-tô."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
}
