struct vi1 poems[] = {
	{1, "1 Lời của Nê-hê-mi, con trai Ha-ca-lia. Năm thứ hai mươi, nhằm tháng Kít-lơ, xảy khi tôi đương ở tại kinh đô Su-sơ,"},
	{2, "2 có một người trong anh em tôi tên là Ha-na-ni với vài người Giu-đa đến tôi hỏi thăm chúng về dân Giu-đa đã được thoát khỏi, tức những kẻ đã bị bắt làm phu tù còn sót lại, và hỏi luôn về những việc Giê-ru-sa-lem."},
	{3, "3 Các người ấy nói với tôi rằng: Những kẻ bị bắt làm phu tù còn sót ở lại trong tỉnh, bị tai nạn và sỉ nhục lắm; còn vách thành của Giê-ru-sa-lem thì hư nát và các cửa nó đã bị lửa cháy."},
	{4, "4 Khi tôi nghe các lời ấy, bèn ngồi mà khóc, cư tang mấy ngày; tôi cữ ăn và cầu nguyện Ðức Chúa của các từng trời, mà rằng:"},
	{5, "5 Ôi! Giê-hô-va Ðức Chúa của các từng trời, tức Ðức Chúa Trời cực đại và đáng kinh, hay giữ lời giao ước và lòng nhân từ cùng kẻ nào kính mến Ngài và vâng giữ các điều răn của Ngài!"},
	{6, "6 Tôi xin mắt Chúa hãy đoái đến và lỗ tai Chúa lắng nghe lời cầu nguyện của kẻ đầy tớ Chúa mà tôi hiện lúc này hằng ngày và đêm cầu nguyện vì dân Y-sơ-ra-ên, là các tôi tớ Chúa, ở tại trước mặt Chúa và xưng những tội của dân Y-sơ-ra-ên mà chúng tôi đã phạm với Ngài. Và lại tôi và nhà của tổ phụ tôi cũng có phạm tội."},
	{7, "7 Chúng tôi có làm rất ác tệ nghịch cùng Chúa, không vâng giữ điều răn giới mạng và luật lệ của Chúa đã phán dạy Môi-se, là kẻ tôi tớ Chúa."},
	{8, "8 Xin Chúa hãy nhớ lại lời Chúa đã phán dạy Môi-se, kẻ tôi tớ Chúa, rằng: Nếu các ngươi phạm tội, ta sẽ tan rải các ngươi giữa các dân tộc;"},
	{9, "9 còn nếu các ngươi trở lại cùng ta, gìn giữ làm theo các điều răn của ta, dầu khi kẻ bị tan lạc của các ngươi ở cuối tận trời, ta cũng sẽ từ đó chiêu tập chúng nó về, và dẫn chúng nó đến nơi ta đã chọn đặng cho danh ta ngự tại đó."},
	{10, "10 Vả, chúng là các tôi tớ và dân sự của Chúa, mà Chúa đã cậy quyền năng và tay mạnh mẽ mà chuộc lại."},
	{11, "11 Chúa ôi! lời cầu nguyện của các tôi tớ Ngài vẫn vui lòng kính sợ danh Ngài; ngày nay xin Chúa hãy làm cho tôi tớ Chúa được may mắn, và ban cho nó tìm được sự nhân từ trước mặt người này. Vả, bấy giờ tôi làm quan tửu chánh của vua."},
};
size_t npoems = sizeof poems / sizeof poems[0];size_t i;for (i=0; i < npoems; ++i) {printf("%s\n", poems[i].msg);}
